Table_Collection = { 
	[1] = {id = 1, ItemId = {53500,53501,53502,53503,53504,53505,53506,53507,53508,53509,53510,53511,53512,53513,53514,53515}, mapID = 27, Icon = 'item_task_61', Desc = 'A.W800年，希默梅思带着不死军团攻入克雷斯特汉姆，皇家军团缚手难敌，王城一夜之间变为死城。有人认为，皇家中有“叛变者”向希默梅思泄露军情。一位秘密调查员根据仅存下来的皇家文献，锁定了十五个嫌疑人，并将这十五个人的资料编集为【古城末日“叛变者”档案】。档案中关于“叛变者”的真实身份并未明确提及，但从那些相关物品的信息中可寻得蛛丝马迹。档案详情在春楠那里可以复查。', RewardStr = '完成GLC.A031号档案的收集%s', Quality = 4, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = 'GLC.A031号档案', RedTip = 10001},
	[2] = {id = 2, ItemId = {53516,53517,53518,53519,53520,53521,53522,53523,53524,53525,53526}, mapID = 28, Icon = 'item_task_62', Desc = '记录了四个互为挚友并深受国王恩宠的驱魔人互相间打了一个赌，然而谁也没有真正在意这场赌局。但命运的嘲讽却悄然降临在他们身上，为了挽救他们的好友卡伦赞德，四人使用了不同的方法，却酿成了一场惨痛悲剧。档案详情在影子驱魔人那里可以复查。', RewardStr = '完成GLC.X114号档案的收集%s', Quality = 4, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = 'GLC.X114号档案', RedTip = 10002},
	[3] = {id = 3, ItemId = {53527,53528,53529,53530,53531,53532,53533,53534}, mapID = 37, Icon = 'item_task_56', Desc = '记录了克雷斯特汉姆古城灭亡前夕举行的一场盛大婚礼。桂瑟兰妮与加布里尔王子因偏见产生重重误会，又因爱而最终携手，然而就在婚礼举行之时，希默梅思降临，将一切美好带入了永不会醒来的梦境中。档案详情在墓地守夜人那里可以复查。', RewardStr = '完成GLC.E109号档案的收集%s', Quality = 4, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = 'GLC.E109号档案', RedTip = 10003},
	[4] = {id = 4, ItemId = {53590,53591,53592,53593,53594}, mapID = 30, Icon = 'item_47004', Desc = '传说是由诸神黄昏中战死的女武神背后双翼所化，象征着永恒以及神话，直到今日，上面还存着些许女武神圣洁的魔力。', RewardStr = '完成永恒之翼的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '永恒之翼', RedTip = 10004},
	[5] = {id = 5, ItemId = {53539,53540,53541,53542,53543,53544,53545,53546,53547}, mapID = 28, Icon = 'item_task_60', Desc = '又名“星尘档案”。它记录着隐匿于龙谷中的神秘组织“漆黑部队”的来历。这些以女武神头盔为标识的战士，曾经都是《尼伯龙根指环》史诗中的英雄人物，他们从英灵殿返回人间，守护着龙谷，行踪神秘。犹如星尘，用微光照亮黑暗。', RewardStr = '完成GLC.A014号档案的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = 'GLC.A014号档案', RedTip = 10005},
	[6] = {id = 6, ItemId = {53553,53554,53555,53556,53548,53549,53551,53550,53552}, mapID = 30, Icon = 'item_task_57', Desc = '一场突如其来的瘟疫，改变了古尔顿的一生。古尔顿拥有对瘟疫的超强抵抗力，这使得他成为解决这次灾难的唯一人选，于是王国利用超魔导士的力量，把几乎所有的瘟疫都集中在古尔顿身上。古尔顿攒钱给卡丽莎制作了花冠，古尔顿的心情无疑是非常开心的。但是好景不长，古尔顿身患瘟疫，不得不从人口密集的城市中搬出来。卡丽莎锲而不舍得找寻着古尔顿，终于在城外的密林处找到了他。古尔顿惊恐着，让卡丽莎不要过来，但是卡丽莎却毫不在意…好人难得好报，古尔顿看错了人心。当危险过后，人们会把所有危险都扼杀在摇篮里，生怕自己再受到伤害，人就是这样的生物。当卡丽莎在绞刑架上大声斥责时，这些冷漠的人对此毫无羞愧之心。而大部分民众也是在冷眼旁观，甚至拍手称快。卡丽莎的逝去对古尔顿造成了相当大的打击。古尔顿选择了报复，而这报复又在情理之中。他释放了自己瘟疫，经过压抑的瘟疫爆发起来更加凶残。顷刻之间国王与王国事务官便双双毙命。事情的最后他让他的挚友凯莱尔带走了他的生命，使瘟疫从此消失。', RewardStr = '完成GLC.X157号档案的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = 'GLC.X157号档案', RedTip = 10006},
	[7] = {id = 7, ItemId = {53557,53558,53559,53560,53561,53562}, mapID = 30, Icon = 'item_task_58', Desc = '王宫的二王子离奇失踪后，王都出现了一个名为“暴食者”的诅咒魔人。骑士代顿奉大王子的命令前去讨伐。不久后，三王子殿下失踪了，与此同时，王宫附近出现了一个诅咒魔人，名为“腐朽者”。爱民如子的大王子绝对不会允许他为祸王都，骑士代顿再次奉命讨伐。接着诅咒魔人“争斗者”出现了，他的诅咒带来了一次次争斗，王都因为魔人的诅咒，流血事件不断发生。大王子决定让骑士代顿惩治这个邪恶的魔人，而与此同时，四王子出门狩猎已经许久没有消息传出来了。大王子对外宣称发现第四位诅咒魔人，再次命令骑士代顿前往消灭诅咒魔人。代顿这才知道，这一切，都是大王子为了继承王位所使的阴谋。对大王子产生疑心的代顿并没有第一时间消灭所谓的诅咒魔人。五王子告知代顿，他的三位哥哥都是死于大王子的阴谋。权力是魔鬼，大王子被魔鬼所诱惑，陷害了三位王子，代顿作为大王子手中的利剑，自然有着无法推卸的责任。代顿决定背叛自己所信奉的骑士精神——忠诚！为了维护自己心中的正义，他要杀死大王子！大王子死后，代顿带回被他藏起来的五王子，告诫五王子，权力是每个人心中的恶魔，一定要战胜这个魔鬼！五王子含泪答应了代顿的请求，而代顿在这大厅自刎结束了自己的一生。', RewardStr = '完成GLC.X163号档案的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = 'GLC.X163号档案', RedTip = 10007},
	[8] = {id = 8, ItemId = {53584,53585,53586,53587,53588,53589}, mapID = 29, Icon = 'item_task_59', Desc = '遭遇袭击的爱德华带着不死的魔物来到了一处隐蔽的洞穴，在这里，他找到了一个黑暗心核，借此他脱离了险境，代价是失去了自己的容貌，以及寿命。他是骑士，尽管他身上充斥着黑暗的力量，但是他坚持认为，光明与黑暗只在一念之间。只有邪恶的人，没有邪恶的力量。他决定用这股力量去帮助需要帮助的人，以及…暗中守护着他的爱人。在黑暗魔物大举入侵的最终时刻，黑骑士爱德华用自己所有的力量以及生命，消灭了魔物的头领。他的爱人妮卡最后认出了他的身份，但是逝去的，终究是逝去了…', RewardStr = '完成GLC.X185号档案的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = 'GLC.X185号档案', RedTip = 10008},
	[9] = {id = 9, ItemId = {53563,53564,53565,53566,53567}, mapID = 29, Icon = 'item_43020', Desc = '“米莫斯知道为了守护自己重要的人，只有做好恶性结局的打算，不沮丧逃避，以自己的力量勇往直前，战斗不止！”', RewardStr = '完成米莫斯的战袍的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '米莫斯的战袍', RedTip = 10010},
	[10] = {id = 10, ItemId = {53568,53569,53570,53571,53572}, mapID = 29, Icon = 'item_12503', Desc = '此箭早已存在，只是无形、无迹，箭矢早已存于天地间，只等待时机凝聚起来，只到国难当头的那一刻，任何勇士只要心中有勇气都能射出此箭。', RewardStr = '完成无形箭的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '无形箭', RedTip = 10011},
	[11] = {id = 11, ItemId = {53573,53574,53575,53576,53577,53578}, mapID = 30, Icon = 'item_40736', Desc = '生命魔剑是一柄有战斗欲望的剑，剑拔出来之后如果不沾血就不可以入鞘。剑 的拥有者必须尊重剑的意愿，不然魔剑就会背叛主人、任意杀戮。', RewardStr = '完成生命魔剑的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '生命魔剑', RedTip = 10012},
	[12] = {id = 12, ItemId = {53579,53580,53581,53582,53583}, mapID = 30, Icon = '19', Desc = '过去有位通晓过去未来的魔法师，却得不到国王的器重，预言了古城的毁灭后居然还惨遭王国的追杀，他悲愤的留下一张命运的纸牌后就消失了，再也没人见过他的行踪。', RewardStr = '完成命运纸牌的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '命运纸牌', RedTip = 10013},
	[13] = {id = 13, ItemId = {53595,53596,53597,53598,53599,53600,53601}, mapID = 44, Icon = 'task_certificate', Desc = '亚尔林无疑是一个悲惨的小人物，过去无情的嘲笑和无尽的羞辱使他变得沉默阴狠，他渴望得到强大的力量来改变现状，这种急切的渴望驱使着他做出了一些不可思议的事情。当建筑工人们在钟楼里挖掘出一块伊米尔之心碎片时，古怪的碎片带来了强大的异变魔物，也带给了亚尔林心中无尽的希望。亚尔林精心设计了一个骗局，当工人们都以为他是一个被魔物杀死的悲惨家伙时，他却死而复生地戴上隐匿的面具，行走在黑暗的深渊里。深渊里的恶魔争相向他咆哮着，亡者的灵魂视他为腹中的美餐，但他却旁若无人地行走着，从他口中传出的声音清晰可闻，“亡者的女武神啊，我愿意做你最忠心的仆人，如果你正感到踌躇不决，那么我可以为你指明方向。”他利用了精明的希默梅思，当亡者的大军在黑夜里出征，亚尔林则脱下隐匿的面具安静地等待着即将收获的果实。自私的奥德里奇和贪婪的希默梅思被欲望蒙蔽了双眼，他们只看到眼前的对手，却忽略了隐藏的敌人。潜伏的亚尔林轻易混入了激斗的人群，在混乱中神鬼不知地取走了伊米尔之心碎片，这不仅让愤怒的希默梅思无功而返，也让奥德里奇陷入了无尽的自责之中。这真是一个完美的计划，亚尔林的隐忍和毒辣终于使他如愿以偿。但令人惋惜的是，亚尔林并没有成为最后的赢家。当他面对梦寐以求的神奇碎片时，他的内心并没有感到安稳，反而强大的巨人力量使他慢慢产生异变，这让他备受煎熬。他在内心质问自己，如果变成了一个人人唾弃的魔物，这一切的努力还有什么意义？沉重的压力和心理的落差使他不堪重负，他可以为了强大的力量变得冷漠阴狠，也可以为了强大的力量毁灭一切。当磅礴的力量从指尖划过，这一刻他彻底释放了心中的压抑。狂暴的巨人力量席卷了钟楼的每个角落，也给每个彷徨的生命画上了句点。这是一个悲惨的故事，当那块古怪的碎片被工人们发现时，他们的命运就已经无法改变了。', RewardStr = '完成钟楼异闻录·毁灭卷的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '钟楼异闻录·毁灭卷', RedTip = 10014},
	[14] = {id = 14, ItemId = {53605,53604,53603,53602,53606}, mapID = 45, Icon = 'task_certificate', Desc = '在漫长的岁月里，围绕着钟楼的种种诡异现象，曾经有无数勇敢的探险者先后进入钟楼，然而所有的探索都是毫无结果的，因为进入钟楼的探险者从没有人能活着走出来。吉伯特和他的伙伴们与其他的探险者一样，他们怀揣着对艾尔帕兰的热爱和无所畏惧的勇气，踏上了前途未知的发现之旅。他们不知道在前面等待着的会是多么严峻的考验，他们也不知道，此刻的钟楼早已在恶魔耶梦加得的掌控下变得冷酷血腥。恶魔的眼神里没有丝毫怜悯，他利用了伊米尔之心的力量，甚至连大贤者留下的黄金时钟也成为他加速时空异变的工具，而吉伯特他们对此竟然毫不知情。一时间，钟楼内混乱的时空里形成了无数可怕的时间漩涡，任何踏入时间漩涡的人将永远从这个世界上消失。吉伯特和所有的探险者一样，遭遇了裂变时空的吞噬。但他是幸运的，机智灵活的反应使他幸免于难，然而他的伙伴们却永远离开了他，他永远记得那些熟悉的身影通过时空甬道的传送消失在无数个平行世界的尽头。明白一切的吉伯特并没有畏缩地逃跑，他将探险的任务坚持到了最后。当他看着面前有些惊讶的恶魔，这个冷血的杀手，他以自己最无畏的灵魂向恶魔发出了最后一击……', RewardStr = '完成钟楼异闻录·噩梦卷的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '钟楼异闻录·噩梦卷', RedTip = 10015},
	[15] = {id = 15, ItemId = {53607,53608,53609,53610}, mapID = 45, Icon = 'item_task_48', Desc = '百年前，艾尔帕兰城外有块能让人忘记时间的石头，歇脚的路人只要一坐在此块石头上便忘记了所有时间和烦恼，不肯再走一步路，城里的一个年轻盗贼请工匠将石头打造成一把匕首，用于行刺。据说用这把匕首靠近敌人，敌人连反抗的意愿都没有，只会乖乖伏降。', RewardStr = '完成时光匕首的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '时光匕首', RedTip = 10016},
	[16] = {id = 16, ItemId = {53611,53612,53613,53614,53615,53616,53617}, mapID = 44, Icon = 'item_task_31', Desc = '根据诺德温的描述，他曾经在某个磁场活跃的洞穴里看到了一角未来，那是天崩地裂，山河破碎的末日场景，他看到了伊斯鲁得倒下的剑士雕像，看到了吉芬损坏的吉芬高塔，那角未来消失之后，他得到了这个发着光的轮盘，他至今不知道开启轮盘的方法。', RewardStr = '完成时间轮盘的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '时间轮盘', RedTip = 10017},
	[17] = {id = 17, ItemId = {53618,53619,53620,53621,53622,53623}, mapID = 45, Icon = 'item_52821', Desc = '来自神秘时空乱流的特殊晶体，通体紫色光泽，四周还包裹着一阵氤氲的淡紫色雾气，纳鲁认为他是来自神秘星空，是星空的馈赠，其中蕴含不知名的力量，似乎与那些异世界来客身上的力量一样……', RewardStr = '完成星界棱晶的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '星界棱晶', RedTip = 10018},
	[18] = {id = 18, ItemId = {53624,53625,53626,53627,53628,53629,53630}, mapID = 44, Icon = 'task_certificate', Desc = '尼克西为人贪婪而好占便宜，因此他被伯纳德所收买。雷萨吉为人傲慢且刚愎自负，因此他被伯纳德所利用。海廷斯暴躁且崇尚暴力，因此他最终被无尽魔物拖死。伯纳德奸诈且做事刁钻，不留余地，因此遭到其余三人在事情发生后无情地舍弃。至于黑鲁，他从未改变过，懦弱且依赖他人，因此最终他的灵魂被魔鬼吞噬。黑鲁死去，遗留的死亡丧钟给予人们警示，傲慢、贪婪、暴躁、奸诈、懦弱，这些人性的弱点，将在时间的流逝之下逐渐暴露。而到了那时，死亡丧钟将会再一次敲响。', RewardStr = '完成钟楼异闻录·丧钟卷的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '钟楼异闻录·丧钟卷', RedTip = 10019},
	[19] = {id = 19, ItemId = {53631,53632,53633,53634,53635}, mapID = 45, Icon = 'task_certificate', Desc = '时间是世界上一切成就的土壤。时间给空想者痛苦，给创造者幸福。德斯黎便是创造者，他在创造一个通向安全的方向牌。他决定扮成鬼影，驱逐那些误入这里的人们。同时他又是勇敢的，他为了救阿奇索伦永陷黑暗，即使在这个时候，他也没有忘记让蒙妮卡接替了他的工作。在这世界上并不缺少创造者，区别在于，一个创造出真正的幸福，而另一个只是幻想罢了。', RewardStr = '完成钟楼异闻录·魅影卷的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '钟楼异闻录·魅影卷', RedTip = 10020},
	[20] = {id = 20, ItemId = {53636,53637,53638,53639}, mapID = 42, Icon = 'item_60003', Desc = '森灵一族的至宝，木、水、土三灵石被伊米尔之心的力量所污染，三灵石滋养下的森灵之心即将受到污染的时候，森灵王果断切断了森灵之心与灵石的联系，虽然森灵之心没有遭受污染，但是失去了三灵石的供养，森灵之心逐渐失去了力量，现在森灵王正在寻找让森灵之心重新恢复活力的方法。', RewardStr = '完成森灵之心的收集%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '森灵之心', RedTip = 10021},
	[21] = {id = 21, ItemId = {53644,53645,53646}, mapID = 43, Icon = 'item_5261', Desc = '时钟守卫者的头领默斯曼·岁月为冒险者颁发的奖章，奖励冒险者在巨蛇事件中为艾尔帕兰做出的贡献。艾尔帕兰举办了一场盛大的婚礼，新郎诺曼是时钟守卫者领袖默斯曼·岁月的得力助手，冒险者们受邀参加这场浪漫的婚礼，婚礼现场新郎却被毒死，一场悲伤的婚礼揭露了巨蛇耶梦加得的阴谋，牵连出几千年前神族的往事。巨狼芬里尔因为爱神芙蕾雅的诡计被锁在月亮之上，诸神黄昏以后，神族的力量被极大的削弱，月亮上芬里尔的封印也因此而削弱，芬里尔的弟弟耶梦加得希望搭建通向月亮的桥梁拯救哥哥。然而耶梦加得在诸神黄昏中受了重伤，神力削弱的只有全盛时期的万分之一，偶然之间他发现了艾尔帕兰的黄金钟楼蕴含了强大的力量，似乎与巨人伊米尔的力量有些相似，他躲入黄金时钟里修养，等待着一个契机。直到有一天，一位名叫诺曼的年轻商人出现在钟楼，吵吵闹闹的说着什么要为默斯曼分忧，这个声音扰乱了他的休眠，也让他意识到时机到来了。他给诺曼制造了一段虚假的记忆，让诺曼认为自己有个名叫瑞德的弟弟，又化身为人类的模样，饰演诺曼的弟弟，成功混入了时钟守卫者的内部。他利用诺曼对他的信任，不断搜集与黄金时钟有关系的信息，最后成功煽动时钟守护者内部叛变，召唤出古代巴风特，意图杀害默斯曼·岁月。没想到召唤出的魔物——古代巴风特根本不受他的控制，反而想要吞噬他，危机之下诺曼不顾性命救了耶梦加得，也因此受了重伤。耶梦加得被诺曼的行为打动了，他从小被众神排斥，最重要的哥哥也被关在月亮上，从没有人真心对待过他。为了诺曼，耶梦加得与人类合作击败了古代巴风特，把它送回了过去的时间，遗憾的是诺曼的伤势太重，他没有支撑多久就离世了。耶梦加得也因此非常自责，他开始思考自己利用黄金时钟搭建桥梁这件事情是不是正确的。而事情的发展没有给他很多的时间思考，他在钟楼里的所作所为，让这个本来就岌岌可危的空间产生了时间黑洞。另外一个世界的耶梦加得穿越黑洞而来，在那个世界，“耶梦加得”没有选择搭建桥梁拯救哥哥，而是自私的吞噬了黄金时钟的力量。另一个时间线的耶梦加得失去了理智，成为了一个只追求力量的怪物，他为自己取名叫“轮回梦魇”，游走在不同的世界里，不停吞噬着力量强大的一切。轮回梦魇利用耶梦加得不小心造成的黑洞来到这个世界，他嘲讽这个世界的冒险者和仍保有真情的耶梦加得。耶梦加得心情复杂的看着另外一个自己，他意识到如果没有诺曼，也许他也会成为这样一个怪物。他决定要帮助人类击败另一个世界的自己。得到耶梦加得帮助的冒险者打败了轮回梦魇，把它赶回了时间黑洞中，然后封印了黑洞。当一切尘埃落定，耶梦加得，遵守了他与诺曼的诺言，放弃使用黄金时钟拯救魔狼，离开了艾尔帕兰，踏上新的旅途，寻求着其他的途径拯救哥哥。而所有在此战之中表现英勇的冒险者，都被授予了时钟守卫者勋章，纪念这场发生在艾尔帕兰的危机与这一条亦正亦邪的巨蛇。', RewardStr = '完成时钟守卫者勋章的收集后%s', Quality = 4, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '时钟守卫者勋章', RedTip = 10022},
	[23] = {id = 23, ItemId = {53648,53649}, mapID = 48, Icon = 'item_52312', Desc = '姜饼城圣诞树上装饰用的铃铛，摇晃时会发出清脆的响声，是姜饼城的游客最喜欢的旅游纪念品。姜饼城出现小孩子失踪的现象，冒险者接受了圣诞老人的请求帮忙调查，锁定嫌疑人是城中马戏团的小丑加尔。冒险者跟随小女孩珍妮一起观看小丑加尔的表演，珍妮被小丑加尔的表演打动了，她想请小丑吃爸爸妈妈制作的糖果。让人始料不及的是加尔吃了糖果流出激动的泪水，原来加尔和珍妮的奶奶贝蒂早在许多年前就相识，那还是几十年前，他们两小无猜的时候，贝蒂和加尔约定每年都要在姜饼城见面，贝蒂会给加尔他最喜欢的糖果，而加尔也会为贝蒂带来精心准备的表演。之后，因为傀儡巫师毕尤的阴谋，贝蒂的家境败落，父母发生了意外，她没有精力去姜饼城与加尔相聚，而加尔在漫长的等待时光里迷失了自己的灵魂，在毕尤的蛊惑下变成了一个魔物。几十年后，贝蒂的孙女珍妮送给他的糖果重新开启了他的心智，他开始慢慢想起来跟贝蒂的约定。可加尔误把珍妮认作了贝蒂，他以为约定之人终于来到自己的面前，高兴的邀请珍妮观看他最杰出的演出，一场又一场精彩绝伦的表演在姜饼城上演，看着珍妮满脸的笑容，他的心灵得到安抚。但是这也是毕尤阴谋的一部分，她企图利用加尔抓走珍妮威胁圣诞老人，让他交出姜饼城的伊米尔之心的碎片。冒险者经过调查与分析得知了贝蒂与加尔的往事，通过贝蒂的帮助，找到了被绑架的珍妮还有其他的孩子们，粉碎了毕尤的阴谋。而加尔，也因为见到了贝蒂，回忆起他们曾经的约定，从毕尤的操控之中挣脱出来，重新找到了自己的人性。一片祥和的光芒中，他的灵魂得到释放，傀儡巫师毕尤也被众人赶出了姜饼城。每当圣诞树的铃铛响起时，冒险者们总会想起贝蒂和加尔，想起姜饼城的雪景和这个有关糖果的故事。', RewardStr = '完成圣诞树的铃铛的收集后%s', Quality = 4, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '圣诞树的铃铛', RedTip = 10024},
	[24] = {id = 24, ItemId = {53651,53652,53653,53654,53655,53656}, mapID = 49, Icon = 'item_3605', Desc = '炼金术士阿克米的日记本，记载了300年前阿克米收养两个小孩的故事。因为阿克米的老师帮助两位孩子，导致了他身体健康受损，当老师第一次要求阿克米在他离世以后抚养两位孩子时，阿克米非常的抗拒。出于对老师的尊敬，他收养了两个小孩，随着与对方的相处，阿克米成为了一个成熟的父亲。他明白了老师当初的心情，全心全意的对待自己的儿女，对他们倾注了自己所有的父爱。', RewardStr = '完成炼金术士的日记本的收集后%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '炼金术士的日记本', RedTip = 10025},
	[25] = {id = 25, ItemId = {53662,53663,53664,53665,53666,53667,53668,53669,53670}, mapID = 49, Icon = 'item_59', Desc = '坎卜斯是个半羊半魔的怪物，常在平安夜现身，他将考查圣诞村的每一个孩子。这天晚上，孩子们会胆战心惊的察看，摆在门外的鞋子或靴子中装的是圣诞老公公给的馈赠，还是坎卜斯给坏孩子的惩罚。', RewardStr = '完成坎卜斯的礼物的收集后%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '坎卜斯的礼物', RedTip = 10026},
	[26] = {id = 26, ItemId = {53671,53672,53673,53674,53675,53676,53677}, mapID = 50, Icon = '14', Desc = '一桩陈年旧案，究竟隐藏着什么真相！请通过布袋熊们的线索找寻那段遗失在岁月里的时光。', RewardStr = '完成腹语师之死的收集后%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '腹语师之死', RedTip = 10027},
	[27] = {id = 27, ItemId = {53658,53659,53660,53661}, mapID = 50, Icon = 'item_550000', Desc = '传说有三只被诅咒的娃娃，怀揣心事的少女拥着它们入睡，心灵便会被囚禁在娃娃的身体里，陷入拒绝现实，空洞失忆的状态当中。在这玩具工厂就有三只到处袭击冒险者的娃娃！它们身上有锁住少女心灵的容器，找到那些容器，说不定能拯救三个失去心灵的少女！', RewardStr = '完成魔灵娃娃的诅咒的收集后%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '魔灵娃娃的诅咒', RedTip = 10028},
	[28] = {id = 28, ItemId = {53683,53684,53685,53686,53687}, mapID = 50, Icon = 'item_52029', Desc = '一段千年前的往事，讲述了吉尔达女神与太阳神弗雷的爱情故事，太阳神因为一次偶然的机会见到了极光女神吉尔达，他们成为了神族的一对神仙眷侣。可好景不长，诸神的黄昏战役爆发，弗雷死于保护吉尔达，而吉尔达也一直守护着他们的家园。', RewardStr = '完成永恒的夏宫的收集后%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '永恒的夏宫', RedTip = 10029},
	[29] = {id = 29, ItemId = {53678,53679,53680,53681}, mapID = 48, Icon = 'item_3708', Desc = '黑天鹅舞蹈团的舞娘们为了游客制作的圣诞纪念品，存放了各种珍贵香料，拿在手上可以闻到迷人的香味。舞娘珂莲安为了感谢冒险者的帮助，将自己亲手制作的圣诞香包赠送给热心的冒险者。', RewardStr = '完成圣诞香包的收集后%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '圣诞香包', RedTip = 10030},
	[30] = {id = 30, ItemId = {53690,53691}, mapID = 62, Icon = 'item_task_55', Desc = '天水之国是大陆上充满灵气的地方，这里生长着一种常年不谢的花——樱花。这里生长的人们，吃着樱花制作的美食，喝着樱花酿造的美酒，穿着印满了樱花的裙子和衣服，在樱花树下度过了自己的一生。而在这里生长的樱花树，慢慢的也有了灵气，成为了能够思考的灵。她看着满城的樱花，为自己取名叫做樱，可是，身为灵她无法被人类察觉，只能孤孤单单地在天水之国活着。直到有一天，她遇见了一个能够看见他的男孩，那个男孩是天水之国巫术家族的后裔，名叫伊达秀明。被同龄人排斥的秀明和“不存在”的樱灵成为了互相的伴侣，陪伴对方寂寞的生活。好景不长，秀明生病了，看着他日渐憔悴的身影，樱灵在内心做了一个决定。她用自己的灵力补充秀明的身体，治疗秀明的疾病，却让自己原本就脆弱的灵魂变得更加衰弱。发现樱灵异状的秀明并不知道事实，他为了恢复樱灵的力量想尽办法，最后不得不使用了家族的禁术将天水之国地下灵的灵力全部聚集在樱灵身上，维持她的生命。而巨大的力量让樱灵失去了自我变成了没有意识的怪物，只懂得破坏和杀戮。在冒险者的帮助下，秀明解除了整个禁术，也让樱灵变回来原来温柔又善良的模样。这时秀明才知道原来樱灵变弱的原因，为了自己爱人，他请求冒险者将自己身体里樱灵的力量还给她，让她恢复生机。而樱灵同样请求冒险者将自己所有的灵力给秀明，让他重新获得健康的身体……', RewardStr = '完成樱城的故事的收集后%s', Quality = 4, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '樱城的故事', RedTip = 10031},
	[31] = {id = 31, ItemId = {53693,53694,53695,53697,53696}, mapID = 62, Icon = 'item_48539', Desc = '两百年前，离开仙山的小狐狸在大陆上游历，因为一块樱花蛋糕与天水之国的巫女伊达杏结缘。小狐狸满身的灵气引来了伊达家族的好奇，经过她们试探发现了小狐狸仙狐的身份。伊达家族希望将小狐狸的灵魂束缚在伊达家族，成为伊达家族的式神，而身为小狐狸的朋友伊达杏不允许家人这样做。为了小狐狸的自由，伊达杏牺牲了自己的生命，可失去了身体的小狐狸无法回到仙山。为了寻找自己的小狐狸，仙山的神明从遥远的地方来到天水之国，在冒险者的帮助下终于找到了自己的狐狸。神明使用法术复活了小狐狸，却违反了仙山的规定，失去了神明的资格再也回不到仙山，它选择陪伴自己的狐狸，在大陆上游历。', RewardStr = '完成狐狸旧事的收集后%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '狐狸旧事', RedTip = 10032},
	[32] = {id = 32, ItemId = {53699,53700,53701,53702,53703}, mapID = 65, Icon = 'item_3609', Desc = '贤者导师杰拉德发现艾因布洛克原野最近出现了一股不寻常的魔力，同时他感到这股魔力可能与智慧巨人密米尔有着联系，于是带着四位学生外出一同调查。四位学生在原野分别各自调查，分别发现了四种有关智慧巨人密米尔的线索和宝物。收集齐宝物后，杰拉德意图与学生们一起召唤出智慧巨人，但没料到学生之一的格拉被伪神附体，中途破坏。在冒险者的帮助下，众人打败了伪神，救回了格拉，杰拉德将密米尔的宝物赠送给了冒险者。', RewardStr = '完成密米尔传说的收集后%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '密米尔传说', RedTip = 10033},
	[33] = {id = 33, ItemId = {53711,53712,53713,53714,53715,53716,53717,53718}, mapID = 66, Icon = 'item_task_51', Desc = '追寻火鸟之泪的人啊，做着火鸟之梦的人啊，永生到底是恩赐还是诅咒呢，你有答案了吗？每当你迷惑之时，拿出这瓶火鸟之泪吧，注视它，回想起你见过的那些真实幻影，毕竟只有在有限的生命里，你所做的一切，遇见的每一个人才会那么独一无二，值得一生铭记。', RewardStr = '完成火鸟之梦的收集后%s', Quality = 3, AdventureReward = _EmptyTable, RewardProperty = {MaxHp=15}, Name = '火鸟之梦', RedTip = 10034},
