  local mapid, maptype = srcUser:GetMapInfo()
  local AttrFunction = srcUser:GetProperty("AttrFunction")
  local bitfunc = CommonFun.getBits(AttrFunction)
  if (targetUser.boss or targetUser.mini or targetUser.changelinepunish) and targetUser.zoneType == 1 and bitfunc[CommonFun.AttrFunction.JustInViceZone] == 1 then
    return 0, CommonFun.DamageType.Miss
