  result[CommonFun.RoleData.EATTRTYPE_CRI] = (extra[CommonFun.RoleData.EATTRTYPE_CRI] + attr[CommonFun.RoleData.EATTRTYPE_CRI]) * (1 + attr[CommonFun.RoleData.EATTRTYPE_CRIPER])
