  result[CommonFun.RoleData.EATTRTYPE_MOVESPD] = (extra[CommonFun.RoleData.EATTRTYPE_MOVESPD] + attr[CommonFun.RoleData.EATTRTYPE_MOVESPD]) * (1 + attr[CommonFun.RoleData.EATTRTYPE_MOVESPDPER])
