  end
  result[CommonFun.RoleData.EATTRTYPE_ATKPER] = extra[CommonFun.RoleData.EATTRTYPE_ATKPER] + attr[CommonFun.RoleData.EATTRTYPE_ATKPER]
  if result[CommonFun.RoleData.EATTRTYPE_ATKPER] <= -1 then
