  result[CommonFun.RoleData.EATTRTYPE_MATKPER] = extra[CommonFun.RoleData.EATTRTYPE_MATKPER] + attr[CommonFun.RoleData.EATTRTYPE_MATKPER]
  if result[CommonFun.RoleData.EATTRTYPE_MATKPER] <= -1 then
