	[21] = "{id = 21, Speaker = 1042, Text = '[c][ffff00][PlayerName][-][/c]，成为冒险者后会接到危险的任务，完成任务过程中和魔物战斗的时候可不能发呆哦！', Emoji = 0}",
	[22] = "{id = 22, Speaker = 1050, Text = '放心吧！这家伙虽然看起来呆呆的，战斗起来却出奇的敏捷，真不知道点了什么天赋。', Emoji = 0, Action = {num = 1, actionid = 34}}",
	[23] = "{id = 23, Speaker = 1042, Text = '那就好…我已经帮你们登记好了。要注意的是：成为冒险者，需要先进行考试，往前走找到[c][ffff00]教官宝雅[-][/c]了解下吧。', Emoji = 1}",
	[24] = "{id = 24, Speaker = 1054, Text = '是[c][ffff00]赛尼亚[-][/c]和[c][ffff00][PlayerName][-][/c]吗？看起来激情满满啊~来，在这里签下名，准备好就[c][ffff00]参加入团考试[-][/c]吧。', Emoji = 0}",
	[25] = "{id = 25, Speaker = 1054, Text = '由于近期各地魔物异常事件激增，导致[c][ffff00]冒险者公会[-][/c]人手不足，于是[c][ffff00]乐园团[-][/c]也成为正规战斗组织了，所以人员挑选会变得严格些。', Emoji = 0}",
	[26] = "{id = 26, Speaker = 1054, Text = '但也不用太担心~为了协助大家更好地完成考试项目，我们这次提供了独特的新手装备给冒险者。现在就要领取吗？', Option = '现在就要领！', Emoji = 0}",
	[27] = "{id = 27, Speaker = 1054, Text = '好的，这次的考试内容…我看看…有了！击败5只粉嫩嫩、肉嘟嘟、粘乎乎的果冻怪！', Emoji = 0}",
	[28] = "{id = 28, Speaker = 0, Text = '那不是[c][ffff00]波利[-][/c]吗？！', Emoji = 1}",
	[29] = "{id = 29, Speaker = 1054, Text = '嗯！对！就是它们，最近魔物异变，波利也开始暴走了。虽然没有造成很大的损失，但是居民们一直投诉也着实让人头疼，请去教训下它们吧。', Option = '好的！', Emoji = 0}",
	[30] = "{id = 30, Speaker = 1054, Text = '完成了吗？相当不错呢！', Emoji = 2}",
	[31] = "{id = 31, Speaker = 1054, Text = '下一场的考试内容，我看看…这个稍微有点…击败5只毛毛的、软趴趴、慢吞吞的毛虫怪！', Emoji = 0}",
	[32] = "{id = 32, Speaker = 0, Text = '难道说的是[c][ffff00]绿棉虫[-][/c]？！', Emoji = 1}",
	[33] = "{id = 33, Speaker = 1054, Text = '非常正确！魔物变异后的绿棉虫，比原来大多了，虽然对人没什么伤害，但是看到总让人全身不舒服，去控制下它们的数量吧！', Option = '好的！', Emoji = 0}",
	[34] = "{id = 34, Speaker = 1054, Text = '好的！这个也顺利完成了呢！', Emoji = 0}",
	[35] = "{id = 35, Speaker = 1054, Text = '接下来是最后一项内容，这个有些难度…击败3只毛茸茸、软乎乎、活蹦乱跳的长毛怪！', Emoji = 0}",
	[36] = "{id = 36, Speaker = 0, Text = '这次应该是[c][ffff00]疯兔[-][/c]吧…', Emoji = 1}",
	[37] = "{id = 37, Speaker = 1054, Text = '因为受到[c][ffff00]时空裂隙[-][/c]的魔法波动影响，原本温顺的魔物都暴走了，我们正在尝试让他们恢复平静的方法，不过…暂时还没有进展，所以拜托了！', Option = '好吧！', Emoji = 0}",
	[38] = "{id = 38, Speaker = 0, Text = '大叔，你怎么了？', Emoji = 0}",
