	[51] = "{id = 51, Speaker = 1044, Text = '但是[c][ffff00]天使波利[-][/c]非常难缠，飞得快还喜欢偷东西，别说捕捉了，我们的物资装备反倒损失了不少…', Emoji = 0}",
	[52] = "{id = 52, Speaker = 0, Text = '没有正确的方法，肯定抓不到它的。', Emoji = 0}",
	[53] = "{id = 53, Speaker = 1044, Text = '年轻人，我看你也没什么事干，帮我送一批货好吗？[c][ffff00]普隆德拉[-][/c]的[c][ffff00]宠物商人卡博尔[-][/c]大叔对付魔物很有经验，麻烦你去请教他捕捉天使波利的方法。', Emoji = 0}",
	[54] = "{id = 54, Speaker = 0, Text = '我看起来很闲吗…', Emoji = 0}",
	[55] = "{id = 55, Speaker = 1052, Text = '[c][ffff00][PlayerName][-][/c]，来得正好~你还不知道吧，这次的货物，是[c][ffff00]3只魔物[-][/c]~', Emoji = 0, Voice = '访问'}",
	[56] = "{id = 56, Speaker = 0, Text = '魔物？！这怎么运送啊？万一暴走怎么办？', Emoji = 1}",
