	[61] = "{id = 61, Speaker = 0, Text = '[c][ffff00]梦罗克商会[-][/c]托我向你打听，你知道[c][ffff00]捕捉天使波利[-][/c]的方法吗？', Emoji = 0}",
	[62] = "{id = 62, Speaker = 1043, Text = '天使波利啊…没有什么确定的方法可以捉到。不过我知道，[c][ffff00]天使波利[-][/c]经常和一大群波利一起出现。', Emoji = 0}",
