	[153] = "{id = 153, Speaker = 1024, Text = '[c][ffff00]米多娜[-][/c]的推荐信，你是来[c][ffff00]选择[-][/c]自己未来的[c][ffff00]职业[-][/c]的吗？按照惯例我先给你介绍一下职业吧。', Option = '我挺想了解！', Emoji = 0}",
	[154] = "{id = 154, Speaker = 1024, Text = '职业共分为：[c][ffff00]战士[-][/c]、[c][ffff00]法师[-][/c]、[c][ffff00]服事[-][/c]、[c][ffff00]盗贼[-][/c]和[c][ffff00]弓箭手[-][/c]。[c][ffff00]战士[-][/c]英勇无畏，擅长[c][ffff00]近距离战斗[-][/c]，能承受大量攻击，是冲锋陷阵的不二人选。', Emoji = 0}",
	[155] = "{id = 155, Speaker = 1024, Text = '[c][ffff00]法师[-][/c]则是控制[c][ffff00]元素力量[-][/c]的好手，能给予敌人强大的伤害。', Emoji = 0}",
