	[157] = "{id = 157, Speaker = 1024, Text = '[c][ffff00]弓箭手[-][/c]则是天生的狩猎好手，擅长[c][ffff00]远距离攻击[-][/c]对手，经常能够在远处威胁到对手。', Emoji = 0}",
	[158] = "{id = 158, Speaker = 1024, Text = '你必须通过[c][ffff00]试炼[-][/c]才能获得想要的职业。', Emoji = 0}",
