	[1237] = "{id = 1237, Speaker = 1125, Text = '好嘞！这是你的[c][ffff00]变身卷轴[-][/c]！上次聚会，我和朋友们用了几张找点乐子。结果还真有人变成了巴风特，可好玩呢！有需要的话随时来找我吧！'}",
	[1250] = "{id = 1250, Speaker = 0, Text = '看你忧心忡忡的，怎么啦，有心事吗？'}",
	[1251] = "{id = 1251, Speaker = 1120, Text = '啊呀，被发现了…最近下水道突然滋生了好多魔物，讨伐队正忙着清理，都没时间找个人好好倾诉…'}",
	[1252] = "{id = 1252, Speaker = 0, Text = '也是，一个女孩子家，怎么会报名加入下水道讨伐队呢，难道是有什么特殊的癖好吗？！'}",
	[1253] = "{id = 1253, Speaker = 1120, Text = '讨厌啦！才不是呢！是因为…因为…因为[c][ffff00]拉姆[-][/c]也报名了啦~'}",
	[1254] = "{id = 1254, Speaker = 0, Text = '他报名和你有什么关系吗？'}",
	[1255] = "{id = 1255, Speaker = 1120, Text = '当然…当然有关系！你可不要告诉别人，其实我和拉姆从小一起长大，算是青梅竹马，他平时很照顾我，只是感情上有点迟钝而已…'}",
	[1256] = "{id = 1256, Speaker = 1120, Text = '最近下水道魔物泛滥，拉姆自告奋勇报了名想为大家做点贡献…我…我只想和他多点时间相处而已…所以也咬咬牙报了名…'}",
	[1257] = "{id = 1257, Speaker = 1120, Text = '其实我胆子挺小的，尤其在下水道看到这些真实的魔物，简直太可怕了！但只要能伴在拉姆身边，坚持一下就可以的！多点人手也能早点清理完毕，到时就能回到城市和拉姆继续逛街了~'}",
	[1258] = "{id = 1258, Speaker = 0, Text = '爱情的力量啊！勇气可嘉！这样吧，我帮你一起清理吧！'}",
	[1259] = "{id = 1259, Speaker = 1120, Text = '真的吗？太感谢了！根据命令，现在要清理三种魔物，分别是[c][ffff00]白鼠、盗虫、魔菇，每种需要消灭5只[-][/c]！', Option = '包在我身上！'}",
	[1260] = "{id = 1260, Speaker = 1120, Text = '太棒了！原来你的实力也很强啊！能再帮个忙吗？狡猾的[c][ffff00]白鼠[-][/c]趁我们不注意时把[c][ffff00]新型欧拉欧拉[-][/c]给偷走了，[c][ffff00]新型欧拉欧拉[-][/c]是欧拉欧拉的升级版，引诱盗虫更有效~能帮我们夺回来吗？', Option = '没问题！'}",
	[1261] = "{id = 1261, Speaker = 1120, Text = '好样的！现在可以去那边角落使用[c][ffff00]新型欧拉欧拉[-][/c]了，把[c][ffff00]盗虫[-][/c]引诱出来，然后消灭一大波！', Option = '出发！'}",
	[1262] = "{id = 1262, Speaker = 1120, Text = '完成得太出色啦！今天的清理工作能提前完成呢，太谢谢你了！'}",
	[1263] = "{id = 1263, Speaker = 0, Text = '不用客气，举手之劳！只是你默默为拉姆付出了那么多，得鼓起勇气让他知道你的心意，爱情是要主动争取的，可别错过对方…'}",
	[1264] = "{id = 1264, Speaker = 1120, Text = '嗯嗯！讲得很有道理呢！等拉姆忙完，我就找个合适机会向他表白！爱死他了~'}",
	[1265] = "{id = 1265, Speaker = 0, Text = '祝你们有情人终成眷属！到时举办婚礼时记得叫上我啊~'}",
	[1300] = "{id = 1300, Speaker = 1125, Text = '[c][ffff00]10块半透明的布换3张变身卷轴[-][/c]！很划算的交易哦，要换吗？', Option = '好啊！'}",
	[1301] = "{id = 1301, Speaker = 1125, Text = '好嘞！这是你的[c][ffff00]变身卷轴[-][/c]！上次聚会，我和朋友们用了几张找点乐子。结果还真有人变成了巴风特，可好玩呢！有需要的话随时来找我吧！'}",
	[1302] = "{id = 1302, Speaker = 1125, Text = '数量不够呢，别讨价还价哦~等收集到[c][ffff00]10块半透明的布[-][/c]再来找我吧！'}",
	[2001] = "{id = 2001, Speaker = 1001, Text = '身为[c][ffff00]普隆德拉[-][/c]的市民，我非常自豪！', Emoji = 0}",
	[2002] = "{id = 2002, Speaker = 1002, Text = '不知道今天市场会卖些什么，最好是漂亮的礼服。', Emoji = 0}",
	[2003] = "{id = 2003, Speaker = 1003, Text = '每天晚上都会梦到那些可怕的魔物~好害怕。', Emoji = 0}",
	[2049] = "{id = 2049, Speaker = 1049, Text = '东西不贵~应有尽有~来看看哟~！', Emoji = 0}",
	[2053] = "{id = 2053, Speaker = 1053, Text = '额…', Emoji = 0}",
	[2055] = "{id = 2055, Speaker = 1050, Text = '嗨，[c][ffff00][PlayerName][-][/c]，有件事情拜托你哦~', Option = '什么事？', Emoji = 0}",
	[2056] = "{id = 2056, Speaker = 1042, Text = '冒险家有个重要的工作，就是向《米德加兹国家地理》提供各种情报。[c][ffff00]地貌景观、魔物生态[-][/c]都是重要的信息，不仅是冒险家成果的证明，还可以借此获得丰厚的报酬哦~', Option = '挺有兴趣～', Emoji = 2}",
	[2057] = "{id = 2057, Speaker = 1050, Text = '要记录情报，当然是从摄影开始啦~先从[c][ffff00]自拍[-][/c]开始练习吧，嘟嘴~', Option = '…', Emoji = 4}",
	[2058] = "{id = 2058, Speaker = 1042, Text = '拍好了，快看看漂亮吗？！', Emoji = 0}",
	[2059] = "{id = 2059, Speaker = 0, Text = '赛尼亚，你脸上发痘痘了！', Emoji = 5}",
	[2060] = "{id = 2060, Speaker = 1050, Text = '没关系，PS一下就好了。相机送给你了，期待在《米德加兹国家地理》上看到你拍的照片哦~', Emoji = 1}",
	[2061] = "{id = 2061, Speaker = 1050, Text = '[c][ffff00][PlayerName][-][/c]，正经的工作来了！公会面向所有冒险者发布了一个委托，要重新[c][ffff00]调查大陆的所有魔物[-][/c]，从波利到BOSS，都需要最新的情报！', Emoji = 0}",
	[2062] = "{id = 2062, Speaker = 0, Text = '我们对这些魔物信息应该是了如指掌！', Emoji = 0}",
	[2063] = "{id = 2063, Speaker = 1050, Text = '梦罗克的封印破坏之后，受到魔王力量的影响，魔物们都产生了不同程度的变异，生态和习性都已经大不同了，所以要重新获取情报。', Emoji = 0}",
	[2064] = "{id = 2064, Speaker = 0, Text = '这些信息提供给谁？', Emoji = 0}",
	[2065] = "{id = 2065, Speaker = 1050, Text = '雷根贝勒公司，一家生体研究所，组织了最顶级的魔物学者和研究员，希望能找出应对魔物异变的方法。', Emoji = 0}",
	[2066] = "{id = 2066, Speaker = 0, Text = '我知道了，就从波利开始调查吧！', Emoji = 0}",
	[2067] = "{id = 2067, Speaker = 1050, Text = '波利的萌点都被你拍到了呢，[c][ffff00][PlayerName][-][/c]很有摄影师的潜力啊，下次帮我拍写真吧~', Emoji = 2}",
	[2079] = "{id = 2079, Speaker = 1059, Text = '嘛~昨天波伊遇到一支商队在运送很多货物，于是人家就捡了点好玩东西~'}",
	[2080] = "{id = 2080, Speaker = 0, Text = '真的是捡吗？不会是偷来的吧…', Emoji = 1}",
	[2081] = "{id = 2081, Speaker = 1059, Text = '表这样说嘛~反正是好玩的东西，一起来玩吧~啵~'}",
	[2082] = "{id = 2082, Speaker = 0, Text = '这个是…[c][ffff00]枯树枝[-][/c]？！'}",
	[2083] = "{id = 2083, Speaker = 1059, Text = '据说使用[c][ffff00]枯树枝[-][/c]后，会有好玩的事发生哦，快试试看！啵~', Emoji = 5}",
	[2084] = "{id = 2084, Speaker = 1059, Text = '哇哦~那些魔物看起来好强的啵~[c][ffff00]枯树枝[-][/c]果然很有趣啊，啵咿~', Emoji = 2}",
	[2085] = "{id = 2085, Speaker = 0, Text = '这么轻描淡写，我可是差点挂了…话说你不是[c][ffff00]波利[-][/c]吗，为什么会说话？', Emoji = 1}",
	[2086] = "{id = 2086, Speaker = 1059, Text = '人家本来是波利王子哟，受到魔王的诅咒后，说的话就都转变成人类的语言了~啵~'}",
	[2087] = "{id = 2087, Speaker = 1059, Text = '因为语言不通，波伊被赶出了波利岛，于是没有波利陪我玩了~啵~'}",
	[2088] = "{id = 2088, Speaker = 0, Text = '什么鬼诅咒…总之委托完成了，入团考试不轻松啊！'}",
	[2089] = "{id = 2089, Speaker = 1059, Text = '再见噜，下次记得找人家一起玩~啵伊~', Emoji = 2}",
	[2090] = "{id = 2090, Speaker = 1059, Text = '竟然有魔物出现了！波伊吓了一跳呢，好刺激，再玩一次吧~啵~'}",
	[2091] = "{id = 2091, Speaker = 1054, Text = '委托都完成了吗？[c][ffff00][PlayerName][-][/c]很能干呢！'}",
	[2092] = "{id = 2092, Speaker = 0, Text = '那么，我算通过入团考试了吗？'}",
	[2093] = "{id = 2093, Speaker = 1054, Text = '入团流程是没错啦，不过我刚接到情报，来自[c][ffff00]梦罗克[-][/c]的商队似乎遇到了些麻烦，现在真缺人手，有点困扰呢~', Option = '我去帮忙！'}",
	[2094] = "{id = 2094, Speaker = 1054, Text = '真体贴呢，[c][ffff00][PlayerName][-][/c]~根据猎鹰带来的情报，伤员就在西南方，麻烦你前往查探一下吧 ！', Option = '好的！'}",
	[2095] = "{id = 2095, Speaker = 1054, Text = '很有干劲呢！[c][ffff00][PlayerName][-][/c]！这次的考试项目是[c][ffff00]两件委托任务[-][/c]，请认真完成好再来找我吧！'}",
	[2096] = "{id = 2096, Speaker = 1060, Text = '呜呜呜…[c][ffff00]拉比丝[-][/c]，你去哪里了？', Emoji = 24}",
	[2097] = "{id = 2097, Speaker = 0, Text = '你和同伴走散了吗？'}",
	[2098] = "{id = 2098, Speaker = 1060, Text = '拉比丝是我的宠物，一只非常温顺的[c][ffff00]疯兔[-][/c]。我们从艾尔贝塔送信过来，但进入普隆德拉南门区域后，拉比丝突然变得有点奇怪。', Emoji = 19}",
	[2099] = "{id = 2099, Speaker = 0, Text = '能说说详细的情况吗？'}",
	[2100] = "{id = 2100, Speaker = 1060, Text = '起初拉比丝有点不安，我就一路安抚它。直到今天早上，拉比丝体型大了不少，还变得非常暴躁，我想把它关起来免得伤害到别人，没想到它直接把我撞倒后就跑掉了。', Emoji = 1}",
	[2101] = "{id = 2101, Speaker = 0, Text = '别担心，会没事的…我先帮你[c][ffff00]找到拉比丝[-][/c]吧！'}",
	[2152] = "{id = 2152, Speaker = 0, Text = '[c][ffff00]雾月森林[-][/c]…这里是疯兔平时最爱来的地方，找找看吧！'}",
	[2153] = "{id = 2153, Speaker = 0, Text = '哇！有魔物出现！被包围了！', Emoji = 1}",
	[2154] = "{id = 2154, Speaker = 0, Text = '疯兔怎么都狂暴了？得仔细调查出原因才行！'}",
	[2155] = "{id = 2155, Speaker = 0, Text = '有一只体型巨大的疯兔冲过来了，是异变吗？！', Emoji = 18}",
	[2156] = "{id = 2156, Speaker = 0, Text = '呼，总算打退了…不知道是不是拉比丝，疯兔异变的情况很严重，得先回去跟[c][ffff00]班尼[-][/c]商量下对策。'}",
	[2157] = "{id = 2157, Speaker = 1060, Text = '体型巨大的疯兔？那一定是拉比丝~狂暴了？不，不可能的！'}",
	[2158] = "{id = 2158, Speaker = 0, Text = '很抱歉，暂时还没有办法恢复异变的魔物。拉比丝已经找到了，那我的任务算完成了吗？', Emoji = 28}",
	[2159] = "{id = 2159, Speaker = 1060, Text = '等等！还有个办法，请你帮帮我！这是拉比丝从小戴着的[c][ffff00]蝴蝶结[-][/c]，请拿给拉比丝看，它一定会认出来的！'}",
	[2160] = "{id = 2160, Speaker = 0, Text = '那我去试试看吧，希望有用~'}",
	[2161] = "{id = 2161, Speaker = 0, Text = '拉比丝？'}",
	[2162] = "{id = 2162, Speaker = 1063, Text = '…'}",
	[2163] = "{id = 2163, Speaker = 0, Text = '看这体型应该是[c][ffff00]拉比丝[-][/c]！来，看看这是什么，班尼让我带给你的哦~你一定记得吧！'}",
	[2164] = "{id = 2164, Speaker = 0, Text = '[c][ffff00]拉比丝[-][/c]恢复平静了，体型也逐渐变回正常了，主人的信物还真有用呢！'}",
	[2165] = "{id = 2165, Speaker = 0, Text = '[c][ffff00]拉比丝[-][/c]，班尼很担心你，跟我一起回去吧！'}",
	[2166] = "{id = 2166, Speaker = 1064, Text = '…', Emoji = 2}",
	[2167] = "{id = 2167, Speaker = 1060, Text = '拉比丝！你终于回来了！我就知道你不会离开我的！'}",
	[2168] = "{id = 2168, Speaker = 1060, Text = '谢谢你，冒险者！关于你的事迹，我会向乐园团提出表彰的！'}",
	[2169] = "{id = 2169, Speaker = 0, Text = '多亏拉比丝，我们找到一个让异变魔物恢复正常的办法…'}",
	[2170] = "{id = 2170, Speaker = 1060, Text = '我也会向部长汇报的。[c][ffff00][PlayerName][-][/c]，你的任务完成得非常出色啊！'}",
	[2172] = "{id = 2172, Speaker = 1024, Text = '冒险者在[c][ffff00]进阶到二转[-][/c]之后，可以开始[c][ffff00]修炼其他职业[-][/c]，从而变得更强。'}",
	[2173] = "{id = 2173, Speaker = 1024, Text = '除了[c][ffff00]能学得更多技能，还能获得专属坐骑、称号，练满之后还可以永久保留职业被动属性加成[-][/c]哦~'}",
	[2174] = "{id = 2174, Speaker = 1024, Text = '另外，转职之后，以前的职业进度是保留的，可以随时转回来，因此请放心修炼你喜欢的职业吧。', Option = '{我要转职,0}{没兴趣,9}'}",
	[2175] = "{id = 2175, Speaker = 0, Text = '…'}",
	[2176] = "{id = 2176, Speaker = 1024, Text = '唔，你看起来还没有二转啊。修炼过程中三心二意可是不行的，先练好现在的职业再来吧！', Option = '{好吧,0}'}",
	[2177] = "{id = 2177, Speaker = 1024, Text = '恩，看来你已经熟练掌握了现在的职业技巧呢~有兴趣的话，可以开始修炼第二职业了~'}",
	[2178] = "{id = 2178, Speaker = 1024, Text = '职业导师都在这里，你可以找他们了解[c][ffff00]转职[-][/c]的事情。', Option = '{我知道了,0}'}",
	[2179] = "{id = 2179, Speaker = 1024, Text = '[c][ffff00][PlayerName][-][/c]，要修炼其他职业的话，找对应的职业导师就行了，他们都在这里。', Option = '{谢谢,0}'}",
	[2180] = "{id = 2180, Speaker = 1024, Text = '如果有职业修炼的烦恼，可以随时来找我~'}",
	[2181] = "{id = 2181, Speaker = 1101, Text = '要转职成为剑士吗，不错的想法呢。可是你不是已经是剑士了吗，剑士公会怎么有这么糊涂的家伙存在，快给我滚！', Option = '{对不起,0}'}",
	[2182] = "{id = 2182, Speaker = 1101, Text = '想成为剑士吗，不错的想法呢。一个合格的剑士，永远冲在最前方，把背后交给队友，你有这个觉悟吗？'}",
	[2183] = "{id = 2183, Speaker = 0, Text = '正如我愿~'}",
	[2184] = "{id = 2184, Speaker = 1101, Text = '那就接受入门的试炼吧！'}",
	[2185] = "{id = 2185, Speaker = 0, Text = '经过一番激烈的战斗…'}",
	[2186] = "{id = 2186, Speaker = 1101, Text = '看来你有不错的实力和决心了，就此授予你剑士的资格吧！', Option = '{谢谢,0}'}",
	[2187] = "{id = 2187, Speaker = 1101, Text = '[c][ffff00][PlayerName][-][/c]，你已经得到剑士公会的认可，可以随时转职成为剑士！', Option = '{转职剑士,0}{下次再说,9}'}",
	[2188] = "{id = 2188, Speaker = 1101, Text = '反悔了吗？没有强大的意志是不行的，哼！'}",
	[2189] = "{id = 2189, Speaker = 1101, Text = '好了，你已经是一名光荣的剑士了，马上开始修炼吧！'}",
	[2190] = "{id = 2190, Speaker = 1102, Text = '要转职成为魔法师吗，不错的想法呢。可是你不是已经是魔法师了吗，魔法公会怎么有这么糊涂的家伙存在，快给我滚！', Option = '{对不起,0}'}",
	[2191] = "{id = 2191, Speaker = 1102, Text = '想成为魔法师吗，不错的想法呢。一个合格的魔法师，永远冲在最前方，把背后交给队友，你有这个觉悟吗？'}",
