	[2196] = "{id = 2196, Speaker = 1102, Text = '[c][ffff00][PlayerName][-][/c]，你已经得到魔法公会的认可，可以随时转职成为魔法师！', Option = '{转职魔法师,0}{下次再说,9}'}",
	[2197] = "{id = 2197, Speaker = 1102, Text = '反悔了吗？没有强大的意志是不行的，哼！'}",
	[2198] = "{id = 2198, Speaker = 1102, Text = '好了，你已经是一名光荣的魔法师了，马上开始修炼吧！'}",
	[2199] = "{id = 2199, Speaker = 1017, Text = '咳咳…不管你相不相信，服装染色在米德加尔特王国是一项遥不可及的技术…'}",
	[2200] = "{id = 2200, Speaker = 1017, Text = '在诸神之战前，精灵族掌握了这项技术。但那场战争中，整个精灵大陆被吞没到次元裂缝中，服装染色的技术就此失传了…'}",
	[2201] = "{id = 2201, Speaker = 1017, Text = '我已经说服卡普拉公司出资，组织了一群神秘的学者，对遗迹中发掘出的远古服装进行研究。如果有进展的话，我会通知你的…', Option = '{再见,0}'}",
	[2205] = "{id = 2205, Speaker = 1272, Text = '材料够了吗，容我算算…'}",
