	[2234] = "{id = 2234, Speaker = 1101, Text = '[c][ffff00]剑士[-][/c]英勇无畏，擅长[c][ffff00]近距离战斗[-][/c]，能承受大量攻击，是冲锋陷阵的不二人选。以后可以进阶为骑士或十字军。', Option = '{我要转职,0}{再想想,1}'}",
	[2235] = "{id = 2235, Speaker = 1102, Text = '[c][ffff00]魔法师[-][/c]是控制[c][ffff00]元素力量[-][/c]的好手，能给予敌人强大的伤害。以后可以进阶为巫师或贤者。', Option = '{我要转职,0}{再想想,1}'}",
	[2236] = "{id = 2236, Speaker = 1103, Text = '[c][ffff00]盗贼[-][/c]擅长速攻和闪避，力量成长也不错，还可以使用中毒的间接攻击手段，在初期就可以面对强敌。以后可以进阶为刺客或流氓。', Option = '{我要转职,0}{再想想,1}'}",
	[2237] = "{id = 2237, Speaker = 1105, Text = '[c][ffff00]服事[-][/c]被誉为神的使者，在战斗中主要以[c][ffff00]治疗[-][/c]、[c][ffff00]鼓舞[-][/c]队友为主，为团队带来持久战力。以后可以进阶为牧师或武僧。', Option = '{我要转职,0}{再想想,1}'}",
	[2238] = "{id = 2238, Speaker = 1104, Text = '[c][ffff00]弓箭手[-][/c]是天生的狩猎好手，擅长[c][ffff00]远距离攻击[-][/c]，经常能够在远处威胁到对手。以后可以进阶为猎人或诗人/舞娘。', Option = '{我要转职,0}{再想想,1}'}",
	[2239] = "{id = 2239, Speaker = 1106, Text = '[c][ffff00]商人[-][/c]擅长和金钱打交道，随着携带者手推车的同时还可以使用金钱攻击这样的强力技能。以后可以进阶为铁匠或炼金术士。', Option = '{我要转职,6}{再想想,1}'}",
	[2240] = "{id = 2240, Speaker = 1101, Text = '好样的！外面魔物猖獗，人手正紧缺呢。', Emoji = 20}",
	[2241] = "{id = 2241, Speaker = 1101, Text = '最近下水道滋生了大批魔物，公会好多人都被派去清剿了…不过别开心得太早，要成为一名真正的[c][ffff00]剑士[-][/c]得先通过考试！'}",
	[2242] = "{id = 2242, Speaker = 1101, Text = '考试内容是：[c][ffff00]击败波利之王[-][/c]！不要轻视这波利家族的首领，最近心智受到魔化后可不好惹，好好教训帮它恢复正常吧！完成考试后来找我吧！', Option = '{开始考试,0}{没准备好,5}'}",
	[2243] = "{id = 2243, Speaker = 1101, Text = '这就把你传送到[c][ffff00]转职试炼场[-][/c]，考试开始！祝你好运！', Option = 'GO！', Emoji = 23}",
	[2244] = "{id = 2244, Speaker = 1101, Text = '哟，实力不错嘛，恭喜你通过了考试！波利之王得到教训后也恢复了正常，正准备回波利岛呢~', Emoji = 26}",
	[2245] = "{id = 2245, Speaker = 1101, Text = '怎么样？通过刚刚的战斗是不是学到了很多？没错，身为[c][ffff00]剑士[-][/c]，光会攻击是不够的，生存才最重要。'}",
	[2246] = "{id = 2246, Speaker = 1101, Text = '团战中，冲在前线的时候要有清醒的头脑，对目标造成伤害的同时要时刻注意防御，要坚持到最后一刻！'}",
	[2247] = "{id = 2247, Speaker = 1101, Text = '好了，你已经成功通过考试，很厉害嘛！现在马上开始转职！', Option = '转职！'}",
	[2248] = "{id = 2248, Speaker = 1101, Text = '恭喜你！你现在已经成为人人羡慕的[c][ffff00]剑士[-][/c]了！希望你往后能踊跃参与剑士公会举办的各种活动！', Option = '{谢谢！,0}', Emoji = 20}",
	[2249] = "{id = 2249, Speaker = 1101, Text = '我就说吧，不要轻敌~但也别气馁，准备好了可以找我重新考试~', Option = '{重新考试,3}{没准备好,5}', Emoji = 25}",
	[2250] = "{id = 2250, Speaker = 1101, Text = '等你准备好了，可以随时开始考试~', Option = '{离开,2}'}",
	[2259] = "{id = 2259, Speaker = 1051, Text = '好了，你知道转职之后第一件事是什么吗？…当然是[c][ffff00]学习新技能[-][/c]！'}",
	[2260] = "{id = 2260, Speaker = 1051, Text = '每个职业都有强力好用的[c][ffff00]技能[-][/c]，我们转职就是为了学习这些有用的技能，熟练使用之后就可以进阶更高级的职业，明白了吗？', Option = '{明白了,0}'}",
	[2261] = "{id = 2261, Speaker = 1051, Text = '那就快去找职业导师吧，学完技能之后，我们就可以开始大冒险啦！'}",
	[2262] = "{id = 2262, Speaker = 1051, Text = '我们就可以开始大冒险啦！', Option = '{…,0}'}",
