	[2274] = "{id = 2274, Speaker = 1102, Text = '考试内容是：[c][ffff00]收集5颗魔法药草和3颗魔法獠牙[-][/c]！魔法光芒草产出魔法药草，魔化野猪则会掉落魔力獠牙…就这些，完成考试后来找我吧！', Option = '{开始考试,0}{没准备好,5}'}",
	[2275] = "{id = 2275, Speaker = 1102, Text = '这就把你传送到[c][ffff00]转职试炼场[-][/c]，考试开始！祝你好运！', Option = 'GO！', Emoji = 23}",
	[2276] = "{id = 2276, Speaker = 1102, Text = '太棒了，恭喜你通过了考试！这些材料一会就被送去魔法公会，真是帮了大忙了，谢谢你！', Emoji = 26}",
	[2277] = "{id = 2277, Speaker = 1102, Text = '瞧见了吧，连野猪都会放火属性技能~身为魔法师，平时需要了解很多魔法知识，通过不断钻研才能习得更强大的魔法。'}",
	[2278] = "{id = 2278, Speaker = 1102, Text = '团战中，魔法师可以施展不同的魔法技能，施展强大的法术攻击。关键是如何针对不同的目标选择最适宜的法术作出应对~'}",
	[2279] = "{id = 2279, Speaker = 1102, Text = '好了，你已经成功通过考试，很厉害嘛！现在马上开始转职！', Option = '转职！'}",
