	[2372] = "{id = 2372, Speaker = 1105, Text = '[c][ffff00]技能[-][/c]是我们最强的力量，要成为优秀的冒险家，不光战斗技巧，还有很多生存技能需要掌握。我先教你几个基本技能吧，等你练熟悉之后，再来找我。', Option = '谢谢'}",
	[2373] = "{id = 2373, Speaker = 1105, Text = '一边冒险，一边在实战中锻炼自己的[c][ffff00]职业技能[-][/c]，就是冒险家的生存方式，也是新人成长最快的途径，请加油吧！', Option = '再见'}",
	[2374] = "{id = 2374, Speaker = 1106, Text = '[c][ffff00]技能[-][/c]是我们最强的力量，要成为优秀的冒险家，不光战斗技巧，还有很多生存技能需要掌握。我先教你几个基本技能吧，等你练熟悉之后，再来找我。', Option = '谢谢'}",
	[2375] = "{id = 2375, Speaker = 1106, Text = '一边冒险，一边在实战中锻炼自己的[c][ffff00]职业技能[-][/c]，就是冒险家的生存方式，也是新人成长最快的途径，请加油吧！', Option = '再见'}",
	[2380] = "{id = 2380, Speaker = 1017, Text = '出门在外有时也需要换一换心情哦~我试着转换一下你的心情，帮你换一下发型好吗？', Option = '选择发型'}",
	[2412] = "{id = 2412, Speaker = 1118, Text = '这是给这次申请加入讨伐队的支援补给物品，[c][ffff00]红色药水 3瓶[-][/c]、[c][ffff00]牛奶 1 瓶[-][/c]，还有危急时所使用的[c][ffff00]赤色药水 1 瓶[-][/c]。', Option = '下面'}",
	[2415] = "{id = 2415, Speaker = 1118, Text = '现在开始传送到[c][ffff00]普隆德拉下水道[-][/c]。', Option = '再见', Emoji = 23}",
	[2417] = "{id = 2417, Speaker = 1118, Text = '啊，你是讨伐部队的一员吗，这里是进入[c][ffff00]地下水道[-][/c]的入口，你是不是想现在就进去？', Option = '{进去地下水道,0}{不用了！,9}'}",
	[2424] = "{id = 2424, Speaker = 1014, Text = '[c][ffff00]强化装备[-][/c]是最快速有效提升能力的方式，但是不适合新手。[c][ffff04][c][ffff00][PlayerName][-][/c][-][/c]，等你有一些实战经验之后再来吧。', Option = '离开'}",
	[2425] = "{id = 2425, Speaker = 1014, Text = '[c][ffff00]精炼装备[-][/c]可以最直观地减少伤害，是打造终极装备的唯一途径，同时风险也很大。[c][ffff04][c][ffff00][PlayerName][-][/c][-][/c]，等你有一定实力之后再来吧。', Option = '离开'}",
	[3000] = "{id = 3000, Speaker = 0, Text = '哇哦~远处就是著名的景点…[c][ffff00]天空花园[-][/c]吗？！太漂亮了…嗯？那边有人在议论什么…', Option = '去凑凑热闹～'}",
	[3001] = "{id = 3001, Speaker = 1071, Text = '最近接到新的调令，[c][ffff00]城防军小队[-][/c]需尽快前往[c][ffff00]依斯鲁得[-][/c]支援，据说那边出现了一艘诡异的[c][ffff00]幽灵船[-][/c]！…谁在那偷听呢？！'}",
	[3005] = "{id = 3005, Speaker = 1070, Text = '可恶的溜溜猴！没事卖萌就算了，为什么老是捣蛋找我麻烦啊！！'}",
	[3006] = "{id = 3006, Speaker = 0, Text = '不好意思打搅了，请问是[c][ffff00]雷姆[-][/c]吗？我是来自普隆德拉的冒险者，有事想找[c][ffff00]史芬斯学者[-][/c]，你知道他在哪吗？'}",
	[3007] = "{id = 3007, Speaker = 1070, Text = '找我老师啊？你等我会，我得先去教训下这些调皮的[c][ffff00]溜溜猴[-][/c]！好不容易帮老师收集完材料，然后打了个盹就被溜溜猴给顺走了…', Option = '我来帮你吧～'}",
	[3008] = "{id = 3008, Speaker = 1070, Text = '太棒了，我就说走着瞧，哼！不愧是冒险者，谢谢帮忙了！我还要继续收集其他材料，你沿着这条路直走就可以找到老师了。'}",
	[3009] = "{id = 3009, Speaker = 1070, Text = '对了，老师库存不多了，顺路帮忙[c][ffff00]采集点药草、收集些蝗虫后腿[-][/c]带给他吧…他平时一直在钻研，总有一天我也会成为他那样的大学者！'}",
	[3010] = "{id = 3010, Speaker = 1067, Text = '太好了，正愁没空去补充材料呢，太感谢你了！这是我那学徒让你送来的吧，这小子一直挺懂事的…'}",
	[3011] = "{id = 3011, Speaker = 1067, Text = '唔，让我看看这照片…事关重大，我需要点时间来对比研究下…[c][ffff00][PlayerName][-][/c]，我还缺些样本，这段时间再帮个忙去[c][ffff00]收集些枯树枝[-][/c]给我吧~等你回来时应该能出结果了…'}",
	[3012] = "{id = 3012, Speaker = 1067, Text = '[c][ffff00][PlayerName][-][/c]，研究已经出结果了。这是结论报告，给！把这份报告内容告知下[c][ffff00]城防军小队长[-][/c]吧！'}",
	[3013] = "{id = 3013, Speaker = 1067, Text = '看到[c][ffff00]天空花园[-][/c]了吗？最近我感觉那边有异常的魔力波动，不过最近实在太忙抽不出身…', Option = '我去查看吧～'}",
	[3014] = "{id = 3014, Speaker = 1067, Text = '那再好不过了。为了你的安全着想，我来帮你施加一个研究所[c][ffff00]最新研究的魔法[-][/c]吧…(正好缺测试对象呢)'}",
	[3033] = "{id = 3033, Speaker = 1071, Text = '这是…[c][ffff00]史芬斯学者的研究报告[-][/c]…谢谢你了，我马上看下。'}",
	[3034] = "{id = 3034, Speaker = 1071, Text = '对了，有队员正在[c][ffff00]西门的南方营地[-][/c]执行任务。出发支援的时间快到了，能麻烦你去南方营地和队员同步下消息吗？'}",
	[3103] = "{id = 3103, Speaker = 1100, Text = '你好呀，冒险者！'}",
	[3109] = "{id = 3109, Speaker = 1100, Text = '啊！真的吗？！太好了！！终于可以实现儿子的梦想了，我也放心了！但还是好舍不得，不过我想通了。傻小子应该有自己的追求、自己的人生，我该为他感到自豪！'}",
	[3110] = "{id = 3110, Speaker = 1100, Text = '对了，之前[c][ffff00]亚瑞[-][/c]来我这打听你的状况，看神情还蛮重要的，你赶快去找他吧…'}",
	[3124] = "{id = 3124, Speaker = 1076, Text = '他们说的冒险者就是你吗？'}",
	[3125] = "{id = 3125, Speaker = 1076, Text = '是[c][ffff00][PlayerName][-][/c]吗，你还没[c][ffff00]加入公会[-][/c]吧。女武神可是很眷顾有团队精神的冒险者，有兴趣吗？'}",
	[3126] = "{id = 3126, Speaker = 1076, Text = '完成的非常棒。'}",
	[3127] = "{id = 3127, Speaker = 1076, Text = '我给你安排了两场考试，你可以借此来考验你的决心与勇气。'}",
	[3128] = "{id = 3128, Speaker = 1076, Text = '[c][ffff00][PlayerName][-][/c]，你已经得到了女武神的认可！她有事要亲自交代你，快去吧，别让她久等了！'}",
	[3129] = "{id = 3129, Speaker = 1019, Text = '[c][ffff00][PlayerName][-][/c]，你很出色，同时也很特别。我给你讲讲一些尘封的往事吧。千年前，奥丁在与巴风特的旷世一战中虽然险胜，却也让神族大伤元气…'}",
	[3130] = "{id = 3130, Speaker = 1019, Text = '如今奥丁留下的最伟大遗产-克雷斯特汉姆城也已沦陷。为了夺回古城，需要阻止魔族的全面复苏，而这需要每一个冒险者的力量！'}",
	[3131] = "{id = 3131, Speaker = 1076, Text = '在你刚考试时，[c][ffff00]城防队队长[-][/c]和[c][ffff00]布雷纳魔法师[-][/c]都来找过你，好像都挺焦急的，快去找他们了解下情况吧！'}",
	[3137] = "{id = 3137, Speaker = 0, Text = '哎…早知道应该让庞德这个笨小子自己来表白了~'}",
	[3140] = "{id = 3140, Speaker = 1076, Text = '这项考试内容是[c][ffff00]帮助我方公会成员攻下由女武神瓦尔基里幻象守护的华丽金属[-][/c]，从而占领房间获取最终胜利…'}",
	[3141] = "{id = 3141, Speaker = 1076, Text = '完成得很不错嘛！[c][ffff00][PlayerName][-][/c]，你的勇气和实力已经得到了验证！'}",
	[3142] = "{id = 3142, Speaker = 1076, Text = '这项考试内容是[c][ffff00]帮助我方公会成员守护房间内的华丽金属，抵挡住女武神瓦尔基里幻象的攻击[-][/c]。敌方的攻击有陆续好几波，要好好防守哦~'}",
	[3144] = "{id = 3144, Speaker = 1099, Text = '[c][ffff00][PlayerName][-][/c]，我之前已经从魔法公会抽调了很多人手来调查海底的封印，但还是没什么大的进展，海底封印恐怕已经被解除了…'}",
	[3145] = "{id = 3145, Speaker = 1099, Text = '封印解除后，好多魔物逃匿出来了，现在正组织队伍进行清剿呢…还有很多海底的生物也收到影响变异了…你的实力不错，能帮我们去打探下[c][ffff00]海底洞穴[-][/c]的状况吗？'}",
	[3146] = "{id = 3146, Speaker = 0, Text = '拉比丝？'}",
	[3147] = "{id = 3147, Speaker = 1063, Text = '…', Emoji = 19}",
	[3148] = "{id = 3148, Speaker = 0, Text = '看这体型应该是拉比丝！来，看看这是什么，班尼让我带给你的哦~你一定记得吧！'}",
	[3149] = "{id = 3149, Speaker = 1064, Text = '…', Emoji = 11}",
	[3150] = "{id = 3150, Speaker = 0, Text = '拉比丝恢复平静了，体型也逐渐变回正常了，主人的信物还真有用呢！'}",
	[3151] = "{id = 3151, Speaker = 0, Text = '拉比丝，班尼很担心你，跟我一起回去吧！'}",
	[3152] = "{id = 3152, Speaker = 1064, Text = '…', Emoji = 2}",
	[3153] = "{id = 3153, Speaker = 1060, Text = '拉比丝！你终于回来了！我就知道你不会离开我的！'}",
	[3154] = "{id = 3154, Speaker = 1064, Text = '…', Emoji = 27}",
	[3155] = "{id = 3155, Speaker = 1060, Text = '谢谢你，冒险者！不过你是怎么知道它就是拉比丝的？', Emoji = 8}",
	[3156] = "{id = 3156, Speaker = 0, Text = '虽然拉比丝不会说话，但我读懂了它的表情。没想到[c][ffff00]表情[-][/c]也可以传达很多心意！'}",
	[3157] = "{id = 3157, Speaker = 0, Text = '给它展示蝴蝶结的那一刻，我能感受到拉比丝因为害怕而恐惧的内心，它是怕控制不住自己伤害到你才出走的吧…'}",
	[3158] = "{id = 3158, Speaker = 1060, Text = '我最爱的拉比丝啊！冒险者，你的任务完成得非常出色！我会向乐园团表彰你的智慧！'}",
	[3159] = "{id = 3159, Speaker = 1054, Text = '真体贴呢，[c][ffff00][PlayerName][-][/c]~根据猎鹰情报，伤员就在西南方，麻烦你调查一下吧 ！', Option = '好的！'}",
	[3160] = "{id = 3160, Speaker = 0, Text = '大叔，你怎么了？'}",
	[3161] = "{id = 3161, Speaker = 1053, Text = '是冒险者吗？我的腿受伤了…'}",
	[3162] = "{id = 3162, Speaker = 1053, Text = '我是来自[c][ffff00]梦罗克商会的护卫[-][/c]，执行任务的时候遇到魔物袭击，腿受了伤无法行动了。'}",
	[3163] = "{id = 3163, Speaker = 0, Text = '果然这边有大量魔物暴走的迹象！我能做点什么来帮到你呢？'}",
	[3164] = "{id = 3164, Speaker = 1053, Text = '我的腿需要包扎，先勉强凑合下，拜托你收集一些[c][ffff00]绿棉虫[-][/c]的[c][ffff00]棉毛[-][/c]可以吗？', Option = '好的！'}",
	[3165] = "{id = 3165, Speaker = 1053, Text = '谢谢！女神会祝福你的，冒险者！我先简单用棉毛处理下伤口…'}",
	[3166] = "{id = 3166, Speaker = 1053, Text = '我的腿暂时还无法行动，你能帮我通知到[c][ffff00]商队[-][/c]吗？他们就在前往普隆德拉的路上。'}",
	[3167] = "{id = 3167, Speaker = 0, Text = '我这就出发，安心吧！'}",
	[3168] = "{id = 3168, Speaker = 1053, Text = '等一下，冒险者…麻烦你先[c][ffff00]用我的相机拍下我腿上的伤口，把照片交给商队的哈德纳尔[-][/c]，这可能和他要找的魔物有关。'}",
	[3169] = "{id = 3169, Speaker = 0, Text = '事态有些严重，还是先回去向[c][ffff00]考官[-][/c]报备一下吧…'}",
	[3170] = "{id = 3170, Speaker = 1054, Text = '可爱的个性头饰可是冒险者的终极追求之一，作为乐园团的成员可不能错过这份福利！来，送你头饰图纸，2选1哦！'}",
	[3171] = "{id = 3171, Speaker = 0, Text = '这怎么好意思呢…我还没正式入团呢…'}",
	[3172] = "{id = 3172, Speaker = 1054, Text = '别客气啦，这是乐园团的小小心意，同时也算考试的一部分~合格条件嘛，[c][ffff00]收集齐图纸上所需的材料[-][/c]然后制作成功！来，选一张头饰制作图纸吧！', Option = '{娱乐用纸箱,2},{粉嫩的波利,3}'}",
	[3173] = "{id = 3173, Speaker = 1054, Text = '这么快就收集齐材料了啊…那就尝试着[c][ffff00]制作出图纸上的头饰[-][/c]吧！'}",
	[3174] = "{id = 3174, Speaker = 0, Text = '时间不早了，快去找[c][ffff00]哈德纳尔[-][/c]吧…'}",
	[3175] = "{id = 3175, Speaker = 1054, Text = '乐园团的头饰可是很漂亮的哦！'}",
	[3176] = "{id = 3176, Speaker = 0, Text = '(将护卫伤口的照片递给哈德纳尔并简述事情的经过…)'}",
	[3177] = "{id = 3177, Speaker = 1044, Text = '护卫的事情我已经知道了，谢谢你的帮助，可这伤口并不是[c][ffff00]天使波利[-][/c]所造成的。'}",
	[3178] = "{id = 3178, Speaker = 0, Text = '难道你们在找天使波利？'}",
	[3179] = "{id = 3179, Speaker = 1044, Text = '是的，我们受到梦罗克商会的委托，在这里搜寻天使波利，希望可以捕捉到它。'}",
	[3180] = "{id = 3180, Speaker = 1044, Text = '但天使波利非常难缠，飞得快还喜欢偷东西。别说捕捉了，我们的物资装备反倒损失了不少…'}",
	[3181] = "{id = 3181, Speaker = 0, Text = '没有正确的方法，肯定抓不到它的吧…'}",
	[3182] = "{id = 3182, Speaker = 1044, Text = '年轻人，我看你也没什么事干，帮我送一批货好吗？[c][ffff00]宠物商人卡博尔[-][/c]大叔对付魔物很有经验，麻烦你去请教他捕捉天使波利的方法。'}",
	[3183] = "{id = 3183, Speaker = 0, Text = '我看起来很闲吗…'}",
	[3184] = "{id = 3184, Speaker = 1044, Text = '耽误了你不少时间，借你神兽坐骑送你回去考试吧…时间一到它就会自行回来，不必担心走失哦~毕竟这可是神兽~'}",
	[3185] = "{id = 3185, Speaker = 1119, Text = '噢~新来的冒险者啊，如果你不知道冒险家应该从何做起，我建议你了解一下《米德加兹国家地理》杂志。', Option = '杂志？'}",
	[3186] = "{id = 3186, Speaker = 1119, Text = '《米德加兹国家地理》是冒险者公会初版的杂志，各地的冒险家都会向杂志投稿，包括地理发现、遗迹调查、魔物研究等等内容，包罗万象，可以说是整个大陆最重要的一本杂志了。'}",
	[3187] = "{id = 3187, Speaker = 1078, Text = '有上千万用户通过《国家地理》杂志分享他们平日摄制的珍贵照片，初心者你也有最新的作品吗？'}",
	[3188] = "{id = 3188, Speaker = 0, Text = '额~我是来领取我的相机的…'}",
	[3189] = "{id = 3189, Speaker = 1078, Text = '噢~原来是这样，看来你已经加入乐园团了，拿好了！这是借给你的“回忆记录器”！'}",
	[3190] = "{id = 3190, Speaker = 1078, Text = '不要错过每一个瞬间哦，你马上就会就职了，难道你不想记录下你就职前的样子吗？许多年后那可是珍贵的回忆哦~ '}",
	[3191] = "{id = 3191, Speaker = 1119, Text = '好啦，你已经记录了自己的初心之旅，这身打扮也就快和你告别了！去吧！就职大厅里有人等待着你~'}",
	[3193] = "{id = 3193, Speaker = 1118, Text = '冒险者，忘掉之前过家家的游戏吧，在我给你第一份任务前，你得先要去[c][ffff00]防具商人[-][/c]那儿购买一件合身的[c][ffff00]披风[-][/c]。'}",
	[3194] = "{id = 3194, Speaker = 0, Text = '我的第一个任务是什么，我已经迫不及待了！'}",
	[3195] = "{id = 3195, Speaker = 1118, Text = '让我看看，有了~最近[c][ffff00]下水道[-][/c]出了些问题，大量的低等魔物在那里滋生。'}",
	[3196] = "{id = 3196, Speaker = 1118, Text = '由于牵涉到魔物，还会影响到普隆德拉的正常运作，所以大家都很重视。冒险者公会已经临时组建了[c][ffff00]下水道讨伐队[-][/c]，你也去帮助他们一起清理吧！'}",
	[3197] = "{id = 3197, Speaker = 0, Text = '额…有点重口味…居然一开始就去下水道…好吧！'}",
	[3198] = "{id = 3198, Speaker = 1068, Text = '你来的正好，冒险者！这边急需人手，下水道的情况仍然在恶化！'}",
	[3199] = "{id = 3199, Speaker = 1120, Text = '情况不太好，得先把这里的[c][ffff00]白鼠[-][/c]清理一下~我实在是受不了了~'}",
	[3200] = "{id = 3200, Speaker = 1120, Text = '呼~悄悄地告诉你哦，其实我真的很害怕老鼠，尤其是白鼠。有次梦见它们在我身上乱爬，一下子就把我吓醒了，还失眠了好几天！'}",
	[3201] = "{id = 3201, Speaker = 1121, Text = '小心点哦~这些白鼠可能带有病毒！我需要采样一些它们的[c][ffff00]牙齿[-][/c]带回去做研究~能帮我弄一些吗？', Option = '没问题！'}",
	[3202] = "{id = 3202, Speaker = 1121, Text = '好奇怪，这些白鼠似乎是吸收到了某些粉尘才会这样的狂暴'}",
	[3203] = "{id = 3203, Speaker = 1121, Text = '我之前就准备了一些雷根公司的[c][ffff00]诱鼠喷雾[-][/c]，你用它来引诱大一些的白鼠，带回活体给我进行深入研究…'}",
	[3204] = "{id = 3204, Speaker = 0, Text = '好大！！'}",
	[3205] = "{id = 3205, Speaker = 1123, Text = '我的上帝啊！！！'}",
	[3206] = "{id = 3206, Speaker = 1122, Text = '快跑啊，我可不想见上帝！！'}",
	[3207] = "{id = 3207, Speaker = 1122, Text = '[c][ffff00]魔化的白鼠[-][/c]！…我决定立刻离开这个鬼地方！', Option = '别害怕'}",
	[3208] = "{id = 3208, Speaker = 1122, Text = '这些管道根本没有问题，而是魔物太多造成了管道堵塞。'}",
	[3209] = "{id = 3209, Speaker = 1122, Text = '这里平时就有很多魔物在活动，现在魔物不但暴走还出现了巨大化，是一个很严重的问题啊！'}",
	[3210] = "{id = 3210, Speaker = 0, Text = '看来这地方的魔物状况和拉比丝之前遇到的一模一样啊…'}",
	[3211] = "{id = 3211, Speaker = 1122, Text = '拉比丝是什么？冒险者，你既然都来了，可以帮我消灭一些[c][ffff00]盗虫[-][/c]吗？这些可不是我们修理工的工作范围…', Option = '没问题！'}",
	[3212] = "{id = 3212, Speaker = 1122, Text = '谢谢你！刚才看你在引诱白鼠，你不会是想要抓它们回去炸了吃吧?'}",
	[3213] = "{id = 3213, Speaker = 0, Text = '吃？！当然不是！但我的确要抓几只回去，刚引来了那么大个的白鼠，我的喷雾不小心掉了…'}",
	[3214] = "{id = 3214, Speaker = 1122, Text = '那你不用担心，我之前正好抓到几只个头小的，本来想回去当宠物的！看在帮我忙的份上，就给你吧！'}",
	[3215] = "{id = 3215, Speaker = 0, Text = '好啊！太棒了，那我不客气了！你也早点撤离下水道吧，待得越久越不安全。'}",
	[3216] = "{id = 3216, Speaker = 0, Text = '天哪…你怎么了？！'}",
	[3217] = "{id = 3217, Speaker = 0, Text = '[c][ffff00]白鼠的吱吱声[-][/c]引来了同类的攻击…'}",
	[3218] = "{id = 3218, Speaker = 1130, Text = '吱吱吱~吱吱吱~'}",
	[3219] = "{id = 3219, Speaker = 0, Text = '[c][ffff00]白鼠的吱吱声[-][/c]再次引来了同类的攻击…'}",
	[3220] = "{id = 3220, Speaker = 0, Text = '还真够义气的，得快点把这些麻烦送给[c][ffff00]莫戈沙[-][/c]~'}",
	[3221] = "{id = 3221, Speaker = 1121, Text = '[c][ffff00]伊米尔粉尘[-][/c]…下水道的魔物吸收了一些[c][ffff00]伊米尔粉尘[-][/c]才会变不安。'}",
	[3222] = "{id = 3222, Speaker = 1068, Text = '冒险者，在深处还有一名修理工，请你去告诉他请立刻离开这里！', Option = '好的！'}",
	[3223] = "{id = 3223, Speaker = 1123, Text = '啊~啊啊~~~不要过来…'}",
	[3224] = "{id = 3224, Speaker = 1123, Text = '看来彼得也能赚一笔啊，哈哈哈！'}",
	[3225] = "{id = 3225, Speaker = 1123, Text = '尾巴！有一个长着尾巴的人…好可怕！'}",
	[3226] = "{id = 3226, Speaker = 1122, Text = '这么大！我们得找几把铲子就能把它给搬出来！'}",
	[3227] = "{id = 3227, Speaker = 1123, Text = '难道真的是幻觉吗？！我刚真的看到一个长着尾巴的怪人！果然在下面呆久了就精神恍惚了…冒险者，这里[c][ffff00]盗虫[-][/c]泛滥，既然你来了就帮我消灭一些[c][ffff00]盗虫[-][/c]吧！'}",
	[3228] = "{id = 3228, Speaker = 1123, Text = '这些烦人的[c][ffff00]盗虫[-][/c]，下水道的水管里难道藏着宝藏？！没事老喜欢聚在这里…搞的水管都堵住了！'}",
	[3229] = "{id = 3229, Speaker = 1123, Text = '年轻人，我收回我刚才的话，你看看这卡在管道里的是什么，似乎是好大的金块啊！'}",
	[3230] = "{id = 3230, Speaker = 1122, Text = '哇哦~竟然有这样的好事，看来波兹的运气也不错嘛！'}",
	[3231] = "{id = 3231, Speaker = 1123, Text = '不可思议，帮我把彼得叫来，我需要他的帮助！'}",
	[3232] = "{id = 3232, Speaker = 1123, Text = '冒险者，和我们一起把，成功的话我们都能分到金子！'}",
	[3233] = "{id = 3233, Speaker = 1123, Text = '一人一把，很好，走吧！'}",
	[3234] = "{id = 3234, Speaker = 1123, Text = '我想你们应该准备好了吧！'}",
	[3235] = "{id = 3235, Speaker = 1122, Text = '赶快开始吧！'}",
	[3236] = "{id = 3236, Speaker = 1122, Text = '越来越闪耀了，我感觉非常好！'}",
	[3237] = "{id = 3237, Speaker = 1123, Text = '我这辈子都没见到这么闪耀的光芒！'}",
	[3238] = "{id = 3238, Speaker = 1123, Text = '等一下，有点问题啊！！'}",
	[3239] = "{id = 3239, Speaker = 1068, Text = '你们真是越帮越忙！'}",
	[3240] = "{id = 3240, Speaker = 1122, Text = '我现在感觉非常的糟糕…'}",
	[3241] = "{id = 3241, Speaker = 1123, Text = '我只想快点离开这个鬼地方！'}",
	[3242] = "{id = 3242, Speaker = 1068, Text = '冒险者，各地都在发生魔物暴走的事件，你需要更小心才行！', Option = '抱歉'}",
	[3243] = "{id = 3243, Speaker = 1014, Text = '你的朋友[c][ffff00]赛尼亚[-][/c]也到了下水道，你赶快过去和她汇合吧，黄金盗虫不会对任何人手下留情！'}",
	[3244] = "{id = 3244, Speaker = 1014, Text = '哦…原来是这样啊，没想到下水道情况那么严重！', Option = '我想要变强！'}",
	[3245] = "{id = 3245, Speaker = 1014, Text = '哈哈哈哈哈~你没找错人，就由我[c][ffff00]忽克连[-][/c]来帮助你[c][ffff00]强化装备[-][/c]，让它们变得更加精良牢固！', Option = '强化装备'}",
	[3246] = "{id = 3246, Speaker = 1014, Text = '怎么样，这祖传的手艺还不错吧！年轻人，现在你已经整装待发了！西门还有驻军的城防队，还有一位老学者在那里长期研究，你可以和他们同步下这里的异常状况…'}",
	[3247] = "{id = 3247, Speaker = 1028, Text = '看起来你已经准备好了，这才像样嘛！刚才又来了一个女剑士，她听说有黄金虫就直接冲向南部区域了，现在的年轻人可真有冲劲啊！'}",
	[3248] = "{id = 3248, Speaker = 0, Text = '那个女剑士名字是叫[c][ffff00]赛尼亚[-][/c]吗？'}",
	[3249] = "{id = 3249, Speaker = 1068, Text = '我想想…是叫赛尼亚！你赶快过去和她汇合吧，若真的遇到[c][ffff00]黄金虫[-][/c]，以一个新手剑士可是很难对付的！'}",
	[3250] = "{id = 3250, Speaker = 0, Text = '这里很危险！'}",
	[3251] = "{id = 3251, Speaker = 1051, Text = '喂~喂~喂，[c][ffff00][PlayerName][-][/c]，你看看清楚，我可是一个潜力不凡的剑士啊！比起我，你才更危险吧！'}",
	[3252] = "{id = 3252, Speaker = 0, Text = '果然有[c][ffff00]赛尼亚[-][/c]的独家风范啊…说不过你…要不我们比比谁清理魔物的速度快？'}",
	[3253] = "{id = 3253, Speaker = 1051, Text = '哈哈哈，比就比！快来清理这些[c][ffff00]盗虫[-][/c]吧！小心点，可别受伤哦！我可不是服事哦~'}",
	[3254] = "{id = 3254, Speaker = 0, Text = '这点任务好像没什么问题，轻松搞定！[c][ffff00]赛尼亚[-][/c]，你怎么了，受伤了？'}",
	[3255] = "{id = 3255, Speaker = 1051, Text = '没关系！本小姐一时疏忽罢了，不小心被盗虫咬了一口…'}",
	[3256] = "{id = 3256, Speaker = 0, Text = '红肿得那么厉害，你别动。我去帮你找点[c][ffff00]魔菇[-][/c]身上的[c][ffff00]魔粉[-][/c]，据说消肿效果不错…'}",
	[3257] = "{id = 3257, Speaker = 0, Text = '来，我帮你擦！知道了，我会轻点的啦…'}",
	[3258] = "{id = 3258, Speaker = 1051, Text = '呀！好羞人啊！还是我自己来吧~可别小看我，这些小伤不算什么！'}",
	[3259] = "{id = 3259, Speaker = 1051, Text = '我接到了任务需要寻找一位摄影师，还愣在那里干什么，和我一起找到他把！', Option = '知道啦！'}",
	[3260] = "{id = 3260, Speaker = 1124, Text = '太好了！终于有人来了！果然祈祷是有用的！'}",
	[3261] = "{id = 3261, Speaker = 0, Text = '你难道不知道最近下水道很危险吗？怎么还会在这特殊时期下来摄影呢？'}",
	[3262] = "{id = 3262, Speaker = 1124, Text = '这你就不懂了吧，魔物异常，这才是摄影的绝佳机会啊！平时还有专职的战地摄影师给[c][ffff00]《国家地理》[-][/c]提供优质作品呢！这可是我努力的方向啊！'}",
	[3263] = "{id = 3263, Speaker = 0, Text = '好吧，那你现在可以走了吗？下面真的太不安全了！'}",
	[3264] = "{id = 3264, Speaker = 1124, Text = '我还需要拍摄几张珍贵的照片呢，这里魔菇大泛滥啊，这可是上好的素材啊，你能为我拍摄1张吗？'}",
	[3265] = "{id = 3265, Speaker = 0, Text = '不行！为你的安全着想，你必须立刻离开这里！'}",
	[3266] = "{id = 3266, Speaker = 1124, Text = '如果你愿意为我拍摄的话，我会给你看一张我刚刚拍到的非常珍贵的照片。', Option = '勉强答应'}",
	[3267] = "{id = 3267, Speaker = 0, Text = '可恶！偏偏在这个时候！'}",
	[3268] = "{id = 3268, Speaker = 1124, Text = '哎~你的摄影技术还有待提高啊~这次我先将就一下啦~不管怎么样，谢谢你！'}",
	[3269] = "{id = 3269, Speaker = 0, Text = '咦？不是说有一张很珍贵的照片吗？你在下水道有发现什么异常了吗？'}",
	[3270] = "{id = 3270, Speaker = 1124, Text = '你自己看吧，刚开始的时候我也吓一跳呢！简直不敢相信自己的眼睛，这下应该可以上[c][ffff00]《国家地理》[-][/c]头条了！', Option = '接过照片'}",
	[3271] = "{id = 3271, Speaker = 1051, Text = '…'}",
	[3272] = "{id = 3272, Speaker = 1124, Text = '是啊，很罕见吧！在下水道里发现两条人鱼，大的那条好像挺不好惹的，所以我只在远处偷拍了几张照片，没敢靠近…'}",
	[3273] = "{id = 3273, Speaker = 0, Text = '赛尼亚！…'}",
	[3274] = "{id = 3274, Speaker = 1124, Text = '虽然很不甘心，不过我也想搞明白到底是怎么回事！我理理东西先回去了，我们后会有期！'}",
	[3275] = "{id = 3275, Speaker = 1051, Text = '！！！！怎么会这样！我们要赶紧找到这些人鱼！！'}",
	[3276] = "{id = 3276, Speaker = 0, Text = '得赶快找到他们！看起来他们似乎需要帮助，并不像是狂暴的魔物…'}",
	[3277] = "{id = 3277, Speaker = 1126, Text = '吼！吼！！！离我们远点，不要过来，人类！'}",
	[3278] = "{id = 3278, Speaker = 1051, Text = '别害怕，我们是冒险者不是坏人！你们需要帮助吗？'}",
	[3279] = "{id = 3279, Speaker = 1126, Text = '虚情假意！就是你们惹的祸！人类将粗大的管道连通了大海，我们才会被卷入肮脏不堪的下水道！海神会惩罚你们的！'}",
	[3280] = "{id = 3280, Speaker = 1051, Text = '他身上有伤，一定是[c][ffff00]盗虫[-][/c]干的！'}",
	[3281] = "{id = 3281, Speaker = 1051, Text = '得赶紧去弄点[c][ffff00]魔粉[-][/c]来，否则伤口红肿发炎会有生命危险的！对了，魔物的体质和人类不太一样，需要大量的魔粉才能起效！'}",
	[3282] = "{id = 3282, Speaker = 1126, Text = '走开！我宁死也不要人类为我治疗！水源的污染已经害死了我的家人和我的同胞！我们和人类势不两立！'}",
	[3283] = "{id = 3283, Speaker = 1051, Text = '好吧，你可以不接受…但你的孩子也受了伤，她的生命力可没那么顽强！你作为母亲，得为孩子多考虑点吧！'}",
	[3284] = "{id = 3284, Speaker = 1126, Text = '(转眼看着小小的卢西娅)'}",
	[3285] = "{id = 3285, Speaker = 1126, Text = '…[c][ffff00]卢西娅[-][/c]，我最心爱的孩子！好吧！但是如果你们想加害于她，我一定拼死让你们付出代价的！'}",
	[3286] = "{id = 3286, Speaker = 1127, Text = '…', Emoji = 17}",
	[3287] = "{id = 3287, Speaker = 1051, Text = '好可爱的小人鱼呀，那么小还不太会说话。别担心，我们治愈好你，就马上送你们回大海，回到自己的家…'}",
	[3288] = "{id = 3288, Speaker = 1126, Text = '别指望我会感谢你们！因为这一切都是你们人类造成的！但…[c][ffff00]卢西娅[-][/c]…'}",
	[3289] = "{id = 3289, Speaker = 1051, Text = '你别多想了，我们现在就送你们回去，在这种环境越久对你们伤害越大…万一你们被讨伐队的人看到，可就真的麻烦了…'}",
	[3290] = "{id = 3290, Speaker = 1051, Text = '[c][ffff00][PlayerName][-][/c]，我在这陪着他们。你先去清理一下前面的魔物吧，顺便查下通向大海的管道在哪里…', Option = '好的'}",
	[3291] = "{id = 3291, Speaker = 0, Text = '这回可不会再被盗虫攻击了吧…'}",
	[3292] = "{id = 3292, Speaker = 1129, Text = '年轻人，你来通往海底洞穴的入口干什么'}",
	[3293] = "{id = 3293, Speaker = 0, Text = ' 没…没什么…下面比较阴暗，一时没找到出口…'}",
	[3294] = "{id = 3294, Speaker = 1129, Text = '··'}",
	[3295] = "{id = 3295, Speaker = 1129, Text = '不过请放心，刚才我们一小队人已经追踪着血迹寻过去了…'}",
	[3296] = "{id = 3296, Speaker = 0, Text = ' (我得赶紧回去)'}",
	[3297] = "{id = 3297, Speaker = 1128, Text = '冒险者，靠后退！这只狂暴的海底魔物非常危险！让我们来对付它！'}",
	[3298] = "{id = 3298, Speaker = 0, Text = '等一下，危险！'}",
	[3299] = "{id = 3299, Speaker = 1126, Text = '可恶的人类！你们难道还没有认识到自己的罪行吗？！'}",
	[3300] = "{id = 3300, Speaker = 1128, Text = '冒险者，赶快离开那里！'}",
	[3301] = "{id = 3301, Speaker = 0, Text = '卢西沙发出了一声极其刺耳的尖叫声…悲鸣而壮烈…'}",
	[3302] = "{id = 3302, Speaker = 1128, Text = '“啊！怎么回事，好晕啊！”', Emoji = 19}",
	[3303] = "{id = 3303, Speaker = 1126, Text = '快走，带走卢西娅，让她活下去！'}",
	[3304] = "{id = 3304, Speaker = 1051, Text = '[c][ffff00][PlayerName][-][/c]，你带着小人鱼先走，我来把它引开！'}",
	[3305] = "{id = 3305, Speaker = 1051, Text = '什么意思？！[c][ffff00]卢西沙[-][/c]，那你怎么办呢？！'}",
	[3306] = "{id = 3306, Speaker = 1126, Text = '我的伤太重了，被污染侵蚀后还被盗虫围攻。刚才用尽力气吟唱后，我体内好像有些异常的物质在滋生！快走啊！等[c][ffff00]卢西娅[-][/c]长大后告诉她，她的母亲很爱她！'}",
	[3307] = "{id = 3307, Speaker = 1051, Text = '不行！要走一起走啊！'}",
	[3308] = "{id = 3308, Speaker = 0, Text = '走吧！赛尼亚！卫兵快要恢复意识了！再不走，连[c][ffff00]卢西娅[-][/c]都有危险了！'}",
	[3309] = "{id = 3309, Speaker = 0, Text = '糟糕！还有卫兵，实在没办法了，找机会打晕他们吧！'}",
	[3310] = "{id = 3310, Speaker = 0, Text = '[c][ffff00]卢西娅[-][/c]，我们只能送到你这里了，你一定要活下去啊！我们后会有期'}",
	[3311] = "{id = 3311, Speaker = 0, Text = '我没事…'}",
	[3312] = "{id = 3312, Speaker = 1127, Text = '…', Emoji = 24}",
	[3313] = "{id = 3313, Speaker = 0, Text = '我得赶快回去。'}",
	[3314] = "{id = 3314, Speaker = 1128, Text = '年轻人，你刚才去哪了，我们醒来后，人鱼不见了，我还以为你们遇难了。'}",
	[3315] = "{id = 3315, Speaker = 0, Text = '[c][ffff00]赛尼亚[-][/c]，太好了你没事。'}",
	[3316] = "{id = 3316, Speaker = 1051, Text = '[c][ffff00]黄金盗虫[-][/c]并没有攻击我，而是去往了下水道深处。'}",
	[3317] = "{id = 3317, Speaker = 1124, Text = '…我偷偷跟随你们过来的，没想到拍摄下了那么不幸的一幕，你看。', Option = '接过照片'}",
	[3318] = "{id = 3318, Speaker = 0, Text = '…这是…卢西沙狂暴时的照片…'}",
	[3319] = "{id = 3319, Speaker = 0, Text = '我必须把这些照片尽快上交给研究所，这里的污染和人鱼的狂暴太异常了！里奥，谢谢你的照片！'}",
	[3320] = "{id = 3320, Speaker = 1128, Text = '冒险者，[c][ffff00]下水道有一条捷径通向普隆德拉的西门[-][/c]，你去那里看看吧。西门还有驻军的城防队，还有一位老学者在那里长期研究，你可以和他们同步下这里的异常状况…'}",
	[3321] = "{id = 3321, Speaker = 1108, Text = '冒险者你终于来了，[c][ffff00]沉船[-][/c]的动乱暂时控制住了，请协助普隆德拉魔物研究院成员彻底进行排查。分队长正在沉船等着你呢！'}",
	[3325] = "{id = 3325, Speaker = 1140, Text = 'xxxxxxxxxxxxx'}",
	[3326] = "{id = 3326}",
	[3329] = "{id = 3329, Speaker = 0, Text = '吉芬魔法公会的布雷纳大人似乎特别在意海底的异动呢…整个公会的人估计都在忙着想法重新封印呢~'}",
	[3347] = "{id = 3347, Speaker = 1138, Text = '果然这些血液有问题，需要进一步调查。帮我再采集些[c][ffff00]白布和遗骨[-][/c]回来吧~'}",
	[3348] = "{id = 3348, Speaker = 1138, Text = '这些魔粉的功效很棒，是很好的医疗素材。'}",
	[3349] = "{id = 3349, Speaker = 1138, Text = '死人遗骨也不对，总感觉有点异常…'}",
	[3366] = "{id = 3366, Speaker = 1135, Text = '干的漂亮！接下来我们仔细调查一下，看有没有新的线索！'}",
	[3371] = "{id = 3371, Speaker = 1135, Text = '我在这接着研究这本日志，说不定会有其它发现！麻烦你再深入调查一下吧！', Option = '好的！'}",
	[3407] = "{id = 3407, Speaker = 1126, Text = '冒险者！消灭我！请你快消灭我！我体内有一些奇怪的东西，我快控制不住自己了！我在逐渐丧失理智！记得保护好卢西娅…啊！！！脑袋好痛！吼！！！'}",
	[3408] = "{id = 3408, Speaker = 1073, Text = '[c][ffff00][PlayerName][-][/c]，想去体验一下战斗的乐趣么？我可以送你前往[c][ffff00]打斗场[-][/c]哦~', Option = 'Fighting！', Emoji = 20}",
	[3417] = "{id = 3417, Speaker = 1078, Text = '不错哦~拍的是中央喷泉啊~好赞！[c][ffff00][PlayerName][-][/c]很有摄影天赋啊~以后多多向杂志投稿吧，期待你的表现哦~', Option = '再见'}",
	[3420] = "{id = 3420, Speaker = 1098, Text = '小伙子，[c][ffff00]柏伊亚岚岛[-][/c]是么？马上送你过去…'}",
	[3421] = "{id = 3421, Speaker = 1147, Text = '已经到啦，魔法公会的[c][ffff00]艾休斯[-][/c]就在岛的那边，快去吧！'}",
	[3422] = "{id = 3422, Speaker = 1148, Text = '现在进入[c][ffff00]海底洞穴[-][/c]已经非常危险，长老不得不亲自进入洞穴内检查封印点，时间紧迫！快去找长老了解具体的情况吧…'}",
	[3423] = "{id = 3423, Speaker = 1148, Text = '还有一些探险队的人在洞穴里，希望他们现在没事吧…'}",
	[3426] = "{id = 3426, Speaker = 1151, Text = '[c][ffff00]魔法公会长老[-][/c]正在海底洞穴施法，[c][ffff00]洞穴二层[-][/c]暂时不对外开放！'}",
	[3427] = "{id = 3427, Speaker = 1151, Text = '哟，能和我说上话可不容易~RO体验得怎么样？看样子挺认真的嘛~'}",
	[3428] = "{id = 3428, Speaker = 1151, Text = '上次在海底洞穴深处捡到好东西了，反正我是NPC用不上，就大方送给你吧！对你也没什么追求，到时记得多多捧场多多充值就是了，达不成指标的话我可会消失的，拜托拜托~'}",
	[3429] = "{id = 3429, Speaker = 0, Text = '对不起，[c][ffff00]卢西沙[-][/c]！我不得不…真的对不起…愿你在神的世界中得到安详…'}",
	[3430] = "{id = 3430, Speaker = 1128, Text = '危险！冒险者，这只人鱼完全暴走了，赶快击败它，不然它会承受更痛苦的伤害。'}",
	[3431] = "{id = 3431, Speaker = 1128, Text = '冒险者拥有[c][ffff00]女神项链[-][/c]后，可以[c][ffff00]将狂暴的魔物消灭后净化它[-][/c]，就能让魔物之灵安息~现在快[c][ffff00]净化这条人鱼让她安详离去[-][/c]吧！'}",
	[3432] = "{id = 3432, Speaker = 0, Text = '…卢西沙，一路走好！'}",
	[3444] = "{id = 3444, Speaker = 1153, Text = '…'}",
	[3454] = "{id = 3454, Speaker = 0, Text = '咦？那边有个人，看起来是个有故事的人，去看看！', Emoji = 6}",
	[3455] = "{id = 3455, Speaker = 1155, Text = '…', Emoji = 9}",
	[3457] = "{id = 3457, Speaker = 1155, Text = '…', Emoji = 19}",
	[3458] = "{id = 3458, Speaker = 1155, Text = '…', Emoji = 24}",
	[3460] = "{id = 3460, Speaker = 1149, Text = '我的伙伴们在海底洞穴发现了财宝的线索，可是一个拥有巨大羊角，带着镰刀的死神的家伙让整个海底洞穴都暴走了起来…'}",
	[3461] = "{id = 3461, Speaker = 1149, Text = '我逃了出来，可是探险队有一些不要命的家伙任然在洞穴里趁乱探索财宝，真是太危险了，能帮忙把他们都救出来吗？', Option = '我尽力！'}",
	[3462] = "{id = 3462, Speaker = 1156, Text = '冒险者，远古的封印被破除，魔王的部分力量已经被施放，由于时间紧迫需要请先收集一些被螃蟹吞掉的[c][ffff00]贡品碎片[-][/c]，还有要清理掉一些[c][ffff00]海葵[-][/c]，等会再详细解释给你听这里的情况。'}",
	[3463] = "{id = 3463, Speaker = 1156, Text = '好的，有了这些碎片我就能为稳定镜像争取时间了。'}",
	[3464] = "{id = 3464, Speaker = 1156, Text = '好险，核心暂时稳定住了。'}",
	[3465] = "{id = 3465, Speaker = 0, Text = '这里的封印到底怎么回事？'}",
	[3466] = "{id = 3466, Speaker = 1156, Text = '在这座海神像的下面是一种叫做镜像封印的远古封印法，但是封印核心已经被破坏，镜像点提供的能量无法被核心吸收，就会变的极其不稳定甚至发生大爆炸，整个海底洞穴，包括亚岚岛都会被摧毁。'}",
	[3467] = "{id = 3467, Speaker = 0, Text = '爆炸？那这里的探险队成员现在不是很危险吗！'}",
	[3468] = "{id = 3468, Speaker = 1156, Text = '嗯，除非拥有“海洋精华”才能彻底的修复封印，目前我只能暂时稳定住这里的情况，关闭镜像点来让爆炸损失降到最低。'}",
	[3469] = "{id = 3469, Speaker = 0, Text = '不，我一定要想办法解救海底！'}",
	[3470] = "{id = 3470, Speaker = 1156, Text = '年轻人，我过来的路上看到很多被海葵刺伤的人，这里有[c][ffff00]药剂[-][/c]赶快去治疗他们，让他们尽快撤离。'}",
	[3471] = "{id = 3471, Speaker = 1156, Text = '做的好，冒险者，现在去北面看一下还有没有受伤的人。'}",
	[3472] = "{id = 3472, Speaker = 0, Text = '你没事吧，这里非常危险，请赶快撤离。'}",
	[3473] = "{id = 3473, Speaker = 1157, Text = '这里的情况简直糟透了，魔物暴走我根本无法靠近他们，我的孩子们一定会非常失望我没能带回‘海底的大发现’，冒险者你能帮帮我吗？我不想让孩子们失望。'}",
	[3474] = "{id = 3474, Speaker = 0, Text = '不让孩子们失望？这好办！'}",
	[3475] = "{id = 3475, Speaker = 1157, Text = '孩子们喜欢闪亮的东西，这些他们一定喜欢！'}",
	[3476] = "{id = 3476, Speaker = 1157, Text = '有了这张照片就能把海底的美景带去给孩子们了！'}",
	[3477] = "{id = 3477, Speaker = 0, Text = '这些钳子装在手上会不会像巴尔坦星人呢？'}",
	[3478] = "{id = 3478, Speaker = 1157, Text = '谢谢你，冒险者，我会告诉孩子们这是一个勇敢的陌生人，送他们的礼物！'}",
	[3479] = "{id = 3479, Speaker = 0, Text = '好了，你赶快离开这里吧！'}",
	[3480] = "{id = 3480, Speaker = 1157, Text = '好的我们后会有期！'}",
	[3481] = "{id = 3481, Speaker = 0, Text = '这附近应该没有其他探险者了，先去镜像点吧！'}",
	[3482] = "{id = 3482, Speaker = 0, Text = '你好，我是冒险者公会的[c][ffff00][PlayerName][-][/c]，这里看来就是寇长老所说的第一个镜像点了！'}",
	[3483] = "{id = 3483, Speaker = 1158, Text = '是的，[c][ffff00][PlayerName][-][/c]，镜像点被破坏了，我们需要先修复镜像点，然后再关闭它！'}",
	[3484] = "{id = 3484, Speaker = 1158, Text = '先为我获取一些细长的触须，我需要中和结晶中和掉这里的不稳定能量，才能施法修复镜像！'}",
	[3485] = "{id = 3485, Speaker = 1158, Text = '非常好，不稳定能量暂时去除了！'}",
	[3486] = "{id = 3486, Speaker = 1158, Text = '接下来我需要海底洞穴内非常珍贵的一种物质，[c][ffff00]碧青结晶[-][/c]，我已经派学徒去寻找了，冒险者你也帮助我一起寻找吧！'}",
	[3487] = "{id = 3487, Speaker = 0, Text = '碧青结晶是吗，好的。'}",
	[3488] = "{id = 3488, Speaker = 0, Text = '有人受伤了？你是魔法师公会的学徒吗？'}",
	[3489] = "{id = 3489, Speaker = 1160, Text = '是的，我叫雷姆，你就是冒险者吧，我追踪一条[c][ffff00]人鱼[-][/c]来到这里。'}",
	[3490] = "{id = 3490, Speaker = 0, Text = '人鱼？难道人鱼拥有着[c][ffff00]碧青结晶[-][/c]？'}",
	[3491] = "{id = 3491, Speaker = 1160, Text = '未必，但是有希望可以找到，在这之前你能帮我清理掉一些这里的[c][ffff00]海葵[-][/c]吗，他们一堆堆的太难缠了。'}",
	[3492] = "{id = 3492, Speaker = 0, Text = '卢西娅！是[c][ffff00]卢西娅[-][/c]吗？'}",
	[3493] = "{id = 3493, Speaker = 1163, Text = ' …', Emoji = 26}",
	[3494] = "{id = 3494, Speaker = 0, Text = '啊~真的是你，哈哈，没想到这么快就又见面了。', Emoji = 30}",
	[3495] = "{id = 3495, Speaker = 1163, Text = ' …', Emoji = 27}",
	[3496] = "{id = 3496, Speaker = 1159, Text = '真是奇怪，我的探测器明明显示这里有能量极高的海底宝藏，没想到确实这只小东西。'}",
	[3497] = "{id = 3497, Speaker = 0, Text = '你也别想打坏主意，这里没有你们所谓的宝藏，快离开这里吧，这里变的很危险了。'}",
	[3498] = "{id = 3498, Speaker = 0, Text = '[c][ffff00]卢西娅[-][/c]，别担心，我会想办法拯救海底洞穴的，你知道哪里有[c][ffff00]碧青结晶[-][/c]吗？'}",
	[3499] = "{id = 3499, Speaker = 1163, Text = '…', Emoji = 19}",
	[3500] = "{id = 3500, Speaker = 0, Text = '好吧，我得赶紧回去问下[c][ffff00]乌拉蕾雅[-][/c]，这里没有[c][ffff00]碧青结晶[-][/c]，我们再想想其他办法。'}",
	[3501] = "{id = 3501, Speaker = 0, Text = '[c][ffff00]蕾雅[-][/c]，这里很难找到[c][ffff00]碧青结晶[-][/c]，我们还有其他办法吗？'}",
	[3502] = "{id = 3502, Speaker = 1158, Text = '冒险者，我知道你尽力了，不过就在刚才镜像彻底损坏了，已经无法修复了。'}",
	[3503] = "{id = 3503, Speaker = 0, Text = '那现在怎么办？', Emoji = 18}",
	[3504] = "{id = 3504, Speaker = 1158, Text = '你先去[c][ffff00]第二镜像点[-][/c]看下情况吧，争取可以关闭它，目前的情况只能把爆炸损失降到最小了，除非找到传说中的“海洋精华”，但是那不太可能了。'}",
	[3505] = "{id = 3505, Speaker = 1161, Text = '冒险者，来的早不如来的巧！这里的不稳定能量已经成功驱散了，我收集了附近所有的[c][ffff00]碧青结晶[-][/c]，现在修复用的结晶浓度有些高了。'}",
	[3506] = "{id = 3506, Speaker = 0, Text = '原来是你…怪不得[c][ffff00]碧青结晶[-][/c]都没有了。'}",
	[3507] = "{id = 3507, Speaker = 1161, Text = '我需要中和一下这些[c][ffff00]碧青结晶[-][/c]，冒险者，为我找一些[c][ffff00]达拉蛙的体液[-][/c]来，我需要制成中和剂。'}",
	[3508] = "{id = 3508, Speaker = 1161, Text = '好的冒险者，我现在需要准备吟唱修复了，请确保我周围的魔物不来打扰我，那边有个人，叫他走开这里很危险。'}",
	[3509] = "{id = 3509, Speaker = 1162, Text = '哈哈哈哈哈，宝藏，我终于找到宝藏了，哈哈哈哈哈哈哈哈哈！'}",
	[3510] = "{id = 3510, Speaker = 0, Text = '赶快离开这里，你不要命了，这些财宝也换不了你的生命！'}",
	[3511] = "{id = 3511, Speaker = 1162, Text = '不，[c][ffff00]海洋精华[-][/c]一定就在这里，我将拥有无尽的财富！'}",
	[3512] = "{id = 3512, Speaker = 0, Text = '海洋精华？！到底什么才是[c][ffff00]海洋精华[-][/c]？'}",
	[3513] = "{id = 3513, Speaker = 1162, Text = '传说中，藏在海底洞穴的至高宝物，他代表了善良，感恩，美丽，它全身散发着蓝色的光芒，这种宝物可是能卖出一个超高的价格啊！'}",
	[3514] = "{id = 3514, Speaker = 0, Text = '不可理喻！你好自为之吧！'}",
	[3515] = "{id = 3515, Speaker = 1161, Text = '已经没时间再等了，我需要立刻开始仪式。'}",
	[3516] = "{id = 3516, Speaker = 0, Text = '好，拜托了，吉芬的魔法师！'}",
	[3517] = "{id = 3517, Speaker = 1161, Text = '冒险者，在我开始仪式的时候请保护我，在过程中，周围的能量与立场会不稳定，很可能遭受到各种时空裂隙中的魔物的攻击。'}",
	[3518] = "{id = 3518, Speaker = 0, Text = '放心吧，有我在！'}",
	[3519] = "{id = 3519, Speaker = 1307, Text = '动乱的能量引来了时空裂隙中的狂暴魔物。'}",
	[3520] = "{id = 3520, Speaker = 1307, Text = '雷萨开始吟唱。'}",
	[3521] = "{id = 3521, Speaker = 1307, Text = '动乱的能量触怒了海神。'}",
	[3522] = "{id = 3522, Speaker = 1317, Text = '别想修复这些愚蠢的封印，末日即将临近，哈哈哈哈哈哈。'}",
	[3523] = "{id = 3523, Speaker = 0, Text = '[c][ffff00]巴风特[-][/c]，果然是你，我一定不会让你得逞的。'}",
	[3524] = "{id = 3524, Speaker = 1318, Text = '混蛋人类，海洋已经千沧百孔了，你们还想要什么？！'}",
	[3525] = "{id = 3525, Speaker = 0, Text = '不，我们只是想拯救海洋！'}",
	[3526] = "{id = 3526, Speaker = 1318, Text = '你们谁都救不了！'}",
	[3527] = "{id = 3527, Speaker = 1161, Text = '糟糕了，[c][ffff00]巴风特[-][/c]之前到过这里，并且把伊米尔的粉尘散布在这里！'}",
	[3528] = "{id = 3528, Speaker = 1161, Text = '加之刚才的动乱，这里的镜像也修复失败了，冒险者，赶快去通知长老，离开这里！'}",
	[3529] = "{id = 3529, Speaker = 0, Text = '好可恶的[c][ffff00]巴风特[-][/c]，[c][ffff00]卢西娅[-][/c]我又一次没能保护好对你重要的东西！'}",
	[3530] = "{id = 3530, Speaker = 1156, Text = '是吗…大家都尽力了，核心刚才就已经起反应了，我也大概猜到了镜像失手了。'}",
	[3531] = "{id = 3531, Speaker = 0, Text = '我们该怎么办…就真的没有其他办法了吗？'}",
	[3532] = "{id = 3532, Speaker = 1156, Text = '带着大家快走吧，在我用尽全身的魔法能量前还可以撑一段时间！'}",
	[3533] = "{id = 3533, Speaker = 0, Text = '不~~长老…为什么我什么都保护不了，为什么总要有人牺牲？'}",
	[3534] = "{id = 3534, Speaker = 0, Text = '[c][ffff00]卢西娅[-][/c]？'}",
	[3535] = "{id = 3535, Speaker = 1163, Text = '…', Emoji = 26}",
	[3536] = "{id = 3536, Speaker = 0, Text = '[c][ffff00]卢西娅[-][/c]，这蓝色的这是什么？！'}",
	[3537] = "{id = 3537, Speaker = 1156, Text = '这是…海洋精华原来真的存在啊，[c][ffff00]人鱼之心[-][/c]就是海洋精华。'}",
	[3538] = "{id = 3538, Speaker = 0, Text = '[c][ffff00]卢西娅[-][/c]…！！'}",
	[3539] = "{id = 3539, Speaker = 1163, Text = '…', Emoji = 27}",
	[3540] = "{id = 3540, Speaker = 1156, Text = '这条人鱼是来报恩的，你曾经帮助过她吗？冒险者。'}",
	[3541] = "{id = 3541, Speaker = 0, Text = '…[c][ffff00]卢西娅[-][/c]。谢谢你…你的心意我了解了。'}",
	[3542] = "{id = 3542, Speaker = 1156, Text = '不管怎么样，谢谢你冒险者，你的所作所为拯救了我们大家。'}",
	[3543] = "{id = 3543, Speaker = 0, Text = '不，是属于海洋的生命拯救了我们和他们自己。'}",
	[3544] = "{id = 3544, Speaker = 1156, Text = '果然这些无价之宝只有属于海洋的生命体才能拥有，有了[c][ffff00]海洋精华[-][/c]我现在就可以修复封印。'}",
	[3545] = "{id = 3545, Speaker = 0, Text = '那边！人鱼往那去了~快去看看。'}",
	[3546] = "{id = 3546, Speaker = 0, Text = '这里有一个！', Option = '救助探险队员'}",
	[3547] = "{id = 3547, Speaker = 0, Text = '呼~终于忙完了。'}",
	[3548] = "{id = 3548, Speaker = 0, Text = '…', Emoji = 25}",
	[3549] = "{id = 3549, Speaker = 0, Text = '…', Emoji = 12}",
	[3550] = "{id = 3550, Speaker = 0, Text = '原来真的是[c][ffff00]巴风特[-][/c]，还好只是个分身…'}",
	[3551] = "{id = 3551, Speaker = 1163, Text = '…', Emoji = 27}",
	[3570] = "{id = 3570, Speaker = 1111, Text = '[c][ffff00][PlayerName][-][/c]在[c][ffff00]依斯鲁得[-][/c]的时候非常感谢你的帮助，我现在也已经是伟大的魔法师中的一份子了！'}",
	[3573] = "{id = 3573, Speaker = 1178, Text = '是否向女神像祈祷？', Option = '{虔诚的祈祷,2},{暂时离开,3}'}",
	[3574] = "{id = 3574, Speaker = 1178, Text = '伟大的女神像恢复了昔日的光辉，在她的周围你感到力量正在汇聚。'}",
	[3575] = "{id = 3575, Speaker = 1178, Text = '石像依然安静地屹立着。'}",
	[3590] = "{id = 3590, Speaker = 1313, Text = '好在吉芬四周有强力的魔法罩，强大的魔物才无法作怪！'}",
	[3591] = "{id = 3591, Speaker = 1313, Text = '因此一些力量比较弱的旅行家也可以放心的移动至都市四周。'}",
	[3592] = "{id = 3592, Speaker = 1313, Text = '不过也不能因此就掉以轻心，要是不小心跨越的话，可是会出现强力的魔物的喔！'}",
	[3593] = "{id = 3593, Speaker = 1313, Text = '我以前没见过你把？'}",
	[3667] = "{id = 3667, Speaker = 1352, Text = '不要拿我和那些低等哥布灵相提并论，[c][ffff00]虹色元素[-][/c]并不是其他学者哥布灵可以提炼出的。'}",
	[3727] = "{id = 3727, Speaker = 1008, Text = '等等~冒险者，这里有一只向我们求助的哥布灵，你应该听听他说的。'}",
	[3728] = "{id = 3728, Speaker = 0, Text = '是吗？！那就告诉我，你们的目的是什么？', Emoji = 1}",
	[3729] = "{id = 3729, Speaker = 1325, Text = '其实我们并不想攻击吉芬，事实上我们非常的害怕魔法师们，只是不久前，国王突然下令攻击吉芬，我们只能听从命令…我好久没回家了。', Emoji = 24}",
	[3730] = "{id = 3730, Speaker = 0, Text = '…果然还是让我去找你们的国王聊聊！！', Emoji = 12}",
	[3731] = "{id = 3731, Speaker = 1325, Text = '就在昨天发生了一件事，格哥布不知道该怎么说，但你跟着格哥布就会知道了。'}",
	[3732] = "{id = 3732, Speaker = 1325, Text = '那天我在营地门口看到了几个魔法师。'}",
	[3733] = "{id = 3733, Speaker = 1325, Text = '很不凑巧，他们被巡逻的弓箭哥布灵发现了。'}",
	[3734] = "{id = 3734, Speaker = 1325, Text = '我听到其他的哥布灵说，快点找绳子帮他们绑起来。'}",
	[3735] = "{id = 3735, Speaker = 1325, Text = '暴露行踪后的魔法师往另一个出口逃去，可是哥布灵的数量越来越多。'}",
	[3736] = "{id = 3736, Speaker = 1325, Text = '他们用尽全力终于逃到了另一个出口，可是[c][ffff00]哥布灵国王[-][/c]带领高级士兵出现再了他们面前。'}",
	[3737] = "{id = 3737, Speaker = 1325, Text = '魔法师已经没有力量与国王为敌于是往回逃往了断桥处，被哥布灵士兵包围。'}",
	[3738] = "{id = 3738, Speaker = 1326, Text = '感谢你来救我们冒险者。'}",
	[3739] = "{id = 3739, Speaker = 0, Text = '快走，我来保护你们。'}",
	[3740] = "{id = 3740, Speaker = 1326, Text = '呼好险，这些哥布灵见人就抓，营地里除了一些食物和武器外没有任何东西。', Emoji = 10}",
	[3741] = "{id = 3741, Speaker = 0, Text = '刚才我见到了他们的国王了，我觉得是个冒牌货！'}",
	[3742] = "{id = 3742, Speaker = 1325, Text = '啊！~冒牌货~冒牌货！！，我要去通知我的朋友去。'}",
	[3743] = "{id = 3743, Speaker = 0, Text = '格哥布，快去阻止这场闹剧吧！'}",
	[3744] = "{id = 3744, Speaker = 1326, Text = '冒险者，就在我们被困断桥时，看到南面有几个商人似乎遇到了危险。'}",
	[3745] = "{id = 3745, Speaker = 0, Text = '看来这个冒牌国王还能再得瑟一会，我先去一次南面。'}",
	[3746] = "{id = 3746, Speaker = 1327, Text = '冒险者，我们从梦罗克过来救助，看来吉芬这边情况也不容乐观啊！'}",
	[3747] = "{id = 3747, Speaker = 0, Text = '你们没事吧？'}",
	[3748] = "{id = 3748, Speaker = 1327, Text = '可别小看梦罗克商人的战斗能力哦，不过大量的哥布灵的确让我们浪费了一点时间。'}",
	[3749] = "{id = 3749, Speaker = 0, Text = '梦罗克也发生了什么吗？'}",
	[3750] = "{id = 3750, Speaker = 1327, Text = '[c][ffff00]金字塔[-][/c]最近非常动荡，我们担心是封印出现了某些问题，正要求助魔法师们，没想到吉芬目前是这样一个情况！'}",
	[3751] = "{id = 3751, Speaker = 0, Text = '这边还是比较危险，我先护送你们去[c][ffff00]吉芬城[-][/c]内吧。'}",
	[3752] = "{id = 3752, Speaker = 1327, Text = '清理一下这里的哥布灵吧，数量太多了。'}",
	[3753] = "{id = 3753, Speaker = 1327, Text = '那些[c][ffff00]喷射哥布灵[-][/c]太嚣张了！'}",
	[3754] = "{id = 3754, Speaker = 1327, Text = '感谢你，冒险者，剩下的事交给我们就可以了。'}",
	[3755] = "{id = 3755, Speaker = 1322, Text = '你还真是个多管闲事的能手啊，情况我已经了解了，那个冒牌货你能搞定吗？'}",
	[3756] = "{id = 3756, Speaker = 0, Text = '[c][ffff00]哥布灵营地[-][/c]的士兵太多了，我需要确定冒牌国王的位置。'}",
	[3757] = "{id = 3757, Speaker = 1322, Text = '这种小事在魔法师的眼里不值一提，那边的[c][ffff00][c][ffff00]载具管理员[-][/c][-][/c]帮助你。'}",
	[3758] = "{id = 3758, Speaker = 0, Text = '哈哈，你也不像你表现出来的那样无情嘛。'}",
	[3759] = "{id = 3759, Speaker = 1322, Text = '少废话，快点搞定这场荒唐的闹剧。'}",
	[3814] = "{id = 3814, Speaker = 1156, Text = '孩子，你的勇气与智慧我想我比在场的其他人更了解，回到你的导师那里，是时候进阶了！'}",
	[3815] = "{id = 3815, Speaker = 1321, Text = '当你准备完成后请回到吉芬，我会告诉你下一步该怎么做。'}",
	[3842] = "{id = 3842, Speaker = 1015, Text = '这样就可以啦，每当你想家的时候记得使用[c][ffff00]蝴蝶翅膀[-][/c]，我等你哦。', Emoji = 2}",
	[3853] = "{id = 3853, Speaker = 1322, Text = '恶梦の王の一片よ，世界のいましめ解き放たれし…[c][ffff00]神々の魂すらも打ち碎き[-][/c]'}",
	[3860] = "{id = 3860, Speaker = 1381, Text = '幸运四叶草顾名思义，拥有4片叶子，现在你将完成第一片的任务-荣耀。'}",
	[3862] = "{id = 3862, Speaker = 1381, Text = '四叶草见证了你的荣耀。下面进行第二片的任务-财富，我需要一枚来自商人的钱币。'}",
	[3863] = "{id = 3863, Speaker = 1381, Text = '我将给予你一点点小帮助，请快找来吧。'}",
	[3867] = "{id = 3867, Speaker = 1381, Text = '不错啊，这么快就找到了，下面进行第三片的任务。'}",
	[3869] = "{id = 3869, Speaker = 1381, Text = '我心中也曾有那个她，好吧第四片的任务是-健康。'}",
	[3883] = "{id = 3883, Speaker = 0, Text = '这样做会不会有危险?'}",
	[3889] = "{id = 3889, Speaker = 0, Text = '魔物解决了，但这样的实验太残忍了。'}",
	[3900] = "{id = 3900, Speaker = 1382, Text = '谢谢你，我借给你一只[c][ffff00]大嘴鸟[-][/c]，他平时可是只为骑士们提供服务哦。'}",
	[3915] = "{id = 3915, Speaker = 1044, Text = '你难道忘记他们了吗?来吧呆呆和萌萌选择一个。'}",
	[3923] = "{id = 3923, Speaker = 2123, Text = '呼噜噜…呼噜噜…'}",
	[3953] = "{id = 3953, Speaker = 2123, Text = '学会[c][ffff00]装死[-][/c]技能。', Option = '下面'}",
	[4017] = "{id = 4017, Speaker = 1071, Text = '冒险者，最近各地都出现了问题，我们城防队对周边地区的支援增大，已经非常缺少人手了。'}",
	[4018] = "{id = 4018, Speaker = 1071, Text = '看板上的任务已经快粘不下了。', Option = '看板任务？'}",
	[4019] = "{id = 4019, Speaker = 1071, Text = '是啊，各个公会都会在看板张贴悬赏任务，这些任务里大到与国家有关的，小到帮卡普拉小姐找小狗。'}",
	[4020] = "{id = 4020, Speaker = 1071, Text = '是不管大小，都是需要认真完成的，报酬也是非常丰厚的。', Option = '我知道了'}",
	[4021] = "{id = 4021, Speaker = 1071, Text = '来吧，一起帮看板小姐减减压。'}",
	[4055] = "{id = 4055, Speaker = 0, Text = '我必须吧这些照片交给魔物研究院的人。'}",
	[4056] = "{id = 4056, Speaker = 1128, Text = '年轻人，听说有一位老学者住在[c][ffff00]普隆德拉[-][/c]的西侧的那片森林里，你可以从他身上获得帮助，但是现在请先回到艾里克那里，向他说明下水道的情况。'}",
	[4057] = "{id = 4057, Speaker = 1118, Text = '怎么愁眉苦脸的，下水道的任务完成得非常好。'}",
	[4058] = "{id = 4058, Speaker = 1118, Text = '赶快在你的[c][ffff00]冒险手册[-][/c]里记上一笔。'}",
	[4059] = "{id = 4059, Speaker = 1118, Text = '什么！你还没有[c][ffff00]冒险手册[-][/c]？', Option = '我还是个新人'}",
	[4060] = "{id = 4060, Speaker = 1118, Text = '好吧，每个冒险者都会拥有属于自己的[c][ffff00]冒险手册[-][/c]，用他来记录你冒险的点点滴滴，这不但能积累你的经验，还能丰富你的回忆。'}",
	[4061] = "{id = 4061, Speaker = 1118, Text = '拿好了，这是你的[c][ffff00]冒险手册[-][/c]。'}",
	[4062] = "{id = 4062, Speaker = 1118, Text = '总有那么一天，你老了，退休之后这本[c][ffff00]冒险手册[-][/c]，将是你最宝贵的财富。', Option = '谢谢！'}",
	[4063] = "{id = 4063, Speaker = 1118, Text = '好了，之后的冒险会越来越刺激哦~像你这样穿的破破烂烂的可是很危险的~'}",
	[4064] = "{id = 4064, Speaker = 1118, Text = '在普隆德拉城内有一位传说的中的铁匠的后代，他的名字叫[c][ffff00]忽克连[-][/c]，你可以找他谈谈哦~'}",
	[4101] = "{id = 4101, Speaker = 0, Text = '似乎惊动了种子的守护者！！(必须先消灭守护者)'}",
	[5014] = "{id = 5014, Speaker = 1501, Text = '[c][ffff00][PlayerName][-][/c]已经经历过一些战斗，现在就可以打开冒险手册进行汇报了。', Option = '好的'}",
	[5039] = "{id = 5039, Speaker = 1503, Text = '…'}",
	[5048] = "{id = 5048, Speaker = 1118, Text = '[c][ffff00][PlayerName][-][/c]，在继续你的冒险前，可以在我这里找到提升实力的捷径，我相信你一定会有所作为！'}",
	[5049] = "{id = 5049, Speaker = 1118, Text = '[c][ffff00][PlayerName][-][/c]，在继续你的冒险前，可以在我这里找到提升实力的捷径，我相信你一定会有所作为！'}",
	[5061] = "{id = 5061, Speaker = 1191, Text = '现在就要返回普隆德拉吗？', Option = '确定'}",
	[5062] = "{id = 5062, Speaker = 1192, Text = '现在就要返回依斯鲁得吗？', Option = '确定'}",
	[5064] = "{id = 5064, Speaker = 1430, Text = '[c][ffff00][PlayerName][-][/c]，想离开这里了么？我可以送你前往[c][ffff00]依斯鲁得[-][/c]哦~'}",
	[5065] = "{id = 5065, Speaker = 1058, Text = '音乐不但可以治愈人心，还能给人们的内心带来力量。你也一定是这样认为的吧！', Option = '{赞同,2}{没兴趣,1}', Emoji = 2}",
	[5092] = "{id = 5092, Speaker = 1068, Text = '没想到这么快又见面了，勇敢的冒险者。'}",
	[5100] = "{id = 5100, Speaker = 1075, Text = '[c][ffff00]女武神[-][/c]也认可了你的实力，她有话想亲自对你说。'}",
	[5111] = "{id = 5111, Speaker = 1581, Text = '呀，[c][ffff00][PlayerName][-][/c]，在这能撞见，看来我们挺有缘嘛。初次见面，送你个礼物箱，以后斐扬洞窟遇见还望多多关照！', Option = '领取礼包'}",
	[5112] = "{id = 5112, Speaker = 1581, Text = '斐扬部分区域在改建，可不能把我可爱的外衣弄脏了。听说这里有[c][ffff00]萌动测试[-][/c]，就过来随便逛逛！看到大家萌萌哒，心里好舒畅，啦啦啦~'}",
	[6004] = "{id = 6004, Speaker = 1604, Text = '战斗，挑战！测试冒险者的时候到了！这里可不是随随便便就能通过的地方，准备好展示自己的实力了吗？！'}",
	[7043] = "{id = 7043, Speaker = 1704, Text = '哈！亲爱的冒险者，我是品客公司驻RO大使，这里有一些本公司推出的薯片礼盒和专属表情、头饰，有兴趣看看吗？'}",
	[7046] = "{id = 7046, Speaker = 1015, Text = '[c][ffff00]卡普拉[-][/c]竭诚为您提供最全面的服务！另外仓库服务需要手续费[c][ffff00]%sZeny[-][/c]哦~~', Emoji = 0, Action = {num = 1, actionid = 200}}",
	[7047] = "{id = 7047, Speaker = 4277, Text = '[c][ffff00]卡普拉[-][/c]竭诚为您提供最全面的服务！另外仓库服务需要手续费[c][ffff00]%sZeny[-][/c]哦~~'}",
	[7049] = "{id = 7049, Speaker = 9303, Text = '欢迎大家来到换装舞台！我是舞台管理员，很高兴认识大家。'}",
	[8102] = "{id = 8102, Speaker = 0, Text = '完成了！去把假人放好吧！'}",
	[8149] = "{id = 8149, Speaker = 2162, Text = '隔了好久，在这个秋天我又回来啦~虽然你可能不认识我，但是我还记得你的名字哦~[c][ffff00][PlayerName][-][/c]'}",
	[8209] = "{id = 8209, Speaker = 1120, Text = '你能帮帮我吗？我脚受了点轻伤现在走不了路了。', Option = '发生了什么？', Emoji = 18}",
	[8210] = "{id = 8210, Speaker = 1120, Text = '最近下水道突然滋生好多魔物，我在清理魔物的时候被该死的盗虫伤到了。', Option = '你不要紧吧？'}",
	[8211] = "{id = 8211, Speaker = 1120, Text = '脚上的伤倒是没什么，我用点药应该就没事了，但是我更担心被讨伐队开除。', Option = '为什么？', Emoji = 24}",
	[8212] = "{id = 8212, Speaker = 1120, Text = '因为我还没有完成上级给我的清理工作，我绝不能因此被讨伐队开除，我有不得不待在讨伐队的理由。'}",
	[8213] = "{id = 8213, Speaker = 0, Text = '这里这么危险，为什么不离开？'}",
	[8214] = "{id = 8214, Speaker = 1120, Text = '现在可不是闲聊的时候，你能先帮我清理这三种魔物吗，分别是[c][ffff00]白鼠、盗虫、魔菇，每种需要消灭10只[-][/c]！清理完了我再慢慢告诉你为什么。', Option = '包在我身上！'}",
	[8215] = "{id = 8215, Speaker = 1120, Text = '太棒了，没想到你这么有本事。', Emoji = 20}",
	[8216] = "{id = 8216, Speaker = 1120, Text = '现在让我来告诉你为什么我不能离开讨伐队吧。'}",
	[8217] = "{id = 8217, Speaker = 1120, Text = '我这一切都是为了我心爱的青梅竹马—[c][ffff00]拉姆[-][/c]。', Option = '哦？'}",
	[8218] = "{id = 8218, Speaker = 1120, Text = '因为下水道魔物的泛滥，拉姆自告奋勇的加入了讨伐队。'}",
	[8219] = "{id = 8219, Speaker = 1120, Text = '他从小就粗心，我放心不下他就跟着他一起加入了讨伐队。', Option = '你真勇敢！'}",
	[8220] = "{id = 8220, Speaker = 1120, Text = '但是前不久因为拉姆的粗心，讨伐队研究的新型欧拉欧拉诱虫剂被狡猾的白鼠偷走了。', Option = '那真是太糟糕了。'}",
	[8221] = "{id = 8221, Speaker = 1120, Text = '那可是讨伐队打算用来清理盗虫的。'}",
	[8222] = "{id = 8222, Speaker = 1120, Text = '我不想拉姆因此受到惩罚，所以我一定要替他找回来些并完成清理任务。', Option = '我也来帮忙吧！'}",
	[8223] = "{id = 8223, Speaker = 1120, Text = '真的吗？那真是太感谢你了！'}",
	[8224] = "{id = 8224, Speaker = 1120, Text = '你真是太厉害了！现在可以帮我去那边角落使用[c][ffff00]新型欧拉欧拉[-][/c]吗，把[c][ffff00]盗虫[-][/c]引诱出来，然后清理一大波！', Option = '出发！'}",
	[8225] = "{id = 8225, Speaker = 1120, Text = '完成得太出色啦！这样就能够帮拉姆将功补过啦！'}",
	[8226] = "{id = 8226, Speaker = 1120, Text = '真不知道该怎么谢你。', Option = '这不算什么。', Emoji = 6}",
	[8227] = "{id = 8227, Speaker = 1120, Text = '其实我从小胆子就特别小，以前都是拉姆来保护我。'}",
	[8228] = "{id = 8228, Speaker = 1120, Text = '但是在魔物暴走的非常时期，我希望自己能为了心爱的拉姆变得勇敢一些，陪在他身边为他出一份力。', Option = '你真的很勇敢。'}",
	[8229] = "{id = 8229, Speaker = 1120, Text = '等我忙完，就准备找个合适机会向拉姆表白！', Option = '祝福你们！', Emoji = 27}",
	[8230] = "{id = 8230, Speaker = 1601, Text = '搬家注意事项：\n1.公会搬家需要消耗公会中%s/20个华丽金属\n2.搬家时长：2小时', SubViewId = 3}",
	[8244] = "{id = 8244, Speaker = 2631, Text = '冒险者你还没能收集到材料啊？你还是先把材料收集好了再来找我吧！'}",
	[8245] = "{id = 8245, Speaker = 2631, Text = '我爸爸说，不能白拿别人的东西呢，我要告诉你一个秘密。'}",
	[8246] = "{id = 8246, Speaker = 2631, Text = '微笑小姐那边有一份图纸，是我父亲给微笑小姐的。一般是不卖的哦~~现在你可以去买那个图纸了，因为你曾经帮了我的忙呢！'}",
