	[9314] = "{id = 9314, Speaker = 3220, Text = '请尽快干掉那些信徒，防止孩子们发生危险'}",
	[9315] = "{id = 9315, Speaker = 3225, Text = '谢谢你骑士领主，这些孩子受伤了，他们必须马上接受治疗'}",
	[9316] = "{id = 9316, Speaker = 3225, Text = '神官们已经准备就绪了，骑士领主请前往东边与休朗克汇合'}",
	[9319] = "{id = 9319, Speaker = 3225, Text = '快到我这里来，骑士，不要站在那些尸体的身边'}",
	[9343] = "{id = 9343, Speaker = 3220, Text = '骑士领主们救出了一批孩子，只是他们都很虚弱了'}",
	[9344] = "{id = 9344, Speaker = 3220, Text = '请立刻找到那些孩子们并且治愈他们，他们就在西边，和骑士领主们在一起'}",
	[9348] = "{id = 9348, Speaker = 3225, Text = '我在这些信徒的身上发现了一些不太好的情况'}",
	[9359] = "{id = 9359, Speaker = 3224, Text = '卡巴克在骑士领主的帮助下突袭了左侧的祭坛，可是他似乎在那里遇到了一些麻烦'}",
	[9360] = "{id = 9360, Speaker = 3224, Text = '冒险者，他就在金字塔二层的西南边，请立刻赶去与他汇合'}",
	[9381] = "{id = 9381, Speaker = 3324, Text = '沙蜂部队是由一些沙漠的原住民组成的自卫部队，是这里唯一拥有战斗能力的组织。'}",
	[9382] = "{id = 9382, Speaker = 3324, Text = '虽然只有20人不到，但是仍然保护着留在苏克拉沙漠中的住民，为了安全起见沙蜂部队的成员每天也只会被安排到2个危险的任务。'}",
	[9708] = "{id = 9708, Speaker = 3348, Text = '终于解脱了……这种感觉……冒险者，很感谢你的帮忙，请你一定要守护好斐扬！'}",
	[9723] = "{id = 9723, Speaker = 3222, Text = '冒险者吗，我们已经削弱了这只怪物，请立刻击败它'}",
	[9798] = "{id = 9798, Speaker = 3355, Text = '…死…死吧…'}",
	[9799] = "{id = 9799, Speaker = 3355, Text = '谢谢你冒险者，在无尽的黑暗中，我迎来了光明…告诉智善，要好好活着…'}",
	[9801] = "{id = 9801, Speaker = 2647, Text = '去点首歌吧！'}",
	[9802] = "{id = 9802, Speaker = 2647, Text = '看来你已经点好了呢。'}",
	[9995] = "{id = 9995, Speaker = 3368, Text = '不好！魔物果然变异了！冒险者，快干掉他，我发现他的力量远比犬妖们强大！'}",
	[10001] = "{id = 10001, Speaker = 0, Text = '嗯？好像有什么东西过来了！', Emoji = 0}",
	[10002] = "{id = 10002, Speaker = 0, Text = '好像还有魔物！', Emoji = 0}",
	[10011] = "{id = 10011, Speaker = 1055, Text = '呜哇~可恶的人类！快溜！', Emoji = 0}",
	[10012] = "{id = 10012, Speaker = 0, Text = '够笨的，你有翅膀却不会飞吗?', Emoji = 0}",
	[10013] = "{id = 10013, Speaker = 1055, Text = '你看我的身材可以飞吗？！更何况人家只是戴了[c][ffff00]天使发圈[-][/c]而已，根本不是天使波利，你认错了啦！', Emoji = 1}",
	[10014] = "{id = 10014, Speaker = 0, Text = '那…哪里可以找到[c][ffff00]天使波利[-][/c]？', Emoji = 10}",
	[10015] = "{id = 10015, Speaker = 1055, Text = '人家才不会告诉你~哼，可恶的人类！赶紧溜~', Emoji = 0}",
	[10016] = "{id = 10016, Speaker = 0, Text = '等一下！…你的[c][ffff00]天使发圈[-][/c]样子不错，可以借我戴下吗？', Emoji = 0}",
	[10017] = "{id = 10017, Speaker = 1055, Text = '…滚开！', Emoji = 1}",
	[10051] = "{id = 10051, Speaker = 1102, Text = '我是你的引导者，你愿意接受[c][ffff00]元素的指引[-][/c]吗？', Emoji = 0}",
	[10052] = "{id = 10052, Speaker = 1102, Text = '接受元素的指引，你将得到元素的庇佑，操纵一切[c][ffff00]元素之力[-][/c]。', Option = '成为[c][ffff00]魔法师[-][/c]！', Emoji = 0}",
	[10053] = "{id = 10053, Speaker = 1103, Text = '有意思，你想成为[c][ffff00]盗贼[-][/c]？', Emoji = 0}",
	[10054] = "{id = 10054, Speaker = 1103, Text = '[c][ffff00]反应、闪躲、高速[-][/c]，这些是盗贼必备的素质，准备好了吗？', Option = '成为[c][ffff00]盗贼[-][/c]！', Emoji = 0}",
	[10055] = "{id = 10055, Speaker = 1104, Text = '隐藏在大自然中的[c][ffff00]猎杀者[-][/c]，听起来怎么样？加入我们吧！', Emoji = 0}",
	[10056] = "{id = 10056, Speaker = 1104, Text = '在[c][ffff00]远距离击败[-][/c]对手就是我们弓箭手的[c][ffff00]特点[-][/c]，你也可以做到这些。', Option = '成为[c][ffff00]弓箭手[-][/c]！', Emoji = 0}",
	[10057] = "{id = 10057, Speaker = 1105, Text = '我们都是[c][ffff00]神的侍从[-][/c]，沐浴着圣母的恩赐，并传播给世人。来我们身边吧，我的朋友。', Emoji = 0}",
	[10058] = "{id = 10058, Speaker = 1105, Text = '[c][ffff00]侍奉神圣的信仰[-][/c]，是我们为之奋斗一生的伟大职业。我的朋友，你准备好了吗？', Option = '成为[c][ffff00]服事[-][/c]！', Emoji = 0}",
	[10059] = "{id = 10059, Speaker = 1106, Text = '很不错的年轻人，你看起来就很有经商的头脑，怎么样？要不要一起[c][ffff00]创造财富[-][/c]？', Emoji = 0}",
	[10060] = "{id = 10060, Speaker = 1106, Text = '拥有坚强的体魄、出色的战力和灵活的头脑，才能成为一个优秀的[c][ffff00]商人[-][/c]。加入我们，这笔买卖肯定不亏！', Option = '成为[c][ffff00]商人[-][/c]！', Emoji = 0}",
	[30000] = "{id = 30000, Speaker = 4001, Text = '冒险者们，虽然我不知道你们从何而来，但我仍然要感谢你们的帮助，现在必须尽快进入封印之间，巴风特早已偷偷潜入其中。'}",
	[30001] = "{id = 30001, Speaker = 4000, Text = '哪些没完没了的一群挡住了我们的去路，勇敢的孩子们，请立即击破他们'}",
	[30002] = "{id = 30002, Speaker = 4000, Text = '很多，蚁群渐渐的退缩了，继续将它们全部击退吧！'}",
	[30003] = "{id = 30003, Speaker = 4002, Text = '进展很顺利，接下来我们分开行动，从四个入口包围祭坛。'}",
	[30004] = "{id = 30004, Speaker = 4000, Text = '那是什么！？这里的情况看起来非常糟糕，冒险者们，击退哪些狂暴的魔物'}",
	[30005] = "{id = 30005, Speaker = 4003, Text = '我感觉到了狂暴的魔物正在减少。'}",
	[30006] = "{id = 30006, Speaker = 4001, Text = '哈哈，看起来进展顺利'}",
	[30007] = "{id = 30007, Speaker = 4000, Text = '只剩下最后一只了，快干掉它！'}",
	[30008] = "{id = 30008, Speaker = 4000, Text = '冒险者们，下面的蚁群交给我们，请继续前往中央入口！'}",
	[30009] = "{id = 30009, Speaker = 4002, Text = '无法进入？那头恶魔净耍些雕虫小技！我们将消弱哪些法阵，请立即击碎它们。'}",
	[30010] = "{id = 30010, Speaker = 2202, Text = '不自量力的人类！你们休想师徒靠近我！'}",
	[30011] = "{id = 30011, Speaker = 2202, Text = '我为你们准备了一些“礼物”希望你们能喜欢，哈哈哈哈哈哈哈哈”'}",
	[30012] = "{id = 30012, Speaker = 4001, Text = '看起来有些大家伙在等着我们，海陆曼你还有力气吗？”'}",
	[30013] = "{id = 30013, Speaker = 4000, Text = '噢，亚瑞看来你有些怕了啊，我大概只用了20%的力气，哼哼！'}",
	[30014] = "{id = 30014, Speaker = 4003, Text = '那些人偶看起来有些奇怪，先把周围的敌人肃清，靠近哪些魔力人偶时请小心'}",
	[30015] = "{id = 30015, Speaker = 4002, Text = '这些不堪一击的木乃伊，简直在浪费我们的时间！一口气把它们全部解决！'}",
	[30016] = "{id = 30016, Speaker = 2202, Text = '干的不错啊人类！那么接下来你们又该如何应对呢？'}",
	[30017] = "{id = 30017, Speaker = 4002, Text = '这些人偶非常危险，请尝试逐一击破。'}",
	[30018] = "{id = 30018, Speaker = 4003, Text = '击碎法阵时，请小心伏击。'}",
