	[50048] = "{id = 50048, Speaker = 6626, Text = '出来吧，大人的追随者们！'}",
	[50049] = "{id = 50049, Speaker = 6626, Text = '加油吧，诺曼…希望你不要太过狼狈!'}",
	[50050] = "{id = 50050, Speaker = 6625, Text = '冒险者，你自己小心！'}",
	[50051] = "{id = 50051, Speaker = 6626, Text = '动手！'}",
	[50052] = "{id = 50052, Speaker = 6755, Text = '哥哥！'}",
	[50053] = "{id = 50053, Speaker = 6626, Text = '哦？看来你的弟弟醒了呢。'}",
	[50054] = "{id = 50054, Speaker = 6755, Text = '哥哥！你快走啊！'}",
	[50055] = "{id = 50055, Speaker = 6756, Text = '看看你哥哥那狼狈样！'}",
	[50056] = "{id = 50056, Speaker = 6625, Text = '我不会走的！'}",
	[50057] = "{id = 50057, Speaker = 6755, Text = '哥哥…'}",
	[50058] = "{id = 50058, Speaker = 6756, Text = '诺曼，我已经看到你身上的伤痕了！'}",
	[50059] = "{id = 50059, Speaker = 6756, Text = '哼…没想到你还是撑下来了。'}",
	[50060] = "{id = 50060, Speaker = 6756, Text = '不过你也是强弩之末了。'}",
	[50061] = "{id = 50061, Speaker = 6756, Text = '现在，你的对手是我！'}",
	[50062] = "{id = 50062, Speaker = 6755, Text = '哥哥！你快走啊！你不应该来这里的！'}",
	[50063] = "{id = 50063, Speaker = 6625, Text = '说什么呢！'}",
	[50064] = "{id = 50064, Speaker = 6625, Text = '瑞德，我会把你安全带回家的！'}",
	[50065] = "{id = 50065, Speaker = 6756, Text = '嘿…真是兄弟情深…'}",
	[50066] = "{id = 50066, Speaker = 6756, Text = '让我见识一下你的力量吧！'}",
	[50067] = "{id = 50067, Speaker = 6625, Text = '把！我！的！弟！弟！还！给！我！'}",
	[50068] = "{id = 50068, Speaker = 6756, Text = '去死吧！诺曼！'}",
	[50069] = "{id = 50069, Speaker = 6625, Text = '冒险者…我弟弟就拜托你了…'}",
	[50070] = "{id = 50070, Speaker = 6756, Text = '不可能，这种情况你都能伤我！'}",
	[50071] = "{id = 50071, Speaker = 6755, Text = '哥…!'}",
	[50072] = "{id = 50072, Speaker = 6755, Text = '你！你这个贱女人！！！'}",
	[50073] = "{id = 50073, Speaker = 6756, Text = '算…算你们走运。'}",
	[50074] = "{id = 50074, Speaker = 6756, Text = '霍兰，我们走！'}",
	[50075] = "{id = 50075, Speaker = 6755, Text = '她倒是跑的挺快！', Option = '救你哥'}",
	[50076] = "{id = 50076, Speaker = 6755, Text = '我一定会收拾她的，等着吧！'}",
	[50162] = "{id = 50162, Speaker = 6776, Text = '极限？呵，人类存在的意义不就是突破一个又一个极限吗？'}",
	[50188] = "{id = 50188, Speaker = 6493, Text = '看到房间中心的那颗闪着光芒的球体了吗？那就是奇点球。'}",
	[50189] = "{id = 50189, Speaker = 6493, Text = '它的四周弥漫着空间的力量，恐怕耶梦加得不会放弃这股力量的。'}",
	[50190] = "{id = 50190, Speaker = 6493, Text = '果然我还是很不放心，冒险者，我会跟你一同前去找默斯曼。'}",
	[50191] = "{id = 50191, Speaker = 6493, Text = '让默斯曼多派遣人来守护奇点球。'}",
	[50192] = "{id = 50192, Speaker = 6493, Text = '至于卡泰尔…你的那些稀奇古怪的阵法、毒药可以先布置起来了。'}",
	[50193] = "{id = 50193, Speaker = 6775, Text = '没问题，会长！'}",
	[50194] = "{id = 50194, Speaker = 6493, Text = '那么冒险者，我们先去找默斯曼吧！', Option = '好的'}",
	[50208] = "{id = 50208, Speaker = 6775, Text = '竟然回来得这么快。'}",
	[50209] = "{id = 50209, Speaker = 6493, Text = '你究竟是谁？放下奇点球！'}",
	[50210] = "{id = 50210, Speaker = 6775, Text = '嘿…我是谁？你说我是谁呢？'}",
	[50211] = "{id = 50211, Speaker = 6493, Text = '你…你不是！！'}",
	[50212] = "{id = 50212, Speaker = 6493, Text = '妮薇儿！？'}",
	[50213] = "{id = 50213, Speaker = 6493, Text = '居然是你！！', Emoji = 1}",
	[50214] = "{id = 50214, Speaker = 6779, Text = '哈哈哈，我伪装了这么久，就是为了看你吃惊的表情。'}",
	[50215] = "{id = 50215, Speaker = 6779, Text = '这感觉可真不错。'}",
	[50216] = "{id = 50216, Speaker = 6779, Text = '威克斯！亲眼看着你的宝贝被我夺走吧！'}",
	[50217] = "{id = 50217, Speaker = 6779, Text = '奇点球，我就收下了！'}",
	[50218] = "{id = 50218, Speaker = 6493, Text = '该死，冒险者，拦下她！'}",
	[50219] = "{id = 50219, Speaker = 6779, Text = '冒险者，你还是那么难缠…咳咳…'}",
	[50220] = "{id = 50220, Speaker = 6493, Text = '交出奇点球，妮薇儿！'}",
	[50221] = "{id = 50221, Speaker = 6779, Text = '拖延的时间已经足够了，接我的人也应该到了。'}",
	[50222] = "{id = 50222, Speaker = 6493, Text = '什么？！'}",
	[50223] = "{id = 50223, Speaker = 6626, Text = '嗯？妮薇儿，你受伤了？'}",
	[50224] = "{id = 50224, Speaker = 6779, Text = '带…带我离开…'}",
	[50225] = "{id = 50225, Speaker = 6626, Text = '好！嘿嘿…冒险者、威克斯，告诉默斯曼，我们离见面的时刻不远了！'}",
	[50226] = "{id = 50226, Speaker = 6493, Text = '该死的！我的心血…冒险者，你去告诉默斯曼，奇点球被盗走了。'}",
	[50227] = "{id = 50227, Speaker = 6493, Text = '我会组织炼金术士公会的人尽力找到妮薇儿的下落。'}",
	[50228] = "{id = 50228, Speaker = 6493, Text = '一定要夺回奇点球！'}",
	[50235] = "{id = 50235, Speaker = 6776, Text = '啊！这位冒险者，请等一等！', Option = '？？？'}",
	[50236] = "{id = 50236, Speaker = 6776, Text = '我是炼金术士协会的史派克博士，想必你一定听过我的名字吧？', Option = '{没有,2},{你是谁,2},{有点印象,3}'}",
	[50237] = "{id = 50237, Speaker = 6776, Text = '什么？你连我都不认识？？', Emoji = 12}",
	[50238] = "{id = 50238, Speaker = 6776, Text = '只是有点印象？真是个没见识的冒险者。'}",
	[50239] = "{id = 50239, Speaker = 6776, Text = '算了…现在不是计较这个的时候。'}",
	[50240] = "{id = 50240, Speaker = 6776, Text = '冒险者，现在由我、伟大的史派克博士向你隆重推荐我的研究成果——极限精炼！', Option = '{极限精炼,4},{不相信精炼,4},{那是什么,4}'}",
	[50241] = "{id = 50241, Speaker = 6776, Text = '哦，相信我，极限精炼与忽克连的精炼是不同的。'}",
	[50242] = "{id = 50242, Speaker = 6776, Text = '当你的武器已经强化到极限，你会认为武器的威力再也无法提升了吧？'}",
	[50243] = "{id = 50243, Speaker = 6776, Text = '不！精炼应该是没有极限的！而神秘的魔法与炼金术帮助我做到了这一点！'}",
	[50244] = "{id = 50244, Speaker = 6776, Text = '我的极限精炼奥妙就在于——能够突破精炼的极限。'}",
	[50245] = "{id = 50245, Speaker = 6776, Text = '怎么样？愿意继续听我说下去吗？', Option = '{有点意思,6},{魔法炼金？,6},{没兴趣,5}'}",
	[50246] = "{id = 50246, Speaker = 6776, Text = '呵，好吧冒险者，你错失了一个可以变得强大的机会。'}",
	[50247] = "{id = 50247, Speaker = 6776, Text = '看！你果然很感兴趣。'}",
	[50248] = "{id = 50248, Speaker = 6776, Text = '极限炼金是炼金术与魔法交织相溶的完美手艺，是我二十年的研究成果。'}",
	[50249] = "{id = 50249, Speaker = 6776, Text = '你可以通过交付一些材料，再经由我手的提炼，就能获得一种叫做“极限精华”的物质。'}",
	[50250] = "{id = 50250, Speaker = 6776, Text = '这种精华能够使得已经精炼过的装备在每一个阶段都能突破极限，产生全新的能力。', Option = '这么强？'}",
	[50251] = "{id = 50251, Speaker = 6776, Text = '当然！这点毋庸置疑！'}",
	[50252] = "{id = 50252, Speaker = 6776, Text = '我想每个冒险者都不会拒绝增强自己实力的机会。', Option = '{你说的对,7},{也不一定,7},{这可不好说,7}'}",
	[50253] = "{id = 50253, Speaker = 6776, Text = '总之，这项技术只有我认可的冒险者才能得到。'}",
	[50254] = "{id = 50254, Speaker = 6776, Text = '你想得到极限精炼所带来的种种好处吗？', Option = '当然想'}",
	[50255] = "{id = 50255, Speaker = 6776, Text = '我曾经是一个魔法学徒，我感兴趣的只是研究各种调和试验，结果不出意外，我无法完成学业。'}",
	[50256] = "{id = 50256, Speaker = 6776, Text = '虽然我不在乎学业，但是这总归是我一生的污点。'}",
	[50257] = "{id = 50257, Speaker = 6776, Text = '现在我的研究已经成熟了，但是知名度还不高，如果你能在冒险者中帮我宣传一下我的技术，那就太棒了。'}",
	[50258] = "{id = 50258, Speaker = 6776, Text = '我要为自己正名，即使是无法战斗的人，也会拥有强大的一面！', Option = '{小事一桩,8},{这简单,8},{我帮你,8}'}",
	[50259] = "{id = 50259, Speaker = 6776, Text = '就是这么简单，那么我现在就把这项技术交给你。'}",
	[50260] = "{id = 50260, Speaker = 6776, Text = '好好利用它，他会让你的实力更上一层楼的！'}",
	[50262] = "{id = 50262, Speaker = 6781, Text = '（[c][ffff00][PlayerName][-][/c]的眼前出现一名少女，她的装扮明显有别于这个世界。）', Option = '{上前搭讪,4},{转身就走,2}'}",
	[50263] = "{id = 50263, Speaker = 6781, Text = '（[c][ffff00][PlayerName][-][/c]尝试搭讪，少女的眼眸平静如水，没有激起一点火花。）', Option = '{继续搭讪,5},{转身就走,2}'}",
	[50264] = "{id = 50264, Speaker = 6781, Text = '（[c][ffff00][PlayerName][-][/c]继续搭讪，少女仍然没有任何反应，一丝表情都没有流露。）', Option = '{继续搭讪,6},{转身就走,2}'}",
	[50265] = "{id = 50265, Speaker = 6781, Text = '（[c][ffff00][PlayerName][-][/c]终于明白，眼前的少女是个人偶，是继续搭讪还是离开这里？）', Option = '{继续搭讪,7},{转身就走,2}'}",
	[50266] = "{id = 50266, Speaker = 6781, Text = '……'}",
	[50267] = "{id = 50267, Speaker = 6781, Text = '（[c][ffff00][PlayerName][-][/c]仍然不死心的继续搭讪……）好吧好吧，你赢了……你就是要跟我对着干对么。你就是看到一个画风不一样的角色在RO里面觉得肯定会有个什么任务什么的不是么。'}",
	[50268] = "{id = 50268, Speaker = 6781, Text = '好吧好吧……我知道你想要任务，反正你们这群人就是为了不停做任务才玩这个游戏的不是么，做完任务拿奖励不是么！好吧好吧……'}",
	[50269] = "{id = 50269, Speaker = 6781, Text = '去[c][ffff00]高山上的水疗素天图书馆附近[-][/c]寻找不属于这个世界的物体吧。你能够一眼就看出来的，然后就能做完这个任务了。'}",
	[50270] = "{id = 50270, Speaker = 6781, Text = '（[c][ffff00][PlayerName][-][/c]决定立刻前往[c][ffff00]高山上的水疗素天图书馆附近[-][/c]。）', Option = '{出发,10},{再听一遍,8}'}",
	[50271] = "{id = 50271, Speaker = 6781, Text = '（[c][ffff00][PlayerName][-][/c]的眼前出现了一个“明显不属于这个世界的物体”）', Option = '{上前确认,13},{跟我没关系,11}'}",
	[50272] = "{id = 50272, Speaker = 6781, Text = 'OK，先在可以把这玩意儿带回去找那个少女吧~！', Option = '{立刻回去,14},{你到底是谁,15},{少女是谁,16}'}",
	[50273] = "{id = 50273, Speaker = 6781, Text = '等等？你就没有任何疑问了吗？好吧，是我多嘴了，完成你的任务吧，就趁现在，没有打打杀杀的愚蠢环节是否让你觉得无聊？连篇的废话是否让你提不起精神？还有到底你在做什么？带着这些疑问回去找艾希吧。'}",
	[50274] = "{id = 50274, Speaker = 6781, Text = '我？没看到上面写着两个字“旁白么？……你特么肯定先在想吐槽为什么旁白没有语音对吧……因为在《艾希》里我被弄坏了啊！我也想说话啊！'}",
	[50275] = "{id = 50275, Speaker = 6781, Text = '《艾希》的主角艾希啊！这个联动活动你肯定或多或少知道了吧。这特么是个联动活动啊！就是为了给你们安利《艾希》做的啊！否则干嘛艾希要站在那给你派发任务啊。你们不也是为了联动的任务奖励才来做这个任务的么。'}",
	[50276] = "{id = 50276, Speaker = 0, Text = '（手上的机器又回到名叫艾希的少女身边，这些都发生在很短的时间，虽然你对他们还不甚了解，但是你总觉得在哪里听过他们的故事，甚至已经经历过他们的故事，想起这件事也不过是回来的路上思绪的一闪）'}",
	[50277] = "{id = 50277, Speaker = 6781, Text = '哦~那么还是由我来郑重地代替艾希来感谢玩家你吧~感谢您，亲爱的冒险者。'}",
	[50278] = "{id = 50278, Speaker = 6781, Text = '看似毫无意义的一个“任务”你能愿意接受并认真完成。'}",
	[50279] = "{id = 50279, Speaker = 6781, Text = '什么？大声点？你说希望听到艾希开口说话？…这…'}",
	[50280] = "{id = 50280, Speaker = 6781, Text = '好吧，我不得不遗憾但是诚实地告知你，其实我们尝试的第一个联动活动，还没法做到太大的内容规模呢？因为啊~我们把重点都放在了！'}",
	[50281] = "{id = 50281, Speaker = 6781, Text = '等我说完我最后的几句对话你就能从艾希那里解锁购买超次元道具箱里的特别物品了。'}",
	[50282] = "{id = 50282, Speaker = 6781, Text = '虽然时间不长，但是到了最后还是真有点舍不得呢。作为一个玩家，你真的会在乎一个旁白吗？在乎他说的话吗？哈哈哈~这些想必你自己有自己的答案。'}",
	[50283] = "{id = 50283, Speaker = 6781, Text = '愿黑星照常升起。'}",
	[50304] = "{id = 50304, Speaker = 6779, Text = '霍兰…我要见大人！大人一定有办法救我的！'}",
