	[50564] = "{id = 50564, Speaker = 6625, Text = '现在所有的线索全丢了！', Option = '别泄气'}",
	[50565] = "{id = 50565, Speaker = 6625, Text = '我们得快点回默斯曼大人那儿看看。'}",
	[50566] = "{id = 50566, Speaker = 6625, Text = '我有种非常不好的预感。'}",
