	[50607] = "{id = 50607, Speaker = 6645, Text = '你这愚蠢的家伙，本来我就是为了利用你。'}",
	[50608] = "{id = 50608, Speaker = 6645, Text = '瑞德这个人只是我编造的一个记忆而已。'}",
	[50609] = "{id = 50609, Speaker = 6625, Text = '什么！？'}",
	[50610] = "{id = 50610, Speaker = 6645, Text = '你没有听错，你根本就没有弟弟。'}",
