	[50616] = "{id = 50616, Speaker = 6645, Text = '魔狼才是我真正的哥哥。'}",
	[50617] = "{id = 50617, Speaker = 6645, Text = '而你只是个被我利用的代替品。'}",
	[50618] = "{id = 50618, Speaker = 6645, Text = '我再也不是瑞德了，再见诺曼。'}",
	[50662] = "{id = 50662, Speaker = 6645, Text = '那几个时钟守卫者都不见了！'}",
	[50663] = "{id = 50663, Speaker = 6645, Text = '看来我们和他们走散了。'}",
	[50664] = "{id = 50664, Speaker = 6645, Text = '这里是…另一个世界线的钟楼地下！？'}",
	[50665] = "{id = 50665, Speaker = 0, Text = '地上有闪闪发光的金色蛇鳞和…'}",
	[50666] = "{id = 50666, Speaker = 6645, Text = '这些都是…魔物的…'}",
	[50667] = "{id = 50667, Speaker = 6645, Text = '尸体？'}",
	[50668] = "{id = 50668, Speaker = 6645, Text = '真是恶劣的手段，看来轮回梦魇把这里魔物的力量全吸收了。'}",
	[50669] = "{id = 50669, Speaker = 6645, Text = '这里得分头行动！我可以帮你们去找队员们，'}",
