	[50671] = "{id = 50671, Speaker = 6645, Text = '不想没命的就小心！！这里到处都是梦魇的手下。'}",
	[50672] = "{id = 50672, Speaker = 6795, Text = '看来我们又见面了，另一个我。'}",
	[50673] = "{id = 50673, Speaker = 6645, Text = '不好意思，世界只允许有一条巨蛇存在。'}",
	[50674] = "{id = 50674, Speaker = 6795, Text = '嘿，干嘛和我对着干呢。'}",
