	[50933] = "{id = 50933, Speaker = 2177, Text = '唔……这位路过的冒险者，停下你匆忙的脚步，来这里瞧一瞧～'}",
	[50934] = "{id = 50934, Speaker = 2177, Text = '你知道现在是什么节日吗？友情提示：最最神秘的惊魂夜！', Option = '万圣节'}",
	[50935] = "{id = 50935, Speaker = 2177, Text = '唔……没错，今天可是我们恶魔开派对的节日！害怕吗，冒险者？小心走在路上碰见了可怕的女巫哦～'}",
	[50936] = "{id = 50936, Speaker = 2177, Text = '你看上去很从容的样子啊！那么，无知的冒险者，你要加入到恶魔的狂欢之中吗？加入我的狂欢，会有好礼相送哦～', Option = '我要！'}",
	[50937] = "{id = 50937, Speaker = 2177, Text = '嗬嗬嗬嗬，[c][ffff00]每个账号只能领取一次礼物[-][/c]，你现在登录的角色是[c][ffff00][PlayerName][-][/c]！确定要用这个名字参加我的派对吗？', Option = '{我确定,3},{更换角色,2}'}",
	[50938] = "{id = 50938, Speaker = 2177, Text = '唔……明智的决定，恶魔的派对可不是所有人都有胆量参加的～'}",
	[50939] = "{id = 50939, Speaker = 2177, Text = '你的胆量可不小啊，冒险者！这是给你应得的奖励～捣蛋鬼的糖果盒～要好好利用，否则……嘿嘿嘿嘿嘿……当钟声敲响时，你会从梦中进入我的派对～让你领会什么是真正的万圣惊魂夜！'}",
	[50944] = "{id = 50944, Speaker = 6645, Text = '趁现在！把它推回古代！'}",
	[50956] = "{id = 50956, Speaker = 1103, Text = '…那家伙管理的[c][ffff00]流氓公会[-][/c]就在[c][ffff00]梦罗克城外[-][/c]呢~记得没错的话，就在城外西南方。'}",
	[51011] = "{id = 51011, Speaker = 6926, Text = '[c][ffff00][PlayerName][-][/c]，为了寻求心中的自由！为了让我们骄傲的旗帜插到大陆的任何地方，占领所有的山顶！占领所有的海角！我们流氓公会欢迎你！'}",
	[51012] = "{id = 51012, Speaker = 6927, Text = '穿上新衣服的感觉如何？'}",
