	[51889] = "{id = 51889, Speaker = 6973, Text = '他好像要说些什么！'}",
	[51890] = "{id = 51890, Speaker = 6981, Text = '危险，珍妮甜心！'}",
	[52040] = "{id = 52040, Speaker = 6944, Text = '姜饼城的居民们，还有远道而来的游客们。今天我们美丽的家园发生了一件可怕的事情。'}",
	[52041] = "{id = 52041, Speaker = 6944, Text = '女神之手的爪牙侵入到圣诞村，毕尤带着她的小丑帮手还有一群傀儡，绑架了我们的孩子。'}",
	[52042] = "{id = 52042, Speaker = 6944, Text = '我们的小丽莎，还有小内特在几天前就失踪了。'}",
	[52043] = "{id = 52043, Speaker = 6944, Text = '之后，陆续又有许多小孩失踪……'}",
	[52044] = "{id = 52044, Speaker = 6944, Text = '经过这位冒险者地调查发现…'}",
	[52045] = "{id = 52045, Speaker = 6944, Text = '邪恶的小丑将我们的孩子变为了傀儡，受他驱使！'}",
	[52046] = "{id = 52046, Speaker = 6944, Text = '小丑还继续在姜饼城里，我们要齐心协力抓住他，还有毕尤。'}",
	[52047] = "{id = 52047, Speaker = 6944, Text = '拯救我们的孩子！'}",
	[52048] = "{id = 52048, Speaker = 5204, Text = '拯救孩子，拯救孩子！'}",
	[52049] = "{id = 52049, Speaker = 0, Text = '许多傀儡出现袭击了众人。'}",
	[52050] = "{id = 52050, Speaker = 6944, Text = '让我们一起去马戏团帐篷抓住那只小丑，找出毕尤！'}",
	[52051] = "{id = 52051, Speaker = 6973, Text = '可是，可是……可是加尔是好人呀，他救了我两次了。'}",
	[52070] = "{id = 52070, Speaker = 5217, Text = '我叫绮丽，是竹杨酒家老板的妹妹。'}",
	[52071] = "{id = 52071, Speaker = 5217, Text = '你从远处跑过来的时候，我看见了你身上冒险者的徽章。请问，你是普隆德拉冒险者协会的冒险者吗？', Option = '是的'}",
	[52072] = "{id = 52072, Speaker = 5217, Text = '真羡慕你呀，我也想加入冒险者协会，我想成为一名神射手！可是哥哥认为这不是淑女应该做的事情，不让我去普隆德拉。'}",
	[52073] = "{id = 52073, Speaker = 5217, Text = '我自己也没有自信，我这样的人，可以成为冒险者吗？'}",
	[52074] = "{id = 52074, Speaker = 5217, Text = '我听说如果有导师勋章，就可以向知名的冒险者学习了！'}",
	[52075] = "{id = 52075, Speaker = 5217, Text = '冒险者，如果你有[c][ffff00]4000枚导师勋章[-][/c]可以跟我交换哦，我会给你我最喜欢的美瞳[c][ffff00]时之轮·黄[-][/c]哦。'}",
	[52076] = "{id = 52076, Speaker = 5217, Text = '请问你想要跟我交换吗？', Option = '{我想交换,2},{我不想换,3}'}",
	[52077] = "{id = 52077, Speaker = 5217, Text = '那好吧，看来我没有机会成为一个冒险者了。'}",
	[52078] = "{id = 52078, Speaker = 5217, Text = '真的吗？让我看看你的导师勋章。'}",
	[52079] = "{id = 52079, Speaker = 5217, Text = '你根本没有这么多导师勋章嘛，我要4000枚哦！'}",
	[52080] = "{id = 52080, Speaker = 5217, Text = '你真是太好啦，冒险者！这是我的[c][ffff00]时之轮·黄[-][/c]，希望你喜欢哦！'}",
	[52081] = "{id = 52081, Speaker = 5218, Text = '我最近听了一个有关人鱼的故事，是西边的吟游诗人汉斯跟我讲的。'}",
	[52082] = "{id = 52082, Speaker = 5218, Text = '他说有一个小美人鱼，爱上了溺水的王子，却最终变成了海面的泡沫。'}",
	[52083] = "{id = 52083, Speaker = 5218, Text = '那个美人鱼，有一双纯白色的眼睛……'}",
	[52084] = "{id = 52084, Speaker = 5218, Text = '我制作了一双白色的美瞳，希望戴上它的人，可以获得完美的爱情。'}",
	[52085] = "{id = 52085, Speaker = 5218, Text = '不要像可怜的小美人鱼一样……'}",
	[52086] = "{id = 52086, Speaker = 5218, Text = '冒险者，你想要我的美瞳吗？只需要[c][ffff00]420枚乐园币[-][/c]，我就把[c][ffff00]人鱼之心·白[-][/c]给你，也把我的祝福给你。', Option = '{我想要,2},{我不想要,3}'}",
	[52087] = "{id = 52087, Speaker = 5218, Text = '好吧，看来你不想要完美的爱情…'}",
	[52088] = "{id = 52088, Speaker = 5218, Text = '让我检查一下你有没有带齐乐园币！'}",
	[52089] = "{id = 52089, Speaker = 5218, Text = '只有[c][ffff00]75级以上[-][/c]的冒险者，带了[c][ffff00]超过420枚乐园币[-][/c]才能从我这里得到[c][ffff00]人鱼之心·白[-][/c]哦！'}",
	[52090] = "{id = 52090, Speaker = 5218, Text = '交易达成，这是你的美瞳，戴上它的时候，要想起美人鱼哦！'}",
	[52091] = "{id = 52091, Speaker = 5219, Text = '艾尔帕兰的河水真美，比我见过的其他河流都要蓝。'}",
	[52092] = "{id = 52092, Speaker = 5219, Text = '冒险者，我是一名美瞳设计师，四处巡游想找到最美丽的美瞳颜色。'}",
	[52093] = "{id = 52093, Speaker = 5219, Text = '我想我在艾尔帕兰找到了我想要的颜色，这种蓝，让我无法自拔的爱上了它。我认为这就是幻想的颜色！'}",
	[52094] = "{id = 52094, Speaker = 5219, Text = '我为艾尔帕兰的河流设计了一款美瞳[c][ffff00]朝之幻想·蓝[-][/c]。'}",
	[52095] = "{id = 52095, Speaker = 5219, Text = '你想要试试这款美瞳吗？只收[c][ffff00]1200枚乐园币[-][/c]哦。', Option = '{我想试试,2},{我没兴趣,3}'}",
	[52096] = "{id = 52096, Speaker = 5219, Text = '好吧，看来你对美的理解还不够深刻……'}",
	[52097] = "{id = 52097, Speaker = 5219, Text = '快把[c][ffff00]1200枚乐园币[-][/c]给我吧！'}",
	[52098] = "{id = 52098, Speaker = 5219, Text = '什么嘛，你还没有资格向我购买，只有[c][ffff00]95级以上[-][/c]的冒险者，花费[c][ffff00]1200枚乐园币[-][/c]才能从我这里买到[c][ffff00]朝之幻想·蓝[-][/c]。'}",
	[52099] = "{id = 52099, Speaker = 5219, Text = '交易成功！我会继续四处旅行，找到更美的颜色的，要多多关注美瞳哦！'}",
	[52242] = "{id = 52242, Speaker = 5301, Text = '……真难过，我无法应对这种情况……为什么事请会变成这样？', Option = '怎么了？'}",
	[52278] = "{id = 52278, Speaker = 6959, Text = '我要去雪原找他……我不能把他弄丢了，我的儿子和儿媳还说今年要回家……'}",
	[52436] = "{id = 52436, Speaker = 5314, Text = '表演好不好看？哈哈哈哈哈哈！'}",
	[52437] = "{id = 52437, Speaker = 2631, Text = '好看！好看！不要吃我！'}",
	[52438] = "{id = 52438, Speaker = 0, Text = '有傀儡从笼子里钻出来！赶走它们！'}",
	[52439] = "{id = 52439, Speaker = 5315, Text = '真麻烦，不能让你扰乱希默梅思大人的计划。'}",
	[52440] = "{id = 52440, Speaker = 5315, Text = '下地狱吧！'}",
	[52441] = "{id = 52441, Speaker = 5315, Text = '来，让我们的曼陀罗为你们舞起来！'}",
	[52442] = "{id = 52442, Speaker = 5315, Text = '有两下子，你到底是谁？'}",
	[52443] = "{id = 52443, Speaker = 5315, Text = '是谁也不重要了，再看看我们“可爱”的乖乖巧巧！'}",
	[52444] = "{id = 52444, Speaker = 5315, Text = '可恶可恶可恶！！！你们这些干扰希默梅思大人的虫子！'}",
	[52445] = "{id = 52445, Speaker = 5315, Text = '让我来教训你们！'}",
	[52446] = "{id = 52446, Speaker = 0, Text = '快救救孩子们吧！'}",
	[52447] = "{id = 52447, Speaker = 5310, Text = '手流血了，好痛哦！呜呜呜……谢谢你冒险者！'}",
	[52448] = "{id = 52448, Speaker = 0, Text = '解救了娇娇，把她送出了小丑的邪恶空间。'}",
	[52449] = "{id = 52449, Speaker = 5311, Text = '都是怪物，好可怕！呜呜呜……谢谢你冒险者！'}",
	[52450] = "{id = 52450, Speaker = 0, Text = '解救了美美，把她送出了小丑的邪恶空间。'}",
	[52451] = "{id = 52451, Speaker = 5312, Text = '我好痛，好害怕！呜呜呜……谢谢你冒险者！'}",
	[52452] = "{id = 52452, Speaker = 0, Text = '解救了天天，把他送出了小丑的邪恶空间。'}",
	[52453] = "{id = 52453, Speaker = 5313, Text = '我想爸爸妈妈了！呜呜呜……谢谢你冒险者！'}",
	[52454] = "{id = 52454, Speaker = 0, Text = '解救了牛牛，把他送出了小丑的邪恶空间。'}",
	[52482] = "{id = 52482, Speaker = 5317, Text = '我们的小朋友们还真是不乖啊~'}",
	[52483] = "{id = 52483, Speaker = 5317, Text = '你们还想再看一遍欢迎仪式是吗！'}",
	[52484] = "{id = 52484, Speaker = 5317, Text = '去吧，我的小可爱们！'}",
	[52485] = "{id = 52485, Speaker = 5317, Text = '好吧，欢迎仪式不喜欢没关系~'}",
	[52486] = "{id = 52486, Speaker = 5317, Text = '那你们一定是想看小蝴蝶做数学题对不对！'}",
	[52487] = "{id = 52487, Speaker = 5317, Text = '一加一等于几呢~'}",
	[52488] = "{id = 52488, Speaker = 5318, Text = '等于死亡！！！！！'}",
	[52489] = "{id = 52489, Speaker = 5317, Text = '哎哟哟，小朋友还真是凶呀~'}",
	[52490] = "{id = 52490, Speaker = 5317, Text = '表演不好看，那不如我们还是来做游戏~~'}",
	[52491] = "{id = 52491, Speaker = 5317, Text = '来呀，敲碎有惊喜~~'}",
	[52492] = "{id = 52492, Speaker = 5316, Text = '叽叽叽叽！！'}",
	[52493] = "{id = 52493, Speaker = 0, Text = '雪球仿佛在提醒着自己，阴谋的存在。'}",
	[52494] = "{id = 52494, Speaker = 5317, Text = '恭喜你们！抽中今晚的特等奖！ '}",
	[52495] = "{id = 52495, Speaker = 5317, Text = '去地狱的免费末班车~~'}",
	[52496] = "{id = 52496, Speaker = 5317, Text = '可恶，真是群废物。'}",
	[52497] = "{id = 52497, Speaker = 5317, Text = '不过让你这坏孩子空手而归也不错，嘿嘿嘿。'}",
	[52498] = "{id = 52498, Speaker = 5316, Text = '叽叽叽叽！！'}",
	[52588] = "{id = 52588, Speaker = 0, Text = '加尔从圆形的旋转舞台缓缓地转了上来。'}",
	[52589] = "{id = 52589, Speaker = 5228, Text = '你们是谁！！'}",
	[52590] = "{id = 52590, Speaker = 5228, Text = '你们是怎么知道这个暗号的！！', Emoji = 12}",
	[52591] = "{id = 52591, Speaker = 5226, Text = '你逃不掉了！说！失踪的孩子们都被你藏哪儿去了！'}",
	[52592] = "{id = 52592, Speaker = 5228, Text = '噢，我知道了，你们是来…'}",
	[52593] = "{id = 52593, Speaker = 5228, Text = '找我玩儿的吗？'}",
	[52594] = "{id = 52594, Speaker = 5228, Text = '霍嘿嘿嘿…'}",
	[52595] = "{id = 52595, Speaker = 5228, Text = '我们来~~~'}",
	[52596] = "{id = 52596, Speaker = 5228, Text = '做游戏！！'}",
	[52597] = "{id = 52597, Speaker = 5228, Text = '你们这些人！熊宝宝陪你们做游戏。'}",
	[52598] = "{id = 52598, Speaker = 5228, Text = '你们有什么不满意！？'}",
	[52599] = "{id = 52599, Speaker = 5228, Text = '既然不喜欢熊宝宝~那么你们一定是喜欢我的小木兵们！'}",
	[52600] = "{id = 52600, Speaker = 5228, Text = '别小看他们扛的枪，打起人来可是有点疼的呀！霍嘿嘿嘿~'}",
	[52601] = "{id = 52601, Speaker = 5228, Text = '噢~~我的团员们，都被你们消灭了呀~'}",
	[52602] = "{id = 52602, Speaker = 5228, Text = '加尔，大-危-机~~霍嘿嘿嘿~'}",
	[52603] = "{id = 52603, Speaker = 5228, Text = '加尔只能自己陪你们玩玩了！'}",
	[52604] = "{id = 52604, Speaker = 5227, Text = '冒险者小心！'}",
	[52605] = "{id = 52605, Speaker = 5228, Text = '这里是我的马戏团！谁让你们进来的！'}",
	[52606] = "{id = 52606, Speaker = 5226, Text = '告诉我们，孩子在哪儿！'}",
	[52607] = "{id = 52607, Speaker = 5228, Text = '什么孩子~霍嘿嘿嘿~'}",
	[52608] = "{id = 52608, Speaker = 5227, Text = '别演了加尔！'}",
	[52609] = "{id = 52609, Speaker = 5227, Text = '毕尤！果然是你在背后操纵这一切！'}",
	[52610] = "{id = 52610, Speaker = 5229, Text = '嘻嘻嘻嘻，加尔可不能让你们轻易抓走。'}",
	[52611] = "{id = 52611, Speaker = 5229, Text = '他可是我重要的手下呢~'}",
	[52612] = "{id = 52612, Speaker = 5227, Text = '不好！让加尔跑了！'}",
	[52613] = "{id = 52613, Speaker = 5229, Text = '你们这么喜欢玩游戏，就让我的傀儡陪你们玩个特别开心的游戏！'}",
	[52614] = "{id = 52614, Speaker = 5229, Text = '猜猜哪个才是本体~~~', Emoji = 2}",
	[52688] = "{id = 52688, Speaker = 5333, Text = '这里…'}",
	[52689] = "{id = 52689, Speaker = 5334, Text = '被改造的如此阴森恐怖，真是不适合小孩子待啊！！'}",
	[52690] = "{id = 52690, Speaker = 5334, Text = '加尔…！'}",
	[52691] = "{id = 52691, Speaker = 5335, Text = '霍嘿嘿嘿~你们是谁啊~'}",
	[52692] = "{id = 52692, Speaker = 5334, Text = '加尔，快把珍妮还给我们，她不是你要找的人！'}",
	[52693] = "{id = 52693, Speaker = 5335, Text = '你少乱说，我的贝蒂就在这里~'}",
	[52694] = "{id = 52694, Speaker = 5336, Text = '雪橇哥哥！！'}",
	[52695] = "{id = 52695, Speaker = 5335, Text = '不管是谁！！都不能打扰我和贝蒂在一起的珍贵时光！！！'}",
	[52696] = "{id = 52696, Speaker = 5335, Text = '唔！！！还真是难缠的家伙！！你们怎么听不懂人话呢！！'}",
	[52697] = "{id = 52697, Speaker = 5335, Text = '不要打扰我和贝蒂！！！！'}",
	[52698] = "{id = 52698, Speaker = 5334, Text = '加尔！那真的不是贝蒂！'}",
	[52699] = "{id = 52699, Speaker = 5334, Text = '你的贝蒂就在这个圣诞村中，她要来见你了！'}",
	[52700] = "{id = 52700, Speaker = 5335, Text = '不，不是！不是！霍嘿嘿嘿~'}",
	[52701] = "{id = 52701, Speaker = 5335, Text = '你…'}",
	[52702] = "{id = 52702, Speaker = 5335, Text = '贝蒂…'}",
	[52703] = "{id = 52703, Speaker = 0, Text = '加尔面部表情变得十分痛苦。'}",
	[52705] = "{id = 52705, Speaker = 5335, Text = '该死的！！'}",
	[52710] = "{id = 52710, Speaker = 5337, Text = '加尔啊加尔，你还在等什么，让我来帮你。'}",
	[52711] = "{id = 52711, Speaker = 5337, Text = '把你的珍妮完全变成傀儡娃娃~让她永远陪在你身边~！！'}",
	[52712] = "{id = 52712, Speaker = 5334, Text = '雪球！！你怎么来了！'}",
	[52713] = "{id = 52713, Speaker = 5316, Text = '叽叽！叽叽叽！'}",
	[52714] = "{id = 52714, Speaker = 5325, Text = '我全想起来了！！加尔，我和你的一切…'}",
	[52715] = "{id = 52715, Speaker = 5325, Text = '我回来了！'}",
	[52716] = "{id = 52716, Speaker = 5325, Text = '你不认识我了吗？你看我为你带来了什么…'}",
	[52717] = "{id = 52717, Speaker = 5325, Text = '是你最爱吃的奶油夹心巧克力。'}",
	[52718] = "{id = 52718, Speaker = 5325, Text = '快回想起来吧…你宝贵的回忆，你真正想要的东西。'}",
	[52719] = "{id = 52719, Speaker = 0, Text = '加尔的身上也发出了淡淡的暖暖的金光。'}",
	[52720] = "{id = 52720, Speaker = 5335, Text = '呜呜呜…贝蒂…'}",
	[52721] = "{id = 52721, Speaker = 5325, Text = '加尔…抱歉，没能遵守我们的约定。'}",
	[52722] = "{id = 52722, Speaker = 5325, Text = '明明答应过你，每年的圣诞节，都来姜饼城见你。'}",
	[52723] = "{id = 52723, Speaker = 5335, Text = '贝…贝蒂…你为什么没有回来…'}",
	[52724] = "{id = 52724, Speaker = 5325, Text = '因为我的父母被毕尤给杀害了，从那以后我被姑姑收养，无法再回圣诞村…'}",
	[52725] = "{id = 52725, Speaker = 5337, Text = '没用的加尔！混蛋老太婆！！！'}",
	[52726] = "{id = 52726, Speaker = 5335, Text = '你这个女人！！一直操纵我！！告诉我珍妮就是贝蒂！！'}",
	[52727] = "{id = 52727, Speaker = 5337, Text = '呃啊！'}",
	[52728] = "{id = 52728, Speaker = 5335, Text = '我！不会再做你的傀儡！'}",
	[52729] = "{id = 52729, Speaker = 5337, Text = '哼！！你们破坏了我的计划，我要把你们通通做成傀儡！！！！'}",
	[52730] = "{id = 52730, Speaker = 5337, Text = '该死的冒险者！！！！！'}",
	[52731] = "{id = 52731, Speaker = 5338, Text = '我代表圣诞老人和神圣的节日！'}",
	[52732] = "{id = 52732, Speaker = 5338, Text = '正式将你这邪恶的灵魂。'}",
	[52733] = "{id = 52733, Speaker = 5338, Text = '从纯洁的姜饼城驱逐！！'}",
	[52735] = "{id = 52735, Speaker = 0, Text = '雪球身上又一次泛起了温暖的白色光芒。'}",
	[52736] = "{id = 52736, Speaker = 5337, Text = '呃啊啊啊啊啊啊！'}",
	[52739] = "{id = 52739, Speaker = 0, Text = '随着毕尤的消失，雪球再次发出暖暖的白光。'}",
	[52740] = "{id = 52740, Speaker = 0, Text = '这暖人的白光仿佛传递到了加尔和贝蒂的心中。'}",
	[52741] = "{id = 52741, Speaker = 5340, Text = '呜呜呜，你明天就要走了，什么时候回来？'}",
	[52742] = "{id = 52742, Speaker = 5326, Text = '别哭了，给你一颗奶油夹心巧克力好不好。'}",
	[52743] = "{id = 52743, Speaker = 5340, Text = '我不要巧克力呢！我舍不得你！'}",
	[52744] = "{id = 52744, Speaker = 5326, Text = '好了，男孩子不能老是哭鼻子，被你师父看到又该骂你了。'}",
	[52745] = "{id = 52745, Speaker = 5326, Text = '你加油练习，成为一个特别厉害的小丑。我呢，就让爸爸做一大堆好吃的糖果。'}",
	[52746] = "{id = 52746, Speaker = 5326, Text = '然后，我每年的圣诞节都在这里和你见面。'}",
	[52747] = "{id = 52747, Speaker = 5326, Text = '我给你带好吃的糖果，你表演好看的马戏给我看，好不好。'}",
	[52748] = "{id = 52748, Speaker = 0, Text = '小加尔擦了擦脸上的泪水。'}",
	[52749] = "{id = 52749, Speaker = 5340, Text = '好…'}",
	[52750] = "{id = 52750, Speaker = 5340, Text = '拉钩。'}",
