	[53859] = "{id = 53859, Speaker = 5279, Text = '号外号外，少女偶像ATF将会在普隆德拉选拔冒险者参与偶像日常体验哦。'}",
	[53860] = "{id = 53860, Speaker = 5279, Text = '如果能够顺利通过体验，将会有丰厚的奖励！'}",
	[53861] = "{id = 53861, Speaker = 5279, Text = '注意注意！选拔日期是[c][ffff00]1月18日5点[-][/c]，完成体验的日期是[c][ffff00]2月22日5点[-][/c]，请大家多多关注哦！'}",
	[53862] = "{id = 53862, Speaker = 5278, Text = '您好，冒险者，我看你观察这边很久了哦，是对我有些好奇吗？'}",
	[53863] = "{id = 53863, Speaker = 5278, Text = '我是异世界的人气偶像ATF，正在普隆德拉游历，你愿意带我去RO世界里转一转吗？', Option = '{我愿意,2},{暂时没空,3}'}",
	[53864] = "{id = 53864, Speaker = 5278, Text = '那真是可惜啊，我也不会在普隆德拉等很久，多看看[c][ffff00]少女的公告[-][/c]哦。'}",
	[53865] = "{id = 53865, Speaker = 5278, Text = '嗯……你看上去很高兴的样子嘛，可不要以为我会轻易相信你。', Emoji = 26}",
	[53866] = "{id = 53866, Speaker = 5278, Text = '我只会信任完成我的考验的人哦，准备好了就来找我吧！'}",
	[53867] = "{id = 53867, Speaker = 5278, Text = '嗯，看上去胸有成竹的样子嘛，看来是个经验丰富的冒险者。'}",
	[53868] = "{id = 53868, Speaker = 5278, Text = '我的考验是要学习成为人气偶像。', Option = '太棒了！'}",
	[53869] = "{id = 53869, Speaker = 5278, Text = '喂喂，你不要搞错了什么，做偶像可不是什么简单的事情。', Option = '迫不及待'}",
	[53870] = "{id = 53870, Speaker = 5278, Text = '看来跟你说什么都没用了，那我们开始[c][ffff00]第一个[-][/c]考验吧。'}",
	[53871] = "{id = 53871, Speaker = 5278, Text = '作为人气偶像，每天都有繁忙的练习，唱歌是最必要的锻炼。一定要有绵长的气息和准确的音调，你愿意体会唱歌训练吗？ 	', Option = '{好的！,4},{稍等片刻,5}'}",
	[53872] = "{id = 53872, Speaker = 5278, Text = '嗯嗯，不要爽约哦！'}",
	[53873] = "{id = 53873, Speaker = 5278, Text = '听我唱一遍，记住我的[c][ffff00]节奏[-][/c]和[c][ffff00]音调[-][/c]哦！'}",
	[53874] = "{id = 53874, Speaker = 5278, Text = '[c][ffff00]咪～拉西哆来咪～哆咪～哆咪～拉哆拉发哆拉～[-][/c]'}",
	[53875] = "{id = 53875, Speaker = 5278, Text = '想必你应该记住了哦，那么归你唱啦，第一句。', Option = '{咪～拉西哆来咪,6},{咪～哆拉西咪来,7},{咪～拉哆拉西咪,7}'}",
	[53876] = "{id = 53876, Speaker = 5278, Text = '冒险者，你唱错了，好好回忆一下我刚刚唱了些什么！'}",
	[53877] = "{id = 53877, Speaker = 5278, Text = '第二句，唱出来吧，冒险者。', Option = '{咪哆～咪哆～,8},{哆发～哆发～,8},{哆咪～哆咪～,9}'}",
	[53878] = "{id = 53878, Speaker = 5278, Text = '唱得不错嘛，还有最后一句，唱给我听吧。', Option = '{哆咪发咪拉西～,10},{拉西拉哆发拉～,10},{拉哆拉发哆拉～,11}'}",
	[53879] = "{id = 53879, Speaker = 5278, Text = '哇，冒险者你很有灵性嘛，唱得比我想象得好很多哦。'}",
	[53880] = "{id = 53880, Speaker = 5278, Text = '第一个考验就算你通过了，虽然有点不甘心，但是这么有天赋的歌唱者我也很少碰见。'}",
	[53881] = "{id = 53881, Speaker = 5278, Text = '不过真正的考验可还在后面呢！'}",
	[53882] = "{id = 53882, Speaker = 5278, Text = '冒险者你准备好接受我[c][ffff00]第二个[-][/c]考验了吗？', Option = '{迫不及待,12},{暂时没空,13}'}",
	[53883] = "{id = 53883, Speaker = 5278, Text = '那么你快去完成自己的工作吧，我会在这里等你的。'}",
	[53884] = "{id = 53884, Speaker = 5278, Text = '我怎么觉得，比起冒险者，你更愿意成为一个偶像呢？', Emoji = 14}",
