	[56737] = "{id = 56737, Speaker = 7165, Text = '鬼使招来！！'}",
	[56837] = "{id = 56837, Speaker = 7072, Text = '你好冒险者，酉神已经离开了，而我戌神来到了这里。'}",
	[56838] = "{id = 56838, Speaker = 7072, Text = '新的一年，新的开始！今年就由我带领大家走向幸福！'}",
	[56839] = "{id = 56839, Speaker = 7072, Text = '冒险者，你愿意把我的祝福带给人们吗？', Option = '{愿意,0},{不愿意,2}'}",
	[56840] = "{id = 56840, Speaker = 7072, Text = '好吧，我相信你之后还会来找我的。'}",
	[56841] = "{id = 56841, Speaker = 7072, Text = '正因为有你这样热情的冒险者，这个世界才会变得更加美好。'}",
	[56842] = "{id = 56842, Speaker = 7072, Text = '让我倾听人们的心声，寻找需要祝福与帮助的人。'}",
	[56843] = "{id = 56843, Speaker = 7072, Text = '向往光明的牧师让这个世界少了伤痛，所以才有了这充满欢笑的脸庞。'}",
	[56844] = "{id = 56844, Speaker = 7072, Text = '请把我的祝福带给教会的汤玛斯大主教，他似乎有点小麻烦。'}",
	[56845] = "{id = 56845, Speaker = 7072, Text = '我想你一定不会吝啬这小小的帮助。'}",
	[56846] = "{id = 56846, Speaker = 1228, Text = '我的天，圣水去哪儿了！洒水仪式就要开始了…'}",
	[56847] = "{id = 56847, Speaker = 1228, Text = '啊，冒险者，抱歉，我恐怕没时间和你闲聊，真是失礼…', Option = '怎么了'}",
	[56848] = "{id = 56848, Speaker = 1228, Text = '洒水仪式是教会每年年初都会举办的神圣仪式。'}",
	[56849] = "{id = 56849, Speaker = 1228, Text = '奥丁圣水是这仪式必备的祭祀品，但是现在却遗失了。', Option = '我帮你'}",
	[56850] = "{id = 56850, Speaker = 1228, Text = '这...好吧，我现在也走不开，你问问保管圣水的修女吧。'}",
	[56851] = "{id = 56851, Speaker = 1013, Text = '圣水就放在教会内的，只是前来礼拜的人太多了…'}",
	[56852] = "{id = 56852, Speaker = 1013, Text = '我看到两个人急匆匆地离开教会，圣水也许就被这两个人偷走了。'}",
	[56853] = "{id = 56853, Speaker = 1013, Text = '他们现在还没走远，你快去问问他们！'}",
	[56854] = "{id = 56854, Speaker = 0, Text = '两人丢下了圣水，匆忙跑掉了…'}",
	[56855] = "{id = 56855, Speaker = 1228, Text = '谢谢你的帮助冒险者，接下来我要准备开始圣水仪式了。'}",
	[56856] = "{id = 56856, Speaker = 1228, Text = '也帮我谢谢戌神，感谢他对于教会的祝福。'}",
	[56857] = "{id = 56857, Speaker = 7072, Text = '哈，能让别人感受到幸福是最令我最快乐的事情。'}",
	[56858] = "{id = 56858, Speaker = 7072, Text = '明天记得来找我，把我的祝福带给需要他们的人！'}",
	[56859] = "{id = 56859, Speaker = 7072, Text = '等你很久啦!冒险者。'}",
	[56860] = "{id = 56860, Speaker = 7072, Text = '让我倾听人们的心声，寻找需要祝福与帮助的人。'}",
	[56861] = "{id = 56861, Speaker = 7072, Text = '唔，料理协会的奥尔里昂今天似乎遇到了什么困难。'}",
	[56862] = "{id = 56862, Speaker = 7072, Text = '我感受到了他的焦急，在这新的一年，我不希望他有这样的情绪。'}",
	[56863] = "{id = 56863, Speaker = 7072, Text = '把我的祝福带给奥尔里昂，希望能平息他的焦急。'}",
	[56864] = "{id = 56864, Speaker = 6439, Text = '哦，我的天！这可怎么办才好……', Option = '怎么了'}",
	[56865] = "{id = 56865, Speaker = 6439, Text = '新的一年应该吃点什么？没错！汤圆！国王还在等着我的汤圆！'}",
	[56866] = "{id = 56866, Speaker = 6439, Text = '可是见鬼！我准备的汤圆不见了！'}",
	[56867] = "{id = 56867, Speaker = 6439, Text = '我想应该是被哪位厨师拿错了！'}",
	[56868] = "{id = 56868, Speaker = 6439, Text = '我现在在熬煮高汤离不开，我希望你能寻找一下汤圆。', Option = '交给我'}",
	[56869] = "{id = 56869, Speaker = 0, Text = '汤圆不在这里。'}",
	[56870] = "{id = 56870, Speaker = 0, Text = '桌上只有零散的酒杯。'}",
	[56871] = "{id = 56871, Speaker = 0, Text = '找到了！装着汤圆的盘子安静地放在桌上。'}",
	[56872] = "{id = 56872, Speaker = 6439, Text = '感谢你冒险者！也谢谢戌神的祝福！'}",
	[56873] = "{id = 56873, Speaker = 6439, Text = '这是我的一点心意，带给戌神尝尝吧！'}",
	[56874] = "{id = 56874, Speaker = 7072, Text = '哈！奥尔里昂的手艺，我可得好好尝尝！'}",
	[56875] = "{id = 56875, Speaker = 7072, Text = '唔...明天也要把我的祝福带给需要他们的人哦！'}",
	[56876] = "{id = 56876, Speaker = 7072, Text = '冒险者，你今天可来的真早呀！'}",
	[56877] = "{id = 56877, Speaker = 7072, Text = '让我倾听一下，今天人们的心声~'}",
	[56878] = "{id = 56878, Speaker = 7072, Text = '哈！有了，人们敬爱的王国内务官凯特·索玛斯恐怕有点麻烦。'}",
	[56879] = "{id = 56879, Speaker = 7072, Text = '凯特·索玛斯为王国付出了许多，我想应该帮帮他！'}",
	[56880] = "{id = 56880, Speaker = 7072, Text = '把我的祝福带给他，希望能抚平他因烦闷而发皱的额头。'}",
	[56881] = "{id = 56881, Speaker = 1022, Text = '早晚我都要把南门那群可恶的疯兔铲除干净！', Option = '怎么了'}",
	[56882] = "{id = 56882, Speaker = 1022, Text = '皇家烟花今晚就要用到了，从依斯鲁得运来的货车却被那些该死的兔子给攻击了。', Option = '我帮你'}",
	[56883] = "{id = 56883, Speaker = 1022, Text = '啊！那真是太感谢你了，请尽快把皇家烟花带回来！'}",
	[56884] = "{id = 56884, Speaker = 1022, Text = '哦，我的天，冒险者你可真是太可靠了！'}",
	[56885] = "{id = 56885, Speaker = 1022, Text = '也替我谢谢戌神的祝福，不多说了，我得开始准备了。'}",
	[56886] = "{id = 56886, Speaker = 7072, Text = '我感觉到凯特·索玛斯的心情变得开心起来了。'}",
	[56887] = "{id = 56887, Speaker = 7072, Text = '干得不错冒险者，明天记得来找我。'}",
	[56888] = "{id = 56888, Speaker = 7072, Text = '怎么样才能让这世界上的人们永远开心的生活呢？'}",
	[56889] = "{id = 56889, Speaker = 7072, Text = '这是我一直在思考的问题，每个人都有自己的烦恼。'}",
	[56890] = "{id = 56890, Speaker = 7072, Text = '是的，我感受到了，宝雅似乎有点麻烦事了。'}",
	[56891] = "{id = 56891, Speaker = 7072, Text = '冒险者，去看看吧~把我的祝福带给她！'}",
	[56892] = "{id = 56892, Speaker = 1054, Text = '新年到来了…该带些什么礼物给孩子们呢？'}",
	[56893] = "{id = 56893, Speaker = 1054, Text = '啊！冒险者，你来帮我出出主意吧！'}",
	[56894] = "{id = 56894, Speaker = 1054, Text = '作为乐园团的一份子，应该带什么礼物给孩子们呢？', Option = '{旧衣服,3},{波利玩具,0},{肮脏的虫,3}'}",
	[56895] = "{id = 56895, Speaker = 1054, Text = '过新年，孩子们最期望的是什么呢？', Option = '{吃粽子,3},{上冒险课,3},{发红包,0}'}",
	[56896] = "{id = 56896, Speaker = 1054, Text = '在送完礼物后，我应该对孩子们说些什么呢？', Option = '{新年祝福,4},{冒险故事,3},{危险的魔物,3}'}",
	[56897] = "{id = 56897, Speaker = 1054, Text = '我想孩子们可能不太需要这个…'}",
	[56898] = "{id = 56898, Speaker = 1054, Text = '波利玩具、红包和新年祝福吗？真是个不错的主意！'}",
	[56899] = "{id = 56899, Speaker = 1054, Text = '谢谢你冒险者，我的疑惑被解开了。'}",
	[56900] = "{id = 56900, Speaker = 1054, Text = '戌神已经到来了吗？替我谢谢戌神。'}",
	[56901] = "{id = 56901, Speaker = 1054, Text = '感谢他的祝福，也感谢你的帮助，冒险者。'}",
	[56902] = "{id = 56902, Speaker = 7072, Text = '看来宝雅的疑惑已经被解开了，这真是让人心情舒畅啊。'}",
	[56903] = "{id = 56903, Speaker = 7072, Text = '明天记得来找我哦！唔，这是最后的祝福了。'}",
	[56904] = "{id = 56904, Speaker = 7072, Text = '今天大家都洋溢着开心的笑脸呢！'}",
	[56905] = "{id = 56905, Speaker = 7072, Text = '冒险者，今天的你也要洋溢着笑脸哦！'}",
	[56906] = "{id = 56906, Speaker = 7072, Text = '咦...怎么会...前几天你帮助过的那些人都在寻找你呢！', Option = '什么？'}",
	[56907] = "{id = 56907, Speaker = 7072, Text = '他们都开始焦急起来了！快去吧冒险者！'}",
	[56908] = "{id = 56908, Speaker = 5648, Text = '戌神，感谢你的祝福！'}",
	[56909] = "{id = 56909, Speaker = 5649, Text = '戌神，我想我应该邀请你去参加我们的宴会！'}",
	[56910] = "{id = 56910, Speaker = 5650, Text = '戌神，也许你应该去看看孩子们的笑脸呢！'}",
	[56911] = "{id = 56911, Speaker = 5651, Text = '感谢你的祝福，要看看王国的皇家烟花吗？'}",
	[56912] = "{id = 56912, Speaker = 7072, Text = '你们是来感谢我的吗…'}",
	[56913] = "{id = 56913, Speaker = 7072, Text = '祝福你们，我亲爱的朋友们！'}",
	[56914] = "{id = 56914, Speaker = 5648, Text = '记得有空来多来看看我们！'}",
	[56915] = "{id = 56915, Speaker = 5649, Text = '我的料理你还没有好好尝过呢！'}",
	[56916] = "{id = 56916, Speaker = 5650, Text = '孩子们一定也很想看到你！'}",
	[56917] = "{id = 56917, Speaker = 5651, Text = '感谢你，戌神，有了你的祝福，我们才会更加开心与幸福。'}",
	[56918] = "{id = 56918, Speaker = 7072, Text = '快乐幸福是相互的，我祝福他们，他们也会祝福我。'}",
	[56919] = "{id = 56919, Speaker = 7072, Text = '这才是一个像样的新年！也谢谢你，冒险者。'}",
	[56920] = "{id = 56920, Speaker = 7072, Text = '新年了，这就当我送你的礼物，收下吧！'}",
	[56926] = "{id = 56926, Speaker = 7821, Text = '幸福小姐对话内容'}",
	[56933] = "{id = 56933, Text = '额……你想做什么？'}",
	[56934] = "{id = 56934, Text = '（盯）不要轻举妄动！'}",
	[56935] = "{id = 56935, Text = '我只是一只年兽啊，你确定要我做宠物吗？'}",
	[56936] = "{id = 56936, Text = '想捉我？门都没有~'}",
	[56937] = "{id = 56937, Text = '我还小，肉不多，别捉我了！'}",
	[56938] = "{id = 56938, Text = '还有急事，溜了溜了。'}",
	[56939] = "{id = 56939, Text = '额，再怎么说也是只年兽啊，就这样放弃了吗？'}",
	[56940] = "{id = 56940, Text = '年轻人，想要跟我谈谈心吗？'}",
	[56941] = "{id = 56941, Text = '我们没有在冷战吧？多说说话呗~'}",
	[56942] = "{id = 56942, Text = '喂喂，怎么会被取个这样的名字？'}",
	[56943] = "{id = 56943, Text = '这个名字…不妥吧…'}",
	[56944] = "{id = 56944, Text = '（害羞）突然觉得，这名字有种意外的贴切感……'}",
	[56945] = "{id = 56945, Text = '心平气和，气定神闲，做一只佛系年兽。'}",
	[56946] = "{id = 56946, Text = '年迈的年兽胡子上都有一颗珠子，我好羡慕啊~'}",
	[56947] = "{id = 56947, Text = '皮肤是原谅色，让我也很苦恼啊~'}",
	[56948] = "{id = 56948, Text = '（脸红）我需要摸摸！'}",
	[56949] = "{id = 56949, Text = '我的下巴有点痒，帮我挠一挠。'}",
	[56950] = "{id = 56950, Text = '（奶声奶气）年轻人，你可以帮我捶捶背吗？年纪大了容易腰酸背痛！'}",
	[56951] = "{id = 56951, Text = '咳咳，肚子在咕咕叫啊…'}",
	[56952] = "{id = 56952, Text = '让我饿肚子不合适吧！'}",
	[56953] = "{id = 56953, Text = '养了我就要喂我吃的啊！肚子都瘪了！'}",
	[56954] = "{id = 56954, Text = '哎，发生了什么事情？我怎么见到了一阵光。'}",
	[56955] = "{id = 56955, Text = '其实我也不期待变强，我只希望世界和平。'}",
	[56956] = "{id = 56956, Text = '有一股力量从我的身体里迸发！我变强了！'}",
	[56957] = "{id = 56957, Text = '我觉得你看上去挺顺眼的啊~'}",
	[56958] = "{id = 56958, Text = '今天的你很可爱哟~'}",
	[56959] = "{id = 56959, Text = '我觉得自己的少年心跟你近了一点呢~'}",
	[56960] = "{id = 56960, Text = '（奶声奶气）我的老腰啊喂，快被打断啦！'}",
	[56961] = "{id = 56961, Text = '坚、坚持不下去了，我先溜了……'}",
	[56962] = "{id = 56962, Text = '你就看着我被敌人殴打吗？'}",
