	[56964] = "{id = 56964, Text = '我说，也不要因为升级就太骄傲了~'}",
	[56965] = "{id = 56965, Text = '撒花撒花~又升级啦~'}",
	[56966] = "{id = 56966, Text = '主人，不要离开我，不要倒下。'}",
