	[57853] = "{id = 57853, Speaker = 5318, Text = '我倒要看看你们够不够资格！'}",
	[57854] = "{id = 57854, Speaker = 5318, Text = '出来吧，我的仆从们！'}",
	[57855] = "{id = 57855, Speaker = 1698, Text = '冒险者，快到我们身边来，让我们帮助你'}",
	[57856] = "{id = 57856, Speaker = 2005, Text = '这些元气魔物妨碍到了我可爱的学生们，我会帮助冒险者赶走他们！'}",
	[57857] = "{id = 57857, Speaker = 1221, Text = '原来负责传送的卡普拉们失踪是这些魔物搞得鬼！骑士团！立刻出击！'}",
	[57858] = "{id = 57858, Speaker = 5318, Text = '再多的冒险者都无法阻止我！'}",
	[57859] = "{id = 57859, Speaker = 1221, Text = '源源不断，可恶！'}",
	[57860] = "{id = 57860, Speaker = 1321, Text = '追查了那么久…原来只是一些来自异世界的魔物吗？真是麻烦！'}",
	[57861] = "{id = 57861, Speaker = 5318, Text = '不！！！！不行！更多！我还需要更多的元气！'}",
	[57862] = "{id = 57862, Speaker = 1562, Text = '难怪最近的演出都取消了，原来是小姐姐们都被这群魔物绑架了'}",
	[57863] = "{id = 57863, Speaker = 5318, Text = '留给你们的时间不多了哦？！'}",
	[57864] = "{id = 57864, Speaker = 5318, Text = '我都快忍不住的笑出声来了！咔咔咔咔咔咔咔'}",
	[57865] = "{id = 57865, Speaker = 5318, Text = '时间！！！！到！'}",
	[57866] = "{id = 57866, Speaker = 1702, Text = '冒险者，感谢你们的帮助，我们已经彻底挣脱了束缚'}",
	[57867] = "{id = 57867, Speaker = 1700, Text = '我们要暂时离开一下，坚持住，卡普拉马上就来帮助你们！'}",
	[57868] = "{id = 57868, Speaker = 5318, Text = '魔王。。。降临！！'}",
	[57869] = "{id = 57869, Speaker = 5318, Text = '你们的元气！都归我了！！'}",
	[57870] = "{id = 57870, Speaker = 5318, Text = '让你们见识一下我的新力量！！！'}",
	[57871] = "{id = 57871, Speaker = 1699, Text = '冒险者！我们来了！'}",
