	[64014] = "{id = 64014, Text = '传说我们这一族在月圆时，会长出二只脚走路说！ =.=||  '}",
	[64015] = "{id = 64015, Text = '不要跟裘卡打牌！他都出老千！'}",
	[64016] = "{id = 64016, Text = '你问小恶魔的爸爸是谁?笨蛋，大恶魔啊~'}",
	[64017] = "{id = 64017, Text = '啊！有流星！！还是本大少头上的星星呢... '}",
	[64018] = "{id = 64018, Text = '毕帝特飞龙整日忙碌着，还是像本大少一样悠哉的躺着比较幸福^-^'}",
	[64019] = "{id = 64019, Text = '站在这里又不会有人膜拜 =.=\" '}",
	[64020] = "{id = 64020, Text = '犹豫不决并不是一个王者所该有的~！！'}",
	[64021] = "{id = 64021, Text = '喵~您说我学猫学得像不像呢?'}",
	[64022] = "{id = 64022, Text = '唉！疯免忍不住也好想有个伴耶！'}",
	[64023] = "{id = 64023, Text = '嘿咻嘿咻~月饼在哪里？'}",
	[64024] = "{id = 64024, Text = '呜哇，我昨晚梦到一个波利那么大的月饼！'}",
	[65000] = "{id = 65000, Speaker = 1698, Text = '勇士们，魔王倒下了，你们胜利了！！'}",
	[65001] = "{id = 65001, Speaker = 5318, Text = '这……这……不可能！，不！，我的元气~ （>.<）'}",
	[65002] = "{id = 65002, Speaker = 1699, Text = '元气大魔王，这次就先放过你，快走吧！！'}",
	[65003] = "{id = 65003, Speaker = 5318, Text = '可恶，我还会再回来的……'}",
	[65004] = "{id = 65004, Speaker = 1698, Text = '勇士们，让我帮你们传送回家吧，这次免费哟'}",
	[65005] = "{id = 65005, Speaker = 1698, Text = '勇士们，我们的元气都被吸走了……'}",
	[65006] = "{id = 65006, Speaker = 5318, Text = '哈哈哈，知道我元气大魔王的厉害了吧！！'}",
	[65007] = "{id = 65007, Speaker = 1699, Text = '呜呜~~不行了，我们先撤退吧（>.<）'}",
	[65008] = "{id = 65008, Speaker = 1698, Text = '趁现在，紧急传送……'}",
	[65009] = "{id = 65009, Speaker = 5318, Text = '这就是你们的实力？'}",
	[65010] = "{id = 65010, Speaker = 1698, Text = '大魔王，你现在悔悟还来得及吖'}",
	[65011] = "{id = 65011, Speaker = 5318, Text = '不，休想！我拒绝任何怜悯，还没结束……'}",
	[65012] = "{id = 65012, Speaker = 1699, Text = '没有时间了！！我们快离开这里吧~'}",
	[65013] = "{id = 65013, Speaker = 1008, Text = '最近经常发现有卡普拉失踪，貌似每周二晚上八点都不太对劲…'}",
	[65014] = "{id = 65014, Speaker = 20027, Text = '这里太闷热了，我需要新鲜空气……'}",
	[65015] = "{id = 65015, Speaker = 30022, Text = '美丽与爱的化身，让波利来感化你们吧~'}",
	[65016] = "{id = 65016, Speaker = 30044, Text = '是谁敢打扰我的休息，踏入我的领土就要付出代价！'}",
	[65017] = "{id = 65017, Speaker = 4749, Text = '我们掌握了足够的情报，只要破坏了火焰核心，我们就不会面对传说中强大的火焰领主-莫特奈尔。'}",
	[65018] = "{id = 65018, Speaker = 4749, Text = '现在我们首要的目标就是通过这里！这里聚集着大量的火焰教教徒，我会试图偷偷潜入进去。'}",
	[65019] = "{id = 65019, Speaker = 4749, Text = '我们在熔洞中心汇合，祝你好运冒险者。'}",
	[65020] = "{id = 65020, Text = '前方传来阵阵颂唱之声……'}",
	[65021] = "{id = 65021, Speaker = 6649, Text = '遵循伟大的、至高的火焰领主-莫特奈尔的意志，火焰终将席卷大地！'}",
	[65022] = "{id = 65022, Speaker = 6649, Text = '而我等必将在火焰中，获得新生！'}",
	[65023] = "{id = 65023, Speaker = 6648, Text = '荣耀归于至高领主-莫特奈尔！'}",
	[65024] = "{id = 65024, Speaker = 6649, Text = '荣耀归于至高领主-莫特奈尔！'}",
	[65025] = "{id = 65025, Speaker = 6649, Text = '不速之客！信徒们展现你们的信仰的时刻到来了！'}",
	[65026] = "{id = 65026, Speaker = 6649, Text = '蔑视火焰的无知之人啊，我必将遵从伟大之莫特奈尔的意志，给予你们火焰的制裁！'}",
	[65027] = "{id = 65027, Speaker = 6649, Text = '火焰将吞噬一切！'}",
	[65028] = "{id = 65028, Speaker = 6649, Text = '如此卑微的实力也妄图挑战伟大的火焰领主！'}",
	[65029] = "{id = 65029, Speaker = 6649, Text = '我期待着你被火焰炙烤那一刻的到来。'}",
	[65030] = "{id = 65030, Speaker = 6651, Text = '火焰核心已经成功炼制出来了，多亏了朱诺那些愚昧的民众！'}",
	[65031] = "{id = 65031, Speaker = 6652, Text = '至高的莫特奈尔正在等着我们，快走吧！'}",
	[65032] = "{id = 65032, Speaker = 6650, Text = '恭送二位护法大人！荣耀归于至高领主-莫特奈尔！'}",
	[65033] = "{id = 65033, Speaker = 6651, Text = '哼！主教，你疏忽了，竟然让那些卑微的虫子闯入了圣地！'}",
	[65034] = "{id = 65034, Speaker = 6652, Text = '如果打搅了仪式，你知道后果！'}",
	[65035] = "{id = 65035, Speaker = 6650, Text = '请二位护法大人放心，我会处理好的！'}",
	[65036] = "{id = 65036, Speaker = 6651, Text = '这样最好！'}",
	[65037] = "{id = 65037, Speaker = 6650, Text = '你让我在护法大人面前失态了！'}",
	[65038] = "{id = 65038, Speaker = 6650, Text = '我以至高领主-莫特奈尔发誓，没有人，能够拯救你！'}",
	[65039] = "{id = 65039, Speaker = 6650, Text = '你的实力不值一提！不过我愿意代替莫特奈尔大人赐予你永恒。'}",
	[65040] = "{id = 65040, Speaker = 6650, Text = '在火焰中哀嚎着化为灰烬吧！'}",
	[65041] = "{id = 65041, Speaker = 6650, Text = '咳，没想到你竟然这么强...不过我是不会让你威胁到领主大人的！'}",
	[65042] = "{id = 65042, Speaker = 6650, Text = '荣耀归于至高领主-莫特奈尔！'}",
	[65043] = "{id = 65043, Speaker = 6650, Text = '啊！！！！！'}",
	[65044] = "{id = 65044, Text = '火焰教主教自爆了……'}",
	[65045] = "{id = 65045, Text = '护法的气息还在前面…'}",
	[65046] = "{id = 65046, Text = '护法的气息就在前面不远处了！'}",
	[65047] = "{id = 65047, Speaker = 4749, Text = '左右护法就在这道屏障后面！'}",
	[65048] = "{id = 65048, Speaker = 4749, Text = '冒险者，让我来！'}",
	[65049] = "{id = 65049, Speaker = 4749, Text = '区区一道屏障可拦不住我！'}",
	[65050] = "{id = 65050, Speaker = 4749, Text = '走！进去阻止他们！'}",
	[65051] = "{id = 65051, Speaker = 6651, Text = '名为火焰之物…'}",
	[65052] = "{id = 65052, Speaker = 6652, Text = '生生不息，永恒不灭…'}",
	[65053] = "{id = 65053, Speaker = 6651, Text = '献上火焰核心为祭品…'}",
	[65054] = "{id = 65054, Speaker = 6652, Text = '在沉睡中醒来吧！吾主！莫特奈尔！'}",
	[65055] = "{id = 65055, Speaker = 4749, Text = '冒险者，你们拦住左右护法，我来阻止仪式！'}",
	[65056] = "{id = 65056, Speaker = 6651, Text = '该死的虫子！'}",
	[65057] = "{id = 65057, Speaker = 6652, Text = '在吾等的怒火中忏悔吧！'}",
	[65058] = "{id = 65058, Speaker = 6651, Text = '没用的，不要挣扎了虫子！'}",
	[65059] = "{id = 65059, Speaker = 6652, Text = '吾主即将醒来，而你等将化为灰烬！'}",
	[65060] = "{id = 65060, Speaker = 4749, Text = '啊！！！'}",
	[65061] = "{id = 65061, Speaker = 4749, Text = '不好冒险者，火焰领主要苏醒了！'}",
	[65062] = "{id = 65062, Speaker = 6651, Text = '吾主，您终于醒来了！'}",
	[65063] = "{id = 65063, Speaker = 6652, Text = '吾主，是我们无能，才让这些虫子闯到这里来。'}",
	[65064] = "{id = 65064, Speaker = 6653, Text = '无妨，我忠实的部下！你们退下吧！'}",
	[65065] = "{id = 65065, Speaker = 6653, Text = '也让我活动活动手脚！'}",
	[65066] = "{id = 65066, Speaker = 6651, Text = '如您所愿，吾主！'}",
	[65067] = "{id = 65067, Speaker = 6652, Text = '荣耀归于至高领主-莫特奈尔！'}",
	[65068] = "{id = 65068, Speaker = 6653, Text = '好了！开始进行下半场吧！'}",
	[65069] = "{id = 65069, Speaker = 6653, Text = '火焰生生不息，这一次是你们赢了。'}",
	[65070] = "{id = 65070, Speaker = 6653, Text = '但下次！你们都将在我的火焰中化为飞灰！'}",
	[65071] = "{id = 65071, Speaker = 4749, Text = '离开这里冒险者！熔洞会在火焰领主的消失下坍塌的！'}",
	[71001] = "{id = 71001, Text = '是吗~是吗~我也挺喜欢这个名字！'}",
	[71002] = "{id = 71002, Text = '虽然波利不会念，不过波利还是很喜欢主人这样叫我'}",
	[71003] = "{id = 71003, Text = '原来这就是波利的名字！全新诞生！'}",
	[71011] = "{id = 71011, Text = '主人主人你在做什么呢~ '}",
	[71012] = "{id = 71012, Text = '主人在做什么呢?是不是要给我买好吃的东西呀? '}",
	[71013] = "{id = 71013, Text = '咕噜咕噜…咕噜咕噜…'}",
	[71021] = "{id = 71021, Text = '其实在我身上能摸到宝贝哦！'}",
	[71022] = "{id = 71022, Text = '软绵绵，冰冰凉！'}",
	[71023] = "{id = 71023, Text = '主人，到了该摸摸我的时间了！！！！！'}",
	[71031] = "{id = 71031, Text = '你知道什么是饿扁吗？我快知道了'}",
	[71032] = "{id = 71032, Text = '呜~我要离家出走了啦~都没把我放在心上'}",
	[71033] = "{id = 71033, Text = '波利的肚子里，清澈透明！'}",
	[71041] = "{id = 71041, Text = '波利！波利！UP！UP！'}",
	[71042] = "{id = 71042, Text = '波利UP！LVUP！'}",
	[71043] = "{id = 71043, Text = '波利大王就要出现了！'}",
	[71051] = "{id = 71051, Text = '我感觉到了主人的心意！'}",
	[71052] = "{id = 71052, Text = '我也想变大10倍把主人包起来，保护你'}",
	[71053] = "{id = 71053, Text = '看着主人的时候，周围仿佛都是爱心！'}",
	[71061] = "{id = 71061, Text = '救护车！救护车！'}",
	[71062] = "{id = 71062, Text = '主人，你的幸运星有点走不动啦！'}",
	[71063] = "{id = 71063, Text = '看起来我不是布欧…'}",
	[71071] = "{id = 71071, Text = 'LEVELUP是什么?我看不懂耶~~ '}",
	[71072] = "{id = 71072, Text = '主人又升一级噜！应该给我吃的东西了吧?'}",
	[71073] = "{id = 71073, Text = '娃哈哈哈哈+_+)... 给我吃的东西我会2倍高兴~  '}",
	[71081] = "{id = 71081, Text = '我好想哭~主人死了，我再也见不到您了吗? '}",
	[71082] = "{id = 71082, Text = '主人，您刚刚才答应我要喂我吃东西的，您不要故意躺在么地上赖皮哦 '}",
	[71083] = "{id = 71083, Text = '主人您死了怎么不会喷东西?我想吃啦>_<我快饿死噜！！ '}",
	[71091] = "{id = 71091, Text = '主人您上来了，好想抱抱，抱抱我，今天是不是要带我逛街? '}",
	[71092] = "{id = 71092, Text = '我好想主人呀~'}",
	[71093] = "{id = 71093, Text = '哦！！哦！亲亲~00！！'}",
	[71101] = "{id = 71101, Text = '主人,主人你的头出血了,不要死呀~ '}",
	[71102] = "{id = 71102, Text = '主人,主人痛吗？我给你吹一下吧？~~'}",
	[71103] = "{id = 71103, Text = '主人！ 快打！ 快打！'}",
	[71111] = "{id = 71111, Text = '喂~喂~喂~我的主人超厉害的，大家有看到吗！'}",
	[71112] = "{id = 71112, Text = '主人是仙境第一勇士，我是第一肥波利~~~~  '}",
	[71113] = "{id = 71113, Text = '这真的是超酷，超强，超漂亮的一击！'}",
	[71121] = "{id = 71121, Text = '主人！我好替您高兴~^^~ '}",
	[71122] = "{id = 71122, Text = '在主人面前它还不如一只小波利！'}",
	[71123] = "{id = 71123, Text = '看它还敢不敢欺负波利！看到主人就吓傻了吧！'}",
	[71131] = "{id = 71131, Text = '我有点害怕~~~~~ '}",
	[71132] = "{id = 71132, Text = '我好害怕，会不会有怪物觉得我是一只很好吃的肥波利~！ '}",
	[71133] = "{id = 71133, Text = '你这只不知天高地厚的怪物，等着被我的主人收拾你吧！  '}",
	[71141] = "{id = 71141, Text = '我也想参加大人们的聊天呀！'}",
	[71142] = "{id = 71142, Text = '什么？什么？！你们是在讨论波利吗？'}",
	[71143] = "{id = 71143, Text = '波利聚会时也会经常聊到冒险者呢！'}",
	[71151] = "{id = 71151, Text = '波利早就感觉到这一次必胜'}",
	[71152] = "{id = 71152, Text = '再接再厉！或者，先开心一会吧！'}",
	[71153] = "{id = 71153, Text = '波利觉得，主人的幸运色就是粉红色了！'}",
	[71161] = "{id = 71161, Text = '忽克连一定是作弊了！'}",
	[71162] = "{id = 71162, Text = '下一次，就是下一次了！'}",
	[71163] = "{id = 71163, Text = '要不要我陪主人去喝一杯！'}",
	[71171] = "{id = 71171, Text = '送给波利吧，波利没有头发，不会插进帽子里'}",
	[71172] = "{id = 71172, Text = '如果波利也带上英雄头盔，会不会变成英雄波利呢？'}",
	[71173] = "{id = 71173, Text = '即使波利带上了天使发圈也不会变成天使波利T.T'}",
	[71181] = "{id = 71181, Text = '很亲切的地方，就像在波利岛时的感觉'}",
	[71182] = "{id = 71182, Text = '那面墙上什么时候才能有我的照片呢？'}",
	[71183] = "{id = 71183, Text = '波利也想和他们一样，长出翅膀'}",
	[71191] = "{id = 71191, Text = '波！利！波波利！'}",
	[71192] = "{id = 71192, Text = '主人，波利驾到！'}",
	[71193] = "{id = 71193, Text = '小果冻，前来报道！'}",
	[71201] = "{id = 71201, Text = '我感觉到主人的温度了呢！'}",
	[71202] = "{id = 71202, Text = '主人的手有点冰凉哦~'}",
	[71203] = "{id = 71203, Text = '如果我有手我现在就摸摸你，主人'}",
	[71211] = "{id = 71211, Text = '主人,只给一点不够啦~我还要~我还要~我还要~'}",
	[71212] = "{id = 71212, Text = '主人，你忍心让我就这样上天堂吗? 饿了噜'}",
	[71213] = "{id = 71213, Text = '唉哟~怎么现在才喂我啊~要让我饿死啊'}",
	[71221] = "{id = 71221, Text = '主人~您再喂我的话，我会脸色发青变成波波利了！'}",
	[71222] = "{id = 71222, Text = '主人，我也想转职！'}",
	[71223] = "{id = 71223, Text = '吃太饱了…虽然我是宠物，但是你太过分了=。='}",
	[71231] = "{id = 71231, Text = '波利渐渐地看不清风景了…'}",
	[71232] = "{id = 71232, Text = '波利很期待和你下一次的见面'}",
	[71233] = "{id = 71233, Text = '主人，你有些模糊，是我眼花了吗？'}",
	[71241] = "{id = 71241, Text = '惊喜就在打开的那一刻哦！'}",
	[71242] = "{id = 71242, Text = '会是什么呢？会是什么波利也好期待呢！'}",
	[71243] = "{id = 71243, Text = '即使不拆开礼物，波利也会兴奋的跳起来呢！'}",
	[71251] = "{id = 71251, Text = '波利很轻，所以主人现在的步伐很轻快！'}",
	[71252] = "{id = 71252, Text = '好晃，我有点波利晕！'}",
	[71253] = "{id = 71253, Text = '主人快找一把变大枪来，我要看的更高'}",
	[71261] = "{id = 71261, Text = '波利！增强！'}",
	[71262] = "{id = 71262, Text = '这很适合波利！'}",
	[71263] = "{id = 71263, Text = '不一样的烟火！不一样的波利！'}",
	[71271] = "{id = 71271, Text = '好期待，会是什么礼物呢？'}",
	[71272] = "{id = 71272, Text = '波利很高兴！波波波！'}",
	[71273] = "{id = 71273, Text = '这是给波利的吗？波利！波利！'}",
	[71301] = "{id = 71301, Text = '哼哧~这名字适合圆润的我~'}",
	[71302] = "{id = 71302, Text = '主人，你也太有品味了吧！'}",
	[71303] = "{id = 71303, Text = '呼呼！我喜欢这个名字~'}",
	[71311] = "{id = 71311, Text = '小心我的南瓜派！！'}",
	[71312] = "{id = 71312, Text = '大家都喜欢在泥坑里跳来跳去~'}",
	[71313] = "{id = 71313, Text = '呼...呼…'}",
	[71321] = "{id = 71321, Text = '主人，难道你不想摸一摸圆鼓鼓的我吗~~'}",
	[71322] = "{id = 71322, Text = '要蹭蹭，来嘛来嘛~~'}",
	[71323] = "{id = 71323, Text = '主人不嫌我重的话就抱抱我吧，哼哧~'}",
	[71331] = "{id = 71331, Text = '哼哧，我好像低血糖了，两眼昏花走不动道儿~'}",
	[71332] = "{id = 71332, Text = '主人，你是嫌弃我胖想让我减肥是吗…可是我瘦了就不可爱了呢'}",
	[71333] = "{id = 71333, Text = '我要吃好吃的T_T感觉自己饿了两年'}",
	[71341] = "{id = 71341, Text = '嘿嘿~升级了，感觉自己的分量又重了一斤~~'}",
	[71342] = "{id = 71342, Text = '哇，升级了我是不是可以吃更多南瓜~'}",
	[71343] = "{id = 71343, Text = '呼呼~升级了~能帮主人更好的顶飞敌人啦！！'}",
	[71351] = "{id = 71351, Text = '我们的感情就像我的体重一样在不断的上升~'}",
	[71352] = "{id = 71352, Text = '你真是越来越值得我新来了~哼哧'}",
	[71353] = "{id = 71353, Text = '我们的友谊会像我的脂肪一样厚，嘿嘿'}",
	[71361] = "{id = 71361, Text = '呜哇！！！T_T'}",
	[71362] = "{id = 71362, Text = '主人难道我们的缘分就到这里了吗！！不要啊，我的饭还没吃完呢！'}",
	[71363] = "{id = 71363, Text = '天堂那边好像要请我去吃顿饭…哼哧…呃…'}",
