	[72021] = "{id = 72021, Text = '希望以后每一次的胜利都有我陪伴在主人左右！'}",
	[72022] = "{id = 72022, Text = '主人英勇杀敌的姿态真是让我快迷上您了~'}",
	[72023] = "{id = 72023, Text = '嗷----呜！我永远是您的小弟！！永远追随您！'}",
	[72031] = "{id = 72031, Text = '主人，我们一起冲，gogogo～'}",
	[72032] = "{id = 72032, Text = '嗷呜！！主人快让我帮你咬死它。'}",
	[72033] = "{id = 72033, Text = '交给我吧，看我的利齿，看我的尖爪！'}",
	[72041] = "{id = 72041, Text = '别的小动物都不愿意和我一起玩儿~莫非是我长的有些凶？'}",
	[72042] = "{id = 72042, Text = '我可不是只小狗狗，我是狼，沙漠之王～～'}",
	[72043] = "{id = 72043, Text = '为什么每次看到月亮，我都忍不住想嗥叫呢？'}",
	[72051] = "{id = 72051, Text = '嘿嘿，可能我是主人的幸运儿吧…这么说不会太自大吧…'}",
	[72052] = "{id = 72052, Text = '我已经感觉到主人不止是强了一点点了~！'}",
	[72053] = "{id = 72053, Text = '恭喜主人，贺喜主人。'}",
	[72061] = "{id = 72061, Text = '别气馁，大不了我少吃两根肉骨头，下次肯定可以成功的！'}",
	[72062] = "{id = 72062, Text = '主人T T，不要气馁，今天可能运气不好啦！'}",
	[72063] = "{id = 72063, Text = '主人别生气，我无条件支持您~'}",
	[72071] = "{id = 72071, Text = '制作了这么漂亮的头饰呀，是今天有约会吗？'}",
	[72072] = "{id = 72072, Text = '这个头饰可真漂亮，要是我也可以戴这么漂亮的头饰就好了！'}",
	[72073] = "{id = 72073, Text = '主人戴上头饰可真好看，真让我骄傲！'}",
	[72081] = "{id = 72081, Text = '激动！主人，我们看起来是不是最威风的？'}",
	[72082] = "{id = 72082, Text = '看，我们成了万众瞩目的焦点~'}",
	[72083] = "{id = 72083, Text = '我有些紧张的说不出话…'}",
	[72091] = "{id = 72091, Text = '嗷呜！我的主人，你就是我的主人吗？'}",
	[72092] = "{id = 72092, Text = '从今天开始你就是我的主人了！好吃的好玩的都要给我！'}",
	[72093] = "{id = 72093, Text = '主人，以后就多多关照了呀。'}",
	[72101] = "{id = 72101, Text = '上边点，上边点，再左边点点…嗷呜…主人摸的真舒服'}",
	[72102] = "{id = 72102, Text = '我们狗，不对，是狼，最喜欢被摸背还有脖子！'}",
	[72103] = "{id = 72103, Text = '被主人摸真是狼生最幸福的事情了！'}",
	[72111] = "{id = 72111, Text = '呜…我嘴里…全是…吃的，一会…再说。'}",
	[72112] = "{id = 72112, Text = '我已经吃饱了呢…实在吃不下了'}",
	[72113] = "{id = 72113, Text = '主人，吃完我给你展示一下我新长的尖牙~！'}",
	[72121] = "{id = 72121, Text = '我已经吃饱了呢…实在吃不下了，求放过。'}",
	[72122] = "{id = 72122, Text = '主人，你可能不是想养一头狼是想养一头猪…'}",
	[72123] = "{id = 72123, Text = '实在是吃撑了，如果主人一定要再喂我，我就吐在您面前…'}",
	[72131] = "{id = 72131, Text = '我们好不容易培养的感情又算什么！！'}",
	[72132] = "{id = 72132, Text = '没想到主人居然会收回我…哭哭'}",
	[72133] = "{id = 72133, Text = '可能是我真的没帮上主人什么忙…呜'}",
	[72141] = "{id = 72141, Text = '这是我的一点小心意，送给你了，快拿走吧！'}",
	[72142] = "{id = 72142, Text = '诶！变了个礼物送给你！'}",
	[72143] = "{id = 72143, Text = '让我摸摸口袋…这边没有…这边也没有…啊！原来在这里，送个你啦！'}",
	[72151] = "{id = 72151, Text = '主人，带我奔跑带我飞！'}",
	[72152] = "{id = 72152, Text = '虽然我跑的更快，但是被主人这样对待真开心！'}",
	[72153] = "{id = 72153, Text = '呼呼呼～～～让我们一起向前！出发！'}",
	[72161] = "{id = 72161, Text = '这装备真漂亮，我会好好帮主人打魔物的，我不怕痛！'}",
	[72162] = "{id = 72162, Text = '这装备一点都不重呢，太棒了，我变厉害啦。'}",
	[72163] = "{id = 72163, Text = '有了这件装备，我就能更好的保护主人了。'}",
	[72171] = "{id = 72171, Text = '这个礼物一定能让我变强的，我会更好的支持主人的！'}",
	[72172] = "{id = 72172, Text = '主人，我会好好珍惜的！！'}",
	[72173] = "{id = 72173, Text = '谢谢主人的礼物！感动！想把主人扑倒！！'}",
	[72201] = "{id = 72201, Text = '哇噢～这就是你以后称呼我的方式了吗？'}",
	[72202] = "{id = 72202, Text = '给我起名字了，要对我负责任哦。'}",
	[72203] = "{id = 72203, Text = '我的名字，有什么特殊的含义吗？'}",
	[72211] = "{id = 72211, Text = '最近长胖了好多，有点点想减肥呢。'}",
	[72212] = "{id = 72212, Text = '我可以自由控制头上的叶子抖动哦，不是所有的曼陀罗都可以的。'}",
	[72213] = "{id = 72213, Text = '蹦…蹦…跳…跳…（〜^∇^)〜，主人，我会吵到你吗？'}",
	[72221] = "{id = 72221, Text = '嗯～叶子有点点不舒服，可以帮我揉揉吗？'}",
	[72222] = "{id = 72222, Text = '主人，你为什么不摸摸我？嫌弃我是从土里钻出来的吗？'}",
	[72223] = "{id = 72223, Text = '走不动了( ´△｀) ，胖子走路本来就很累，抱一抱我可以吗？'}",
	[72231] = "{id = 72231, Text = '肚子又饿了，吃太多会不会把你吃穷啊（ ＴДＴ）'}",
	[72232] = "{id = 72232, Text = '这个好像很好吃的样子……'}",
	[72233] = "{id = 72233, Text = '主人，我闻到了，你把食物藏到哪里了？'}",
	[72241] = "{id = 72241, Text = '哎呀，变强了，我可以更好的保护你啦。'}",
	[72242] = "{id = 72242, Text = '一道金光，我的叶子闪闪发亮，嘿嘿嘿。'}",
	[72243] = "{id = 72243, Text = '虽然只是一株草，我也是有理想的！'}",
	[72251] = "{id = 72251, Text = '有一点点喜欢你了呢，只是一点点哦。'}",
	[72252] = "{id = 72252, Text = '开心的可以飞起来啦！'}",
	[72253] = "{id = 72253, Text = '想要跟主人一直在一起呢。'}",
	[72261] = "{id = 72261, Text = '好累，站不稳了，我不想倒下啊。'}",
	[72262] = "{id = 72262, Text = '痛……呜呜呜……真的好痛……肚子被打到了。'}",
	[72263] = "{id = 72263, Text = '主人，我舍不得你，舍不得你，呜呜呜。'}",
	[72271] = "{id = 72271, Text = '咦，变强了吗？主人好厉害！'}",
	[72272] = "{id = 72272, Text = '练级是不是很辛苦呢？我会一直陪着你的。'}",
	[72273] = "{id = 72273, Text = '好想跟小伙伴炫耀，我的主人又变厉害了ヽ(´∀`)ﾉ'}",
	[72281] = "{id = 72281, Text = '主人，你怎么了？不要不理我，我再也不偷吃了。'}",
	[72282] = "{id = 72282, Text = '啊，你们这群混蛋！不许欺负我的主人！'}",
	[72283] = "{id = 72283, Text = '站起来啊，笨蛋主人，你倒下了我怎么办呢？'}",
	[72291] = "{id = 72291, Text = '主人，你来了呀！我今天洗澡了，身上没泥巴啦。'}",
	[72292] = "{id = 72292, Text = '我等你好久了，怎么一直不出现……'}",
	[72293] = "{id = 72293, Text = '你不在的日子，我都会钻到土里消磨时光。'}",
	[72301] = "{id = 72301, Text = '啊！主人！吃药，吃药，快快吃药。'}",
	[72302] = "{id = 72302, Text = '主人，你受伤了，快躲到我后面去！'}",
	[72303] = "{id = 72303, Text = '好深的伤口！主人，你痛不痛？'}",
	[72311] = "{id = 72311, Text = '呼，好累啊，手臂都抬不起来了。'}",
	[72312] = "{id = 72312, Text = '跟主人战斗，我一点也不怕，一定会打败敌人的。'}",
	[72313] = "{id = 72313, Text = '主人，累不累，我帮你擦汗！太高了，擦不到(Ｔ▽Ｔ) '}",
	[72321] = "{id = 72321, Text = '好棒，刚刚那一招叫什么名字？'}",
	[72322] = "{id = 72322, Text = '什么时候，我可以跟主人一样厉害呢？我也想保护主人。'}",
	[72323] = "{id = 72323, Text = '啊！叶子被扯到了，痛╥﹏╥'}",
	[72331] = "{id = 72331, Text = '呐呐，主人，我厉不厉害？夸夸我嘛。'}",
	[72332] = "{id = 72332, Text = '主人，他看上去都好凶啊。'}",
	[72333] = "{id = 72333, Text = '看我的绿叶波波拳，嘿！哈！(´∩｀) '}",
	[72341] = "{id = 72341, Text = '在讨论些什么事情呢？都听不懂……'}",
	[72342] = "{id = 72342, Text = '主人的朋友好多啊，这样我就安心啦。'}",
	[72343] = "{id = 72343, Text = '我有一个，美丽地愿望，长大以后能播种太阳。'}",
	[72351] = "{id = 72351, Text = '发光了！'}",
	[72352] = "{id = 72352, Text = '主人，你这么开心，我也为你高兴起来啦（・∀・）'}",
	[72353] = "{id = 72353, Text = '不愧是主人，轻轻松松就成功了。'}",
	[72361] = "{id = 72361, Text = '主人，不要不开心了，我给你跳舞吧。'}",
	[72362] = "{id = 72362, Text = '我会为你祈祷的，主人。'}",
	[72363] = "{id = 72363, Text = '我可以为你做一点什么吗？'}",
	[72371] = "{id = 72371, Text = '我也有头饰呢，我的小叶子最可爱了，是不是啊，主人？'}",
	[72372] = "{id = 72372, Text = 'WOW！这个比我的小叶子还可爱ヽ(*´Д｀*)ﾉ被比下去了。'}",
	[72373] = "{id = 72373, Text = '金闪闪的，亮晶晶的，主人你的头饰好漂亮啊。'}",
	[72381] = "{id = 72381, Text = '哎呀，还是这里舒服，外面不是暴晒就是下雨。'}",
	[72382] = "{id = 72382, Text = '走在红毯上，我有没有像大明星啊？'}",
	[72383] = "{id = 72383, Text = '战斗回来，可以喝一杯热茶，最惬意了。'}",
	[72391] = "{id = 72391, Text = '这么快就又见面了啊，你肯定很喜欢我吧。'}",
	[72392] = "{id = 72392, Text = '我在蛋里睡了一觉。'}",
	[72393] = "{id = 72393, Text = '其实，宠物在蛋里不知道时间的流逝……不召唤我也没关系啦。'}",
	[72401] = "{id = 72401, Text = '嘻嘻哈哈哈，讨厌(ﾉ≧∀≦)ﾉ，摸到肚皮会痒的！'}",
	[72402] = "{id = 72402, Text = '喵呜～呼呼呼～哎！我…我…我不是猫啊(๑°ㅁ°๑)！！'}",
	[72403] = "{id = 72403, Text = '扭…扭…_(:з」∠)_好难为情。'}",
	[72411] = "{id = 72411, Text = '这个好吃，这个好吃，这个也好好吃Ψ(｀▽´)Ψ'}",
	[72412] = "{id = 72412, Text = '吃饱了啊，我有一点想睡觉了。'}",
	[72413] = "{id = 72413, Text = '好好吃！主人，吃太多了会不会被你嫌弃啊。'}",
	[72421] = "{id = 72421, Text = '不想吃东西啦，越来越胖了。'}",
	[72422] = "{id = 72422, Text = '不想吃，今天的食物不香呢。'}",
	[72423] = "{id = 72423, Text = '吃撑了，也走不动了。'}",
	[72431] = "{id = 72431, Text = '宠物对于主人是一场陪伴，但主人对于宠物来说就是一生。'}",
	[72432] = "{id = 72432, Text = '有一天，你会嫌弃我的能力不够，抛弃我吗？'}",
	[72433] = "{id = 72433, Text = '其实我只是个没心没肺的小怪物，不用为我操心啦(๑•ᴗ•๑)'}",
	[72441] = "{id = 72441, Text = '(/ω＼) 你喜欢吗？我准备了好久。'}",
	[72442] = "{id = 72442, Text = '主人，你照顾我累了吧，偷偷给你补偿哦。'}",
	[72443] = "{id = 72443, Text = '只是我的一点点心意，对我好会一直给你好东西哦。'}",
	[72451] = "{id = 72451, Text = '我有一只小波利，我从来也不骑。'}",
	[72452] = "{id = 72452, Text = 'WOW～跑得飞快～'}",
	[72453] = "{id = 72453, Text = '好想要时间暂停，你就可以永远抱着我跑下去。'}",
	[72461] = "{id = 72461, Text = '曼陀罗进化，黄金曼陀罗！'}",
	[72462] = "{id = 72462, Text = '穿上装备，会不会威武一点呢？'}",
	[72463] = "{id = 72463, Text = 'boom！Sakalaka！我不一样了┌（★ｏ☆）┘'}",
	[72471] = "{id = 72471, Text = '礼物！送给我的吗？我好感动。'}",
	[72472] = "{id = 72472, Text = '别人都因为我从土里钻出来笑话我，谁能想到我有这样一天呢。'}",
	[72473] = "{id = 72473, Text = 'WOW，这个我想买好久了，谢谢主人！'}",
	[72501] = "{id = 72501, Text = '曾经我也有另外一个名字，现在却不记得了……'}",
	[72502] = "{id = 72502, Text = '从今天开始，我就有名字了。'}",
	[72503] = "{id = 72503, Text = '与君初相识，似是故人归。'}",
	[72511] = "{id = 72511, Text = '我感受不到太阳的温暖，也感受不到雨水的冰冷。'}",
	[72512] = "{id = 72512, Text = '亡灵生活的世界，是没有白天和黑夜的，一切皆处在灰暗之中。'}",
	[72513] = "{id = 72513, Text = '有一点无聊……恩？被您听到了？我没有抱怨啦。'}",
	[72521] = "{id = 72521, Text = '这种事情，说出来有点难为情……可以摸一摸我吗？'}",
	[72522] = "{id = 72522, Text = '自从变成鬼魂以后，人类都躲着我，好怀念怀抱的温度。'}",
	[72523] = "{id = 72523, Text = '主人……我……'}",
	[72531] = "{id = 72531, Text = '有点怀念曾经作为人类，可以吃到的食物。'}",
	[72532] = "{id = 72532, Text = '肚子有点饿，不好意思跟您说呢。'}",
	[72533] = "{id = 72533, Text = '是不是要停下来，休息一下，吃点东西呢？'}",
	[72541] = "{id = 72541, Text = '我感到力量从身体里迸发。'}",
	[72542] = "{id = 72542, Text = '我一直在为您努力成长，这份心意，您可以明白吗？'}",
	[72543] = "{id = 72543, Text = '跟您在一起后，我的进步很快呢。'}",
	[72551] = "{id = 72551, Text = '能与您相识，真是一件令人开心的事情。'}",
	[72552] = "{id = 72552, Text = '您对我真好，一直这样好，可以吗？'}",
	[72553] = "{id = 72553, Text = '对我来说，您是最重要的人。'}",
	[72561] = "{id = 72561, Text = '请别为我哭泣，只不过是又一次经历死亡而已。'}",
	[72562] = "{id = 72562, Text = '啊！这些疼痛让我想起来我曾经的经历。'}",
	[72563] = "{id = 72563, Text = '黑暗将我吞没……'}",
	[72571] = "{id = 72571, Text = '可以陪伴您的成长，我感到非常荣幸。'}",
	[72572] = "{id = 72572, Text = '您的努力得到了回报，真令人欣慰。'}",
	[72573] = "{id = 72573, Text = '以后也要加油呀，练习的道路不能停歇。'}",
	[72581] = "{id = 72581, Text = '别害怕，死亡并不可怕，我在您身边。'}",
	[72582] = "{id = 72582, Text = '下面的路，请抓紧我的手，我会带您走出黑暗。'}",
	[72583] = "{id = 72583, Text = '不要！有谁能帮帮忙？主人！'}",
	[72591] = "{id = 72591, Text = '每次见到您，我的不安的心总是会平静下来……'}",
	[72592] = "{id = 72592, Text = '许久不见，您去忙什么了？会不会忘记我呢？'}",
	[72593] = "{id = 72593, Text = '您的心情好像很好呢，遇见了什么开心的事情吗？'}",
	[72601] = "{id = 72601, Text = '请别逞强，我…很担心。'}",
	[72602] = "{id = 72602, Text = '您的伤势过重，请小心行事。'}",
	[72603] = "{id = 72603, Text = '流血了吗？你们这些肮脏的魔物！都从我主人身边退下！'}",
	[72611] = "{id = 72611, Text = '即使力量强大，一旦为非作歹，免不了被惩处的后果。'}",
	[72612] = "{id = 72612, Text = '他也只不过是一个迷失了的灵魂而已。'}",
	[72613] = "{id = 72613, Text = '终于赢了，这场仗打得真不容易，您幸苦了。'}",
	[72621] = "{id = 72621, Text = '怎么样，这次赢得的奖励丰厚吗？'}",
	[72622] = "{id = 72622, Text = '呀！血溅到我身上了，衣服弄脏了。'}",
	[72623] = "{id = 72623, Text = '您越来越厉害了啊，这样我就放心了。'}",
	[72631] = "{id = 72631, Text = '我讨厌伤害别人，但是如果为了您……'}",
	[72632] = "{id = 72632, Text = '请放心，我始终在您身后掩护。'}",
	[72633] = "{id = 72633, Text = '真是一些丑陋的生物，请您小心！'}",
	[72641] = "{id = 72641, Text = '有点不开心，您跟其他人说了这么多话，很久没搭理我了……'}",
	[72642] = "{id = 72642, Text = '您真是一个温柔善良的人呢。'}",
	[72643] = "{id = 72643, Text = '昏昏沉沉……天气让我有点焦虑'}",
	[72651] = "{id = 72651, Text = '成功了！要再接再厉吗？'}",
	[72652] = "{id = 72652, Text = '我感受到了魔法的韵文在流动。'}",
	[72653] = "{id = 72653, Text = '这种光芒…，真的完全不一样了呢！'}",
	[72661] = "{id = 72661, Text = '失败了吗，真可惜……'}",
	[72662] = "{id = 72662, Text = '要再试试看吗？也许下次就成功了。'}",
	[72663] = "{id = 72663, Text = '没做什么可以帮到您，真抱歉！'}",
	[72671] = "{id = 72671, Text = '我也好喜欢头饰呀，有羽毛的最好看了。'}",
	[72672] = "{id = 72672, Text = '真是一件杰出的作品，请您好好珍藏哦。'}",
	[72673] = "{id = 72673, Text = '原来您喜欢这种类型的头饰呀，很适合您呢。'}",
	[72681] = "{id = 72681, Text = '哎，为什么大家都看着我，好害羞。'}",
	[72682] = "{id = 72682, Text = '回到安全的地方了，在外面我一直提心吊胆的呢。'}",
	[72683] = "{id = 72683, Text = '回到公会了呀，是有什么事情要跟朋友商量吗？'}",
	[72691] = "{id = 72691, Text = '终于出来了，这个黑暗的小空间，总会让我想起黄泉的路。'}",
	[72692] = "{id = 72692, Text = '我不在的日子，您可安好？'}",
	[72693] = "{id = 72693, Text = '红叶落于流水，流水奔腾不息，而红叶终将消弭…真希望我可以陪伴您的时间长一点。'}",
	[72701] = "{id = 72701, Text = '多谢您如此温柔的对待我。'}",
	[72702] = "{id = 72702, Text = '您的抚摸对我来说就像春风，和煦又温柔。'}",
	[72703] = "{id = 72703, Text = '如果还活着，我就可以伸出双手，拥抱您一下了。'}",
	[72711] = "{id = 72711, Text = '又尝到了熟悉的味道，是专门为我买的吗？'}",
	[72712] = "{id = 72712, Text = '啊，这股甜甜的味道，让我想起来一些以前的事情。'}",
	[72713] = "{id = 72713, Text = '您的款待，感激不尽！'}",
	[72721] = "{id = 72721, Text = '今天的食物，有点油腻。'}",
	[72722] = "{id = 72722, Text = '我们还有许多任务吧，不能一直这样悠闲吧。'}",
	[72723] = "{id = 72723, Text = '恕我直言，我还不想吃东西。'}",
	[72731] = "{id = 72731, Text = '伴君千里，终有一别。'}",
	[72732] = "{id = 72732, Text = '您之于我，是雾中之花，是水中之月，偶尔一瞬间的光芒，足以照亮我的世界。'}",
	[72733] = "{id = 72733, Text = '每一次离别，都是为了下一次的相逢……我一直这样期待着。'}",
	[72741] = "{id = 72741, Text = '不知道您喜欢些什么，这是我的一点心意。'}",
	[72742] = "{id = 72742, Text = '有个东西，我准备很久了……喜欢吗？'}",
	[72743] = "{id = 72743, Text = '近段时间，感谢您的照顾，请收下这个。'}",
	[72751] = "{id = 72751, Text = '快把我放下来，您…您真是太无礼了！'}",
	[72752] = "{id = 72752, Text = '人类的怀抱，真的好温暖啊。'}",
	[72753] = "{id = 72753, Text = '其实我飘起来更快，但是……'}",
	[72761] = "{id = 72761, Text = '感谢您为我费心，我只是一个卑微的鬼魂而已。'}",
	[72762] = "{id = 72762, Text = '这个和我的飘带搭配起来，好看吗？'}",
	[72763] = "{id = 72763, Text = '啊，好重，飞不起来了……讨厌，不小心说出来了！'}",
	[72771] = "{id = 72771, Text = '您怎么会知道我想要这个，好开心！'}",
	[72772] = "{id = 72772, Text = '您能时常想起我，真感动。'}",
	[72773] = "{id = 72773, Text = '我好喜欢啊，您送的的东西，我都会珍藏起来的！'}",
	[72801] = "{id = 72801, Text = '哇~好棒的名字~'}",
	[72802] = "{id = 72802, Text = '我很欣赏你的品味哦！'}",
	[72803] = "{id = 72803, Text = '嗯~我就勉强接受吧，谁让你是我的主人呢。'}",
	[72811] = "{id = 72811, Text = '哎，真无聊，今晚要不要出去玩？'}",
	[72812] = "{id = 72812, Text = '没什么事情做，只能发呆啦。'}",
	[72813] = "{id = 72813, Text = '要不要找点刺激的事情做呢！'}",
	[72821] = "{id = 72821, Text = '突然感觉好冷，主人，我需要你的呵护。'}",
	[72822] = "{id = 72822, Text = '主人，能把你温暖的大手借给我一下吗？'}",
	[72823] = "{id = 72823, Text = '我开始有点想念主人轻柔抚摸的感觉了。'}",
	[72831] = "{id = 72831, Text = '主人！让我们来野餐吧！  '}",
	[72832] = "{id = 72832, Text = '天哪，好饿，还没到进餐的时间吗？'}",
	[72833] = "{id = 72833, Text = '肚子饿，已经没力气说话啦。'}",
	[72841] = "{id = 72841, Text = '力量增强的感觉真棒！'}",
	[72842] = "{id = 72842, Text = '哇~真是好幸运啊~'}",
	[72843] = "{id = 72843, Text = '我不会让你失望的，主人！'}",
	[72851] = "{id = 72851, Text = '现在看来跟你在一起还算是个明智的决定！'}",
	[72852] = "{id = 72852, Text = '每天和主人在一起，真是太幸福了~'}",
	[72853] = "{id = 72853, Text = '主人，我会永远陪在你身边的！'}",
	[72861] = "{id = 72861, Text = '要不要先叫救护车……'}",
	[72862] = "{id = 72862, Text = '真倒霉，还是太弱了……'}",
	[72863] = "{id = 72863, Text = '仰望着天空也是一种解脱呢。'}",
	[72871] = "{id = 72871, Text = '真厉害，主人是我心中的偶像！'}",
	[72872] = "{id = 72872, Text = '这也没啥了不起的，比起我还差的远咧~'}",
	[72873] = "{id = 72873, Text = '哇！难得啊，我们一起进步吧！'}",
	[72881] = "{id = 72881, Text = '看吧，早就让你努力进步啦。'}",
	[72882] = "{id = 72882, Text = '主人，我会为你报仇的。'}",
	[72883] = "{id = 72883, Text = '哎，怎么会这样，你要学会保护自己。'}",
	[72891] = "{id = 72891, Text = '主人，我们接下来要去哪里冒险？'}",
	[72892] = "{id = 72892, Text = '你总算来了，我已经准备好了。'}",
	[72893] = "{id = 72893, Text = '今天的天气真不错，又是精彩的一天！'}",
	[72901] = "{id = 72901, Text = '你要小心了，现在的你随时可能遭遇危险。'}",
	[72902] = "{id = 72902, Text = '你还是去恢复点生命值吧，这才是理智的行为。'}",
	[72903] = "{id = 72903, Text = '小心！小心！你的状态非常不好。'}",
	[72911] = "{id = 72911, Text = '哇，主人，你是超人吗？'}",
	[72912] = "{id = 72912, Text = '真有你的，我的主人就是不一样！'}",
	[72913] = "{id = 72913, Text = '好棒哦！真厉害，不过你要继续努力哦~  '}",
	[72921] = "{id = 72921, Text = '还不错，主人，你还是有点实力的嘛~'}",
	[72922] = "{id = 72922, Text = '这样才配做我的主人嘛！'}",
	[72923] = "{id = 72923, Text = '这点成绩，你可不能骄傲哦。'}",
	[72931] = "{id = 72931, Text = '主人，你这软绵绵的攻击可不行哦~'}",
	[72932] = "{id = 72932, Text = '主人，要不要我帮忙啊~~我很强大的哦~'}",
	[72933] = "{id = 72933, Text = '快打啊~主人你总不能让我出手吧~~'}",
	[72941] = "{id = 72941, Text = '哼，人类就是喜欢聊一些无聊的话题。'}",
	[72942] = "{id = 72942, Text = '跟别人聊天就不理我了，哼！'}",
	[72943] = "{id = 72943, Text = '主人和别人聊天的时候，我真的很无聊。'}",
	[72951] = "{id = 72951, Text = '哼，走了一点小运气罢了。'}",
	[72952] = "{id = 72952, Text = '快谢谢我！幸运是我带给你哒~'}",
	[72953] = "{id = 72953, Text = '真是没天理…这都能成功…'}",
	[72961] = "{id = 72961, Text = '嘻嘻嘻…果然失败了嘛…'}",
	[72962] = "{id = 72962, Text = '别生气啊主人…这次不行还有下次嘛~~'}",
	[72963] = "{id = 72963, Text = '主人，你的运气实在是不怎么样呢…'}",
	[72971] = "{id = 72971, Text = '哼，看上去还不错啦！'}",
	[72972] = "{id = 72972, Text = '如果带在我的头上会更好看！'}",
	[72973] = "{id = 72973, Text = '我也想要头饰了…我戴上一定很好看！'}",
	[72981] = "{id = 72981, Text = '这就是人类强者的聚集地吗…'}",
	[72982] = "{id = 72982, Text = '庄严、肃穆，也只有高贵的我，才能配得上这里的气氛呢！'}",
	[72983] = "{id = 72983, Text = '这就是主人你口中的家吗…'}",
	[72991] = "{id = 72991, Text = '看来将来又会有一个小可爱屈服在主人的魔爪之下…'}",
	[72992] = "{id = 72992, Text = '这样我也能多个玩伴了呢，还不算太坏。'}",
	[72993] = "{id = 72993, Text = '看不出来，你还挺有爱心的嘛~'}",
	[73001] = "{id = 73001, Text = '我，我可没要你摸我！'}",
	[73002] = "{id = 73002, Text = '…主人的抚摸才没有那么舒服呢！'}",
	[73003] = "{id = 73003, Text = '手拿开啦~主人的抚摸…有点抵挡不住呢…'}",
	[73011] = "{id = 73011, Text = '咕噜噜…你听错了！这不是肚子叫…'}",
	[73012] = "{id = 73012, Text = '有点想念那些食物的味道了…'}",
	[73013] = "{id = 73013, Text = '味道真不错…再来一点…'}",
	[73021] = "{id = 73021, Text = '嗝~主人你想撑死我吗…'}",
	[73022] = "{id = 73022, Text = '肚子圆鼓鼓的…吃不下了啦。'}",
	[73023] = "{id = 73023, Text = '肚子撑死了…就像人类怀孕一样…哈哈…'}",
	[73031] = "{id = 73031, Text = '好啦，我去休息了…'}",
	[73032] = "{id = 73032, Text = '想我的时候随时召唤我哦~~'}",
	[73033] = "{id = 73033, Text = '我还不想休息呢，不过既然是主人的意思的话…'}",
	[73041] = "{id = 73041, Text = '主人，你是在讨我的欢心嘛~~'}",
	[73042] = "{id = 73042, Text = '我就勉为其难接受你的礼物好了~'}",
	[73043] = "{id = 73043, Text = '宠物大礼包…主人还是很讨喜的嘛~'}",
	[73051] = "{id = 73051, Text = '四周都是主人的气味呢…'}",
	[73052] = "{id = 73052, Text = '不行不行，我可不能沉迷在主人的温柔中…'}",
	[73053] = "{id = 73053, Text = '这样看起来，主人还是很不错的啦~'}",
	[73061] = "{id = 73061, Text = '主人你总算想起来给我穿衣服了！'}",
	[73062] = "{id = 73062, Text = '嘻嘻，这样再也不会有人叫我暴露狂了！'}",
	[73063] = "{id = 73063, Text = '挺合身的…主人这次很棒哦~'}",
	[73071] = "{id = 73071, Text = '这次倒是好好尽了主人的职责呢。'}",
	[73072] = "{id = 73072, Text = '我就收下好了，不收下的话主人的面子可没处放了~'}",
	[73073] = "{id = 73073, Text = '主人，我可不会谢谢你的~'}",
	[73101] = "{id = 73101, Text = '小恶魔也有自己的名字了吗？'}",
	[73102] = "{id = 73102, Text = '主人取得名字意外得不错哦…'}",
	[73103] = "{id = 73103, Text = '其实我不太喜欢这个名字…不过既然是主人取得话…'}",
	[73111] = "{id = 73111, Text = '好想恶作剧哦…'}",
	[73112] = "{id = 73112, Text = '我小恶魔也会有这么无聊的一天呢…'}",
	[73113] = "{id = 73113, Text = '好像找个人欺负一下下…'}",
	[73121] = "{id = 73121, Text = '小心点…不要碰到我的钢叉…'}",
	[73122] = "{id = 73122, Text = '好痒啊~~不要摸我的尾巴啦~'}",
	[73123] = "{id = 73123, Text = '主人你这是在抚摸一个小恶魔哦~~'}",
	[73131] = "{id = 73131, Text = '我要吞噬灵魂——这才是小恶魔该吃的食物嘛~'}",
	[73132] = "{id = 73132, Text = '嘻嘻嘻…快给小恶魔吃的~~'}",
	[73133] = "{id = 73133, Text = '没有东西填肚子…小恶魔要开始恶作剧了！'}",
	[73141] = "{id = 73141, Text = '力量也是我们小恶魔追求的目标呢！'}",
	[73142] = "{id = 73142, Text = '现在可以尝试捉弄一下主人了！'}",
	[73143] = "{id = 73143, Text = '哇咔咔咔…主人我又强大了呢！'}",
	[73151] = "{id = 73151, Text = '小恶魔喜欢恶作剧，小恶魔也喜欢主人…'}",
	[73152] = "{id = 73152, Text = '恶作剧和主人选一个…我选主人！'}",
	[73153] = "{id = 73153, Text = '主人真是太好了，小恶魔很庆幸有这样的主人哦~'}",
	[73161] = "{id = 73161, Text = '可恶…好痛哦！我一定要你好看！'}",
	[73162] = "{id = 73162, Text = '以我小恶魔的名义诅咒你！'}",
	[73163] = "{id = 73163, Text = '即便是我死了，恶作剧也不会停止的！'}",
	[73171] = "{id = 73171, Text = '主人强大了呢！嘻嘻，小恶魔也要努力！'}",
	[73172] = "{id = 73172, Text = '主人，你感受到了恶魔的力量了吗？'}",
	[73173] = "{id = 73173, Text = '只有主人强大了，小恶魔才会更强大哦？'}",
	[73181] = "{id = 73181, Text = '咯咯咯咯…主人你怎么这么不小心~~'}",
	[73182] = "{id = 73182, Text = '对于恶魔来说死亡不值得恐惧~~主人也不要害怕哦~'}",
	[73183] = "{id = 73183, Text = '主人快起来啦！小恶魔这次好好帮忙好不好？'}",
	[73191] = "{id = 73191, Text = '主人，是不是想念小恶魔的恶作剧了呀？'}",
	[73192] = "{id = 73192, Text = '小恶魔一直在这里等着主人回来呢~'}",
	[73193] = "{id = 73193, Text = '欢迎回来，我可爱的主人~~'}",
	[73201] = "{id = 73201, Text = '主人主人，赶紧治疗一下自己啊~~'}",
	[73202] = "{id = 73202, Text = '可惜我不会治疗术，不然主人可不会受伤呢！'}",
	[73203] = "{id = 73203, Text = '主人你先治疗，我去帮你打败它！'}",
	[73211] = "{id = 73211, Text = '小恶魔的朋友们都羡慕我有这样一个强大的主人呢！'}",
	[73212] = "{id = 73212, Text = '实力才是恶魔唯一的法则，主人我更加崇拜你了呢！'}",
	[73213] = "{id = 73213, Text = '这个大家伙看起来也不像我想象中那么厉害嘛！'}",
	[73221] = "{id = 73221, Text = '嘻嘻，这种程度对主人来说是小菜一碟啦！'}",
	[73222] = "{id = 73222, Text = '对于主人来说，这种魔物又算得了什么？'}",
	[73223] = "{id = 73223, Text = '这种魔物，嘻嘻嘻，我努力努力也可以打败的哦~~'}",
	[73231] = "{id = 73231, Text = '我也要拿小钢叉狠狠地戳它的屁股~'}",
	[73232] = "{id = 73232, Text = '嗨呀，这个家伙怎么这么耐揍啊~~'}",
	[73233] = "{id = 73233, Text = '只有我小恶魔的主人才会这么强大呢~~'}",
	[73241] = "{id = 73241, Text = '主人，再不理我我就要恶作剧啦~'}",
	[73242] = "{id = 73242, Text = '主人沉迷聊天，不理我了呢……'}",
	[73243] = "{id = 73243, Text = '人类为什么会有这么多话要说…'}",
	[73251] = "{id = 73251, Text = '嘻嘻，谁说小恶魔会带来厄运的？'}",
	[73252] = "{id = 73252, Text = '小恶魔只会带来好运，对不对主人？'}",
	[73253] = "{id = 73253, Text = '主人的运气真是让人羡慕啊…'}",
	[73261] = "{id = 73261, Text = '主人主人，别灰心，下一次一定成功！'}",
	[73262] = "{id = 73262, Text = '竟然失败了…我诅咒你下次一定成功！话说这种诅咒有效吗？'}",
	[73263] = "{id = 73263, Text = '忽克连这个家伙不会是故意的吧？'}",
	[73271] = "{id = 73271, Text = '哇，主人你带上这个头饰更加帅气了呢！'}",
	[73272] = "{id = 73272, Text = '只有主人才配得上这些光鲜的头饰呢…'}",
	[73273] = "{id = 73273, Text = '小恶魔也能拥有自己的头饰吗？恐怕要特制的吧…'}",
	[73281] = "{id = 73281, Text = '哦天哪，这里有一股我讨厌的气息…是女武神吗？'}",
	[73282] = "{id = 73282, Text = '主人的家看上去很不错哦…'}",
	[73283] = "{id = 73283, Text = '哼哼，如果有人欺负主人的话，就算是女武神也拦不住我！'}",
	[73291] = "{id = 73291, Text = '嘻嘻嘻，又有新的恶作剧对象了~~'}",
	[73292] = "{id = 73292, Text = '不知道这次会孵化出哪个小可爱呢…'}",
	[73293] = "{id = 73293, Text = '小恶魔又有新的玩伴了呢！'}",
	[73301] = "{id = 73301, Text = '尾巴好痒哦~~主人摸摸头就好…'}",
	[73302] = "{id = 73302, Text = '想念主人温暖的手了…'}",
	[73303] = "{id = 73303, Text = '这世界只有主人的双手才有一丝温度…'}",
	[73311] = "{id = 73311, Text = '更多~小恶魔要更多的灵魂~~'}",
	[73312] = "{id = 73312, Text = '主人主人，小恶魔想吃点什么~~'}",
	[73313] = "{id = 73313, Text = '如果是灵魂的话~~还是算了...主人不喜欢小恶魔吃灵魂呢…'}",
	[73321] = "{id = 73321, Text = '小恶魔不想吃这些了…'}",
	[73322] = "{id = 73322, Text = '小恶魔的肚子圆滚滚的，快要变成小恶魔气球了~~'}",
	[73323] = "{id = 73323, Text = '主人主人，你也吃点吧…小恶魔吃不下了…'}",
	[73331] = "{id = 73331, Text = '小恶魔也想休息休息了呢…'}",
	[73332] = "{id = 73332, Text = '虽然还没有玩够，不过小恶魔还是会听主人的话的…'}",
	[73333] = "{id = 73333, Text = '啊…只好下次再出来恶作剧了…'}",
	[73341] = "{id = 73341, Text = '主人主人，这是送给小恶魔的吗？'}",
	[73342] = "{id = 73342, Text = '小恶魔最喜欢礼物了~~'}",
	[73343] = "{id = 73343, Text = '小恶魔也很喜欢拆礼物的感觉呢~~'}",
	[73351] = "{id = 73351, Text = '啊…主人的怀抱好温暖…'}",
	[73352] = "{id = 73352, Text = '没有主人…小恶魔还在冰冷的吉芬地下游荡呢…'}",
	[73353] = "{id = 73353, Text = '主人真好…最喜欢主人了~~'}",
	[73361] = "{id = 73361, Text = '啊哈…那些魔物很难伤害到小恶魔了呢…'}",
	[73362] = "{id = 73362, Text = '有了这个保障，小恶魔可以尽情的恶作剧了呢…'}",
	[73363] = "{id = 73363, Text = '太让小恶魔感动了…主人主人，你真好…'}",
	[73371] = "{id = 73371, Text = '嘻嘻…还是主人了解我喜欢什么…'}",
	[73372] = "{id = 73372, Text = '谢谢主人…小恶魔最喜欢这些道具啦！'}",
	[73373] = "{id = 73373, Text = '只要是主人送的，小恶魔都很喜欢哦…'}",
	[73401] = "{id = 73401, Text = '喝~原来你想这样叫我啊'}",
	[73402] = "{id = 73402, Text = '呼~被起名字了呢'}",
	[73403] = "{id = 73403, Text = '以后就别叫我地龙了啊！'}",
	[73411] = "{id = 73411, Text = '呦~巧克力是什么味道的呢？难道是小野猪味道的吗'}",
	[73412] = "{id = 73412, Text = '咻咻咻~好想找东西咬'}",
	[73413] = "{id = 73413, Text = '啤~做点啥好呢'}",
	[73421] = "{id = 73421, Text = '呀~这种时候要是有哪个家伙来抚摸我一下该有多好呀~'}",
	[73422] = "{id = 73422, Text = '睦~居然没人理我'}",
	[73423] = "{id = 73423, Text = '哼~好痒啊~'}",
	[73431] = "{id = 73431, Text = '噗噗噗，我感觉现在能吃下10只虎王！'}",
	[73432] = "{id = 73432, Text = '休~别和我说话，我正饿着呢'}",
	[73433] = "{id = 73433, Text = '客~饿，现在极度饥饿'}",
	[73441] = "{id = 73441, Text = '嗡~我听见了清脆的鼓掌'}",
	[73442] = "{id = 73442, Text = '咻~我的胃口又变大了'}",
	[73443] = "{id = 73443, Text = '谬~我需要计算下还差多少级我就能变成大龙了'}",
	[73451] = "{id = 73451, Text = '孟~下次的肉啥时候端上来？'}",
	[73452] = "{id = 73452, Text = '咯~看来你挺了解我喜欢吃的'}",
	[73453] = "{id = 73453, Text = '憋~别指望我会那么轻易叫你主人'}",
	[73461] = "{id = 73461, Text = '绣帕…'}",
	[73462] = "{id = 73462, Text = '别谷…'}",
	[73463] = "{id = 73463, Text = '立硝…'}",
	[73471] = "{id = 73471, Text = '哞~你稍微变强了嘛'}",
	[73472] = "{id = 73472, Text = '定~看起来你很开心呦'}",
	[73473] = "{id = 73473, Text = '莫~你就快能赶上我了呢'}",
	[73481] = "{id = 73481, Text = '节~真是弱啊'}",
	[73482] = "{id = 73482, Text = '噶~你怎么就倒下了呢'}",
	[73483] = "{id = 73483, Text = '琼~还不快点站起来'}",
	[73491] = "{id = 73491, Text = '比~太慢了啊'}",
	[73492] = "{id = 73492, Text = '配~才来啊'}",
	[73493] = "{id = 73493, Text = '区~你是谁啊'}",
	[73501] = "{id = 73501, Text = '个~你的腿在发抖哦'}",
	[73502] = "{id = 73502, Text = '联~出那么多汗，看来你快不行了啊'}",
	[73503] = "{id = 73503, Text = '感~要是我的话，我就该磕一块肉补充一下了'}",
	[73511] = "{id = 73511, Text = '巨~干得还算不错'}",
	[73512] = "{id = 73512, Text = '诶~挺有实力的嘛'}",
	[73513] = "{id = 73513, Text = '系~有两下子'}",
	[73521] = "{id = 73521, Text = '麦~还行~'}",
	[73522] = "{id = 73522, Text = '坡~还行~还行~'}",
	[73523] = "{id = 73523, Text = '嚯~瞧瞧'}",
	[73531] = "{id = 73531, Text = '咬咬咬~'}",
	[73532] = "{id = 73532, Text = '惹~就是这个咯'}",
	[73533] = "{id = 73533, Text = '呼~要上了哦！'}",
	[73541] = "{id = 73541, Text = '喂~他这是在找我搭讪吗'}",
	[73542] = "{id = 73542, Text = '某~他刚说什么，其实我没仔细听'}",
	[73543] = "{id = 73543, Text = '图~这可不是在偷听呢'}",
	[73551] = "{id = 73551, Text = '布~闪亮闪亮'}",
	[73552] = "{id = 73552, Text = '让~看起来你很开心嘛~'}",
	[73553] = "{id = 73553, Text = '嘿~那人都敲出火花了'}",
	[73561] = "{id = 73561, Text = '曼~别不开心嘛~'}",
	[73562] = "{id = 73562, Text = '哎~破烂碎片都溅到我身上啦'}",
	[73563] = "{id = 73563, Text = '次~再来一次试试呗'}",
	[73571] = "{id = 73571, Text = '莫~你头上又开新的鲜花了嘛'}",
	[73572] = "{id = 73572, Text = '鸥~你的脸又便奇怪'}",
	[73573] = "{id = 73573, Text = '呼~人类为何喜欢往头上挂那么多奇怪的东西'}",
	[73581] = "{id = 73581, Text = '接~这个家除了你还有谁？介绍一下'}",
	[73582] = "{id = 73582, Text = '古~这里的音乐还挺好听'}",
	[73583] = "{id = 73583, Text = '流~这里有二楼吗'}",
	[73591] = "{id = 73591, Text = '噗留~噗留~'}",
	[73592] = "{id = 73592, Text = '刮呀~刮呀~'}",
	[73593] = "{id = 73593, Text = '明敏~明敏~'}",
	[73601] = "{id = 73601, Text = '丘~还不错还不错~'}",
	[73602] = "{id = 73602, Text = '而~再上去点~左边一点'}",
	[73603] = "{id = 73603, Text = '其~再下来点~右边一点'}",
	[73611] = "{id = 73611, Text = '啃啃啃'}",
	[73612] = "{id = 73612, Text = '嚼嚼嚼'}",
	[73613] = "{id = 73613, Text = '吮吮吮'}",
	[73621] = "{id = 73621, Text = '呗~能打包吗~一会儿我再热热吃'}",
	[73622] = "{id = 73622, Text = '哎~只能看看，吃不下了'}",
	[73623] = "{id = 73623, Text = '复~饱啦饱啦~'}",
	[73631] = "{id = 73631, Text = '庁~回去睡会儿'}",
	[73632] = "{id = 73632, Text = '系~困了'}",
	[73633] = "{id = 73633, Text = '特~好吧'}",
	[73641] = "{id = 73641, Text = '谬~你是个好家伙'}",
	[73642] = "{id = 73642, Text = '门~我先用用看'}",
	[73643] = "{id = 73643, Text = '案~姑且我就收下了咯~'}",
	[73651] = "{id = 73651, Text = '提~你把我弄痒了'}",
	[73652] = "{id = 73652, Text = '级~你稍微轻点'}",
	[73653] = "{id = 73653, Text = '呼~你开心了吧'}",
	[73661] = "{id = 73661, Text = '呢~我其实还能带更多'}",
	[73662] = "{id = 73662, Text = '福~偷偷告诉你，我以前是个帅气的剑圣'}",
	[73663] = "{id = 73663, Text = '金~呦~这个看上去有点意思'}",
	[73671] = "{id = 73671, Text = '李~还有吗？还有吗？'}",
	[73672] = "{id = 73672, Text = '图~下次你点按钮的时候干脆点'}",
	[73673] = "{id = 73673, Text = '更~我确实地收到了哦'}",
	[73701] = "{id = 73701, Text = '人类，我就准许你怎么称呼我吧~嘻嘻'}",
	[73702] = "{id = 73702, Text = '喂~也告诉你的名字呀~'}",
	[73703] = "{id = 73703, Text = '你们人类起的名字真是奇怪~'}",
	[73711] = "{id = 73711, Text = '人类~找点事来做啊，不觉得现在很无聊吗。'}",
	[73712] = "{id = 73712, Text = '哦？你问我为啥在玩花花，还是在这里等了太久。'}",
	[73713] = "{id = 73713, Text = '在这里够久了，你想想有啥好玩的来玩玩，要不来捉迷藏？'}",
	[73721] = "{id = 73721, Text = '人类，我准许你摸摸我的头哦~怀着感激的心吧。'}",
	[73722] = "{id = 73722, Text = '你不觉得忘记做些啥了吗，人类？（伸出头）'}",
	[73723] = "{id = 73723, Text = '人类，要不来玩个游戏吧，看看你能不能摸到我。'}",
	[73731] = "{id = 73731, Text = '人类，献出你的供品来。'}",
	[73732] = "{id = 73732, Text = '比起巧克力甜甜圈，我其实更喜欢原味黄金甜甜圈。'}",
	[73733] = "{id = 73733, Text = '别以为恶魔就不用进食啊，人类。'}",
	[73741] = "{id = 73741, Text = '吼吼，同时看到天使与恶魔，这景色如何？'}",
	[73742] = "{id = 73742, Text = '毋庸置疑我恢复了点能力，你害怕了？'}",
	[73743] = "{id = 73743, Text = '这个特效值五毛~我喜欢~'}",
	[73751] = "{id = 73751, Text = '人类，你很有趣，看来有必要多了解下你。'}",
	[73752] = "{id = 73752, Text = '人类，你来猜猜看，我现在在想什么？'}",
	[73753] = "{id = 73753, Text = '告诉你个秘密，我挺中意你的。'}",
	[73761] = "{id = 73761, Text = '人类…你让我很生气。'}",
	[73762] = "{id = 73762, Text = '这…这不可能…'}",
	[73763] = "{id = 73763, Text = '太失败了…'}",
	[73771] = "{id = 73771, Text = '哦吼~看起来你比之前强壮一些了呢，人类。'}",
	[73772] = "{id = 73772, Text = '如果你能变得更强一点，我也不用出手啦~'}",
	[73773] = "{id = 73773, Text = '人类，我那么努力究竟能达到什么程度，我很兴趣呢。'}",
	[73781] = "{id = 73781, Text = '啧啧啧…就那么轻易倒下了么。'}",
	[73782] = "{id = 73782, Text = '哦~看看你，死亡正围绕着你。'}",
	[73783] = "{id = 73783, Text = '比起恶魔，难道你其实更喜欢死神？'}",
	[73791] = "{id = 73791, Text = '呦~你来了啊~人类，嘻嘻。'}",
	[73792] = "{id = 73792, Text = '睁开眼第一个就看到我，你是否也改感到荣幸呢，人类。'}",
	[73793] = "{id = 73793, Text = '你还活着呀~人类，不错不错，今天玩什么呢？'}",
	[73801] = "{id = 73801, Text = '看你的样子是陷入苦战了啊。'}",
	[73802] = "{id = 73802, Text = '我可是能感受到你的生命力正在流失哦~'}",
	[73803] = "{id = 73803, Text = '你能感受到你正身处危机吗人类，我可有提醒过你了哦~'}",
	[73811] = "{id = 73811, Text = '哼哼，我喜欢你打败强者的样子，看上去还挺帅气呢~'}",
	[73812] = "{id = 73812, Text = '你很期待我会恭喜你吧~而且我并不想那么做呢~嘻嘻嘻。'}",
	[73813] = "{id = 73813, Text = '哦~真是漂亮。'}",
	[73821] = "{id = 73821, Text = '别骄傲，人类，你只不过打败了个比我弱的而已。'}",
	[73822] = "{id = 73822, Text = '只不过如此，你还不至于骄傲起来了吧。'}",
	[73823] = "{id = 73823, Text = '这个也太轻松了，你给我表演下打败那些大家伙们试试，如何？'}",
	[73831] = "{id = 73831, Text = '你说的就是这个家伙咯？'}",
	[73832] = "{id = 73832, Text = '人类，你想打的就是这个呀~'}",
	[73833] = "{id = 73833, Text = '哦？揍这个就可以了吧~简单简单。'}",
	[73841] = "{id = 73841, Text = '看不出来你还挺能聊的嘛，人类。'}",
	[73842] = "{id = 73842, Text = '有时候我也会觉得你们人类的语言也挺好听的。'}",
	[73843] = "{id = 73843, Text = '你在和谁说话呢？不妨告诉我听听，嘻嘻。'}",
	[73851] = "{id = 73851, Text = '看你高兴的样子，成功了呢~'}",
	[73852] = "{id = 73852, Text = 'BINGO~我也来给你配个音。'}",
	[73853] = "{id = 73853, Text = '你还真是乐此不疲地在这个玩具上花钱呢，人类。'}",
	[73861] = "{id = 73861, Text = '哎呀呀，就这样坏了呢，还能用吗？'}",
	[73862] = "{id = 73862, Text = '再来一次试试，说不定下次…哦~我还是不说了，嘻嘻。'}",
	[73863] = "{id = 73863, Text = '不就是敲坏了嘛~没啥大不了的，人类，你还好好活着就行。'}",
	[73871] = "{id = 73871, Text = '人类的美学我还真是看不懂呢~嘻嘻嘻。'}",
	[73872] = "{id = 73872, Text = '对于恶魔来说，美丽的事物也是值得研究的，比如我的小花花。'}",
	[73873] = "{id = 73873, Text = '戴上这个就会变的容易获得人心吗？嘻嘻嘻，人类还真有趣。'}",
	[73881] = "{id = 73881, Text = '亨利希~亨利希~？哦，这小家伙又跑哪里去了。嗯？哎呀呀，我搞错地方了呢，嘻嘻。'}",
	[73882] = "{id = 73882, Text = '这里有点太亮了，我更喜欢暗一点的，呼呼呼。'}",
	[73883] = "{id = 73883, Text = '总觉得有什么长着翅膀的家伙正在看着我…'}",
	[73891] = "{id = 73891, Text = '人类，是你召唤的我吗？'}",
	[73892] = "{id = 73892, Text = '我且问你，你就是我的马斯塔吗？'}",
	[73893] = "{id = 73893, Text = '呼~这里就是人类的世界啊~'}",
	[73901] = "{id = 73901, Text = '看上去，你会是一个好仆人的，嘻嘻~嗯？我可没说什么，嘻嘻嘻嘻。'}",
	[73902] = "{id = 73902, Text = '你可不会是因为听说小山羊的毛摸起来舒服才来的吧。嘻嘻，骗你的。'}",
	[73903] = "{id = 73903, Text = '哦？难不成你看穿我的渴求了吗。'}",
	[73911] = "{id = 73911, Text = '呦~人类，你来的正是时候。'}",
	[73912] = "{id = 73912, Text = '嘻嘻~让我来好好看看这次的供品是啥。'}",
	[73913] = "{id = 73913, Text = '人类，下次也换点更劲爆的食物给我试试如何？嘻嘻，骗你的。'}",
	[73921] = "{id = 73921, Text = '哦~你再喂食我也吃不了哦。（我正在假装用官方系统的语言说着）'}",
	[73922] = "{id = 73922, Text = '人类，你这是在浪费哦~我还是好心地提醒一下吧~嘻嘻。'}",
	[73923] = "{id = 73923, Text = '该说你是阔绰呢，还是看不出我已经吃饱了呢？嘻嘻。'}",
	[73931] = "{id = 73931, Text = '呀~不需要我了吗，你还真是薄情呢。'}",
	[73932] = "{id = 73932, Text = '人类，你要快点再次找我出来哦~我还想玩呢~嘻嘻。'}",
	[73933] = "{id = 73933, Text = '就先姑且回去睡一会儿吧~'}",
	[73941] = "{id = 73941, Text = '对于恶魔来说，只要是供品都是好东西呢，嘻嘻。'}",
	[73942] = "{id = 73942, Text = '还有吗？还有吗？更多也不嫌多，嘻嘻。'}",
	[73943] = "{id = 73943, Text = '收到礼物的人类会很开心吧？那我会很开心哦~哼哼。'}",
	[73951] = "{id = 73951, Text = '看来，你很会“抓”恶魔呢~'}",
	[73952] = "{id = 73952, Text = '不过话说真的，你手法还不错呀~'}",
	[73953] = "{id = 73953, Text = '山羊的毛皮真的像传说中那么舒服吗？你们人类还真难搞懂呢，嘻嘻。'}",
	[73961] = "{id = 73961, Text = '呦~虽然扛着这东西已经很重了，不过我也不介意再多来两件的呢~'}",
	[73962] = "{id = 73962, Text = '来来来~还有吗？再多都可以哦~嘻嘻。'}",
	[73963] = "{id = 73963, Text = '让我用的吗？还真是谢谢了呢，呵呵呵呵。'}",
	[73971] = "{id = 73971, Text = '懂得献上供品的人类还真是可爱呢，嘻嘻。'}",
	[73972] = "{id = 73972, Text = '不错不错，我会像我爸爸美言你几句的，嘻嘻嘻嘻。'}",
	[73973] = "{id = 73973, Text = '其实你也会希望收到礼物吧，嘻嘻，这点上，人类和恶魔是一样的哦~'}",
	[74001] = "{id = 74001, Text = '主人取得名字真好听~~我也有名字了呢~'}",
	[74002] = "{id = 74002, Text = '只要是主人取得名字，我都很喜欢！'}",
	[74003] = "{id = 74003, Text = '主人为我取新的名字啦~~真开心~~'}",
	[74011] = "{id = 74011, Text = '呼…呀，主人，我差点睡着了…'}",
	[74012] = "{id = 74012, Text = '好无聊哦…主人带我去玩嘛~~'}",
	[74013] = "{id = 74013, Text = '啊~~~好无聊哦~~在这样我要睡着了啦… '}",
	[74021] = "{id = 74021, Text = '主人主人，帮我理一理毛发嘛~~~'}",
	[74022] = "{id = 74022, Text = '满地打滚求主人摸摸~~~'}",
	[74023] = "{id = 74023, Text = '想念主人温暖的手了…'}",
	[74031] = "{id = 74031, Text = '主人主人，我有点饿了…'}",
	[74032] = "{id = 74032, Text = '主人今天会给我吃美味的绿草嘛~~'}",
	[74033] = "{id = 74033, Text = '呲溜…不小心口水流下来了…'}",
	[74041] = "{id = 74041, Text = '在主人的悉心照料下，兔兔更加健康了呢！'}",
	[74042] = "{id = 74042, Text = '嘻嘻~我越来越强壮啦~~'}",
	[74043] = "{id = 74043, Text = '主人主人，我升级啦！现在我可以帮到你了嘛~'}",
	[74051] = "{id = 74051, Text = '主人总是这么温柔……'}",
	[74052] = "{id = 74052, Text = '呜呜呜…越来越喜欢和主人在一起了~'}",
	[74053] = "{id = 74053, Text = '超级超级喜欢主人的~~(´,,•ω•,,‘) '}",
	[74061] = "{id = 74061, Text = '主人…身体好痛哦…(๑>m<๑)'}",
	[74062] = "{id = 74062, Text = '我又失败了…什么时候才能真正帮到主人呢…'}",
	[74063] = "{id = 74063, Text = '别担心…主人…我会回来哒！'}",
	[74071] = "{id = 74071, Text = '哇！主人升级了呢！我们可以去更多的地方冒险啦！'}",
	[74072] = "{id = 74072, Text = '好想像主人一样强大哦…嗯嗯…我会努力的！(..•˘_˘•..)'}",
	[74073] = "{id = 74073, Text = '主人真的很棒棒哦~'}",
	[74081] = "{id = 74081, Text = '呜呜…主人快起来啦！(╥╯^╰╥)'}",
	[74082] = "{id = 74082, Text = '主人主人，不要灰心，我们会报仇的！(ง •̀_•́)ง'}",
