	[81845] = "{id = 81845, Speaker = 3323, Text = '小金尚……'}",
	[81846] = "{id = 81846, Speaker = 3323, Text = '终于见到你了……'}",
	[81847] = "{id = 81847, Speaker = 3323, Text = '金恩……'}",
	[81863] = "{id = 81863, Speaker = 4283, Text = '那么机会总是留给有准备的人的。'}",
	[81897] = "{id = 81897, Speaker = 0, Text = '这里模糊的有一些奇怪的脚印..看着并不像是人类的脚印'}",
	[81898] = "{id = 81898, Speaker = 0, Text = '这时，旁边的南瓜地传来一阵骚动。'}",
	[81901] = "{id = 81901, Speaker = 3073, Text = '那就拜托你了。'}",
	[81902] = "{id = 81902, Speaker = 0, Text = '稻田中居然钻出一只全身是伤的小狐狸！嘴里居然叼着李准准备送给小玉的簪子。'}",
	[81923] = "{id = 81923, Speaker = 0, Text = '不远处，听到一阵熊的嘶吼声'}",
	[81934] = "{id = 81934, Speaker = 0, Text = '小玉用尾巴一扫，一阵迷雾飘过，雾散开时李准已经在小玉身旁睡着了。'}",
	[81941] = "{id = 81941, Speaker = 0, Text = '石箭穿透小玉轻盈的身躯，发出耀眼的光芒，能看到小玉的身体一点一点的在消失。'}",
	[81943] = "{id = 81943, Speaker = 0, Text = '小玉带着幸福的笑容，消失在空气中。留下一簇闪闪发光的狐尾毛飘落在手中。'}",
	[81947] = "{id = 81947, Speaker = 0, Text = '说完这句，李准放声痛哭，斐扬城从此多了一段凄美的爱情故事。'}",
	[81949] = "{id = 81949, Speaker = 0, Text = '一阵耀眼的强光后，一位清秀妙曼的身影出现在眼前。'}",
	[82060] = "{id = 82060, Speaker = 4295, Text = '你有变傻光线枪吗？'}",
	[82061] = "{id = 82061, Speaker = 4295, Text = '喂！喂！可别拿着什么奇怪的东西指着我。'}",
	[82062] = "{id = 82062, Speaker = 4295, Text = '总感觉有人在找我……'}",
	[82063] = "{id = 82063, Speaker = 4295, Text = '想要……变大……'}",
	[82064] = "{id = 82064, Speaker = 4295, Text = '你的把戏一点都不合我的心意……'}",
	[82065] = "{id = 82065, Speaker = 4295, Text = '欧！世界突然变大了！'}",
	[82066] = "{id = 82066, Speaker = 4295, Text = '还差一点点，就差一点点！！'}",
	[82067] = "{id = 82067, Speaker = 4295, Text = '这不是变胖！这是变强！'}",
	[82068] = "{id = 82068, Speaker = 4295, Text = '感觉……还差一点'}",
	[82069] = "{id = 82069, Speaker = 4295, Text = '我的体型还有所欠缺！'}",
	[82070] = "{id = 82070, Speaker = 4295, Text = '离我想要的，还差一点！'}",
	[82071] = "{id = 82071, Speaker = 4295, Text = '快了！就快要突破了！'}",
	[82072] = "{id = 82072, Speaker = 4295, Text = '渺小的人类！感受面具恐惧吧！'}",
	[82073] = "{id = 82073, Speaker = 4295, Text = '我要揍扁你们！猥琐的人类！'}",
	[82074] = "{id = 82074, Speaker = 4295, Text = '刚才那个一直射我的人类，我要敲扁你的头！'}",
	[82075] = "{id = 82075, Speaker = 4295, Text = '我觉得...我是我的爸爸！'}",
	[82076] = "{id = 82076, Speaker = 4295, Text = '既然变大了我，就要对我负责！'}",
	[82077] = "{id = 82077, Speaker = 4295, Text = '我是不是开始学会控制我的新力量了？'}",
	[82078] = "{id = 82078, Speaker = 4295, Text = '谁？谁还敢叫我小不点？'}",
	[82079] = "{id = 82079, Speaker = 4295, Text = '你在打量我怀里的宝贝？！'}",
	[82080] = "{id = 82080, Speaker = 4295, Text = '看上去你已经开始好怕我了！'}",
	[82081] = "{id = 82081, Speaker = 4295, Text = '我只是……有点不习惯。'}",
	[82082] = "{id = 82082, Speaker = 4295, Text = '完蛋了！'}",
	[82083] = "{id = 82083, Speaker = 4295, Text = '我会记住你的！'}",
	[82084] = "{id = 82084, Speaker = 4295, Text = '还是不够厉害……'}",
	[82178] = "{id = 82178, Speaker = 2190, Text = '是否开始爱情占卜？'}",
	[82179] = "{id = 82179, Speaker = 2190, Text = '你的同伴正在等候邀请，请耐心等待。'}",
	[82180] = "{id = 82180, Speaker = 2190, Text = '很遗憾占卜失败，你可能并没有达成以下条件：一、你需要是处于牵手状态；二、你需要是处于组队并且仅有二人组队状态；三、仅有均未占卜过的玩家方可进行占卜。'}",
	[82181] = "{id = 82181, Speaker = 2190, Text = '很遗憾，你的同伴没有接受邀请。'}",
	[82445] = "{id = 82445, Speaker = 4316, Text = '现在我最后再问你一次，你准备好了吗？', Option = '{开始吧,5},{我还没准备好,41}'}",
	[82446] = "{id = 82446, Speaker = 4316, Text = '那么机会总是留给有准备的人的。'}",
	[82458] = "{id = 82458, Speaker = 4317, Text = '现在我最后再问你一次，你准备好了吗？', Option = '{开始吧,5},{我还没准备好,41}'}",
	[82469] = "{id = 82469, Speaker = 4319, Text = '现在我最后再问你一次，你准备好了吗？', Option = '{开始吧,5},{我还没准备好,41}'}",
	[82484] = "{id = 82484, Speaker = 2190, Text = '想测试你们之间的默契值吗？想得到关于未来的指引吗？那就来双人命运占卜吧！请要注意哦~点击我手中的火，会选择火上方相应的答案，记得要耐心等待对方选择哦~'}",
	[82495] = "{id = 82495, Speaker = 2189, Text = '吼哦？~看你的表情，还沉浸在之前的情人节收到本命巧克力的幸福中吗？这次白色情人节是回礼的时候了。想要亲手做一份白色情人节巧克力吗？材料需要：[c][ffff00]糖果[-][/c]×5和[c][ffff00]蜂胶[-][/c]×1，最后烘焙费用：[c][ffff00]20000Zeny[-][/c]。', Option = '{确认,2},{取消,1}', Emoji = 4}",
	[82496] = "{id = 82496, Speaker = 2189, Text = '嘻嘻~请拿好了哦~记得作为回礼送给之前送你本命巧克力的人哦~', Emoji = 20}",
	[82497] = "{id = 82497, Speaker = 2189, Text = '哎呀呀，先不要心急哦。制作白色本命巧克力所需的素材为：[c][ffff00]糖果[-][/c]×5和[c][ffff00]蜂胶[-][/c]×1，以及烘焙费用：[c][ffff00]20000Zeny[-][/c]哦~准备好了再来找我吧~', Emoji = 21}",
	[82501] = "{id = 82501, Speaker = 2189, Text = '嘻嘻~一份充满惊喜和心意的情话书签哦~你可以送给最重要的那个TA呢~只需一个[c][ffff00]纯洁白纸[-][/c]和一个[c][ffff00]黑色染料[-][/c],还有[c][ffff00]10000Zeny[-][/c]就可以进行制作啦~', Option = '{确认,2},{取消,1}'}",
	[82502] = "{id = 82502, Speaker = 2189, Text = '嘻嘻~请拿好了哦~具体送给对方后会收到怎么样的寄语呢？很好奇呢~', Emoji = 5}",
	[82503] = "{id = 82503, Speaker = 2189, Text = '哎呀呀~制作恋之絮语所需的素材是：[c][ffff00]纯洁白纸[-][/c]×1和[c][ffff00]黑色染料[-][/c]×1,还有[c][ffff00]10000Zeny[-][/c]的制作费用就可以啦~准备好了再来找我吧~', Emoji = 17}",
	[82534] = "{id = 82534, Speaker = 4326, Text = '离世的时候到了。'}",
	[82535] = "{id = 82535, Speaker = 4326, Text = '该走的路他们已经走尽了，所信的道他们已经守住了。'}",
	[82536] = "{id = 82536, Speaker = 4326, Text = '从此以后，至高无上的神将会赐给他们公义的冠冕。'}",
	[82537] = "{id = 82537, Speaker = 4326, Text = '不但赐给他们，也赐给凡是爱慕神灵显现的人！'}",
	[82574] = "{id = 82574, Speaker = 4330, Text = '背叛者，你竟然还敢出现在我面前！这次我一定不会放过你！'}",
	[82575] = "{id = 82575, Speaker = 4330, Text = '啊！！！你让我愤怒了！出来吧！无尽的冤魂！'}",
	[82576] = "{id = 82576, Speaker = 4330, Text = '愤怒的火焰必将把你们燃为灰烬！！'}",
	[82585] = "{id = 82585, Speaker = 4271, Text = '祝.......福，你们....'}",
	[82586] = "{id = 82586, Speaker = 4271, Text = '今天真是一个使人快乐的日子'}",
	[82587] = "{id = 82587, Speaker = 4271, Text = '幸福的恋人，祝福你们'}",
	[82588] = "{id = 82588, Speaker = 4271, Text = '我看到眼前有很强的白光....'}",
	[82589] = "{id = 82589, Speaker = 4271, Text = '真...幸福...'}",
	[82590] = "{id = 82590, Speaker = 4271, Text = '.....祝福你冒险者'}",
	[82591] = "{id = 82591, Speaker = 4271, Text = '为什么要打扰我们！'}",
	[82592] = "{id = 82592, Speaker = 4271, Text = '真心....相爱'}",
	[82593] = "{id = 82593, Speaker = 4271, Text = '我愿意'}",
	[82594] = "{id = 82594, Speaker = 4266, Text = '小心这些魂魄，他们已经失去了理智，请谨慎靠近。'}",
	[82595] = "{id = 82595, Speaker = 4266, Text = '冒险者，站在最前端的灵魂怨气有些不同，请千万小心！'}",
	[82596] = "{id = 82596, Speaker = 4266, Text = '可怜的恋人，可悲的结果，让他们安息吧冒险者。'}",
	[82597] = "{id = 82597, Speaker = 4031, Text = '活人？为什么要破坏我的傀儡？'}",
	[82598] = "{id = 82598, Speaker = 4271, Text = '冒险者，我们是不是可以尝试着和它谈谈？'}",
	[82599] = "{id = 82599, Speaker = 4031, Text = '有时候真的很佩服人类的异想天开，先断气再来谈吧！', Option = '立刻击败它'}",
	[82600] = "{id = 82600, Speaker = 4302, Text = '喵，老板你小心一点呀！看本喵的！'}",
	[82601] = "{id = 82601, Speaker = 4302, Text = '喵！专心一点啦老板！你这样我也很难做的！'}",
	[82602] = "{id = 82602, Speaker = 4302, Text = '喵！在本喵面前，老板你是不会有事的！'}",
	[82603] = "{id = 82603, Speaker = 4302, Text = '老板，你被本喵B套餐的香味惊醒了嘛？'}",
	[82614] = "{id = 82614, Speaker = 1236, Text = '那家伙又来了，你知道吗？最近大家都在议论呢。', Option = '议论什么？'}",
	[82615] = "{id = 82615, Speaker = 1236, Text = '看来你还不知道？每次他出现后，大家都不敢出门了。'}",
	[82616] = "{id = 82616, Speaker = 1236, Text = '是个非常麻烦的家伙。', Option = '你在说谁？'}",
	[82617] = "{id = 82617, Speaker = 1236, Text = '不知道我在说什么？我说的是那个幽灵！'}",
	[82618] = "{id = 82618, Speaker = 1236, Text = '就是最近经常出现在[c][ffff00]委托看板附近[-][/c]的家伙。路过的人都被它吓得不轻呢！', Option = '有那么可怕？'}",
	[82619] = "{id = 82619, Speaker = 1236, Text = '别太掉以轻心了，他可不是个简单的人物。'}",
	[82620] = "{id = 82620, Speaker = 1236, Text = '近期不要再靠近那块委托看板了，过几天，城防军可能会派人来拆了那块看板，将附近封锁起来。'}",
	[82621] = "{id = 82621, Speaker = 1236, Text = '也许整个依斯鲁得都会被封锁。', Option = '那太糟糕了！'}",
	[82622] = "{id = 82622, Speaker = 1236, Text = '没办法，因为他，镇民都纷纷惶恐地搬走了。也许这座历史悠久的小镇也到了它落幕的一刻吧。这么说，感觉有点悲伤呢。', Option = '得想想办法'}",
	[82623] = "{id = 82623, Speaker = 0, Text = '似乎感觉到了一股无形的寒气……'}",
	[82624] = "{id = 82624, Speaker = 0, Text = '一个模糊的身影渐渐出现在面前，看起来像是一个脸色苍白、神色阴郁的少年，浑身散发着令人不想靠近的气息……', Option = '仔细调查'}",
	[82625] = "{id = 82625, Speaker = 9969, Text = '……你看得到我吗？别紧张，我不会伤害你。', Option = '{准备战斗,4},{亲切微笑,5}'}",
	[82626] = "{id = 82626, Speaker = 9969, Text = '对、对不起！我不是故意的！总有人要来攻击我，所以情不自禁就出手了……'}",
	[82627] = "{id = 82627, Speaker = 9969, Text = '听我说……我没有恶意，只是有件紧急的事正让我发愁，没有人愿意听我解释。', Option = '什么事？'}",
	[82628] = "{id = 82628, Speaker = 9969, Text = '不是我想吓唬那些人，我也不知道为什么自己会在这里。'}",
	[82629] = "{id = 82629, Speaker = 9969, Text = '为了寻找答案，我跟踪了一支冒险队四处搜索，然后去了一个[c][ffff00]海底洞穴[-][/c]。'}",
	[82630] = "{id = 82630, Speaker = 9969, Text = '洞穴的深处似乎隐藏着一座古代遗迹，冒险队打算深入遗迹寻找埋藏的宝藏。但是没多久，情况就不对劲了。'}",
	[82631] = "{id = 82631, Speaker = 9969, Text = '进去的所有人都像着了魔似的，忽然自相残杀起来。', Option = '真是诡异'}",
	[82632] = "{id = 82632, Speaker = 9969, Text = '我想找人去救他们，可是没有人相信我，他们都见到我就跑。', Option = '我可以帮忙'}",
	[82633] = "{id = 82633, Speaker = 9969, Text = '真的吗？太好了！情况紧急，事不宜迟，我们马上就去[c][ffff00]海底洞穴[-][/c]吧！'}",
	[82634] = "{id = 82634, Speaker = 9970, Text = '这里所有的东西都是我的！谁敢抢，我就揍扁他！！！'}",
	[82635] = "{id = 82635, Speaker = 9969, Text = '是冒险队那些人的声音！'}",
	[82636] = "{id = 82636, Speaker = 9969, Text = '小心！这些魔物似乎会影响人的思维，冒险队的人就是因此而发狂的，唯独我没有受到影响。'}",
	[82637] = "{id = 82637, Speaker = 9969, Text = '让我来对付这些魔物，你快去里面救人！', Option = '{我不能丢下你,4},{那就交给你了,7}'}",
	[82638] = "{id = 82638, Speaker = 9969, Text = '我一个人足够应付。里面的人很危险，一秒钟也不能耽搁！快去！', Option = '我这就去'}",
	[82639] = "{id = 82639, Speaker = 9977, Text = '你是谁！也是来抢财宝的吗？滚！不然我杀了你！'}",
	[82640] = "{id = 82640, Speaker = 9977, Text = '我都干了些什么……？', Option = '告知详情'}",
	[82641] = "{id = 82641, Speaker = 9977, Text = '看来这是我们贪财的报应。', Option = '继续了解情况'}",
	[82642] = "{id = 82642, Speaker = 9977, Text = '我们原来是梦罗克商旅会的人，偶然去普隆德拉做生意时，听说沉没在海底的神殿中埋藏着古王朝的宝物，于是我们就想将它据为己有。然而还没到达神殿遗迹，我们就先丧失了心智。'}",
	[82643] = "{id = 82643, Speaker = 9977, Text = '谢谢你让我清醒过来，只是，你能想办法再救救我的队友们吗？他们应该还活着。', Option = '怎么救？'}",
	[82644] = "{id = 82644, Speaker = 9977, Text = '我在一本海洋生物书上看到，用[c][ffff00]海葵[-][/c]的[c][ffff00]珊瑚精华[-][/c]能使迷失心智的人恢复清醒。我必须在这里守着他们，你能帮我去寻找[c][ffff00]珊瑚精华[-][/c]吗？'}",
	[82645] = "{id = 82645, Speaker = 9977, Text = '除了倒在这里的几个，还有其他队员去探索洞穴别的地方了，他们可能也遇难了。麻烦你多找一些[c][ffff00]珊瑚精华[-][/c]。'}",
	[82646] = "{id = 82646, Speaker = 9977, Text = '剩下的那些人，也拜托你去救他们了。我还得留在这里为这几名队友处理伤情。'}",
	[82647] = "{id = 82647, Speaker = 9984, Text = '谢谢你，这里似乎很危险，我们不准备继续深入了，请转告其他人，尽快离开。'}",
	[82648] = "{id = 82648, Speaker = 9988, Text = '幸好有你的帮忙，不然我们都会葬身此地。现在想想，真是不值得为了那些宝藏而送命，我们准备立刻撤离。'}",
	[82649] = "{id = 82649, Speaker = 9977, Text = '非常谢谢你为我们做的这一切。以后，如果你有什么需要，尽管来梦罗克找我们！'}",
	[82650] = "{id = 82650, Speaker = 9977, Text = '这座洞穴隐藏着可怕的力量，只要呆在这里就很危险，你跟我们一起走吧？', Option = '我还不能走'}",
	[82651] = "{id = 82651, Speaker = 9977, Text = '还有人在这个洞穴里？！'}",
	[82652] = "{id = 82652, Speaker = 9977, Text = '那么，让我们帮你吧！', Option = '委婉谢绝'}",
	[82653] = "{id = 82653, Speaker = 9977, Text = '的确我们现在想帮你也有些力不从心，再逗留下去，可能又会丧失心智。'}",
	[82654] = "{id = 82654, Speaker = 9977, Text = '不过，有件东西或许可以帮上你。'}",
	[82655] = "{id = 82655, Speaker = 9977, Text = '这是我在海底洞穴中唯一发现的一件宝物，据古书记载，这个[c][ffff00]太阳石头箍[-][/c]如果带在身上，能驱除魔物的影响。我就是因为拿着它，才比其他人坚持得久一点。'}",
	[82656] = "{id = 82656, Speaker = 9977, Text = '经过这次的经历，我已经决定，从今以后摒弃对宝藏的执迷，老老实实做生意。这个辟邪的头箍，你就拿着吧！'}",
	[82657] = "{id = 82657, Speaker = 9977, Text = '但愿你能尽快找回你的朋友，平安离开。'}",
	[82658] = "{id = 82658, Speaker = 9969, Text = '别过来！那只巨大的绿蛙就在附近，这里很危险，你快逃出去吧！'}",
	[82659] = "{id = 82659, Speaker = 9969, Text = '让你跑，为什么呆着不跑呢？！', Option = '{吓呆了,3},{加入战斗,3}'}",
	[82660] = "{id = 82660, Speaker = 9969, Text = '这头怪物受海底洞穴中的魔力滋养，浑身充满了剧毒，就算是我，渐渐也受到了毒性的侵蚀。你留在这很危险！', Option = '{决定留下,4},{暂时离开,1}'}",
	[82661] = "{id = 82661, Speaker = 9969, Text = '真拿你没办法。本来我只是想让你来救人，可不是让你来送死的啊。'}",
	[82662] = "{id = 82662, Speaker = 9969, Text = '别小看这只大青蛙，说起来，它也是我的老对手了。只要吸入它呼出的毒气，就会四肢无力，身不由己被它控制。注意[c][ffff00]闭气[-][/c]！'}",
	[82663] = "{id = 82663, Speaker = 9969, Text = '注意千万别吸入它放出的毒气，不过也别把自己闷死了！'}",
	[82664] = "{id = 82664, Speaker = 9969, Text = '啊……！'}",
	[82665] = "{id = 82665, Speaker = 9969, Text = '冒险者……你快……跑吧……别管我……了……'}",
	[82666] = "{id = 82666, Speaker = 9969, Text = '你……你把它们都干掉了吗？真厉害……你真让我刮目相看。'}",
	[82667] = "{id = 82667, Speaker = 9969, Text = '没想到结果我还是一点忙都没帮上。不过那只大青蛙会无限复活，没过多久，可能又会在某处重生。'}",
	[82668] = "{id = 82668, Speaker = 9969, Text = '我果然是个自以为是的失败者，为了恋慕的心上人而舍弃自己的王国，结果却连表白的勇气都没有。'}",
	[82669] = "{id = 82669, Speaker = 9969, Text = '看到自己的好友变成面目可憎的魔物，我却没有勇气全力应战……曾坚信自己的王国会流传百世，可它现在却变成了海底的一片废墟。'}",
	[82670] = "{id = 82670, Speaker = 9969, Text = '我的名字，和我的古王朝，也没有人记得了。'}",
	[82671] = "{id = 82671, Speaker = 9969, Text = '大家只叫我“死灵”，一个死去的灵魂。'}",
	[82672] = "{id = 82672, Speaker = 9969, Text = '你也觉得我很可笑是吗？', Option = '{是很可笑,8},{不可笑,9}'}",
	[82673] = "{id = 82673, Speaker = 9969, Text = '不过，我想到一件事。', Option = '什么事？'}",
	[82674] = "{id = 82674, Speaker = 9969, Text = '嘿嘿嘿……'}",
	[82675] = "{id = 82675, Speaker = 9969, Text = '如果能借用别人的身体，我就可以复活，并且重建我的王朝。'}",
	[82676] = "{id = 82676, Speaker = 9969, Text = '而我发现，眼前就有一个非常合适的人选，不但拥有强大的精神力，而且身体素质也很不错。连那只大青蛙的毒性都侵蚀不了你，我等了很久，第一次遇到这样优秀的容器。', Option = '你说什么！'}",
	[82677] = "{id = 82677, Speaker = 9969, Text = '这是……！！！'}",
	[82678] = "{id = 82678, Speaker = 9969, Text = '为什么我送给菲力卡的头箍，会在你手里？！！啊啊啊啊……这个光芒……啊啊啊……'}",
	[82679] = "{id = 82679, Speaker = 9969, Text = '我的脑袋一片混乱……我是怎么了……'}",
	[82680] = "{id = 82680, Speaker = 9969, Text = '刚才应该是我沾染的毒性发作了，所以忽然暴躁起来。我现在需要去角落静一静……'}",
	[82681] = "{id = 82681, Speaker = 9969, Text = '我已经平静多了。'}",
	[82682] = "{id = 82682, Speaker = 9969, Text = '有件事我不明白，这个太阳石头箍是我当初在追求菲力卡时送给她的，不过她好像并不怎么在意，一次也没有见她戴过。'}",
	[82683] = "{id = 82683, Speaker = 9969, Text = '为什么它会在海底废墟里？菲力卡难道来过我的王国……'}",
	[82684] = "{id = 82684, Speaker = 9969, Text = '如果我能再次见到她，将过去不敢说出口的话亲口对她说一次，就好了。'}",
	[82685] = "{id = 82685, Speaker = 9969, Text = '现在，只剩下这个头箍陪着我了。'}",
	[82686] = "{id = 82686, Speaker = 9969, Text = '哼，冒险家，虽然我是被魔物的毒性控制而一时意识失控，不过复国的计划我是不会放弃的。看在这个头箍的份上，也许是菲力卡想要我放你一马，今天就算了。你可要小心了，我们总会再见面的。'}",
	[82687] = "{id = 82687, Speaker = 9969, Text = '就这么消失了……看来得向莎西尔.迪文再问问情况。'}",
	[82688] = "{id = 82688, Speaker = 1236, Text = '你碰到那个幽灵了？我的警告，你怎么不听呢！'}",
	[82689] = "{id = 82689, Speaker = 1236, Text = '冒险家，这个世界还有许多你未知的危险事物。过多的好奇心会葬送你的小命！', Option = '{我不怕死,2},{我知错了,2}'}",
	[82690] = "{id = 82690, Speaker = 1236, Text = '这次出现的幽灵，经过我的调查，就是被禁锢在吉芬魔法塔地下，受到诅咒而永不消亡的死灵。至于他为什么能离开那里，出现在依斯鲁得，恐怕和近期发生的次元裂隙魔力动荡有关。', Option = '寻问死灵来历'}",
	[82691] = "{id = 82691, Speaker = 1236, Text = '刚说了，别有太多好奇心！'}",
	[82692] = "{id = 82692, Speaker = 1236, Text = '不过你既然问了，我就勉为其难回答一下，免得你冒冒失失再惹祸上身。'}",
	[82693] = "{id = 82693, Speaker = 1236, Text = '死灵出现的原因有很多传说，但没有人可以确定死灵到底是谁。似乎有人刻意抹去了过去的一段历史。'}",
	[82694] = "{id = 82694, Speaker = 1236, Text = '我听长老说过，死灵因为触碰了魔法塔中的禁忌，被禁锢在塔中，肉体死亡后，灵魂却没有消失。'}",
	[82695] = "{id = 82695, Speaker = 1236, Text = '由于想要获得肉体重生的渴望，他的灵魂一次又一次的死而复活。'}",
	[82696] = "{id = 82696, Speaker = 1236, Text = '这是一种古老的禁忌魔法，魔法公会到目前还没有找到消除他的办法，只能加强对魔法塔地下的监视。'}",
	[82697] = "{id = 82697, Speaker = 1236, Text = '好了，我必须把这些情况报告给城防军。近期在外走动要多留神，说不定他还会出现！'}",
	[82701] = "{id = 82701, Speaker = 0, Text = '这时火焰纹章在你的胸口微微发光…'}",
	[82702] = "{id = 82702, Speaker = 4336, Text = '哦…火焰纹章的气息…亲爱的烈焰之黛西，你终于从封印中逃出来了吗？'}",
	[82703] = "{id = 82703, Speaker = 4336, Text = '嗯？你不是黛西！你究竟是谁，为什么火焰纹章会出现在你身上！'}",
	[82704] = "{id = 82704, Speaker = 4336, Text = '不…不不不！我的力量…'}",
	[82716] = "{id = 82716, Speaker = 0, Text = '火焰纹章在你的胸口发出朦胧的柔光…好像要告诉你什么…'}",
	[82717] = "{id = 82717, Speaker = 4339, Text = '火焰的气息…似乎有点熟悉…百年前被封印的新人吗…'}",
	[82718] = "{id = 82718, Speaker = 4339, Text = '看来她也熬不过这令人疯狂的封印，消散了吗…冒险者，你带着这火焰纹章来到这里，是想问我什么吗？', Option = '讲述经过'}",
	[82719] = "{id = 82719, Speaker = 4339, Text = '原来是这样…嗯？真是阴魂不散，你又来这里做什么！'}",
	[82720] = "{id = 82720, Speaker = 4341, Text = '亲爱的安琪，好歹我们曾经同为队友，你为什么不能对我和颜悦色一点呢？'}",
	[82721] = "{id = 82721, Speaker = 4339, Text = '你已经堕落了，杰理！神是不会原谅你的！'}",
	[82722] = "{id = 82722, Speaker = 4341, Text = '神？我放弃抵抗追随黑暗之王，得到了不朽的身躯，而你呢？被镇压在这破石头里几百年，你信奉的神在哪里？'}",
	[82723] = "{id = 82723, Speaker = 4339, Text = '迟早有一天，你会在神威炼狱中凄惨嚎叫！'}",
	[82724] = "{id = 82724, Speaker = 4341, Text = '我的耐心已经用光了！安琪，只要你肯堕落成为暗神官，我可以向你保证，你的地位绝对不会比我低！'}",
	[82725] = "{id = 82725, Speaker = 4339, Text = '住口杰理！你在痴心妄想，我绝不会向黑暗之王低头的！'}",
	[82726] = "{id = 82726, Speaker = 4341, Text = '不可理喻！或许强大的力量能让你清醒一点！'}",
	[82728] = "{id = 82728, Speaker = 4340, Text = '你永远不会有机会了…杰理…'}",
	[82738] = "{id = 82738, Speaker = 0, Text = '你的胸前火焰纹章微微发烫，似乎在提醒着什么…'}",
	[82739] = "{id = 82739, Speaker = 4344, Text = '哟…猎物上钩啦…嘿嘿，这下可以好好地活动一下筋骨了！'}",
	[82740] = "{id = 82740, Speaker = 4345, Text = '热身到此结束！停手，停手！嘿嘿…'}",
	[82751] = "{id = 82751, Speaker = 4345, Text = '记住我的名字，冰霜猎人珀尔！'}",
	[82755] = "{id = 82755, Speaker = 0, Text = '胸前火焰纹章急促地闪着光，似乎在提醒，有危险正在靠近…'}",
	[82756] = "{id = 82756, Speaker = 4347, Text = '哼…又是迷幻之王的走狗么…这次倒是聪明，变成了冒险者的模样，我怎么会轻易上当！'}",
	[82767] = "{id = 82767, Speaker = 4348, Text = '带我离去，我会亲手送迷幻之王下地狱！'}",
	[82786] = "{id = 82786, Speaker = 4330, Text = '啊！！！你让我愤怒了！出来吧！无尽的冤魂！'}",
	[82787] = "{id = 82787, Speaker = 4330, Text = '愤怒的火焰必将把你们燃为灰烬！'}",
	[82788] = "{id = 82788, Speaker = 4336, Text = '黑暗降临…一切光明都将毁灭！'}",
	[82789] = "{id = 82789, Speaker = 4336, Text = '在黑暗中，吾将永不腐朽！'}",
	[82790] = "{id = 82790, Speaker = 4341, Text = '为什么！神抛弃了我们，为什么你不肯和我一起投身黑暗！'}",
	[82791] = "{id = 82791, Speaker = 4341, Text = '信奉黑暗，才能获得不朽的力量！'}",
	[82792] = "{id = 82792, Speaker = 4344, Text = '嘿嘿，这个猎物有点儿意思……'}",
	[82793] = "{id = 82793, Speaker = 4344, Text = '有意思，好久没有这么有意思的猎物了！我要动真格了！'}",
	[82794] = "{id = 82794, Speaker = 4347, Text = '融于暗影之中……'}",
	[82799] = "{id = 82799, Speaker = 4350, Text = '西格莉特，你已经无路可逃了。'}",
	[82919] = "{id = 82919, Speaker = 4360, Text = '斐杨和兽人村喵', Option = '然后呢'}",
	[82956] = "{id = 82956, Speaker = 4366, Text = '谢谢你，陌生人，不过请不要再继续探索，为了明天！'}",
	[82982] = "{id = 82982, Speaker = 4273, Text = '我感觉到你们的位置，我将开始进行魔力灌注，请小心周围。'}",
	[82983] = "{id = 82983, Speaker = 4273, Text = '就快完成了，我将继续加大灌注量，坚持住，冒险者。'}",
	[82984] = "{id = 82984, Speaker = 4273, Text = '瑞克，我已经能看到你们，连接已经形成，等等，小心身后！'}",
	[82985] = "{id = 82985, Speaker = 4273, Text = '瑞克，冒险者，你们没事真是太好了，请赶快返回营地。'}",
	[83014] = "{id = 83014, Speaker = 4368, Text = '喵，老板你也太脆弱了吧！'}",
	[83015] = "{id = 83015, Speaker = 4368, Text = '喝！竟然敢伤害我的老板！'}",
	[83016] = "{id = 83016, Speaker = 4368, Text = '喵！我就知道老板你会很快回来的！'}",
	[83017] = "{id = 83017, Speaker = 4368, Text = '老板，我还以为你还要再在地板上睡一会呢。'}",
	[83018] = "{id = 83018, Speaker = 4364, Text = '[c][ffff00]%s[-][/c]先生，您的礼物到啦！'}",
	[83019] = "{id = 83019, Speaker = 4364, Text = '[c][ffff00]%s[-][/c]小姐姐，您的礼物到啦！'}",
	[83020] = "{id = 83020, Speaker = 4364, Text = '您的礼物已签收~'}",
	[83021] = "{id = 83021, Speaker = 4364, Text = '您拒绝了这份赠礼！'}",
	[83027] = "{id = 83027, Speaker = 4273, Text = '我的法力已经开始最大限度地输入到传送媒介中，请千万小心，冒险者。'}",
	[83080] = "{id = 83080, Speaker = 4352, Text = '要是苏醒召唤出什么我可就不负责了，你确定吗？', Option = '确定'}",
	[83096] = "{id = 83096, Speaker = 4374, Text = '不！我的灵魂…我的永生！你会付出代价的！'}",
	[83097] = "{id = 83097, Speaker = 4375, Text = '感谢你把我们从这被诅咒的手札中释放出来…'}",
	[83112] = "{id = 83112, Speaker = 4375, Text = '请等一下！'}",
	[83135] = "{id = 83135, Speaker = 4377, Text = '黑暗的背叛者，堕落的未亡者，铭记尔等身负的锁链！'}",
	[83136] = "{id = 83136, Speaker = 4377, Text = '伟大的黑暗之王赐予我无匹的伟力，吾是这根锁链的主人！'}",
	[83137] = "{id = 83137, Speaker = 4377, Text = '遵从黑暗之王者的意志，淹没光明吧！'}",
	[83177] = "{id = 83177, Speaker = 4377, Text = '遵从暗黑之王的契约，解放世界的戒令…'}",
	[83179] = "{id = 83179, Speaker = 4377, Text = '以绝望之名，将眼前生灵的魂与血奉上…'}",
	[83182] = "{id = 83182, Speaker = 4377, Text = '你竟敢破坏伟大的仪式！'}",
	[83183] = "{id = 83183, Speaker = 4377, Text = '我诅咒你！你的灵魂将永受折磨！啊！！'}",
	[83186] = "{id = 83186, Speaker = 4385, Text = '你休想！'}",
	[83213] = "{id = 83213, Speaker = 4394, Text = '什么？你的冒险手册不止解锁3个哥布灵面具'}",
	[83214] = "{id = 83214, Speaker = 4394, Text = '好吧，但是我也不会为你的每一次进步鼓掌'}",
	[83215] = "{id = 83215, Speaker = 4394, Text = '除非，你解锁到了5个哥布灵面具，我将给予你第二份礼物', Option = '{我没解锁,3},{我解锁了面具,0}'}",
	[83216] = "{id = 83216, Speaker = 4394, Text = '我是说我需要你解锁5个哥布灵面具我才给你第二份奖励，是我没说清楚吗？'}",
	[83217] = "{id = 83217, Speaker = 4394, Text = '这还不错！你可以领取第二份礼物'}",
	[83233] = "{id = 83233, Speaker = 4396, Text = '记住，贪心引领了你的死亡！'}",
	[83234] = "{id = 83234, Speaker = 4396, Text = '贪心的人，成我的一部分吧！'}",
	[83235] = "{id = 83235, Speaker = 4396, Text = '还不错，但还不足以撼动我！'}",
	[83236] = "{id = 83236, Speaker = 4396, Text = '卑微的家伙！你让我愤怒了！'}",
	[83237] = "{id = 83237, Speaker = 4396, Text = '…现在的冒险者都已经这么强大了吗…'}",
	[83259] = "{id = 83259, Speaker = 4266, Text = '这对我研究古城的历史有非常巨大的帮助。'}",
	[83299] = "{id = 83299, Speaker = 4397, Text = '我会试图驯服这把剑，但是在驯服过程中，能量的逸散恐怕会招来一些不必要的麻烦。'}",
	[83300] = "{id = 83300, Speaker = 4397, Text = '冒险者，请你守护我片刻！', Option = '好的'}",
	[83301] = "{id = 83301, Speaker = 4397, Text = '小心，魔物出现了！'}",
	[83302] = "{id = 83302, Speaker = 4397, Text = '又有魔物出现了！保护好我！'}",
	[83303] = "{id = 83303, Speaker = 4397, Text = '我就快成功了！'}",
	[83304] = "{id = 83304, Speaker = 4397, Text = '天啊，这次是迷失骑士！'}",
	[83305] = "{id = 83305, Speaker = 4397, Text = '好了冒险者，我已经驯服辉光之灵了。'}",
	[83306] = "{id = 83306, Speaker = 4397, Text = '嘿！多亏了你，这把剑真是太适合我了！'}",
	[83307] = "{id = 83307, Speaker = 4397, Text = '我们先离开这里！'}",
	[83317] = "{id = 83317, Speaker = 4400, Text = '啊——！'}",
	[83318] = "{id = 83318, Speaker = 4400, Text = '呼……呼……杀了我！你这个恶魔！', Option = '你认错了'}",
	[83319] = "{id = 83319, Speaker = 4400, Text = '呼……呼……冒…冒险者？', Option = '是的'}",
	[83320] = "{id = 83320, Speaker = 4400, Text = '求…求求你！救我！或者杀了我！', Option = '怎么救？'}",
	[83321] = "{id = 83321, Speaker = 4400, Text = '呼…呼…解决掉…这些苦痛法阵…'}",
	[83322] = "{id = 83322, Speaker = 4400, Text = '呼…经过了几百年…这些能量早已所剩无几…'}",
	[83323] = "{id = 83323, Speaker = 4400, Text = '呼…只需要…用你自己的力量冲击…就可以破除…', Option = '请…快一点！'}",
	[83324] = "{id = 83324, Speaker = 4400, Text = '我恢复了一些…接下来破除第二个吧。'}",
	[83325] = "{id = 83325, Speaker = 4400, Text = '啊—攻击似乎减弱了…'}",
	[83326] = "{id = 83326, Speaker = 4400, Text = '接下来就是第三个法阵了…'}",
	[83327] = "{id = 83327, Speaker = 4400, Text = '我的身体轻盈了很多！'}",
	[83328] = "{id = 83328, Speaker = 4400, Text = '还剩最后一个了！冒险者。'}",
	[83329] = "{id = 83329, Speaker = 4400, Text = '哈哈！他们对我造成不了什么伤害了！'}",
	[83330] = "{id = 83330, Speaker = 4400, Text = '我终于解脱了！'}",
	[83331] = "{id = 83331, Speaker = 4400, Text = '哈！我终于解脱了！几百年的牢笼，你们见鬼去吧！'}",
	[83332] = "{id = 83332, Speaker = 4400, Text = '我已经迫不及待要离开这里了。'}",
	[83333] = "{id = 83333, Speaker = 4400, Text = '冒险者，我们外面的世界再见吧！'}",
	[83374] = "{id = 83374, Speaker = 4403, Text = '嘿…看看新来的小家伙…哈哈哈哈'}",
	[83375] = "{id = 83375, Speaker = 4403, Text = '竟然是一只波利！'}",
	[83376] = "{id = 83376, Speaker = 4403, Text = '欢迎光临，小家伙。'}",
	[83377] = "{id = 83377, Speaker = 4403, Text = '这里是赤红晶钻的内部空间，而我是这里唯一一个存活的灵魂。', Option = '赤红晶钻？'}",
	[83378] = "{id = 83378, Speaker = 4403, Text = '休密兹王朝的国王休密兹所带的王冠中央的装饰品。'}",
	[83379] = "{id = 83379, Speaker = 4403, Text = '一种极其稀有、拥有未知魔力的宝石。', Option = '好吧。'}",
	[83380] = "{id = 83380, Speaker = 4403, Text = '咦？你居然这么快就变回来了！', Option = '很奇怪吗？'}",
	[83381] = "{id = 83381, Speaker = 4403, Text = '当然！我刚进来的时候可是变成了野猪足足数十年！'}",
	[83382] = "{id = 83382, Speaker = 4403, Text = '你小子竟然才这么一会就变回来了…你身上难道有什么不可告人的秘密…', Option = '你想做什么？'}",
	[83383] = "{id = 83383, Speaker = 4403, Text = '我在想，既然你能这么快就解决变身的问题。'}",
	[83384] = "{id = 83384, Speaker = 4403, Text = '那么你也肯定是可以出去的。'}",
	[83385] = "{id = 83385, Speaker = 4403, Text = '又留下我一个人咯…', Option = '…'}",
	[83386] = "{id = 83386, Speaker = 4403, Text = '不好…赤红梦境又来了！'}",
	[83387] = "{id = 83387, Speaker = 4403, Text = '冒险者，你自求多福吧！到时候我也不能控制我自己！'}",
	[83388] = "{id = 83388, Speaker = 4403, Text = '冒险者！你最好快……留下吧！'}",
	[83389] = "{id = 83389, Speaker = 4403, Text = '哈哈哈！我还没用力，你们这些魔物就已经倒下了吗？'}",
	[83390] = "{id = 83390, Speaker = 4403, Text = '就你们还想留下我？太可笑了！'}",
	[83391] = "{id = 83391, Speaker = 4403, Text = '嘿嘿！我说这些魔族为什么这么不济事，原来后面还有个大家伙！'}",
	[83392] = "{id = 83392, Speaker = 4403, Text = '我要好好跟你玩玩！', Option = '什么？'}",
	[83393] = "{id = 83393, Speaker = 4403, Text = '没想到你还活着，不错嘛！', Option = '问出问题'}",
	[83394] = "{id = 83394, Speaker = 4403, Text = '什么？你打得是我？'}",
	[83395] = "{id = 83395, Speaker = 4403, Text = '不可能！我一直都在与突然出现的黑暗魔族作战！', Option = '确实是这样'}",
	[83396] = "{id = 83396, Speaker = 4403, Text = '……我早该想到的…'}",
	[83397] = "{id = 83397, Speaker = 4403, Text = '曾经也有无数人进入这里，可是每次梦境降临后，都会只剩下我一个人。'}",
	[83398] = "{id = 83398, Speaker = 4403, Text = '我一直以为是魔族太过强大…没想到…是我亲手杀了他们！', Option = '…'}",
	[83399] = "{id = 83399, Speaker = 4403, Text = '冒险者，在你挣脱变身魔咒的时候，我就知道你不是普通人。'}",
	[83400] = "{id = 83400, Speaker = 4403, Text = '我请求你…救我离开这里。'}",
	[83401] = "{id = 83401, Speaker = 4403, Text = '我宁愿死！也不愿意一辈子呆着这种地方！', Option = '我该怎么做'}",
	[83402] = "{id = 83402, Speaker = 4403, Text = '空间并不是稳定的，任何一个空间都存在着薄弱之处。'}",
	[83403] = "{id = 83403, Speaker = 4403, Text = '我呆在这片空间数百年，对这里的薄弱处了若指掌。'}",
	[83404] = "{id = 83404, Speaker = 4403, Text = '我会把薄弱点找出来，而你必须在有限的时间内，通过这处薄弱点！'}",
	[83405] = "{id = 83405, Speaker = 4403, Text = '就是现在！冒险者，空间薄弱点已经出现了！'}",
	[83406] = "{id = 83406, Speaker = 4403, Text = '记住帮我找到离开的方法！'}",
	[83450] = "{id = 83450, Speaker = 4266, Text = '好了，一个完整的王冠重新现世了！'}",
	[83532] = "{id = 83532, Speaker = 3422, Text = '[c][ffff00]【施密特王朝GLC.A031号档案】[-][/c]已解锁……是否解读？', Option = '解读'}",
	[83533] = "{id = 83533, Speaker = 3422, Text = '在对古城末日档案深入研究的过程中，我渐渐发现，解释一切谜团的关键，就是卡珊德拉公主“悲剧的婚礼”事件。'}",
	[83534] = "{id = 83534, Speaker = 3422, Text = 'A.W781年，发生了一场规模庞大的佣兵叛乱事件，海因里希领兵耗时三个月镇压了这场叛乱，事后牵连下狱者无数。'}",
	[83535] = "{id = 83535, Speaker = 3422, Text = 'A.W793年，之前在文献记载中从未出现过的“卡珊德拉公主的未婚夫”忽然出现在我们眼前。'}",
	[83536] = "{id = 83536, Speaker = 3422, Text = '他送给公主的定情信物，一直被公主佩戴在秀发上，两人很快陷入热恋……'}",
	[83537] = "{id = 83537, Speaker = 3422, Text = 'A.W794年，王城夜半发生大火，公主恰巧去了被熊熊大火吞噬的宫殿。巧合的是，刚刚夜巡归来的欧律皮罗斯男爵立刻就冲进火烧的宫殿，救出了被烧伤的卡珊德拉公主。'}",
	[83538] = "{id = 83538, Speaker = 3422, Text = '婚礼随后在公主养伤的日子里大张旗鼓筹办，国王昭告天下，小公主将大婚。尽管舆论四起，欧律皮罗斯男爵仍然非常坚定地要娶公主。'}",
	[83539] = "{id = 83539, Speaker = 3422, Text = 'A.W795年，“悲剧的婚礼”事件。就在婚礼当天，公主宣布自己得了传染病，无法出现在婚礼上。世人谴责欧律皮罗斯男爵太薄情寡义，就此抛弃了公主。而他的仕途却正宏图高展。'}",
	[83540] = "{id = 83540, Speaker = 3422, Text = 'A.W796年，王朝的命运，就像是公主的病患一般，日渐衰落，迎向暮晚最绚丽也最悲伤的余晖。'}",
	[83541] = "{id = 83541, Speaker = 3422, Text = 'A.W800年，希默梅思的不死军团数次对克雷斯特汉姆王城久攻不下，而这一夜，亡灵大军轻易踏平了这个王朝。血色就像是天边红霞，映照着王城中潮水般倒下的士兵。'}",
	[83542] = "{id = 83542, Speaker = 3422, Text = '国王不知去向，他早已不在王城中。守护着这座王城的海因里希司令官被人背后放暗箭，重伤死在希默梅思剑下。'}",
	[83543] = "{id = 83543, Speaker = 3422, Text = '逝者再也不能诉说实情。那个隐藏在幕后的“叛变者”也从此消失在死亡军团的血色旗帜中，没有任何记录说明他之后的去向……'}",
	[83544] = "{id = 83544, Speaker = 3422, Text = '只有一份记录留下了线索。“叛变者”向希默梅思告密，说：“现在，是时候了。这是您等待已久的机会，也是我等待已久的时刻。我将引导您的不死军团通过捷径进入那固若金汤之城。不，它已不再固若金汤，因为对它了解至深的我，就是它的弱点。”'}",
	[83545] = "{id = 83545, Speaker = 3422, Text = '关于这名“叛变者”，我锁定了十五个嫌疑人，并将调查报告记录为[c][ffff00]【古城末日“叛变者”档案】[-][/c]。'}",
	[83546] = "{id = 83546, Speaker = 3422, Text = '百年风霜抹去了真相。古城的秘密被封印在恐怖死寂中。如果能进去探索，也许可以找到答案？'}",
	[83547] = "{id = 83547, Speaker = 3422, Text = '——来自一位秘密调查员的报告。'}",
	[83592] = "{id = 83592, Speaker = 4039, Text = '小心台阶旁的深渊骑士！'}",
	[83593] = "{id = 83593, Speaker = 4039, Text = '请小心靠近台阶上的魔物，如果可以话避免与它交战'}",
	[83594] = "{id = 83594, Speaker = 4033, Text = '人类！！踏入死地，就做我的奴仆吧！'}",
	[83595] = "{id = 83595, Speaker = 4038, Text = '干的漂亮冒险者，这里暂时安全了，立刻驻扎！'}",
	[83596] = "{id = 83596, Speaker = 4040, Text = '我真的不知道这里奇怪的魔物是怎么会出现的'}",
	[83597] = "{id = 83597, Speaker = 4040, Text = '拜托了，帮我解决一下这些奇怪的魔物，我们需要在抽水机里回收一些资源'}",
	[83598] = "{id = 83598, Speaker = 4041, Text = '“我可没有想到会是这样的结果，我有点害怕”'}",
	[83599] = "{id = 83599, Speaker = 4041, Text = '“不敢想象，如果能完美的控制它....”'}",
	[83600] = "{id = 83600, Speaker = 4041, Text = '“这是？就差一点点了，这个样子....很棒”'}",
	[83601] = "{id = 83601, Speaker = 4043, Text = '我只有一个愿望....'}",
	[83602] = "{id = 83602, Speaker = 4043, Text = '那就是和我一样感受绝望！', Option = '怎么了？'}",
	[83603] = "{id = 83603, Speaker = 4043, Text = '是在装无辜吗？见你的神去吧！'}",
	[83604] = "{id = 83604, Speaker = 4408, Text = '冒险者，我希望你能听到我的声音，请尽快找到庞德的灵魂，时间不多了。'}",
	[83605] = "{id = 83605, Speaker = 4409, Text = '你想在死亡空间与我匹敌？哈哈哈哈哈哈哈！'}",
	[83606] = "{id = 83606, Speaker = 4410, Text = '庞德的灵魂就在你第一次遇见他的地方。'}",
	[83607] = "{id = 83607, Speaker = 4410, Text = '你的身体已经有些僵硬了，请抓紧时间。'}",
	[83608] = "{id = 83608, Speaker = 4409, Text = '我要让你们永远地留在这里！'}",
	[83609] = "{id = 83609, Speaker = 4409, Text = '看来你们比我想象得厉害，那么尝尝这些吧！'}",
	[83610] = "{id = 83610, Speaker = 4409, Text = '看来还是要我亲自收拾你们这些蝼蚁！'}",
	[83611] = "{id = 83611, Speaker = 4410, Text = '冒险者！迷幻之王正在封锁这个空间。'}",
	[83612] = "{id = 83612, Speaker = 4410, Text = '可恶！'}",
	[83613] = "{id = 83613, Speaker = 4408, Text = '冒险者，太危险了，这里即将崩塌，快逃吧。', Option = '那你呢？'}",
	[83614] = "{id = 83614, Speaker = 4408, Text = '不用你来担心我，快走！！', Option = '摩根！！'}",
	[83615] = "{id = 83615, Speaker = 4411, Text = '……看起来我成功进来了…'}",
	[83616] = "{id = 83616, Speaker = 4408, Text = '雷藏！真不愧是天才魔法师，看一遍就学会了！'}",
	[83617] = "{id = 83617, Speaker = 4411, Text = '那么接下来是…'}",
	[83618] = "{id = 83618, Speaker = 4412, Text = '这样就可以稳定住空间了吧。'}",
	[83619] = "{id = 83619, Speaker = 4408, Text = '可靠的老师，看来我也要这样称呼你一次了。'}",
	[83620] = "{id = 83620, Speaker = 4409, Text = '这里可不是你们这些蝼蚁可以随意进出的地方！！'}",
	[83621] = "{id = 83621, Speaker = 4412, Text = '闭嘴，卑鄙的恶魔！'}",
	[83622] = "{id = 83622, Speaker = 4413, Text = '你们干了什么！'}",
	[83623] = "{id = 83623, Speaker = 4410, Text = '冒险者，我们只能帮你到这里了，剩下就交给你了！', Option = '击败迷幻之王'}",
	[83624] = "{id = 83624, Speaker = 4413, Text = '你们以为这点小聪明就能击败我！？'}",
	[83625] = "{id = 83625, Speaker = 4414, Text = '我这是在哪里，冒险者？', Option = '我救你出去'}",
	[83626] = "{id = 83626, Speaker = 4414, Text = '你能带我去吉芬吗？我想成为一名优秀的魔法师！', Option = '我们走吧'}",
	[83674] = "{id = 83674, Speaker = 4441, Text = '谁！你是谁？！来人——！有刺客——！'}",
	[83675] = "{id = 83675, Speaker = 3422, Text = '尽管莫里茨公爵垂死反抗，但凶手知道，他活不过今夜。'}",
	[83676] = "{id = 83676, Speaker = 3422, Text = '毒杀了莫里茨公爵之后，月光照出凶手冷酷的脸庞，他投入夜色中，迅速离开了……'}",
	[83677] = "{id = 83677, Speaker = 3422, Text = '卡伦赞德因为死对头莫里茨公爵夜半被毒杀而蒙冤入狱。'}",
	[83678] = "{id = 83678, Speaker = 3422, Text = '四个驱魔人各自想了一个办法解救他们的朋友。'}",
	[83724] = "{id = 83724, Speaker = 4443, Text = '我不知道该怎么感谢你，不仅为我安排了隐蔽的住处，还时常给我送来食物和衣服。感谢上帝，让我认识了你这样的朋友。'}",
	[83725] = "{id = 83725, Speaker = 4429, Text = '谁都有需要帮助的时候，卡伦赞德。别为此内疚，我能做的也不过只有这些而已。'}",
	[83726] = "{id = 83726, Speaker = 4429, Text = '我能帮助你，却不能为你洗清冤屈，让你重返光荣的骑士团。'}",
	[83727] = "{id = 83727, Speaker = 4443, Text = '我想，这已经足够了。'}",
	[83744] = "{id = 83744, Speaker = 4446, Text = '特地把我叫到这里来，有什么事？'}",
	[83745] = "{id = 83745, Speaker = 4430, Text = '尊贵的皇后，据我所知，国王有一个秘密，而这个秘密全天下只有一个人知道。'}",
	[83746] = "{id = 83746, Speaker = 4446, Text = '谁掌握着国王的秘密？'}",
	[83747] = "{id = 83747, Speaker = 4430, Text = '卡伦赞德。'}",
	[83748] = "{id = 83748, Speaker = 4446, Text = '古列弗，你是在陷害他吗？我怎能容忍掌握着国王秘密的人活在这世上。', Option = '您会容忍的'}",
	[83749] = "{id = 83749, Speaker = 4430, Text = '您会容忍的，而且，您还会保护他。'}",
	[83750] = "{id = 83750, Speaker = 4446, Text = '为什么？'}",
	[83751] = "{id = 83751, Speaker = 4430, Text = '只要您希望国王永远不会背叛您，您就会保护卡伦赞德，因为他会成为您牵制国王的一张王牌。'}",
	[83752] = "{id = 83752, Speaker = 4446, Text = '古列弗，这世上恐怕没有人比你更聪明了。其他人救卡伦赞德的方式，都没有你这个一劳永逸。'}",
	[83768] = "{id = 83768, Speaker = 4445, Text = '你……原来你一直隐藏身份躲在王庭……卡伦赞德，你误会了，我并没有想过要判处你……'}",
	[83769] = "{id = 83769, Speaker = 4443, Text = '可惜这一切都已经太迟了，国王陛下。'}",
	[83796] = "{id = 83796, Speaker = 4448, Text = '不要用你肮脏的双手碰到它！'}",
	[83797] = "{id = 83797, Speaker = 4448, Text = '莫里森…不！死去吧！'}",
	[83798] = "{id = 83798, Speaker = 4448, Text = '你们都是魔鬼！'}",
	[83832] = "{id = 83832, Speaker = 4448, Text = '你打破我在他心中的美好！我不会放过你的！'}",
	[83891] = "{id = 83891, Speaker = 4538, Text = '黑暗降临……一切都将毁灭！'}",
	[83892] = "{id = 83892, Speaker = 4538, Text = '在黑暗中，吾将永不腐朽！'}",
	[83893] = "{id = 83893, Speaker = 4341, Text = '为什么！神抛弃了我们，为什么你不肯和我一起投身黑暗！'}",
	[83894] = "{id = 83894, Speaker = 4341, Text = '信奉黑暗，才能获得不朽的力量！'}",
	[83895] = "{id = 83895, Speaker = 4344, Text = '嘿嘿，这个猎物有点儿意思……'}",
	[83896] = "{id = 83896, Speaker = 4344, Text = '有意思，好久没有这么有意思的猎物了！我要动真格了！'}",
	[83897] = "{id = 83897, Speaker = 4347, Text = '融于暗影之中……'}",
	[83898] = "{id = 83898, Speaker = 4330, Text = '啊！！！你让我愤怒了！出来吧！无尽的冤魂！'}",
	[83899] = "{id = 83899, Speaker = 4330, Text = '愤怒的火焰必将把你们燃为灰烬！'}",
	[83900] = "{id = 83900, Speaker = 4366, Text = '不要靠近这里，立即消失！不，就死在这里！'}",
	[83901] = "{id = 83901, Speaker = 4366, Text = '不要出去，也不要再进来！'}",
	[83902] = "{id = 83902, Speaker = 4366, Text = '我要折断这把剑！'}",
	[83903] = "{id = 83903, Speaker = 4374, Text = '嘿嘿……又一个鲜活的灵魂……真是期待你灵魂的滋味啊'}",
	[83904] = "{id = 83904, Speaker = 4374, Text = '在我的魔法中迷失自我吧！'}",
	[83905] = "{id = 83905, Speaker = 4374, Text = '你，无法阻止我的魔法，化为灰烬吧！'}",
	[83906] = "{id = 83906, Speaker = 4266, Text = '干的漂亮冒险者，那么现在，让我们继续探索。'}",
	[83907] = "{id = 83907, Speaker = 4266, Text = '之后的修道院成了那些不死军团的“制造厂”，快阻止它们！'}",
	[83908] = "{id = 83908, Speaker = 4266, Text = '恶灵觉醒了！'}",
	[83989] = "{id = 83989, Speaker = 4470, Text = '前半的密码是32！'}",
	[83990] = "{id = 83990, Speaker = 4471, Text = '我后半的密码是14！'}",
	[83992] = "{id = 83992, Speaker = 0, Text = '这把看似无奇的破旧锈剑居然冒出了一阵黑烟'}",
	[83993] = "{id = 83993, Speaker = 4470, Text = '成为我的剑下亡灵吧！！'}",
	[83994] = "{id = 83994, Speaker = 4470, Text = '快把我手上的这把剑毁掉！！'}",
	[83995] = "{id = 83995, Speaker = 0, Text = '随着剑的毁灭，布拉奇的周身散发出净化之光'}",
	[84055] = "{id = 84055, Speaker = 4471, Text = '成为我的弓箭下亡灵吧！！'}",
	[84056] = "{id = 84056, Speaker = 4471, Text = '快把我手上的这把弓箭毁掉！！'}",
	[84057] = "{id = 84057, Speaker = 0, Text = '随着弓箭的毁灭，布兰德的周身散发出净化之光'}",
	[84112] = "{id = 84112, Speaker = 4492, Text = '神会祝福所有懂得感恩与珍惜的人。神会护佑佳人眷侣终成眷属。神会赐予面包和奶酪，让健康、温暖、快乐永伴你们。'}",
	[84113] = "{id = 84113, Speaker = 4492, Text = '加布里尔王子，你愿意娶桂瑟兰妮小姐为你的妻子吗？无论将来遭遇贫苦、疾病，亦或是富贵、荣华，你都将和她一起面对。'}",
	[84114] = "{id = 84114, Speaker = 4493, Text = '我愿意。'}",
	[84115] = "{id = 84115, Speaker = 4492, Text = '桂瑟兰妮小姐，你愿意嫁给加布里尔王子，让他做你的丈夫吗？无论将来遭遇贫苦、疾病，亦或是富贵、荣华，你都不会舍弃他。'}",
	[84116] = "{id = 84116, Speaker = 4494, Text = '我愿意。'}",
	[84117] = "{id = 84117, Speaker = 4492, Text = '我宣布，你们结为夫妻。'}",
	[84118] = "{id = 84118, Speaker = 4493, Text = '……我爱你，桂瑟兰妮。除了你，我不知道该怎么爱上别人……'}",
	[84119] = "{id = 84119, Speaker = 4495, Text = '真是美妙的一刻……'}",
	[84120] = "{id = 84120, Speaker = 4495, Text = '可惜，美妙的东西都不会长久。'}",
	[84121] = "{id = 84121, Speaker = 4495, Text = '所有不向芙瑞雅虔诚祈福的人，都将被神明抛弃！'}",
	[84122] = "{id = 84122, Speaker = 4495, Text = '自诩为奥丁代言人的愚蠢人类，去地狱举行你们的婚礼吧！'}",
	[84123] = "{id = 84123, Speaker = 3422, Text = '不死军团降临这里，地狱的火焰吞没了露水鲜花与新娘的白纱，而后是死神无情地收割生命，侥幸逃脱的人也只不过是垂死的挣扎……'}",
	[84124] = "{id = 84124, Speaker = 3422, Text = '希默梅思的手下，暗神官，向措手不及的宾客们释放出恐怖的摄魂力量。惊恐的尖叫淹没了清朗的钟声……'}",
	[84125] = "{id = 84125, Speaker = 4496, Text = '不知死活的人类，但凡闯入这里者，都要将灵魂献给我！！'}",
	[84126] = "{id = 84126, Speaker = 3422, Text = '【施密特王朝GLC.E109号档案】已解锁……是否解读？', Option = '解读'}",
	[84127] = "{id = 84127, Speaker = 3422, Text = '古城的地下墓穴中，时常会响起诡异的乐曲声，并没有人在弹奏它们。'}",
	[84128] = "{id = 84128, Speaker = 3422, Text = '听到这些乐曲的人，据说渐渐的会精神失常，只剩下疯狂的念头，不停在墓穴中寻找什么，而忘记自己是谁。'}",
	[84129] = "{id = 84129, Speaker = 3422, Text = '我在一次偶然的机会下，来到这个地下墓穴查探关于那些“神秘乐曲”的真相。'}",
	[84130] = "{id = 84130, Speaker = 3422, Text = '我发现，加布里尔的亡魂并没有散去。'}",
	[84131] = "{id = 84131, Speaker = 3422, Text = '他用悲怨的声音，向聆听者索取着什么……'}",
	[84132] = "{id = 84132, Speaker = 3422, Text = '我将此记录为【“梦中婚礼”安魂曲事件】。'}",
	[84133] = "{id = 84133, Speaker = 3422, Text = '或许整个王城都仍然沉浸在婚礼的钟声中，王军溃败之时，那些士兵们仍聆听着那钟声的悠长之音，堕入地狱……'}",
	[84134] = "{id = 84134, Speaker = 3422, Text = '——来自一位秘密调查员的报告。'}",
	[84138] = "{id = 84138, Speaker = 4495, Text = '接收我希默梅思死亡的祝福！'}",
	[84139] = "{id = 84139, Speaker = 4266, Text = '恶魔祭司头目的气息？冒险者，赶快找到他！不能让“死亡军团”扩充队伍！”'}",
	[84140] = "{id = 84140, Speaker = 4046, Text = '多管闲事的冒险者？'}",
	[84141] = "{id = 84141, Speaker = 4046, Text = '“苟延残喘的人类，这只会浪费你们的时间。”', Option = '停止吧！'}",
	[84142] = "{id = 84142, Speaker = 4046, Text = '“哈哈哈，你是在和我商量吗？天真的人类！”'}",
	[84143] = "{id = 84143, Speaker = 4046, Text = '“接受我新仆从的试炼吧！”'}",
	[84144] = "{id = 84144, Speaker = 4048, Text = '我们服从新主人的命令！'}",
	[84145] = "{id = 84145, Speaker = 4046, Text = '来点新花样怎么样？！'}",
	[84146] = "{id = 84146, Speaker = 4046, Text = '看来你有资格尝试我的最新研究成果！'}",
	[84147] = "{id = 84147, Speaker = 4046, Text = '作为人类，你的表现非常有趣，不过接下来，我可不会手下留情了。'}",
	[84148] = "{id = 84148, Speaker = 4046, Text = '我来给你们看长一场好戏！'}",
	[84149] = "{id = 84149, Speaker = 4046, Text = '让我们来加一点调味品！'}",
	[84150] = "{id = 84150, Speaker = 4046, Text = '还差一点，还差一点点！'}",
	[84151] = "{id = 84151, Speaker = 4046, Text = '哈哈哈哈哈，祝你们玩的愉快！'}",
	[84165] = "{id = 84165, Speaker = 4528, Text = '到吉芬的第一天居然就被一个小姑娘嘲笑了，我会进步的！'}",
	[84166] = "{id = 84166, Speaker = 4408, Text = '冒险者，根据庞德的日记，你需要先找到那个嘲笑他的小姑娘。'}",
	[84167] = "{id = 84167, Speaker = 4408, Text = '小心点，这很有可能是个陷阱。'}",
	[84168] = "{id = 84168, Speaker = 4518, Text = '嘿嘿，庞德大哥哥羞羞，连变朵花出来都不会？？'}",
	[84169] = "{id = 84169, Speaker = 4528, Text = '今天学生会长让我放弃学习魔法，不甘心。'}",
	[84170] = "{id = 84170, Speaker = 4515, Text = '冒险者，请抓紧时间，寻找那位学生会长。'}",
	[84171] = "{id = 84171, Speaker = 4519, Text = '哈哈哈哈，就你这样的乡巴佬还想学习魔法？'}",
	[84172] = "{id = 84172, Speaker = 4528, Text = '考试失败了，被弗里德老师责难了，还要努力！'}",
	[84173] = "{id = 84173, Speaker = 4408, Text = '看来接下来你要找的人就是弗里德！'}",
	[84174] = "{id = 84174, Speaker = 4408, Text = '动作快！这个梦境已经开始因为庞德内心的创伤开始崩溃了！'}",
	[84175] = "{id = 84175, Speaker = 4520, Text = '庞德你的成绩，太令我失望了！'}",
	[84176] = "{id = 84176, Speaker = 0, Text = '弗里德让伤心的庞德失去了学习魔法的希望。'}",
	[84177] = "{id = 84177, Speaker = 4528, Text = '我只是想成为像雷藏大人那样伟大的魔法师，真的这么难吗！！'}",
	[84178] = "{id = 84178, Speaker = 4408, Text = '看来庞德自卑的内心把自己伪装成雷藏了！现在只有你能帮他了！'}",
	[84179] = "{id = 84179, Speaker = 4521, Text = '我是雷藏大人！！'}",
	[84180] = "{id = 84180, Speaker = 4521, Text = '我要杀光嘲笑我的所有人！！！'}",
	[84181] = "{id = 84181, Speaker = 4521, Text = '你也是来嘲笑我的吗！', Option = '找回自己！'}",
	[84182] = "{id = 84182, Speaker = 4521, Text = '劝我也没用的！让你们这些混蛋都尝尝我内心的愤怒。'}",
	[84183] = "{id = 84183, Speaker = 4522, Text = '别过来！我…我…', Option = '战胜自己！'}",
	[84184] = "{id = 84184, Speaker = 4522, Text = '已经...来不及了！！'}",
	[84185] = "{id = 84185, Speaker = 4523, Text = '就是现在！把日记本交给庞德让他找回初心！'}",
	[84186] = "{id = 84186, Speaker = 4523, Text = '对啊！我怎么被这点小挫折打败了？'}",
	[84187] = "{id = 84187, Speaker = 4523, Text = '你真的相信我可以战胜自己吗？', Option = '是的！'}",
	[84188] = "{id = 84188, Speaker = 4523, Text = '谢谢你冒险者，我找回了勇气，可我这是在哪儿？', Option = '我救你出去'}",
	[84189] = "{id = 84189, Speaker = 4523, Text = '我们走！！'}",
	[84190] = "{id = 84190, Speaker = 4408, Text = '快，乘这个梦境没崩坏之前！！找到出口！'}",
	[84191] = "{id = 84191, Speaker = 4527, Text = '你们以为这就结束了？！想跑？想的美！'}",
	[84192] = "{id = 84192, Speaker = 4524, Text = '冒险者！你做的很好！'}",
	[84193] = "{id = 84193, Speaker = 4527, Text = '哈哈哈哈，就你一个糟老头，能做什么？'}",
	[84194] = "{id = 84194, Speaker = 4524, Text = '没错，我是一介糟老头，可你别忘了，我也是普隆德拉皇家骑士团团长！'}",
	[84195] = "{id = 84195, Speaker = 4524, Text = '出来吧，我的勇士们！！'}",
	[84196] = "{id = 84196, Speaker = 4525, Text = '为了光荣的使命！！！'}",
	[84197] = "{id = 84197, Speaker = 4525, Text = '为了海陆曼团长！！！'}",
	[84198] = "{id = 84198, Speaker = 4525, Text = '为了普隆德拉骑士团！！！'}",
	[84199] = "{id = 84199, Speaker = 4524, Text = '冒险者！接下来就交给我吧！', Option = '好！'}",
	[84200] = "{id = 84200, Speaker = 4046, Text = '恩？好像还不够啊！'}",
	[84234] = "{id = 84234, Speaker = 1058, Text = '这里的魔物非常凶猛，不过不用担心，音乐精灵会在这里治愈你们的创伤的！'}",
	[84235] = "{id = 84235, Speaker = 1058, Text = '潮湿肮脏的下水道充满了危险，但是有我在，我会用音乐给予你勇气与力量。'}",
	[84236] = "{id = 84236, Speaker = 1058, Text = '这里的气氛让我也有些不舒服了，不过只要有音乐的伴随，我就会有无限的勇气。'}",
	[84239] = "{id = 84239, Speaker = 4374, Text = '你不可能活着离开这里，哈哈哈！'}",
	[84240] = "{id = 84240, Speaker = 4381, Text = '遵从黑暗之王大人的意志！'}",
	[84288] = "{id = 84288, Speaker = 1691}",
	[84325] = "{id = 84325, Speaker = 1686, Text = '面前的这个温和的魔物似乎会理发...事实上它也确实对发型有所研究...那么...你要理发吗?'}",
	[84328] = "{id = 84328, Speaker = 4532, Text = '福金大人总是这样，一旦有所发现，就无法停止研究…身为管家的我，当然得照顾好他啊！'}",
	[84333] = "{id = 84333, Speaker = 4031, Text = '出来吧，我的仆从们！'}",
	[84335] = "{id = 84335, Speaker = 4031, Text = '拉杆被缓缓的拉动，释放上的魔法效果消散了。'}",
	[84336] = "{id = 84336, Speaker = 4045, Text = '力量……觉醒了！'}",
	[84337] = "{id = 84337, Speaker = 4266, Text = '不在这里……'}",
	[84338] = "{id = 84338, Speaker = 4266, Text = '恶魔的气息开始浓郁了，再往前一些！'}",
	[84339] = "{id = 84339, Speaker = 4047, Text = '了不起，人类，竟然发现能发现我。但是……'}",
	[84340] = "{id = 84340, Speaker = 4047, Text = '我的法术已经准备好了！'}",
	[84341] = "{id = 84341, Speaker = 4266, Text = '我为你们开启了传送阵，从修道院离开吧。'}",
	[84352] = "{id = 84352, Speaker = 4266, Text = '小心这些魂魄，他们已经失去了理智，请谨慎靠近。', Voice = 'Npc/RAID_DestructionNight_84352'}",
	[84353] = "{id = 84353, Speaker = 4266, Text = '冒险者，站在最前端的灵魂怨气有些不同，请千万小心！', Voice = 'Npc/RAID_DestructionNight_84353'}",
	[84354] = "{id = 84354, Speaker = 4266, Text = '可怜的恋人，可悲的结果，让他们安息吧冒险者。', Voice = 'Npc/RAID_DestructionNight_84354'}",
	[84355] = "{id = 84355, Speaker = 4031, Text = '活人？为什么要破坏我的傀儡？', Voice = 'Npc/RAID_DestructionNight_84355'}",
	[84356] = "{id = 84356, Speaker = 4266, Text = '冒险者，我们是不是可以尝试着和它谈谈？', Voice = 'Npc/RAID_DestructionNight_84356'}",
	[84357] = "{id = 84357, Speaker = 4031, Text = '有时候真的很佩服人类的异想天开，先断气再来谈吧！', Option = '击败他！', Voice = 'Npc/RAID_DestructionNight_84357'}",
	[84358] = "{id = 84358, Speaker = 4031, Text = '出来吧，我的仆从们！', Voice = 'Npc/RAID_DestructionNight_84358'}",
	[84359] = "{id = 84359, Speaker = 4266, Text = '干的漂亮冒险者，那么现在，让我们继续探索。', Voice = 'Npc/RAID_DestructionNight_84359'}",
	[84360] = "{id = 84360, Speaker = 4052, Text = '拉杆似乎被固定住了，无法被拉动。'}",
	[84361] = "{id = 84361, Speaker = 4052, Text = '拉杆被缓缓的拉动，魔法阵慢慢消散了。'}",
	[84362] = "{id = 84362, Speaker = 4266, Text = '不能让修道院成了那些不死军团的“制造厂”，快阻止它们！', Voice = 'Npc/RAID_DestructionNight_84362'}",
	[84363] = "{id = 84363, Speaker = 4266, Text = '恶灵觉醒了', Voice = 'Npc/RAID_DestructionNight_84363'}",
	[84364] = "{id = 84364, Speaker = 4266, Text = '终于结束了……不，好像有什么异常？！'}",
	[84365] = "{id = 84365, Speaker = 4266, Text = '是恶魔祭司头目的气息？冒险者，赶快找到他！', Voice = 'Npc/RAID_DestructionNight_84365'}",
	[84366] = "{id = 84366, Speaker = 4266, Text = '不在这里……', Voice = 'Npc/RAID_DestructionNight_84366'}",
	[84367] = "{id = 84367, Speaker = 4266, Text = '恶魔的气息开始浓郁了，再往前一些！', Voice = 'Npc/RAID_DestructionNight_84367'}",
	[84368] = "{id = 84368, Speaker = 4047, Text = '了不起，人类，竟然发现能发现我。但是……', Voice = 'Npc/RAID_DestructionNight_84368'}",
	[84369] = "{id = 84369, Speaker = 4047, Text = '你还能再找到我吗，哈哈哈哈哈！', Voice = 'Npc/RAID_DestructionNight_84369'}",
	[84370] = "{id = 84370, Speaker = 4047, Text = '你们……成功的激怒了我！', Voice = 'Npc/RAID_DestructionNight_84370'}",
	[84371] = "{id = 84371, Speaker = 4046, Text = '多管闲事的冒险者？', Voice = 'Npc/RAID_DestructionNight_84371'}",
	[84372] = "{id = 84372, Speaker = 4046, Text = '“苟延残喘的人类，这只会浪费你们的时间。”', Option = '放弃吧！'}",
	[84373] = "{id = 84373, Speaker = 4046, Text = '“哈哈哈，你是在和我商量吗？天真的人类！”'}",
	[84374] = "{id = 84374, Speaker = 4046, Text = '“接受我新仆从的试炼吧！”', Voice = 'Npc/RAID_DestructionNight_84374'}",
	[84375] = "{id = 84375, Speaker = 4048, Text = '我们服从新主人的命令！', Voice = 'Npc/RAID_DestructionNight_84375'}",
	[84376] = "{id = 84376, Speaker = 4048, Text = '冲锋！', Voice = 'Npc/RAID_DestructionNight_84376'}",
	[84377] = "{id = 84377, Speaker = 4046, Text = '来点新花样怎么样？！', Voice = 'Npc/RAID_DestructionNight_84377'}",
	[84378] = "{id = 84378, Speaker = 4046, Text = '看来你有资格尝试我的最新研究成果！', Voice = 'Npc/RAID_DestructionNight_84378'}",
	[84379] = "{id = 84379, Speaker = 4046, Text = '作为人类，你的表现非常有趣，不过接下来，我可不会手下留情了。', Voice = 'Npc/RAID_DestructionNight_84379'}",
	[84380] = "{id = 84380, Speaker = 4046, Text = '我来给你们看一场好戏！', Voice = 'Npc/RAID_DestructionNight_84380'}",
	[84381] = "{id = 84381, Speaker = 4046, Text = '让我们来加一点调味品！', Voice = 'Npc/RAID_DestructionNight_84371'}",
	[84382] = "{id = 84382, Speaker = 4046, Text = '还差一点，还差一点点！', Voice = 'Npc/RAID_DestructionNight_84372'}",
	[84383] = "{id = 84383, Speaker = 4046, Text = '哈哈哈哈哈，祝你们玩的愉快！', Voice = 'Npc/RAID_DestructionNight_84373'}",
	[84384] = "{id = 84384, Speaker = 4266, Text = '我为你们开启了传送阵，从修道院离开吧。', Voice = 'Npc/RAID_DestructionNight_84384'}",
	[84385] = "{id = 84385, Speaker = 93035, Text = '如此……美妙！', Voice = 'Npc/RAID_DestructionNight_84385'}",
	[84386] = "{id = 84386, Speaker = 93035, Text = '束缚我曾经的伪善们，一起……战斗吧！', Voice = 'Npc/RAID_DestructionNight_84386'}",
	[84436] = "{id = 84436, Speaker = 4543, Text = '他们的首领说，除非你能击败我，我就允许你留在这里。不然，就给我永远滚出龙谷！'}",
	[84514] = "{id = 84514, Speaker = 0, Text = '小诺娃周身冒出可怕的黑烟，居然化身成一只可怕的魔物。'}",
	[84515] = "{id = 84515, Speaker = 0, Text = '珊妮也幻化成了庞德内心的恐惧。'}",
	[84516] = "{id = 84516, Speaker = 6005, Text = '冒险者，这个空间已经被邪恶所腐化，你准备好面对了吗？', Option = '勇往直前'}",
	[84517] = "{id = 84517, Speaker = 6005, Text = '这些恶灵非常强大，请避开他们进入深处。'}",
	[84518] = "{id = 84518, Speaker = 6000, Text = '嗯？有老鼠？'}",
	[84519] = "{id = 84519, Speaker = 6001, Text = '小心那头魔物！', Option = '是莎西尔！'}",
	[84520] = "{id = 84520, Speaker = 6002, Text = '这些奇怪的魔法真让人难受！', Option = '艾勒梅斯！'}",
	[84521] = "{id = 84521, Speaker = 6002, Text = '还有两个漏网之鱼？冒险者交给你了！'}",
	[84522] = "{id = 84522, Speaker = 6003, Text = '冒险者，虽然有些晚，不过增援到了，请加快步伐。'}",
	[84523] = "{id = 84523, Speaker = 6004, Text = '让正义点燃黑暗！'}",
	[84524] = "{id = 84524, Speaker = 6004, Text = '让死亡远离尘世！'}",
	[84525] = "{id = 84525, Speaker = 6004, Text = '恶灵由魔法驱散！'}",
	[84526] = "{id = 84526, Speaker = 6004, Text = '邪恶由光明驱逐！'}",
	[84527] = "{id = 84527, Speaker = 6004, Text = '即使身处险境！'}",
	[84528] = "{id = 84528, Speaker = 6004, Text = '我们也毫不畏惧！'}",
	[84529] = "{id = 84529, Speaker = 6004, Text = '守护我们所爱的世界！'}",
	[84530] = "{id = 84530, Speaker = 6000, Text = '想释放灵魂？那还得看看你有没有这份实力！'}",
	[84531] = "{id = 84531, Speaker = 6000, Text = '你想尝尝愤怒的结晶吗？蝼蚁！'}",
	[84532] = "{id = 84532, Speaker = 6000, Text = '运气还不错！不过接下来可就不会那么好运了。'}",
	[84533] = "{id = 84533, Speaker = 6000, Text = '嗜血锁链！杀死这些垃圾！'}",
	[84534] = "{id = 84534, Speaker = 6000, Text = '这不可能！你们是不会成功的！'}",
	[84535] = "{id = 84535, Speaker = 6000, Text = '同时面对两条锁链，你们该怎么办呢？！'}",
	[84536] = "{id = 84536, Speaker = 6000, Text = '你们好像又遇到了点麻烦？！你以为你能唤醒他？'}",
	[84537] = "{id = 84537, Speaker = 6000, Text = '你不但无法唤醒他，你也会永远的留在这里！'}",
	[84538] = "{id = 84538, Speaker = 6000, Text = '那么，现在你们又将如何应对呢？！人类！'}",
	[84539] = "{id = 84539, Speaker = 6005, Text = '看来只有最后一个办法了。'}",
	[84540] = "{id = 84540, Speaker = 6006, Text = '你现在才想到这个结果吗？'}",
	[84541] = "{id = 84541, Speaker = 6003, Text = '我们将暂时把所有的魔法能量全部灌输给庞德的灵魂。'}",
	[84542] = "{id = 84542, Speaker = 6007, Text = '希望他获得能量后能够暂时的解决一下这里的麻烦。'}",
	[84543] = "{id = 84543, Speaker = 6007, Text = '能量不会持续很长时间，请各位小英雄们，抓紧时间！'}",
	[84544] = "{id = 84544, Speaker = 6000, Text = '徒劳的把戏吗！？'}",
	[84545] = "{id = 84545, Speaker = 6008, Text = '老师，摩根，还有冒险者，谢谢你们！', Option = '庞德！'}",
	[84546] = "{id = 84546, Speaker = 6000, Text = '这不可能！这不可能！！！！'}",
	[84547] = "{id = 84547, Speaker = 6010, Text = '黑暗之王，看来你麻烦大了啊。'}",
	[84548] = "{id = 84548, Speaker = 6000, Text = '英灵？女武神也要插手吗？'}",
	[84549] = "{id = 84549, Speaker = 6010, Text = '我们只是不想日后的工作加重而已，如果你们一直盘踞在古城，那我们也很困扰啊。'}",
	[84550] = "{id = 84550, Speaker = 6008, Text = '就趁现在，封印它们！'}",
	[84551] = "{id = 84551, Speaker = 6008, Text = '谢谢你冒险者，你给了我新的生命。'}",
	[84552] = "{id = 84552, Speaker = 6008, Text = '老师们给我的力量维持不了多久了，我必须马上回到身体苏醒过来。', Option = '快去吧'}",
	[84553] = "{id = 84553, Speaker = 6000, Text = '只剩下你了，我一定要杀死你，可恶！！'}",
	[84554] = "{id = 84554, Speaker = 6000, Text = '我诅咒你，人类！！！'}",
	[84555] = "{id = 84555, Speaker = 6000, Text = '我一定会回来复仇的！我会记住你的！人类…'}",
	[84581] = "{id = 84581, Speaker = 4422, Text = '我能理解，毕竟并不是人人都有勇气牺牲自己去解救他人的。'}",
	[84615] = "{id = 84615, Speaker = 8025, Text = '铁匠比起商人，更加需要一颗创造之心！'}",
	[84616] = "{id = 84616, Speaker = 8000, Text = '铁匠战斗起来也是很凶的。'}",
	[84617] = "{id = 84617, Speaker = 8000, Text = '敲敲打打也是身为铁匠的乐趣啊。'}",
	[84618] = "{id = 84618, Speaker = 8000, Text = '恭喜你成为我们的一员。'}",
	[84619] = "{id = 84619, Speaker = 8000, Text = '有空比试下谁的大地之击更强力吧！'}",
	[84620] = "{id = 84620, Speaker = 8000, Text = '没有钱可是万万不能的'}",
	[84621] = "{id = 84621, Speaker = 8000, Text = '锻造的魅力真是让人沉醉'}",
	[84631] = "{id = 84631, Speaker = 1106, Text = '哈哈哈…恭喜你满足了转职铁匠的条件之一哦！'}",
	[84716] = "{id = 84716, Speaker = 6051, Text = '即将将你传送到迷幻之王的异世界，请注意安全，冒险者。'}",
	[84718] = "{id = 84718, Speaker = 4559, Text = '那真是太遗憾了，一辆实用的手推车可以成为你最坚实可靠的伙伴呢。'}",
	[84723] = "{id = 84723, Speaker = 0, Text = '看来庞德已经暴走了。'}",
	[84732] = "{id = 84732, Speaker = 1561, Text = '喵！可恶，我山葵的招牌就这么砸了！'}",
	[84733] = "{id = 84733, Speaker = 1561, Text = '老板我都帮你扛下了这么多伤害了，争点气啊！'}",
	[84734] = "{id = 84734, Speaker = 1561, Text = '来！我们重新开始！有我在呐！'}",
	[84735] = "{id = 84735, Speaker = 1561, Text = '老板安心吧，我这次一定会保护好你的！'}",
	[84736] = "{id = 84736, Speaker = 1561, Text = ' 汝等性命，就由妾身收下吧！'}",
	[84737] = "{id = 84737, Speaker = 1561, Text = '愤怒令妾身什么也听不见！'}",
	[84738] = "{id = 84738, Speaker = 1561, Text = '妾身一直都相信您会再次归来的。 '}",
	[84739] = "{id = 84739, Speaker = 1561, Text = '您的归来令妾身充满了力量......'}",
	[84781] = "{id = 84781, Speaker = 0, Text = '您还没有集齐[c][ffff00]5个玫瑰石英[-][/c]，无法唤醒[c][ffff00]守护之盾[-][/c]……'}",
	[84796] = "{id = 84796, Speaker = 0, Text = '周围一片寂静……', Option = '发生了什么事'}",
	[84797] = "{id = 84797, Speaker = 6063, Text = '这里，是杜兰达尔最后一块灵魂碎片——[c][ffff00]坚韧之心[-][/c]的所在地。'}",
	[84798] = "{id = 84798, Speaker = 6063, Text = '你现在被我锁在了剑中。能否离开这里，就看你是否具备足够坚毅、无坚不摧的“心”。'}",
	[84799] = "{id = 84799, Speaker = 6052, Text = '吾王，我的双臂就是守护您和帝国的羽翼，我的心就是筑起帝国万里疆域的铁壁。让我成为您手中的剑，斩杀您的敌人，捍卫您的人民！'}",
	[84800] = "{id = 84800, Speaker = 6053, Text = '凯美拉正迫害着我的人民，然而，我们的军队在她面前如同蝼蚁。那是我们无法战胜的敌人，即使是你，也无法和神族对抗。'}",
	[84801] = "{id = 84801, Speaker = 6052, Text = '只要她是您的敌人，我就会为您斩除她！'}",
	[84802] = "{id = 84802, Speaker = 6054, Text = '太可怕了，杜兰达尔竟然弑神！这会触怒神族！'}",
	[84803] = "{id = 84803, Speaker = 6055, Text = '人类之躯，岂可弑杀神族！万骨枯骸，复活吧！将曾经斩杀你们的杜兰达尔身躯嚼食粉碎！他亵渎了神族，应当得到最惨烈的下场！'}",
	[84804] = "{id = 84804, Speaker = 6052, Text = '既然已经是死人了，就应该乖乖呆在地狱里！我会负责将你们再次送回亡者的世界！只要我还有一口气，你们就休想踏过帝国的城墙！'}",
	[84805] = "{id = 84805, Speaker = 6055, Text = '不自量力的人类，竟敢杀死我的女儿。我会把你鲜血淋漓的遗骸送还给你们的君王！以血肉之躯，和无穷无尽的地狱尸骸对抗，你能坚持多久？'}",
	[84806] = "{id = 84806, Speaker = 6053, Text = '帝国不能没有杜兰达尔的剑，即使违背神的旨意，也必须将他复活。神杀死了他一次，总不会再杀他一次。'}",
	[84807] = "{id = 84807, Speaker = 6056, Text = '他会在七日七夜之后复活。但是，违背神的旨意，您和您的帝国都将步向毁灭。'}",
	[84808] = "{id = 84808, Speaker = 6053, Text = '妖言惑众的魔女！拖出去架上火刑台！'}",
	[84809] = "{id = 84809, Speaker = 6056, Text = '哈哈哈哈……我就是曾被杜兰达尔杀死的凯美拉。现在，帝国已经受到我的诅咒，只要杜兰达尔复活，您的人民终将逃不过厄难！'}",
	[84810] = "{id = 84810, Speaker = 6056, Text = '杜兰达尔苏醒之时，他所看到的，会是那些无辜的人在恐惧悲鸣中死去的情景……'}",
	[84811] = "{id = 84811, Speaker = 6063, Text = '七日七夜后，杜兰达尔苏醒时，他们的国土和人民正受到邻国大军的侵袭。那些入侵者犹如中了邪一般，疯狂残忍地杀害那些手无寸铁的子民……'}",
	[84812] = "{id = 84812, Speaker = 6057, Text = '杜兰达尔，只要你向我跪下认错，我就原谅你弑神的行为。那些子民也不用再受苦受难。'}",
	[84813] = "{id = 84813, Speaker = 6052, Text = '既然我曾是弑神者，那么，我就再做一次弑神者！'}",
	[84814] = "{id = 84814, Speaker = 6063, Text = '杜兰达尔再次斩下了凯美拉的首级。他带领王军击退了侵略者，浑身浴血跪在他的君王面前。'}",
	[84815] = "{id = 84815, Speaker = 6063, Text = '王对他说：“杜兰达尔，只要你还活着，神族就不会放过我的子民。”'}",
	[84816] = "{id = 84816, Speaker = 6063, Text = '杜兰达尔双手将自己的剑平托奉上，回答道：“吾王，既然我是弑神者，就让我以‘弑神者’之名死去吧。我将荣光献给帝国，而我愿将自己的身躯与名字奉献给笼罩帝国的黑暗。”'}",
	[84817] = "{id = 84817, Speaker = 6063, Text = '杜兰达尔：“我死而复生，这副身躯已被地狱的罪恶侵蚀。请在此，将我斩杀。”'}",
	[84818] = "{id = 84818, Speaker = 6063, Text = '千年以来，那个国度尽管已消失，杜兰达尔的剑仍然屹立在那个国度的废墟之上。'}",
	[84819] = "{id = 84819, Speaker = 6063, Text = '骑士，你明白什么是[c][ffff00]骑士之魂[-][/c]了吗？', Option = '也许吧……'}",
	[84820] = "{id = 84820, Speaker = 6063, Text = '[c][ffff00]誓约之刃[-][/c]会协助你看透邪恶……'}",
	[84821] = "{id = 84821, Speaker = 6063, Text = '[c][ffff00]守护之盾[-][/c]会庇佑你破除万难……'}",
	[84822] = "{id = 84822, Speaker = 6063, Text = '[c][ffff00]钢铁之躯[-][/c]会赐予你强大武力……'}",
	[84823] = "{id = 84823, Speaker = 6063, Text = '[c][ffff00]坚韧之心[-][/c]会成为你意志之柱……'}",
	[84824] = "{id = 84824, Speaker = 6063, Text = '具备这四样东西，你才能驾驭[c][ffff00]弑神者[-][/c]。'}",
	[84825] = "{id = 84825, Speaker = 6063, Text = '而终有一天，你也会用你的剑，为守护你必须守护的东西，献上你的生命。你的[c][ffff00]骑士之魂[-][/c]将献给[c][ffff00]弑神者[-][/c]。'}",
	[84826] = "{id = 84826, Speaker = 6063, Text = '请记住，太阳的光辉会引导你，黑夜的星辰亦将陪伴你。'}",
	[85053] = "{id = 85053, Speaker = 6078, Text = '最近我们《国家地理》正致力于调查野外魔物的生存现状，令人吃惊的是，调查发现萌萌的波利因为缺乏有力的防护手段竟然受到了来自各方面的极大威胁。'}",
	[85054] = "{id = 85054, Speaker = 6078, Text = '冒险者，你能去野外帮我们拍一张波利的照片吗？有效的信息来源会让我们做出更合理的评估。'}",
	[85056] = "{id = 85056, Speaker = 6077, Text = '冒险者，作为守护者，我有必要提醒你，你带来了莱弥亚家族的传承之物吗？', Option = '{给你传承卷轴,2},{暂时没有,3}'}",
	[85057] = "{id = 85057, Speaker = 6077, Text = '很抱歉，你不能证明自己的身份，请立刻离开这里。'}",
	[85058] = "{id = 85058, Speaker = 6077, Text = '不错，这正是我们莱弥亚家族世代相承的信物，我想你就是我们等待已久的有缘之人。'}",
	[85059] = "{id = 85059, Speaker = 6077, Text = '那么，有缘人，你得到了我的认可，请挑选一件你喜欢的武器。（盟约·烈光—剑；赫克拉斯神弓—弓；阿斯加德之怒—锤；光辉晨星—法杖；龙之意志—矛；冰火之心—斧；暗影之殇—拳刃。请仔细挑选。）', Option = '{盟约·烈光,4},{赫克拉斯神弓,5},{阿斯加德之怒,6},{光辉晨星,7},{龙之意志,8},{冰火之心,9},{暗影之殇,10}'}",
	[85060] = "{id = 85060, Speaker = 6077, Text = '那么冒险者，这把武器从现在起就属于你了，希望你可以最大限度地发挥出它的威力。'}",
	[85061] = "{id = 85061, Speaker = 6078, Text = '非常出色的冒险者，我很满意你的表现。'}",
	[85062] = "{id = 85062, Speaker = 6078, Text = '最近斐扬的农田似乎遭受了鼠患，附近的居民反映称这是一种行动迅捷、全身齿白的鼠类魔物所为……但普隆德拉下水道的白鼠又怎么会出现在斐扬呢，真是令人疑惑。'}",
	[85063] = "{id = 85063, Speaker = 6078, Text = '冒险者，请你去拍摄一张白鼠的照片回来，也许我们要重新评估一下野外魔物的变异现状。'}",
	[85064] = "{id = 85064, Speaker = 6078, Text = '你做得很好，谢谢你的帮忙，冒险者。'}",
	[85065] = "{id = 85065, Speaker = 6092, Text = '有些魔物看上去十分可爱，但往往这只是蒙蔽敌人的手段，在敌人疏忽大意的瞬间它们就会发出凶狠致命的攻击。'}",
	[85066] = "{id = 85066, Speaker = 6092, Text = '魔菇就是这种极具欺诈性的魔物，冒险者，接下来请你为我拍摄一张魔菇的照片，我会告诉你更详细的信息。'}",
	[85067] = "{id = 85067, Speaker = 6092, Text = '谢谢你冒险者，这正是我需要的。'}",
	[85068] = "{id = 85068, Speaker = 6092, Text = '听说西门的溜溜猴最近总是无故攻击路过的冒险者，这种行为已经引起了众多冒险者的敌意……'}",
	[85069] = "{id = 85069, Speaker = 6092, Text = '冒险者，我们要调查清楚溜溜猴异变的原因，首先请你为我拍摄一张西门溜溜猴的照片吧。'}",
	[85070] = "{id = 85070, Speaker = 6092, Text = '你做得很好，这是你应得的奖励，请收好。'}",
	[85071] = "{id = 85071, Speaker = 6093, Text = '西门的树精拥有长久的寿命和厚实的防御，如果没有意外情况的发生，它们几乎可以一直生存下去，因而它们可能是最长寿的魔物物种之一吧。'}",
	[85072] = "{id = 85072, Speaker = 6093, Text = '冒险者，请你帮我去西门拍摄一张树精的照片吧，这可是真是振奋人心的发现呢。'}",
	[85073] = "{id = 85073, Speaker = 6093, Text = '干的不错，冒险者，很高兴有你的帮忙。'}",
	[85074] = "{id = 85074, Speaker = 6093, Text = '你知道吗？海葵虽然是一种极具危险性的捕食魔物，但它们的外表十分艳丽，就像一朵朵开在深蓝海水里的缤纷花朵。'}",
	[85075] = "{id = 85075, Speaker = 6093, Text = '冒险者，你能为我带来一张海葵的照片吗？这可是很有收藏价值呢。'}",
	[85076] = "{id = 85076, Speaker = 6093, Text = '这可是我期待已久的东西呢，这次多亏你了，冒险者。'}",
	[85077] = "{id = 85077, Speaker = 6094, Text = '生活在沉没之船的邪骸海盗可以说是非常独特的魔物物种，因为它们生前本是人类，但是一些无法言明的变故使它们异变成了魔物。'}",
	[85078] = "{id = 85078, Speaker = 6094, Text = '冒险者，请你为我拍摄一张邪骸海盗的照片，这对于研究它们的生活习性是十分必要的。'}",
	[85079] = "{id = 85079, Speaker = 6094, Text = '真是优秀的冒险者呢，你得到了我的认可。'}",
	[85080] = "{id = 85080, Speaker = 6094, Text = '最近在普隆德拉展开了一场异常激烈的争论，而争论的中心竟然是海底洞穴的螃蟹是否可以食用……'}",
	[85081] = "{id = 85081, Speaker = 6094, Text = '冒险者，请你为我拍摄一张螃蟹的照片，我想我们有必要重新研究一下野外魔物的生存特性了。'}",
	[85082] = "{id = 85082, Speaker = 6094, Text = '冒险者，真高兴认识你呢，对你的帮忙我表示最衷心的感谢。'}",
	[85083] = "{id = 85083, Speaker = 6095, Text = '听说海底的人鱼是海洋精灵的化身，它们不仅拥有清纯美艳的容貌，还有着一颗淳朴善良的心。'}",
	[85084] = "{id = 85084, Speaker = 6095, Text = '冒险者，你能去海底帮我拍一张人鱼的照片吗？这些可爱的精灵喜欢自由自在地游荡在海洋的怀抱里。'}",
	[85085] = "{id = 85085, Speaker = 6095, Text = '你可真是厉害，我果然没有看错人。'}",
	[85086] = "{id = 85086, Speaker = 6095, Text = '一直以来，我都被一件事所困扰，究竟那些哥布灵是怎么飞行的呢？'}",
	[85087] = "{id = 85087, Speaker = 6095, Text = '冒险者，你能为我拍摄一张喷射哥布灵的照片吗？我想它们的喷射装置一定很神奇。'}",
	[85088] = "{id = 85088, Speaker = 6095, Text = '太棒了冒险者，我就知道你不会令我失望的。'}",
	[85089] = "{id = 85089, Speaker = 6096, Text = '听说生活在妙勒尼山脉的彩蝶精灵是女神的使者，凡是见过彩蝶精灵并诚心许愿的人，一定可以梦想成真。'}",
	[85090] = "{id = 85090, Speaker = 6096, Text = '冒险者，请你为我带来一张彩蝶精灵的照片吧，我有好多愿望要对它们诉说呢。'}",
	[85091] = "{id = 85091, Speaker = 6096, Text = '非常出色的冒险者，我很满意你的表现。'}",
	[85092] = "{id = 85092, Speaker = 6096, Text = '我想你一定见到过那些手持平底锅、形似煎蛋的魔锅蛋吧，它们本来可以成为大嘴鸟，但是当完整的大嘴鸟蛋被打破它们也因失去了梦想而变得性格暴躁……'}",
	[85093] = "{id = 85093, Speaker = 6096, Text = '冒险者，我需要一张清晰的魔锅蛋照片，我们需要更多地了解魔物的起源。'}",
	[85094] = "{id = 85094, Speaker = 6096, Text = '你做得很好，谢谢你的帮忙，冒险者。'}",
	[85095] = "{id = 85095, Speaker = 6097, Text = '据说生活苏克拉的黑蚁其实是一种杂食性的魔物，它们什么都吃而且平常极少外出活动，所以它们的个头可以长得那么大。'}",
	[85096] = "{id = 85096, Speaker = 6097, Text = '冒险者，你能为我拍摄一张黑蚁的照片吗？我对它们越来越感兴趣了。'}",
	[85097] = "{id = 85097, Speaker = 6097, Text = '谢谢你冒险者，这正是我需要的。'}",
	[85098] = "{id = 85098, Speaker = 6097, Text = '生活在金字塔深处的木乃伊全身都笼罩着一种神秘感，尤其是它们身上缠绕着的白色绷带，让人很有奋力撕开的欲望。'}",
	[85099] = "{id = 85099, Speaker = 6097, Text = '冒险者，请你为我拍摄一张木乃伊的照片吧，我想更多地了解关于它们的信息。'}",
	[85100] = "{id = 85100, Speaker = 6097, Text = '你做得很好，这是你应得的奖励，请收好。'}",
	[85101] = "{id = 85101, Speaker = 6098, Text = '我想一直以来我们都陷入了一个误区，斐扬的大脚熊根本不像其他魔物那样凶残狂暴，相反它们都是大自然的守护者，喜欢和平安宁的生活。'}",
	[85102] = "{id = 85102, Speaker = 6098, Text = '冒险者，请你为我拍摄一张大脚熊的照片，大家需要知道事情的真相。'}",
	[85103] = "{id = 85103, Speaker = 6098, Text = '干的不错，冒险者，很高兴有你的帮忙。'}",
	[85104] = "{id = 85104, Speaker = 6098, Text = '为什么转转蛋一直可以360度旋转，并且不会感到眩晕不适呢？这可能是本世纪最难回答的问题吧！'}",
	[85105] = "{id = 85105, Speaker = 6098, Text = '冒险者，我想我需要你的帮助，请带给我一张转转蛋的照片，对于这种魔物我们还需要更细致地研究。'}",
	[85106] = "{id = 85106, Speaker = 6098, Text = '这可是我期待已久的东西呢，这次多亏你了，冒险者。'}",
	[85107] = "{id = 85107, Speaker = 6099, Text = '你知道吗？对于各种魔物的信息我们现在还知之甚少，就像金字塔里的马尔杜克，它们少言寡语行事隐秘，从来没有人见过它们面具之下的真面目。'}",
	[85108] = "{id = 85108, Speaker = 6099, Text = '冒险者，请你为我拍摄一张马尔杜克的照片吧，我们现在非常需要你的帮助。'}",
	[85109] = "{id = 85109, Speaker = 6099, Text = '真是优秀的冒险者呢，你得到了我的认可。'}",
	[85110] = "{id = 85110, Speaker = 6099, Text = '最近总有斐扬的居民在地下洞穴的入口看到类似鬼影的物体，虽然我一再安慰他们，但他们坚称洞穴里的鬼女已经蜂拥来到斐扬城中，这真是令人苦恼。'}",
	[85111] = "{id = 85111, Speaker = 6099, Text = '冒险者，如果你能为我拍摄一张鬼女的照片，我想我就可以让大家仔细辨认以消除无谓的恐慌。'}",
	[85112] = "{id = 85112, Speaker = 6099, Text = '冒险者，真高兴认识你呢，对你的帮忙我表示最衷心的感谢。'}",
	[85113] = "{id = 85113, Speaker = 6100, Text = '最新的研究发现，克特森林的狂暴野猫并不完全像人们说的那样凶狠好斗，在很多时候它们更像温顺可爱的宠物小猫，区分的标准只是在于你是否闯入了它们的领地范围。'}",
	[85114] = "{id = 85114, Speaker = 6100, Text = '冒险者，你能为我拍摄一张狂暴野猫的照片吗？这可真是个有趣的发现。'}",
	[85115] = "{id = 85115, Speaker = 6100, Text = '你可真是厉害，我果然没有看错人。'}",
	[85116] = "{id = 85116, Speaker = 6100, Text = '听说很多冒险者都曾有过被小恶魔捉弄的经历，别看它们体型弱小，但却是天生的捣蛋鬼，一旦被它们缠上很难轻易脱身，我想它们可能是最令冒险者头疼的魔物之一吧。'}",
	[85117] = "{id = 85117, Speaker = 6100, Text = '冒险者，请为我拍摄一张小恶魔的照片吧，虽然这可能有点难度，但却一定难不倒你。'}",
	[85118] = "{id = 85118, Speaker = 6100, Text = '太棒了冒险者，我就知道你不会令我失望的。'}",
	[85119] = "{id = 85119, Speaker = 6101, Text = '很多魔物本身都有一些怪异的行为，至今无法得到合理的解释。比如古城郊外的绿贝勒斯，它们最大的喜好就是收集金灿灿的东西，可天知道它们收集这些东西的目的……'}",
	[85120] = "{id = 85120, Speaker = 6101, Text = '冒险者，请你去古城郊外拍摄一张绿贝勒斯的照片给我，对于魔物的研究我们还需要进一步的深入。'}",
	[85121] = "{id = 85121, Speaker = 6101, Text = '非常出色的冒险者，我很满意你的表现。'}",
	[85122] = "{id = 85122, Speaker = 6101, Text = '最近总有人问我赤苍蝇和苍蝇有什么不一样，对于这个问题，我只能说，如果你发现面前的苍蝇战斗经验很丰富时，那么你面对的就一定是赤苍蝇……'}",
	[85123] = "{id = 85123, Speaker = 6101, Text = '冒险者，请你为我拍摄一张赤苍蝇的照片吧，虽然它们看起来和普通苍蝇并没有什么分别。'}",
	[85124] = "{id = 85124, Speaker = 6101, Text = '你做得很好，谢谢你的帮忙，冒险者。'}",
	[85125] = "{id = 85125, Speaker = 6102, Text = '虽然古城的恶魔女仆拥有着性感妖娆的容貌，但它们却是天生的杀手，在美艳的外表下，你闻到了致命的气息吗？'}",
	[85126] = "{id = 85126, Speaker = 6102, Text = '冒险者，请带给我一张恶魔女仆的照片，我们要时刻提防各种潜在的危险。'}",
	[85127] = "{id = 85127, Speaker = 6102, Text = '谢谢你冒险者，这正是我需要的。'}",
	[85128] = "{id = 85128, Speaker = 6102, Text = '听说有一种叫做史汀的魔物，外形十分奇特，它们的身躯酷似一段伸长的手臂，在阴暗潮湿的下水道里时刻可见它们的身影。'}",
	[85129] = "{id = 85129, Speaker = 6102, Text = '冒险者，你能去帮我拍摄一张史汀的照片吗？我已经迫不及待想要看一看这种魔物的模样了。'}",
	[85130] = "{id = 85130, Speaker = 6102, Text = '你做得很好，这是你应得的奖励，请收好。'}",
	[85131] = "{id = 85131, Speaker = 6103, Text = '我一直都不太敢靠近古城地下墓地，那真是一个可怕的地方，想一想到处都是阴森森的雾气和凶煞可怖的魔物，我就感觉双脚一阵发软。'}",
	[85132] = "{id = 85132, Speaker = 6103, Text = '冒险者，你能去古城墓地拍摄一张恶灵的照片给我吗？我想你一定可以帮到我。'}",
	[85133] = "{id = 85133, Speaker = 6103, Text = '真是乐于助人的冒险者呢，谢谢你。'}",
	[85134] = "{id = 85134, Speaker = 6103, Text = '冒险者，我想你一定知道猫小友吧，迄今为止，无数冒险者在猫小友那里进行过装备附魔，他可算是个家喻户晓的大名人了呢。'}",
	[85135] = "{id = 85135, Speaker = 6103, Text = '那么请你为我拍摄一张猫小友的照片吧，感谢他为冒险者们所带来的喜悦和欢笑。'}",
	[85136] = "{id = 85136, Speaker = 6103, Text = '干的不错，冒险者，很高兴有你的帮忙。'}",
	[85137] = "{id = 85137, Speaker = 6104, Text = '听说在斐扬有一位声名显赫的占卜师，她的名字叫做星痕，真是人如其名，她可以通过观察天象的细微变化来预测人世的祸福变迁，斐扬正是有她的庇佑才能一直风调雨顺和平安定。'}",
	[85138] = "{id = 85138, Speaker = 6104, Text = '冒险者，请为我拍摄一张星痕的照片吧，我虽然从来没有见过她，却也钦佩她的本事。'}",
	[85139] = "{id = 85139, Speaker = 6104, Text = '这可是我梦寐以求的东西呢，这次多亏你了，冒险者。'}",
	[85140] = "{id = 85140, Speaker = 6104, Text = '说起卡鲁船长，大家一定会想到那个风雨无阻、任劳任怨的伟岸身影，无论我们出海前往何处，总是离不开他的带领和指引。'}",
	[85141] = "{id = 85141, Speaker = 6104, Text = '冒险者，请为我拍摄一张卡鲁船长的照片，让我们感谢他为冒险者们带来的一切便利。'}",
	[85142] = "{id = 85142, Speaker = 6104, Text = '真是优秀的冒险者呢，你得到了我的认可。'}",
	[85143] = "{id = 85143, Speaker = 6105, Text = '让我们来说说现在最伟大的铁匠忽克连，他用自己最精湛完美的技艺帮助了无数冒险者锻造出心仪的武器，同时也以自己最坚韧不拔的意志激励着更多的冒险者去克服未知的困难和挑战。'}",
	[85144] = "{id = 85144, Speaker = 6105, Text = '对于他的勇气和毅力我感到敬佩，冒险者，请为我拍摄一张忽克连的照片吧。'}",
	[85145] = "{id = 85145, Speaker = 6105, Text = '谢谢你，冒险者，我想你也一定对忽克连心怀敬意。'}",
	[85146] = "{id = 85146, Speaker = 6105, Text = '伟大的梦罗克之盾巴尔洛将军，我想你一定听说过，在那场激烈的梦罗克之战中，浴血奋战的巴尔洛指挥大陆卫队英勇地击退了魔物的进攻，一战成名。'}",
	[85147] = "{id = 85147, Speaker = 6105, Text = '冒险者，请为巴尔洛将军拍摄一张照片，让我们对他致以最崇高的敬意。'}",
	[85148] = "{id = 85148, Speaker = 6105, Text = '太棒了冒险者，你瞧照片中的巴尔洛将军是多么高大伟岸啊。'}",
	[85149] = "{id = 85149, Speaker = 6106, Text = '天才魔法师、吉芬的领袖雷藏，在进军古城之后领导众人对抗贪婪的黑暗之王，从他身上我看到了最英勇无畏的意志，那足以覆灭一切污秽和邪恶。'}",
	[85150] = "{id = 85150, Speaker = 6106, Text = '冒险者，请你去古城拍摄一张雷藏的照片，让我们对他的嫉恶如仇表示最真诚的感谢。'}",
	[85151] = "{id = 85151, Speaker = 6106, Text = '冒险者，你做得很好，我们应当以雷藏为榜样，不屈不挠，坚持正义。'}",
	[85200] = "{id = 85200, Speaker = 6081, Text = '据说与朋友一起手牵手能够使大家心意相通，提升双方的信任度，从而增强友谊哦。'}",
	[85201] = "{id = 85201, Speaker = 6081, Text = '尝试与你所在公会的任意一名成员牵手一分钟，感受下信任的感觉吧。'}",
	[85202] = "{id = 85202, Speaker = 6081, Text = '怎么样，受人信任的感觉是不是很美妙呢。'}",
	[85203] = "{id = 85203, Speaker = 6082, Text = '勇敢的冒险者不会止步于外界的艰难险阻，同样也不会怯懦于出色的人际交往。'}",
	[85204] = "{id = 85204, Speaker = 6082, Text = '尝试与你所在公会的任意三名成员牵手一分钟，传达你的友谊吧。'}",
	[85205] = "{id = 85205, Speaker = 6082, Text = '看来你在公会里很受欢迎呢，我要好好向你学习了。'}",
	[85206] = "{id = 85206, Speaker = 6082, Text = '一名出色的冒险者，对自己忠实，也对别人真挚。'}",
	[85207] = "{id = 85207, Speaker = 6082, Text = '尝试与你所在公会的任意五名成员牵手一分钟，让别人感受你的真诚吧。'}",
	[85208] = "{id = 85208, Speaker = 6082, Text = '你的表现真是太完美了，我想你的朋友们一定感受到你的真诚了。'}",
	[85209] = "{id = 85209, Speaker = 6082, Text = '喵！可恶，我山葵的招牌就这么砸了！'}",
	[85210] = "{id = 85210, Speaker = 6082, Text = '老板我都帮你扛下了这么多伤害了，争点气啊！'}",
	[85211] = "{id = 85211, Speaker = 6082, Text = '来！我们重新开始！有我在呐！'}",
	[85212] = "{id = 85212, Speaker = 6082, Text = '老板安心吧，我这次一定会保护好你的！'}",
	[85268] = "{id = 85268, Speaker = 6118, Text = '哦！该死的！它咬了我！'}",
	[85281] = "{id = 85281, Speaker = 6120, Text = '听说这里有洁白无暇的玉蝶花，为什么我什么都见不到呢？'}",
	[85282] = "{id = 85282, Speaker = 6120, Text = '我的花冠可就差这点东西就完成了呢！'}",
	[85294] = "{id = 85294, Speaker = 6120, Text = '卡丽莎做了什么？为什么你们要这么对她！'}",
	[85295] = "{id = 85295, Speaker = 6121, Text = '因为她违反了国王陛下的命令！'}",
	[85296] = "{id = 85296, Speaker = 6120, Text = '她那么善良，怎么可能会违法？'}",
	[85297] = "{id = 85297, Speaker = 6121, Text = '她来看你了！这就已经违背了国王的命令了。'}",
	[85298] = "{id = 85298, Speaker = 6121, Text = '我们绝对不会允许她带着你身上的瘟疫再次进入城镇！'}",
	[85299] = "{id = 85299, Speaker = 6120, Text = '我根本没有碰到她…每次她来见我，我都包裹得严严实实的…'}",
	[85300] = "{id = 85300, Speaker = 6120, Text = '你们答应我好好照顾她…你们欺骗我！'}",
	[85301] = "{id = 85301, Speaker = 6120, Text = '你们会后悔的！我一定要报复，我要报复！我要你们陪葬！'}",
	[85328] = "{id = 85328, Speaker = 6121, Text = '瘟疫已经夺取数千人的性命了，王宫中的医师们却从你身上看到了希望。'}",
	[85329] = "{id = 85329, Speaker = 6120, Text = '是因为我免疫瘟疫的体质吗？'}",
	[85330] = "{id = 85330, Speaker = 6121, Text = '据我了解，你是一个非常正派的人，我想你会接受王国的决定的。'}",
	[85331] = "{id = 85331, Speaker = 6120, Text = '为了心中的正义，我愿意，但是我希望王国能够善待卡丽莎…'}",
	[85332] = "{id = 85332, Speaker = 6121, Text = '如你所愿，古尔顿，只要能解决瘟疫，王国不会亏待你们的。'}",
	[85333] = "{id = 85333, Speaker = 6121, Text = '好了诸位！瘟疫转移魔法开始吧！'}",
	[85334] = "{id = 85334, Speaker = 6120, Text = '啊啊啊啊！啊——'}",
	[85335] = "{id = 85335, Speaker = 6121, Text = '忍耐！古尔顿！为了王国！'}",
	[85336] = "{id = 85336, Speaker = 6120, Text = '啊！！！……呼…为了…王国…'}",
	[85337] = "{id = 85337, Speaker = 6121, Text = '成功了吗？'}",
	[85338] = "{id = 85338, Speaker = 6123, Text = '成功了。'}",
	[85345] = "{id = 85345, Speaker = 6127, Text = '古尔顿？你在哪儿？你要抛弃我了吗？'}",
	[85346] = "{id = 85346, Speaker = 6120, Text = '别！别过来！'}",
	[85347] = "{id = 85347, Speaker = 6127, Text = '亲爱的，你到底怎么了？你为什么会住到这种荒无人烟的地方来？'}",
	[85348] = "{id = 85348, Speaker = 6120, Text = '这不是你应该管的事情！我们结束了！卡丽莎！'}",
	[85349] = "{id = 85349, Speaker = 6127, Text = '亲爱的，这不是真的！我不相信…'}",
	[85350] = "{id = 85350, Speaker = 6120, Text = '我的身体已经变得不再属于我自己了…瘟疫时刻折磨着我，我的皮肤不断的腐烂却又不断的重生…'}",
	[85351] = "{id = 85351, Speaker = 6120, Text = '但是如果能牺牲我一人，让城市里的人们都安然无恙…那我的牺牲是值得的。'}",
	[85352] = "{id = 85352, Speaker = 6127, Text = '古尔顿！你知道我不会嫌弃你的，无论你变成什么样子…'}",
	[85353] = "{id = 85353, Speaker = 6120, Text = '你本该有令人羡慕的生活…你不该为我这样的一个人浪费时间…或许我现在已经不能称之为人了…'}",
	[85354] = "{id = 85354, Speaker = 6120, Text = '回去吧，卡丽莎，我想一个人冷静一下，天快黑了…回去的时候一定要小心…我身怀瘟疫，恐怕不能送你到城市里了…'}",
	[85362] = "{id = 85362, Speaker = 6127, Text = '你们欺骗了他！他以为拯救了你们，你们却这样对待他，你们的良知是喂给了那些恶魔吗？'}",
	[85363] = "{id = 85363, Speaker = 6121, Text = '没错，他是拯救了我们，但是我们不能放任与他接触的人再次进入城里。'}",
	[85364] = "{id = 85364, Speaker = 6127, Text = '放我走好吗？我愿意和他生活在一起，即使死了，我也心甘情愿！'}",
	[85365] = "{id = 85365, Speaker = 6121, Text = '这不可能！我们不会允许瘟疫的载体存活在世间。'}",
	[85366] = "{id = 85366, Speaker = 6121, Text = '动手吧，刽子手！我宣布，对卡丽莎·摩尔即刻施行绞刑！'}",
	[85367] = "{id = 85367, Speaker = 6127, Text = '等着吧…你…你们…都…不得…好死！'}",
	[85368] = "{id = 85368, Speaker = 6121, Text = '很可惜，你看不到那一天了…烧了吧！'}",
	[85377] = "{id = 85377, Speaker = 6120, Text = '没想到吧，你们这些外表光鲜，内心却腐朽不堪的蛆虫！'}",
	[85378] = "{id = 85378, Speaker = 6129, Text = '你！你是怎么进来的？卫兵！卫兵！'}",
	[85379] = "{id = 85379, Speaker = 6120, Text = '谢谢你凯莱尔…'}",
	[85380] = "{id = 85380, Speaker = 6122, Text = '古尔顿…我的挚友，你确定你要这么做？'}",
	[85381] = "{id = 85381, Speaker = 6120, Text = '卡丽莎死后我的内心无时无刻不在忍受着折磨，我要他们死！'}",
	[85382] = "{id = 85382, Speaker = 6122, Text = '好吧…'}",
	[85383] = "{id = 85383, Speaker = 6129, Text = '不，你不要过来！卫兵！！'}",
	[85384] = "{id = 85384, Speaker = 6121, Text = '古尔顿！你听我说！卡丽莎的死是国王下的命令！跟我没关系！'}",
	[85385] = "{id = 85385, Speaker = 6129, Text = '你！！！！'}",
	[85386] = "{id = 85386, Speaker = 6120, Text = '放心…你们一个都活不了，去地狱忏悔吧！'}",
	[85387] = "{id = 85387, Speaker = 6120, Text = '哈哈哈哈哈哈哈！！！！卡丽莎，你看到了吗？！我这就来陪你！'}",
	[85388] = "{id = 85388, Speaker = 6120, Text = '凯莱尔，我的挚友！送我一程吧！'}",
	[85389] = "{id = 85389, Speaker = 6122, Text = '听我说古尔顿，你没必要…'}",
	[85390] = "{id = 85390, Speaker = 6120, Text = '别说了凯莱尔，没有卡丽莎，我活着有什么意义？'}",
	[85391] = "{id = 85391, Speaker = 6120, Text = '看看我的皮肤！它在瘟疫的折磨下不断的溃烂，却又因为我这该死的体质不断的重生。'}",
	[85392] = "{id = 85392, Speaker = 6120, Text = '我厌恶这样子的自己，难道你要让我与瘟疫共存于世，这样半死不活的苟活下去吗？'}",
	[85393] = "{id = 85393, Speaker = 6120, Text = '拜托了，凯莱尔，杀了我！'}",
	[85421] = "{id = 85421, Speaker = 6001, Text = '你果然还是来了，就那么不相信我吗？'}",
	[85422] = "{id = 85422, Speaker = 6002, Text = '我可没那闲工夫担心你，只是无名让我来看看你们到底要让他等到什么时候！'}",
	[85423] = "{id = 85423, Speaker = 6001, Text = '你什么时候才能改掉你这嘴硬的坏毛病呢？'}",
	[85424] = "{id = 85424, Speaker = 6002, Text = '别废话了，带着这只小猫咪赶快走吧！'}",
	[85425] = "{id = 85425, Speaker = 6008, Text = '冒险者，我想我们可以击败黑暗之王！'}",
	[85426] = "{id = 85426, Speaker = 6000, Text = '你们以为你们在我所创造的空间内能够击败我？？？'}",
	[85427] = "{id = 85427, Speaker = 0, Text = '随着黑暗之王的败亡，死亡空间开始崩塌，时空扭曲……'}",
	[85428] = "{id = 85428, Speaker = 6008, Text = '冒险者！', Option = '什么都看不见了'}",
	[85429] = "{id = 85429, Speaker = 6003, Text = '真不让人省心啊。'}",
	[85430] = "{id = 85430, Speaker = 6007, Text = '你居然又进来了，我还以为你的魔力已经耗尽了。'}",
	[85431] = "{id = 85431, Speaker = 6003, Text = '别废话了，快带着冒险者离开这里。'}",
	[85432] = "{id = 85432, Speaker = 6007, Text = '哈哈，你干得非常出色冒险者，接下来你只需要闭上眼睛就行了。', Option = '闭上双眼'}",
	[85442] = "{id = 85442, Speaker = 6110, Text = '获得任务道具[c][ffff00]古尔顿的日记[1][-][/c]。'}",
	[85443] = "{id = 85443, Speaker = 6111, Text = '获得任务道具[c][ffff00]古尔顿的日记[2][-][/c]。'}",
	[85444] = "{id = 85444, Speaker = 6112, Text = '获得任务道具[c][ffff00]古尔顿的日记[3][-][/c]。'}",
	[85445] = "{id = 85445, Speaker = 6113, Text = '获得任务道具[c][ffff00]古尔顿的日记[4][-][/c]。'}",
	[85446] = "{id = 85446, Speaker = 6139, Text = '啊哈~冒险者，告诉你一个令人兴奋的消息！米德加尔特大陆上一年一度的庆典就要开始了！'}",
	[85447] = "{id = 85447, Speaker = 6139, Text = '今年的主题是——大冒险！要知道在这片充满危险的大陆上，冒险可是永恒的话题！'}",
	[85448] = "{id = 85448, Speaker = 6139, Text = '所有生物的内心都是渴望着冒险的，即使它是只野猪。', Option = '野猪？'}",
	[85449] = "{id = 85449, Speaker = 6139, Text = '当然！在接受王国的邀请来到这里的路上，我就曾遭遇到这样一头小野猪。'}",
	[85450] = "{id = 85450, Speaker = 6139, Text = '从它热诚的眼中，我看到了冒险的光芒！但是它现在似乎陷入了困境。'}",
	[85451] = "{id = 85451, Speaker = 6139, Text = '我很想帮助它！但是我身上还有着王国的使命。'}",
	[85452] = "{id = 85452, Speaker = 6139, Text = '你能够去代替我看看它吗？它的名字叫做咿维。', Option = '好吧。'}",
	[85453] = "{id = 85453, Speaker = 6140, Text = '哼哧…你、你是谁？', Option = '居然会说话'}",
	[85454] = "{id = 85454, Speaker = 6140, Text = '哼哧…别小瞧我！我可不是一般的野猪。'}",
	[85455] = "{id = 85455, Speaker = 6140, Text = '要不是我的肚子饿了…我一定要你见识见识我的厉害！', Option = '饿了'}",
	[85456] = "{id = 85456, Speaker = 6140, Text = '哼哧…有、有点…你这什么表情！这是一点点饿而已！', Option = '想吃什么'}",
	[85457] = "{id = 85457, Speaker = 6140, Text = '嗯…听说人类的海鲜特别好吃~我很想尝一尝…', Option = '…'}",
	[85458] = "{id = 85458, Speaker = 6140, Text = '我只是想多吃点而已！哼哧…我真的不是很饿！'}",
	[85459] = "{id = 85459, Speaker = 6140, Text = '哼哧…我老远就闻到了蟹肉的香气了！'}",
	[85460] = "{id = 85460, Speaker = 6140, Text = '哼哧…味道真不错…', Option = '…'}",
	[85461] = "{id = 85461, Speaker = 6140, Text = '呼…吃撑了…我要消化消化肚子里的食物…'}",
	[85462] = "{id = 85462, Speaker = 6140, Text = '从家里出来的时候，我听说米德加尔特王国的风景闻名整个大陆。'}",
	[85463] = "{id = 85463, Speaker = 6140, Text = '你能带我去见见王国的风景吗？', Option = '我带你走走'}",
	[85464] = "{id = 85464, Speaker = 6140, Text = '我带你走走'}",
	[85465] = "{id = 85465, Speaker = 6140, Text = '真的吗？哼哧…你真是个好人！'}",
	[85466] = "{id = 85466, Speaker = 1044, Text = '哈！你带着咿维逛逛么？唔，南门的景色不错呢！'}",
	[85467] = "{id = 85467, Speaker = 1089, Text = '会说话的猪？这倒是有观测的价值…', Option = '快走！'}",
	[85468] = "{id = 85468, Speaker = 1121, Text = '咦…遛狗的倒是不少…这遛猪的倒是很少见啊…'}",
	[85469] = "{id = 85469, Speaker = 1156, Text = '哦…冒险者，好久不见，你这是？', Option = '遛猪！'}",
	[85470] = "{id = 85470, Speaker = 1108, Text = '这是你新养的宠物吗？真是可爱呢…'}",
	[85471] = "{id = 85471, Speaker = 6154, Text = '愿逝去的灵魂得到永远的安息…'}",
	[85472] = "{id = 85472, Speaker = 6140, Text = '啊~~走得太累了…我先回南门了哦，来南门找我吧。'}",
	[85473] = "{id = 85473, Speaker = 6140, Text = '看到那么多美好的景色，我的肚子又饿了…', Option = '这什么逻辑'}",
	[85474] = "{id = 85474, Speaker = 6140, Text = '走了这么远的路，运动量很大的好不！哼哧…', Option = '想吃什么'}",
	[85475] = "{id = 85475, Speaker = 6140, Text = '哼哧…一些材料…我们一族就是以材料为食粮…刚刚吃蟹肉只是想换个口味而已！', Option = '好吧。'}",
	[85476] = "{id = 85476, Speaker = 6140, Text = '饿死啦饿死啦！[QuestParam]个[QuestParam]你带来了吗？', Option = '{带来了,3},{没有,2}'}",
	[85477] = "{id = 85477, Speaker = 6140, Text = '唔…真好吃真好吃…'}",
	[85478] = "{id = 85478, Speaker = 6140, Text = '这点粮食太少了吧。都不够塞牙缝呢！'}",
	[85479] = "{id = 85479, Speaker = 6140, Text = '哼哧…吃饱啦…接下来该找那些波利的麻烦了。', Option = '为什么'}",
	[85480] = "{id = 85480, Speaker = 6140, Text = '我刚到南门的时候，这些波利可没少欺负我呢。'}",
	[85481] = "{id = 85481, Speaker = 6140, Text = '我要欺负回来…哼哧…你会帮我的，对吗？', Option = '好吧'}",
	[85482] = "{id = 85482, Speaker = 6140, Text = '你真是太棒了！我也要像你一样，这样他们就再也不敢欺负我了！'}",
	[85483] = "{id = 85483, Speaker = 6140, Text = '我也要去冒险，成为一头强壮的野猪！'}",
	[85484] = "{id = 85484, Speaker = 6140, Text = '往后的几天我也会为冒险做准备~我听说冒险者公会的导师有一本书。'}",
	[85485] = "{id = 85485, Speaker = 6140, Text = '书名叫《冒险指南》！冒险者你能把这本书带给我吗？', Option = '好的'}",
	[85486] = "{id = 85486, Speaker = 1024, Text = '《冒险指南》？你现在还需要这种初级的书籍吗？', Option = '是的'}",
	[85487] = "{id = 85487, Speaker = 1024, Text = '真是奇怪…拿去吧！'}",
	[85488] = "{id = 85488, Speaker = 6140, Text = '啊哈，我要找地方好好读一读这本书。'}",
	[85489] = "{id = 85489, Speaker = 6140, Text = '冒险者，我们明天再见哦~哼哧… 	'}",
	[85490] = "{id = 85490, Speaker = 6121, Text = '卡丽莎，你竟然偷偷去见古尔顿，你知不知道你已经违反了王国的律令！'}",
	[85491] = "{id = 85491, Speaker = 6140, Text = '哼哧…你来啦你来啦！今天带我去哪里玩呢？', Option = '听你的'}",
	[85492] = "{id = 85492, Speaker = 6140, Text = '我从《冒险指南》中看到依斯鲁得有个有名的雕像呢！据说是一个非常强大的人类。'}",
	[85493] = "{id = 85493, Speaker = 6140, Text = '我想去看看这个雕像，顺便合张影…哼哧…来过了总得留点纪念呀~哼哧…', Option = '好的！'}",
	[85494] = "{id = 85494, Speaker = 6140, Text = '哼哧…拍得真好看…我先去南门洗照片去哦。'}",
	[85495] = "{id = 85495, Speaker = 6140, Text = '哼哧…你来的好慢哦…咿维饿了啦！', Option = '饿了？'}",
	[85496] = "{id = 85496, Speaker = 6140, Text = '你昨天给我的材料都被我吃光了…咿维又要饿肚子了…', Option = '交给我吧。'}",
	[85497] = "{id = 85497, Speaker = 6140, Text = '唉…', Option = '怎么了？'}",
	[85498] = "{id = 85498, Speaker = 6140, Text = '哼哧…得不到的东西才是最好的…', Option = '什么东西？'}",
	[85499] = "{id = 85499, Speaker = 6140, Text = '跟你说你也不明白啦…唉…让我自己呆着吧…'}",
	[85500] = "{id = 85500, Speaker = 6140, Text = '你去找宝雅姐姐玩吧。'}",
	[85501] = "{id = 85501, Speaker = 1054, Text = '咿维啊…这么一说我倒是想起来了…'}",
	[85502] = "{id = 85502, Speaker = 1054, Text = '它回来的时候看到了天使波利呢。我告诉它这是难得一见的魔物呢。'}",
	[85503] = "{id = 85503, Speaker = 1054, Text = '它兴奋了没多久就消沉下去了呢。', Option = '好吧'}",
	[85504] = "{id = 85504, Speaker = 6140, Text = '你怎么又回来了？让我自己呆着不好吗？哼哧…', Option = '到底怎么了'}",
	[85505] = "{id = 85505, Speaker = 6140, Text = '唔…看在你这么想帮我的份上，就给你个机会猜一猜。', Option = '好的'}",
	[85506] = "{id = 85506, Speaker = 6140, Text = '第一个问题，谁是这片大陆上可爱的象征？虽然我不喜欢它…', Option = '{溜溜猴,3},{绿棉虫,3},{波利,0}'}",
	[85507] = "{id = 85507, Speaker = 6140, Text = '第二个问题，什么才是这片大陆上时尚的象征？', Option = '{精美的头饰,0},{闪亮的武器,4},{华美的衣服,4}'}",
	[85508] = "{id = 85508, Speaker = 6140, Text = '第三个问题，什么颜色象征这完美无缺的纯洁？', Option = '{粉色,5},{白色,0},{红色,5}'}",
	[85509] = "{id = 85509, Speaker = 6140, Text = '波利，白色，头饰，看来你已经明白我想要什么了。'}",
	[85510] = "{id = 85510, Speaker = 6140, Text = '唉，看来你还没有懂我的心思。'}",
	[85511] = "{id = 85511, Speaker = 6140, Text = '其实今天在等食物的时候，我发现了一个超酷的波利，他居然有天使的翅膀！'}",
	[85512] = "{id = 85512, Speaker = 6140, Text = '我也要做一只酷猪，一只带着天使翅膀的天使猪！'}",
	[85513] = "{id = 85513, Speaker = 6140, Text = '是啊，我想要那个天使波利身上的发圈，但是我完全不明白，应该怎么得到它…', Option = '这好办'}",
	[85514] = "{id = 85514, Speaker = 6140, Text = '好办？你可能不理解…传说中的天使波利可是相见一面都难啊…', Option = '交给我吧。'}",
	[85515] = "{id = 85515, Speaker = 1506, Text = '天使发圈啊~对我来说确实不困难呢。'}",
	[85516] = "{id = 85516, Speaker = 1506, Text = '小野猪吗？恩，确实需要订做，不过这难不倒我。'}",
	[85517] = "{id = 85517, Speaker = 1506, Text = '但是这世上可没有免费的午餐，你明白的~'}",
	[85518] = "{id = 85518, Speaker = 1506, Text = '你帮我收集10个柔毛来，我就帮你制作~', Option = '好的'}",
	[85519] = "{id = 85519, Speaker = 1506, Text = '你把我需要的材料带过来了吗？', Option = '{带来了,3},{没有,2}'}",
	[85520] = "{id = 85520, Speaker = 1506, Text = '数量不多不少，刚刚好~'}",
	[85521] = "{id = 85521, Speaker = 1506, Text = '这点材料也想打发我？'}",
	[85522] = "{id = 85522, Speaker = 1506, Text = '喏，拿去吧，这是小野猪定制版天使发圈。', Option = '谢谢'}",
	[85523] = "{id = 85523, Speaker = 6140, Text = '真的是…天使发圈…哼哧…这种感觉真是太棒了！'}",
	[85524] = "{id = 85524, Speaker = 6140, Text = '我终于是一个天使猪啦~你真是太好了！哼哧…'}",
	[85525] = "{id = 85525, Speaker = 6140, Text = '我要炫耀给赛利亚看看去~我们明天见哦~'}",
	[85526] = "{id = 85526, Speaker = 6129, Text = '古尔顿！是你！'}",
	[85527] = "{id = 85527, Speaker = 6141, Text = '郁闷…哼哧…咦…是你啊…今天也是令人烦恼的一天呢。', Option = '你怎么了？'}",
	[85528] = "{id = 85528, Speaker = 6141, Text = '赛尼亚她…唉…还是不要麻烦你了…哼哧…每次都麻烦你…', Option = '赛尼亚？'}",
	[85529] = "{id = 85529, Speaker = 1050, Text = '咿维？是啊，一开始它来找我的时候还开开心心的呢。'}",
	[85530] = "{id = 85530, Speaker = 1050, Text = '我当时口渴，去喝了两杯苹果汁，回来之后它就闷闷不乐了。', Option = '苹果汁？'}",
	[85531] = "{id = 85531, Speaker = 1050, Text = '对啊！也许咿维也想尝尝苹果汁呢！可惜我的苹果汁都喝光了…', Option = '原来是这样'}",
	[85532] = "{id = 85532, Speaker = 6141, Text = '哼哧…是的…我很想尝尝苹果汁的滋味…', Option = '真贪吃'}",
	[85533] = "{id = 85533, Speaker = 6141, Text = '哼哧…我、我这是享受生活！', Option = '我去弄吧'}",
	[85534] = "{id = 85534, Speaker = 6141, Text = '你带来了我想要的苹果汁了吗？', Option = '{带来了,3},{没有,2}'}",
	[85535] = "{id = 85535, Speaker = 6141, Text = '哇…这就是苹果汁啊…哼哧…'}",
	[85536] = "{id = 85536, Speaker = 6141, Text = '哼哧…这点苹果汁太少了…'}",
	[85537] = "{id = 85537, Speaker = 6141, Text = '苹果汁太好喝了…这时候有那些材料一起吃就更棒了…', Option = '昨天给了'}",
	[85538] = "{id = 85538, Speaker = 6141, Text = '哼哧…昨天你给我的吃完了…', Option = '…'}",
	[85539] = "{id = 85539, Speaker = 6141, Text = '嗝~~咿维吃饱了…该消化消化食物了…'}",
	[85540] = "{id = 85540, Speaker = 6141, Text = '今天你可以带我去西门玩吗？'}",
	[85541] = "{id = 85541, Speaker = 6141, Text = '我想多拍一些照片。', Option = '当然愿意'}",
	[85542] = "{id = 85542, Speaker = 6141, Text = '不玩了不玩了…哼哧…咿维累了呢…我先回南门了…'}",
	[85543] = "{id = 85543, Speaker = 6141, Text = '哼哧…玩得真开心啊~明天我还要玩。', Option = '好的好的。'}",
	[85544] = "{id = 85544, Speaker = 6141, Text = '那就明天不见不散啦~哼哧…再见。'}",
	[85545] = "{id = 85545, Speaker = 6141, Text = '哼哧…你来啦！你一定想不到，刚刚美羽教会了我一个很有趣的东西。', Option = '什么'}",
	[85546] = "{id = 85546, Speaker = 6141, Text = '哼哼，那就是猜拳！你来陪我玩猜拳嘛！', Option = '这…'}",
	[85547] = "{id = 85547, Speaker = 6141, Text = '就陪我玩玩嘛。就一小会~~哼哧…', Option = '好吧'}",
	[85548] = "{id = 85548, Speaker = 6141, Text = '那么，现在开始吧！三！二！一！出拳！', Option = '{拳头,40},{剪刀,50},{布,60}'}",
	[85549] = "{id = 85549, Speaker = 6141, Text = '嘿，我就说嘛，我猜拳很厉害的。', Option = '{再玩一次,3},{不玩啦,2}'}",
	[85550] = "{id = 85550, Speaker = 6141, Text = '哎呀！真是不走运，让你赢了一局，不过，下次你就没这么好的运气了。'}",
	[85551] = "{id = 85551, Speaker = 6141, Text = '平局啊…不行，怎么说你也得赢我一把才行…', Option = '{再玩一次,3},{不玩啦,2}'}",
	[85552] = "{id = 85552, Speaker = 6141, Text = '哼哧…不好玩不好玩…总是输给你…', Option = '运气好'}",
	[85553] = "{id = 85553, Speaker = 6141, Text = '哼哧…总之你赢了就是不对…就罚你给我找食物吧！', Option = '又吃完了？'}",
	[85554] = "{id = 85554, Speaker = 6141, Text = '哼哧…我个头大了，饭量当然也大了！', Option = '好吧'}",
	[85555] = "{id = 85555, Speaker = 6141, Text = '接下来啊…哼哧…《冒险指南》里说，大陆上有着各种各样的魔物。'}",
	[85556] = "{id = 85556, Speaker = 6141, Text = '学会在魔物的袭击下生存下来才是一个冒险者该做的事呢。'}",
	[85557] = "{id = 85557, Speaker = 6141, Text = '我将来去冒险，这些也是要经历的过程呢。'}",
	[85558] = "{id = 85558, Speaker = 6141, Text = '你能带我见识一下冒险者的战斗吗？', Option = '好吧'}",
	[85559] = "{id = 85559, Speaker = 6141, Text = '这就是战斗…哼哧…我有些累了…'}",
	[85560] = "{id = 85560, Speaker = 6141, Text = '这就是战斗么…太残酷了…不是你死就是我亡…'}",
	[85561] = "{id = 85561, Speaker = 6141, Text = '哼哧…我曾经把冒险视为娱乐，现在看来，冒险根本不是我想想中的那般美好…'}",
	[85562] = "{id = 85562, Speaker = 0, Text = '冒险是人人都向往的，人人都希望能够拥有它！但冒险却不是属于每一个人，它只属于勇敢和无谓的人。'}",
	[85563] = "{id = 85563, Speaker = 0, Text = '冒险，不是为了寻求刺激，不是为了显示个人超凡的能力，而是出于对世界的奉献，出于造福世界的责任感。'}",
	[85564] = "{id = 85564, Speaker = 0, Text = '当我们与魔物奋战的时候，你要知道，如果我们不能抵挡住魔物，那么我们身后那些手无寸铁的平民谁来保护？'}",
	[85565] = "{id = 85565, Speaker = 0, Text = '我们肩负着这样的责任，保护弱者，崇尚正义，如果失去了冒险的初心，那么你的冒险将变得毫无意义。'}",
	[85566] = "{id = 85566, Speaker = 0, Text = '当然，冒险也不仅仅是为了保护他人。我们可以去探索世界的秘密。'}",
	[85567] = "{id = 85567, Speaker = 0, Text = '去深入那些广袤的无人区，寻找珍贵的宝藏，得到数之不尽的财富。'}",
	[85568] = "{id = 85568, Speaker = 0, Text = '当然，最重要的一点。那就是我们可以在冒险的旅程中结识许许多多的小伙伴。'}",
	[85569] = "{id = 85569, Speaker = 0, Text = '在你被魔物围攻的时候，他们会来帮助你；在你深陷古代遗迹的时候，他们会来解救你；在你被人欺负的时候，他们会来保护你。'}",
	[85570] = "{id = 85570, Speaker = 0, Text = '在你难过的时候，他们会来安慰你；在你开心的时候，他们会和你一起大笑，分享你的喜悦。'}",
	[85571] = "{id = 85571, Speaker = 0, Text = '他们的存在才是冒险中最最珍贵的财富。'}",
	[85572] = "{id = 85572, Speaker = 0, Text = '所以，不用害怕冒险中的艰难险阻，你要知道，你的伙伴们在前方等待着你！'}",
	[85573] = "{id = 85573, Speaker = 6141, Text = '谢谢你的开导…你一定是位非常出色的冒险者！', Option = '谢谢夸奖'}",
	[85574] = "{id = 85574, Speaker = 6141, Text = '我决定了！哼哧…我一定要成为像你这样的，充满正义的冒险家！'}",
	[85575] = "{id = 85575, Speaker = 6141, Text = '我要好好整休一下，明天见~哼哧…'}",
	[85576] = "{id = 85576, Speaker = 2160, Text = '你终于来了！咿维已经去找你去了。', Option = '找我？'}",
	[85577] = "{id = 85577, Speaker = 2160, Text = '它说它迫不及待地想要见到你了，就去找你了。', Option = '去哪儿了'}",
	[85578] = "{id = 85578, Speaker = 2160, Text = '看得出来，今天的它非常开心，告诉我一声它就直接走开了…'}",
	[85579] = "{id = 85579, Speaker = 2160, Text = '我也不知道它去哪里了…', Option = '去找它'}",
	[85580] = "{id = 85580, Speaker = 2160, Text = '那我在普隆德拉等你的消息！'}",
	[85581] = "{id = 85581, Speaker = 1054, Text = '那头可爱的小野猪么？今天还一蹦一跳得从我面前经过呢…'}",
	[85582] = "{id = 85582, Speaker = 1054, Text = '我听它嘴里念叨着西门风车什么的…', Option = '谢谢你'}",
	[85583] = "{id = 85583, Speaker = 1067, Text = '啊…我采集样本的时候倒是见到过…如果我没看错它去的方向应该是吉芬。', Option = '多谢'}",
	[85584] = "{id = 85584, Speaker = 1340, Text = '哈哈哈，那头小野猪么，它在吉芬中可是很显眼呢，但是它好像去哥布灵森林找人了呢。'}",
	[85585] = "{id = 85585, Speaker = 1322, Text = '呵…那头摇摇晃晃的猪吗？我在妙勒尼山脉中看到它了…不过它似乎迷路了…'}",
	[85586] = "{id = 85586, Speaker = 1322, Text = '我看你得赶紧找到它，毕竟妙勒尼的那些魔物可不是吃素的！', Option = '明白'}",
	[85587] = "{id = 85587, Speaker = 0, Text = '不好，被魔物包围了！得赶紧找到咿维！'}",
	[85588] = "{id = 85588, Speaker = 0, Text = '可恶，又出现了…根本赶不完…'}",
	[85589] = "{id = 85589, Speaker = 6141, Text = '交给我来解决吧！'}",
	[85590] = "{id = 85590, Speaker = 6143, Text = '这讨厌的气味…可恶！！该死的摩根！'}",
	[85591] = "{id = 85591, Speaker = 6141, Text = '是不是很惊讶？哼唧…我在妙勒尼山脉碰到一位魔法师。'}",
	[85592] = "{id = 85592, Speaker = 6141, Text = '它交给我一个特殊的魔法药剂，可以驱散这些讨厌的蜂兵。'}",
	[85593] = "{id = 85593, Speaker = 6141, Text = '嘻嘻…没想到真的有效果…', Option = '你的样子…'}",
	[85594] = "{id = 85594, Speaker = 6141, Text = '那位魔法师知道了我想要去冒险的心思之后送给我的！酷不酷炫？', Option = '酷…'}",
	[85595] = "{id = 85595, Speaker = 6141, Text = '出来这么久，美羽一定很担心，我先回普隆德拉了，你也快来哦~', Option = '好的'}",
	[85596] = "{id = 85596, Speaker = 6141, Text = '感谢有你的陪伴，冒险者，但我得离开这里了。', Option = '去哪儿'}",
	[85597] = "{id = 85597, Speaker = 6141, Text = '去冒险！托你的福，我找到了冒险的意义。'}",
	[85598] = "{id = 85598, Speaker = 6141, Text = '我也想去找到可靠的同伴呢！我想我的冒险之旅一定非常精彩。'}",
	[85599] = "{id = 85599, Speaker = 6141, Text = '哼哧…不用担心啦，我有机会会回来看你的。', Option = '好吧'}",
	[85600] = "{id = 85600, Speaker = 6141, Text = '走之前，我想跟你合一次影，就在普隆德拉的喷泉。'}",
	[85601] = "{id = 85601, Speaker = 6141, Text = '我想，我会一辈子记住你的帮助的。', Option = '好吧'}",
	[85602] = "{id = 85602, Speaker = 6141, Text = '冒险者，你真是一个非常好的冒险者…我一定会成为你这样的冒险家。'}",
	[85603] = "{id = 85603, Speaker = 6141, Text = '到了那个时候，我会回来找你的！', Option = '我等你'}",
	[85604] = "{id = 85604, Speaker = 6141, Text = '记得帮我跟美羽打声招呼，告诉她，我走了。哼哧…那有机会再见咯…'}",
	[85605] = "{id = 85605, Speaker = 2160, Text = '咿维离开了吗…在它兴高采烈地跟我说它找到了冒险的意义之后，我就知道会有这么一天。'}",
	[85606] = "{id = 85606, Speaker = 2160, Text = '只是我没想到会这么快…呜呜呜…我会想念它的。'}",
	[85690] = "{id = 85690, Speaker = 6165, Text = '我亲爱的弟弟，把父王给你的皇室徽章给我看看。'}",
	[85691] = "{id = 85691, Speaker = 6166, Text = '我的皇室徽章…怎么想起来看这个了？'}",
	[85692] = "{id = 85692, Speaker = 6165, Text = '你就给我看看嘛！又不是不还给你。'}",
	[85693] = "{id = 85693, Speaker = 6166, Text = '这不太好，父王当初可是让我好好保管的呢。'}",
	[85694] = "{id = 85694, Speaker = 6165, Text = '真是麻烦…算了，反正也只剩下你一个了…'}",
	[85695] = "{id = 85695, Speaker = 6166, Text = '只剩我一个？', Emoji = 1}",
	[85696] = "{id = 85696, Speaker = 6166, Text = '难道二哥他们的失踪是你做的？'}",
	[85697] = "{id = 85697, Speaker = 6165, Text = '这是什么话？他们的失踪可与我无关，亲爱的弟弟，他们可是变成了臭名昭著的诅咒魔人啊！'}",
	[85698] = "{id = 85698, Speaker = 6165, Text = '为了不让他们在王都肆虐，我也只能选择忍痛消灭他们了。'}",
	[85699] = "{id = 85699, Speaker = 6166, Text = '无缘无故的，二哥他们怎么会变成诅咒魔人…难道是你？'}",
	[85700] = "{id = 85700, Speaker = 6166, Text = '难怪你想要我的皇室徽章…'}",
	[85701] = "{id = 85701, Speaker = 6165, Text = '不愧是我可爱的弟弟…也难怪父王整天在我们兄弟面前夸你…果然很聪明…'}",
	[85702] = "{id = 85702, Speaker = 6165, Text = '不错，诅咒术确实需要证明诅咒者身份的东西才能施展，但是这也不是必须的…'}",
	[85703] = "{id = 85703, Speaker = 6165, Text = '￥&……￥*&￥**#￥#……'}",
	[85704] = "{id = 85704, Speaker = 6166, Text = '不好！'}",
	[85705] = "{id = 85705, Speaker = 6165, Text = '接受我的诅咒吧！我亲爱的弟弟…'}",
	[85706] = "{id = 85706, Speaker = 6166, Text = '我不会让你得逞的！'}",
	[85707] = "{id = 85707, Speaker = 6165, Text = '可恶，还是让他跑了！代顿？出来！你为什么会让他走掉？'}",
	[85708] = "{id = 85708, Speaker = 6162, Text = '大王子你之前说的是真的吗？'}",
	[85709] = "{id = 85709, Speaker = 6165, Text = '把五王子给我带回来，快去！'}",
	[85710] = "{id = 85710, Speaker = 6162, Text = '……是。'}",
	[85721] = "{id = 85721, Speaker = 6165, Text = '代顿…五王子带来了吗？'}",
	[85722] = "{id = 85722, Speaker = 6162, Text = '被他跑掉了。'}",
	[85723] = "{id = 85723, Speaker = 6165, Text = '跑掉了？！没有抓到人你就敢来见我？'}",
	[85724] = "{id = 85724, Speaker = 6162, Text = '我错手杀了三位王子…我已经是罪人了…只是大殿下，给手足施加诅咒，你真的不后悔吗？'}",
	[85725] = "{id = 85725, Speaker = 6165, Text = '你这是在质疑我吗？你是不是忘记了你自己的身份？不过，事到如今，告诉你也没什么。'}",
	[85726] = "{id = 85726, Speaker = 6165, Text = '从父王卧病之时我就开始谋划，二殿下沃里森英勇善战，三殿下乔尔心思细腻，四殿下克罗多虽然玩心重，但是在处理政务方面，有独到之处！'}",
	[85727] = "{id = 85727, Speaker = 6165, Text = '至于五殿下格利，他的聪慧这王都谁不知道？他们的光芒太过耀眼，而父王却迟迟不立王储…'}",
	[85728] = "{id = 85728, Speaker = 6165, Text = '哼，我只能用我自己的手段来夺得王位，我才是王位唯一的继承者！'}",
	[85729] = "{id = 85729, Speaker = 6165, Text = '自从我在王宫宝库找到这个诅咒的戒指，我才接触到这让人迷醉的力量…你问我后不后悔？我当然不会后悔！'}",
	[85730] = "{id = 85730, Speaker = 6162, Text = '既然如此…那我没什么好说的了…'}",
	[85731] = "{id = 85731, Speaker = 6165, Text = '在你问出问题的时候，我就知道你这个信奉骑士精神的骑士一定会回来找我要说法……'}",
	[85732] = "{id = 85732, Speaker = 6165, Text = '说法我已经给你了，但是恐怕你也离不开这个王宫了！'}",
	[85733] = "{id = 85733, Speaker = 6165, Text = '代顿！你想做什么！还记得你信奉的骑士精神吗？骑士精神最重要的不是忠诚吗？'}",
	[85734] = "{id = 85734, Speaker = 6162, Text = '骑士精神最重要的并不是忠诚，而是正义。而我杀了王子们，我已经违背了骑士精神，我不能算是一个骑士了…'}",
	[85735] = "{id = 85735, Speaker = 6162, Text = '但是，我还是愿意为骑士精神贡献我自己最后的力量！'}",
	[85736] = "{id = 85736, Speaker = 6165, Text = '你要干什么！住手代顿！'}",
	[85737] = "{id = 85737, Speaker = 6162, Text = '虽然我已经不再是骑士了，但是正义将永存我心！'}",
	[85738] = "{id = 85738, Speaker = 6165, Text = '别！别！代顿！'}",
	[85739] = "{id = 85739, Speaker = 6162, Text = '该结束了…'}",
	[85740] = "{id = 85740, Speaker = 6166, Text = '代顿！'}",
	[85741] = "{id = 85741, Speaker = 6162, Text = '五王子，权力是心中的魔鬼，我相信，你一定能战胜魔鬼的，对吗？'}",
	[85742] = "{id = 85742, Speaker = 6166, Text = '是！我一定不会被权力左右的！'}",
	[85743] = "{id = 85743, Speaker = 6162, Text = '那就好…王子们，罪人代顿这就为自己赎罪！'}",
	[85744] = "{id = 85744, Speaker = 6166, Text = '不！'}",
	[85745] = "{id = 85745, Speaker = 6162, Text = '永别了…五殿下…'}",
	[85770] = "{id = 85770, Speaker = 6139, Text = '在接受王国的邀请来到这里的路上，我就曾遭遇到这样一头小野猪。'}",
	[85771] = "{id = 85771, Speaker = 6139, Text = '从它热诚的小眼睛里，我看到了冒险的光芒！'}",
	[85772] = "{id = 85772, Speaker = 6139, Text = '我很想帮助它！但是我身上还有着王国的使命。'}",
	[85773] = "{id = 85773, Speaker = 6139, Text = '你能够去代替我去实现它的愿望吗？它的名字叫做咿维，是的，小野猪咿维。'}",
	[85774] = "{id = 85774, Speaker = 6139, Text = '他每天都会在南门等待着你，直到庆典结束~所以冒险者，我很期待你跟它的相遇哦。'}",
	[85775] = "{id = 85775, Speaker = 6153, Text = '为了参加这次庆典，我们敲萌家族的成员很早就打扮好，准备在庆典中好好玩一玩呢。'}",
	[85776] = "{id = 85776, Speaker = 6153, Text = '但是有些调皮的敲萌家族成员竟然在这几天不见了，他们都说要在米德加尔特大陆好好玩一玩。'}",
	[85777] = "{id = 85777, Speaker = 6153, Text = '敲萌家族·匹格去了斐扬；敲萌家族·贝蒂去了吉芬地下；敲萌家族·卡隆去了迷藏森林；敲萌家族·基诺去了斐扬洞穴二层；敲萌家族·凯瑞去了兽人村；敲萌家族·艾西去了西门；敲萌家族·玛雅去了苏克拉特沙漠。'}",
	[85778] = "{id = 85778, Speaker = 6153, Text = '你们能拍照告诉我他们现在的情况吗？如果拍到了，记得来找我。我会给你准备丰厚的礼物。'}",
	[85779] = "{id = 85779, Speaker = 6153, Text = '哈哈，我就猜到，他们一定不会逃出冒险者的搜索的！'}",
	[85780] = "{id = 85780, Speaker = 6153, Text = '喏，这是你帮我寻找我们敲萌家族成员的奖励，请收好~'}",
	[85781] = "{id = 85781, Speaker = 6153, Text = '我们敲萌家族的那些调皮的成员都被你一一找到了呢。'}",
	[85782] = "{id = 85782, Speaker = 6153, Text = '我可要好好感谢你，这是我们敲萌家族的谢礼，请收好~'}",
	[85783] = "{id = 85783, Speaker = 6140, Text = '哼哧…我可是来参加庆典的，庆典期间我会一直在这里哦~'}",
	[85786] = "{id = 85786, Speaker = 6170, Text = '不留任何活口！！'}",
	[85787] = "{id = 85787, Speaker = 6170, Text = '平息怒气的海达姆渐渐露出了他本来的面貌'}",
	[85788] = "{id = 85788, Speaker = 6170, Text = '我还是第一次遇到能够打败我的冒险者。'}",
	[85818] = "{id = 85818, Speaker = 6170, Text = '通往异空间的大门已经被开启！'}",
	[85819] = "{id = 85819, Speaker = 6170, Text = '接下来就靠你了，冒险者。', Option = '好'}",
	[85828] = "{id = 85828, Speaker = 6173, Text = '要再次查看血雨之书吗', Option = '{需要,0},{不需要,1}'}",
	[85829] = "{id = 85829, Speaker = 0, Text = '石碑晃动纷纷震碎，在浓密的幽暗中，幻月魔女携着权杖露出战栗的微笑。'}",
	[85830] = "{id = 85830, Speaker = 6174, Text = '要进入血雨空间吗？', Option = '{现在就进,0},{没准备好,1}'}",
	[85839] = "{id = 85839, Speaker = 6176, Text = '要再次查看幻月之书吗', Option = '{需要,0},{不需要,1}'}",
	[85840] = "{id = 85840, Speaker = 0, Text = '石碑晃动纷纷震碎，在浓密的幽暗中，血雨魔女携着权杖露出战栗的微笑。'}",
	[85844] = "{id = 85844, Speaker = 6170, Text = '权杖与休密兹的封印激烈的碰撞！'}",
	[85847] = "{id = 85847, Speaker = 0, Text = '伴随着一声金属断裂声，魔剑冒出了阵阵黑烟，粉碎在空气中'}",
	[85851] = "{id = 85851, Speaker = 0, Text = '海达姆露出欣慰的微笑，渐渐随着魔剑也消失在空气中。'}",
	[85919] = "{id = 85919, Speaker = 6216, Text = '嗯？这里…它们不敢进入这里…'}",
	[85920] = "{id = 85920, Speaker = 6216, Text = '这里面难道隐藏着什么令它们害怕的东西吗？'}",
	[85921] = "{id = 85921, Speaker = 6216, Text = '这是？深渊骑士？'}",
	[85922] = "{id = 85922, Speaker = 6216, Text = '那是什么？…迷人的香气…说起来我已经很久没吃东西了…'}",
	[85923] = "{id = 85923, Speaker = 6216, Text = '真想念妮卡的烤肉啊…不管了，看样子，这个应该是能够食用的东西吧。'}",
	[85924] = "{id = 85924, Speaker = 6216, Text = '这是…啊——！'}",
	[85925] = "{id = 85925, Speaker = 6216, Text = '我感到现在充满了力量…等着吧！你们这群该死的魔物！'}",
	[85926] = "{id = 85926, Speaker = 6216, Text = '不！不不不不不……这不是我，我的脸…'}",
	[85934] = "{id = 85934, Speaker = 6221, Text = '…这么多，看来我这条命要交代在这里了……'}",
	[85935] = "{id = 85935, Speaker = 6219, Text = '…妮卡…看来我们今天都逃不掉了…'}",
	[85936] = "{id = 85936, Speaker = 6220, Text = '…能够和你一起死去也是一件开心的事情呢…'}",
	[85937] = "{id = 85937, Speaker = 6225, Text = '你们不会死的。'}",
	[85938] = "{id = 85938, Speaker = 6225, Text = '我的力量取之于你们，但是我拥有着一颗光明的心，黑暗的力量无法支配我！'}",
	[85939] = "{id = 85939, Speaker = 6225, Text = '现在，我就把这股力量还给你们！接受我最后的正义吧！'}",
	[85940] = "{id = 85940, Speaker = 6225, Text = '去死吧！'}",
	[85941] = "{id = 85941, Speaker = 6225, Text = '永别了，妮卡。永别了，阿尔顿斯。切尔西，照顾好妮卡…'}",
	[85942] = "{id = 85942, Speaker = 6220, Text = '你是…爱德华？你是爱德华对吗？'}",
	[85943] = "{id = 85943, Speaker = 6225, Text = '对不起…'}",
	[85944] = "{id = 85944, Speaker = 6220, Text = '不——！！'}",
	[86001] = "{id = 86001, Speaker = 4054, Text = '恶魔尝试者正在大量制造邪恶的傀儡，冒险者，请尽快赶来！'}",
	[86002] = "{id = 86002, Speaker = 4054, Text = '小巴风特★正在侵入古城大厅，我们需要30分钟的时间封印他们，请帮助我们抵御攻击！'}",
	[86003] = "{id = 86003, Speaker = 4054, Text = '我们发现了唤醒祭司！请尽快前来阻止他们的仪式，击败远古的魔物！'}",
	[86004] = "{id = 86004, Speaker = 4054, Text = '我们发现了唤醒祭司！请尽快前来阻止他们的仪式，击败远古的魔物！'}",
	[86005] = "{id = 86005, Speaker = 4054, Text = '封印已经完成！请协助我们驱逐剩余的小巴风特★吧，冒险者。'}",
	[86006] = "{id = 86006, Speaker = 4054, Text = '为……国王……复……仇'}",
	[86014] = "{id = 86014, Speaker = 4054, Text = '冒险者，远古的魔物已经苏醒，快击败它！'}",
	[86015] = "{id = 86015, Speaker = 6089, Text = '波伊从小就有一个愿望，那就是能够自由自在地在天空中展翅飞翔，可是波伊没有翅膀，这可是一直以来困扰波伊的事情呢。', Emoji = 15}",
	[86016] = "{id = 86016, Speaker = 6089, Text = '现在可好了，乘坐南门的波利大气球就可以飞上天空了，那种无拘无束的感觉一定很棒，波伊也想和朋友们一起去尝试呢！冒险者，如果你也对蓝天白云有着独特的情衷，为什么不邀请朋友们一起去感受这别样的快乐呢。'}",
	[86017] = "{id = 86017, Speaker = 6089, Text = '看你的表情就知道这是一次多么幸福的体验啊，你和朋友们在天空中一定经历了难忘的时光，说到这里，波伊也迫不及待想要和朋友们一起去看看了。'}",
	[86024] = "{id = 86024, Speaker = 6092, Text = '你知道吗？有些魔物看上去十分可爱，但这往往只是蒙蔽敌人的手段，在敌人疏忽大意的瞬间，它们就会发出凶狠致命的攻击。'}",
	[86025] = "{id = 86025, Speaker = 6092, Text = '魔菇就是这种极具欺诈性的魔物，它们的可爱面庞下隐藏着一颗嗜血的心。冒险者，请你去消灭一些这种极善伪装的魔物，当然，为了安全起见请带上你的朋友一起行动。'}",
	[86026] = "{id = 86026, Speaker = 6092, Text = '冒险者你做得很好，其实我一直在思考人类与魔物之间的关系，当人类面对魔物的威胁，就需要精诚团结，以集体的力量来应对随时可能发生的危机。'}",
	[86102] = "{id = 86102, Speaker = 0, Text = '空中突然传来一名老者慈祥的嘱托声'}",
	[86103] = "{id = 86103, Speaker = 0, Text = '请把我带去给我亲爱的赫卡珊，替我抚平她的眼泪。'}",
	[86216] = "{id = 86216, Speaker = 6256, Text = '哼，没想到你今天还带了帮手！'}",
	[86217] = "{id = 86217, Speaker = 6269, Text = '可恶！！！'}",
	[86218] = "{id = 86218, Speaker = 6255, Text = '这次我不会再犯相同的错误的！！'}",
	[86219] = "{id = 86219, Speaker = 6269, Text = '不！！！'}",
	[86220] = "{id = 86220, Speaker = 6255, Text = '冒险者！！你快走吧！你已经帮了我很多了！'}",
	[86221] = "{id = 86221, Speaker = 6255, Text = '这次我会与这个魔鬼同归于尽，让他永不见天日！！', Option = '致敬！'}",
	[86222] = "{id = 86222, Speaker = 6255, Text = '谢谢你！王国的荣耀将因你延续！！'}",
	[86223] = "{id = 86223, Speaker = 6255, Text = '接下来就交给我吧！！', Option = '好！'}",
	[86224] = "{id = 86224, Speaker = 6266, Text = '啦啦啦~呜呜~~啦啦啦~~~哎呀呀呀呀~~~这位旅行者~贵安~这样美好的日子里，你是否愿意停下脚步来让我为您演奏一曲关于女神的诗篇？', Option = '{请开始表演,0},{没兴趣…,1}'}",
	[86225] = "{id = 86225, Speaker = 6266, Text = '呜啦啦啦~~~真棒~你愿意聆听这诗歌，那我就开始了~'}",
	[86226] = "{id = 86226, Speaker = 6266, Text = '古之往昔，黄金年代终告完结。罪与罚降临世间，世界之树的枝芽也在叹息着时光。'}",
	[86227] = "{id = 86227, Speaker = 6266, Text = '三位女神告诫着世间的人们，[c][ffff00]勿忘罪恶和黑暗的存在[-][/c]、[c][ffff00]将真实的现在握于手心[-][/c]、[c][ffff00]勇敢开拓无人知晓的未来[-][/c]。'}",
	[86228] = "{id = 86228, Speaker = 6266, Text = '如今女神的祝福与期待依然留存于人间。被继承的心灵将为人们带来福音。其之诗篇分为三，尊敬的旅行者，您想听哪段？', Option = '{恋爱的季节,2},{星座的指引,3},{冒险的极意,4},{稍后再说,1}'}",
	[86229] = "{id = 86229, Speaker = 6266, Text = '其之一，繁华的首都，恋爱中的少年少女，苛求幸福和心之桥的相通。在王城某处，名为[c][ffff00]乌尔德[-][/c]的女神的继承者将为您讲述古往今来的物语。'}",
	[86230] = "{id = 86230, Speaker = 6266, Text = '其之一，古老的沙尘中的绿洲之都，流传着往昔的人们观察星空所得到的启示，去找女神[c][ffff00]贝尔丹迪[-][/c]吧，她会为你解读神秘的星空与你的联系。'}",
	[86231] = "{id = 86231, Speaker = 6266, Text = '其之一，远离尘世的东方海岛，被以波利冠名。那里记录着被书写的冒险者旅人们的新时代的篇章，想要探知神秘的未来，便去寻找女神[c][ffff00]诗蔻迪[-][/c]吧~'}",
	[86254] = "{id = 86254, Speaker = 6270, Text = '恭喜你做出了正确的选择，完成了贪婪之狱考验。'}",
	[86328] = "{id = 86328, Speaker = 4054, Text = '飞行魔书的死亡激怒了飞行魔书★，它们已经出现在了古城大厅！'}",
	[86332] = "{id = 86332, Speaker = 1694, Text = '嗯~竟然胆敢打扰本王优雅的午睡…呼，抬起头来，人类，说出你的渴求。让本王看看是否能满足你！'}",
	[86351] = "{id = 86351, Speaker = 0, Text = '时间好像回到了几百年前……'}",
	[86352] = "{id = 86352, Speaker = 6288, Text = '哥哥，你的决定是错误的！'}",
	[86353] = "{id = 86353, Speaker = 6287, Text = '别担心，我亲爱的妹妹，我马上就要获得永生的力量了！'}",
	[86354] = "{id = 86354, Speaker = 0, Text = '这时他们周围似乎传来海拉邪恶的低语。'}",
	[86355] = "{id = 86355, Speaker = 6289, Text = '查普曼·艾伯特，你准备好和我签约，接受永生的力量了吗？'}",
	[86356] = "{id = 86356, Speaker = 6289, Text = '哈哈哈哈，查普曼·艾伯特！你的时间将永远停在这一天。'}",
	[86357] = "{id = 86357, Speaker = 6289, Text = '作为代价，你的妹妹将被我的手下困在时间的夹缝中，永远替你停住你的时间！！'}",
	[86358] = "{id = 86358, Speaker = 6287, Text = '什么！！'}",
	[86359] = "{id = 86359, Speaker = 6288, Text = '哥哥救我！！'}",
	[86360] = "{id = 86360, Speaker = 6287, Text = '不！！！！'}",
	[86402] = "{id = 86402, Speaker = 6297, Text = '哪里来的人类在此放肆！！'}",
	[86404] = "{id = 86404, Speaker = 6297, Text = '好吧好吧，我知道你来此的目的。', Option = '救人'}",
	[86405] = "{id = 86405, Speaker = 6297, Text = '我也是被海拉逼迫看守这里，至于怎么通过这些陷阱，我只能给你点提示。', Option = '快说'}",
	[86406] = "{id = 86406, Speaker = 6297, Text = '你无法停止所有时间的利刃，但对的开关将使你打开时间的枷锁。'}",
	[86407] = "{id = 86407, Speaker = 6288, Text = '你是来解救我的吗？', Option = '是的'}",
	[86408] = "{id = 86408, Speaker = 6288, Text = '那我们快离开这个鬼地方吧！！'}",
	[86422] = "{id = 86422, Speaker = 6302, Text = '全新的斗技场与最严格的斗技规则已经到来，我们会全面的管理斗技场的一切，当然在斗技场表现抢眼的冒险者，就会得到一些特殊的奖励。'}",
	[86459] = "{id = 86459, Speaker = 4572, Text = '你需要回答石像三个问题，它才会现身。', Option = '我去试试'}",
	[86461] = "{id = 86461, Speaker = 4573, Text = '先回答我的问题吧！'}",
	[86471] = "{id = 86471, Speaker = 0, Text = '看样子我是真的缩到很小进入了桑德亚的毛发之间。'}",
	[86493] = "{id = 86493, Speaker = 4569, Text = '波利~波利~'}",
	[86494] = "{id = 86494, Speaker = 4570, Text = '玻利~玻利~'}",
	[86495] = "{id = 86495, Speaker = 4571, Text = '波力~波力~'}",
	[86502] = "{id = 86502, Speaker = 4574, Text = '什么？你还想来帮本喵挠痒痒……你准备好了吗', Option = '{准备好了,0},{等一下,1}'}",
	[86504] = "{id = 86504, Speaker = 4576, Text = '左边戴着天波发夹的那只叫波利，右边头上飘着鬼火的那只叫波力，中间的那只呢就叫玻利，请记住他们各自最后所处的位置哦'}",
	[86515] = "{id = 86515, Speaker = 6305, Text = '这怎么可能，他竟然在吸收时空裂变的能量……这是……伊米尔之心的力量！'}",
	[86516] = "{id = 86516, Speaker = 6306, Text = '散逸的能量正在破坏钟楼的空间，周围的魔物似乎变得更加强大了！'}",
	[86517] = "{id = 86517, Speaker = 6307, Text = '危险！阻止他！'}",
	[86518] = "{id = 86518, Speaker = 6315, Text = '你们竟敢闯入时空的禁地！'}",
	[86519] = "{id = 86519, Speaker = 6315, Text = '不过在这里，你们要面对的敌人可不是我！哈哈哈！'}",
	[86520] = "{id = 86520, Speaker = 6307, Text = '该死的，我们无法阻止这一切，时空裂变的力量太强大了。'}",
	[86521] = "{id = 86521, Speaker = 6305, Text = '不管怎么样，探索的任务已经完成，我们得赶快离开这里，艾尔帕兰的人们需要知道事情的真相。'}",
	[86527] = "{id = 86527, Speaker = 6307, Text = '糟了，我们似乎被成群的魔物包围了！'}",
	[86528] = "{id = 86528, Speaker = 6305, Text = '都是我的错，如果我不去触碰这座古怪的石碑，就不会引起魔物的警觉了……'}",
	[86529] = "{id = 86529, Speaker = 6305, Text = '伙伴们，很抱歉，是我连累了你们……'}",
	[86530] = "{id = 86530, Speaker = 6306, Text = '这可不像你呢，无论什么时候，你都不会轻言放弃的！'}",
	[86531] = "{id = 86531, Speaker = 6305, Text = '可是这次……'}",
	[86532] = "{id = 86532, Speaker = 6307, Text = '吉娜说得没错，现在可不是道歉的时候，趁着魔物们还没有反应过来，我们得想点办法！'}",
	[86533] = "{id = 86533, Speaker = 6316, Text = '你们竟敢擅闯时空的禁地，无视神意的后果便要接受残酷的审判。'}",
	[86534] = "{id = 86534, Speaker = 6306, Text = '恶魔，你虚伪的内心在颤抖呢，你的身躯不堪一击，你的灵魂正在备受煎熬！'}",
	[86535] = "{id = 86535, Speaker = 6307, Text = '艾尔帕兰的人们不会坐视邪恶的滋生，钟楼的天空将会变得纯净空明。'}",
	[86536] = "{id = 86536, Speaker = 6316, Text = '狂妄的人类，愚昧和无知会把你们推向毁灭的深渊！'}",
	[86537] = "{id = 86537, Speaker = 6316, Text = '而时空的法则，将永远掌控在我的手中！'}",
	[86538] = "{id = 86538, Speaker = 6316, Text = '欢迎加入我们……哈哈哈！！'}",
	[86539] = "{id = 86539, Speaker = 6305, Text = '不！！吉娜！安吉斯！你对他们做了什么！'}",
	[86540] = "{id = 86540, Speaker = 6316, Text = '这便是诋毁神意的后果，在这片时空里，谁也不能抗拒我的力量。'}",
	[86541] = "{id = 86541, Speaker = 6305, Text = '恶魔……我不会放任自己堕入黑暗的深渊，你控制不了我！'}",
	[86542] = "{id = 86542, Speaker = 6305, Text = '再见了，我的……伙伴们！'}",
	[86548] = "{id = 86548, Speaker = 6305, Text = '这里出奇的安静，难道这是我产生的错觉？'}",
	[86549] = "{id = 86549, Speaker = 6306, Text = '我们找遍了钟楼的每个角落，但传闻中的异变并没有任何的征兆显示，事实证明所谓的异变只是一场失控的谣传！'}",
	[86550] = "{id = 86550, Speaker = 6307, Text = '我想我们需要更深入地进入钟楼，伙伴们，我们身上肩负的可是整个艾尔帕兰的安危呢！'}",
	[86551] = "{id = 86551, Speaker = 6305, Text = '真是精美的工艺，看看这座金钟，这是怎样的工匠才能做出这样精致巧妙的设计啊！'}",
	[86552] = "{id = 86552, Speaker = 6306, Text = '很难想象在这充满艺术感的殿堂里会有什么骇人听闻的事情发生！'}",
	[86553] = "{id = 86553, Speaker = 6307, Text = '有些事情并不能光看表面，伙伴们，我想我们需要加快速度了。'}",
	[86554] = "{id = 86554, Speaker = 6305, Text = '这里竟然有些古怪的痕迹，难道我们之前忽略了什么关键线索！'}",
	[86555] = "{id = 86555, Speaker = 6306, Text = '别紧张，我想这块石碑上的痕迹只是一种古老的记时方法，时至今日，已经很少有人能够识别出了。'}",
	[86556] = "{id = 86556, Speaker = 6307, Text = '难道这次的探险真的一无所获？还是我太多心了呢……'}",
	[86557] = "{id = 86557, Speaker = 6305, Text = '我想探索的结果已经显而易见了，我要把事情的真相带给艾尔帕兰的人们，我可是最伟大的探险家！'}",
	[86558] = "{id = 86558, Speaker = 6306, Text = '虽然这么说有些无礼，但这确实是最无聊的探险呢。'}",
	[86559] = "{id = 86559, Speaker = 6307, Text = '不得不承认，探险的结果令我有些失望，但认真地对待每次探险正是我一贯的原则。'}",
	[86560] = "{id = 86560, Speaker = 6307, Text = '好了，伙伴们，我想是时候把我们的探险结果带给艾尔帕兰的人们了，在钟楼里并没有任何的反常，相反这是一个……很美丽的地方。'}",
	[86568] = "{id = 86568, Speaker = 6325, Text = '你竟然逃出了时空的界限，这不可能，没有人比我更了解时空的奥秘！'}",
	[86569] = "{id = 86569, Speaker = 6325, Text = '在这片空间，我才是主宰一切的神！'}",
	[86570] = "{id = 86570, Speaker = 6305, Text = '不，你错了，你只是一个惧怕失败的胆小鬼！'}",
	[86571] = "{id = 86571, Speaker = 6305, Text = '你的虚伪外表掩饰不了内心的软弱，你的自大已经把你推向了死亡的边缘！'}",
	[86572] = "{id = 86572, Speaker = 6305, Text = '你试图利用时空的异变来掩饰自己的丑恶，但混乱的时空里掩埋的不仅有无数无辜者的尸体，还有你已经泯灭的良知。'}",
	[86573] = "{id = 86573, Speaker = 6325, Text = '渺小的人类，你们根本不懂什么是力量！'}",
	[86574] = "{id = 86574, Speaker = 6325, Text = '当我利用伊米尔之心的力量来改造这个世界的时候，我的心中无所畏惧！'}",
	[86575] = "{id = 86575, Speaker = 6325, Text = '看看吧，裂变的空间正向我们展示着最无限的可能性，时间的漩涡创造出无数平行的宇宙，而涌动的异变能量使生命体变得更加强大。'}",
	[86576] = "{id = 86576, Speaker = 6305, Text = '是啊，我永远不会忘记同伴们的身影消失在时空甬道的那一刻，即使每一个平行时空都是截然不同的世界，但他们仍然是我生命中最亲密的伙伴！'}",
	[86577] = "{id = 86577, Speaker = 6305, Text = '恶魔，如果这就是你的手段，那么赌上探险者的尊严，我也不会让这时空的黑洞毁灭钟楼的一切！'}",
	[86578] = "{id = 86578, Speaker = 6325, Text = '你以为这样就能与我抗衡？'}",
	[86579] = "{id = 86579, Speaker = 6305, Text = '不，这还不够！！'}",
	[86580] = "{id = 86580, Speaker = 6305, Text = '就快……成功了！'}",
	[86581] = "{id = 86581, Speaker = 6325, Text = '你想要和我同归于尽？'}",
	[86582] = "{id = 86582, Speaker = 6325, Text = '你的勇气的确令人敬佩，但在这里，你不可能是我的对手！'}",
	[86583] = "{id = 86583, Speaker = 6325, Text = '为了更强大的力量，没人可以阻止我！！'}",
	[86584] = "{id = 86584, Speaker = 6305, Text = '终归还是失败了吗……'}",
	[86585] = "{id = 86585, Speaker = 6305, Text = '伙伴们，对不起！我这就来和你们汇合……'}",
	[86626] = "{id = 86626, Speaker = 6342, Text = '为了骑士的尊严，为了王国的未来，勇士们，拔出你们的武器，让这些恶魔见识见识我们的力量！'}",
	[86627] = "{id = 86627, Speaker = 6341, Text = '亡者的力量正在腐蚀大地，愤怒的嘶吼已经响彻云表，贪婪的欲望必将把你们送入堕落的深渊！'}",
	[86628] = "{id = 86628, Speaker = 6342, Text = '该死的，难道这些亡灵会无穷无尽的出现吗！'}",
	[86629] = "{id = 86629, Speaker = 6341, Text = '交出伊米尔之心碎片，否则亡者的愤怒会让这里血流成河！'}",
	[86630] = "{id = 86630, Speaker = 6342, Text = '恶魔，即使是粉身碎骨，我也不会坐视你得到更强大的力量，对王国的安全造成威胁。'}",
	[86631] = "{id = 86631, Speaker = 6341, Text = '你这是在自找死路！接受亡者的审判吧！'}",
	[86632] = "{id = 86632, Speaker = 6344, Text = '大人，事情似乎发生了变故，我们守护的那块碎片竟然无故消失了。'}",
	[86633] = "{id = 86633, Speaker = 6342, Text = '这究竟是怎么回事！'}",
	[86634] = "{id = 86634, Speaker = 6341, Text = '恩？伊米尔之心的气息……竟然消失了……'}",
	[86635] = "{id = 86635, Speaker = 6341, Text = '不！！给我找出那块碎片，亡灵的勇士们！'}",
	[86656] = "{id = 86656, Speaker = 6350, Text = '该死的，这究竟是什么东西！'}",
	[86657] = "{id = 86657, Speaker = 6350, Text = '托马斯，危险，快离开这里！'}",
	[86658] = "{id = 86658, Speaker = 6339, Text = '不！亚尔林！！'}",
	[86659] = "{id = 86659, Speaker = 6342, Text = '很抱歉，我们似乎来晚了！'}",
	[86660] = "{id = 86660, Speaker = 6339, Text = '这块该死的碎片害死了亚尔林，这个可怜的家伙……我要毁了它！'}",
	[86661] = "{id = 86661, Speaker = 6342, Text = '住手！这块碎片拥有强大的能量，我要把它交给王国的研究机构。'}",
	[86662] = "{id = 86662, Speaker = 6342, Text = '亚尔林是个英雄，他拯救了你，说不定也将会为王国带来伟大的发现。'}",
	[86663] = "{id = 86663, Speaker = 6342, Text = '能够为王国的荣耀献上自己的生命，值得我们尊敬！'}",
	[86678] = "{id = 86678, Speaker = 6354, Text = '你是谁？你想干什么！'}",
	[86679] = "{id = 86679, Speaker = 6350, Text = '嘿嘿，这么快就认不出我了吗？'}",
	[86680] = "{id = 86680, Speaker = 6354, Text = '不，亚尔林，白天的事我只是和你开玩笑，你忘了吗？我们可是朋友！'}",
	[86681] = "{id = 86681, Speaker = 6350, Text = '我忘不了你这个朋友对我的侮辱，而我已经不是那个没用的废物了！'}",
	[86694] = "{id = 86694, Speaker = 6343, Text = '是谁躲在那里！'}",
	[86695] = "{id = 86695, Speaker = 6351, Text = '嘿嘿，果然是完美的计划！'}",
	[86696] = "{id = 86696, Speaker = 6351, Text = '这强大的力量……是属于我的了！'}",
	[86743] = "{id = 86743, Speaker = 6367, Text = '如果这次我们…我们能够出去…我、我一定要涨价！'}",
	[86744] = "{id = 86744, Speaker = 6371, Text = '……'}",
	[86745] = "{id = 86745, Speaker = 6370, Text = '你们还在等什么？快动手！'}",
	[86746] = "{id = 86746, Speaker = 6372, Text = '没用的！'}",
	[86747] = "{id = 86747, Speaker = 6372, Text = '想让我放过你们，也不是不可以…长久以来，我深居钟楼，太过寂寞了……'}",
	[86748] = "{id = 86748, Speaker = 6372, Text = '你们留下一个人陪我吧！'}",
	[86759] = "{id = 86759, Speaker = 0, Text = '这个箱子里都是金币……'}",
	[86760] = "{id = 86760, Speaker = 0, Text = '这个箱子里都是珠宝……'}",
	[86761] = "{id = 86761, Speaker = 0, Text = '这个箱子里都是名贵的艺术品……'}",
	[86762] = "{id = 86762, Speaker = 0, Text = '这是一个破碎的时钟，时钟显示的时间是六点…'}",
	[86773] = "{id = 86773, Speaker = 6368, Text = '事情到了这一步，已经无法挽回了，你的报酬我不要了，我们就此分别吧。'}",
	[86774] = "{id = 86774, Speaker = 6370, Text = '我可以保护你！只要你陪我一起离开这个见鬼的钟楼！'}",
	[86775] = "{id = 86775, Speaker = 6368, Text = '你也会害怕吗？我不需要你的保护。'}",
	[86776] = "{id = 86776, Speaker = 6368, Text = '是你回来了吗？', Option = '是我'}",
	[86777] = "{id = 86777, Speaker = 6368, Text = '没想到你变成了现在的模样，你是想看到我求饶的模样吗？'}",
	[86778] = "{id = 86778, Speaker = 6368, Text = '让你失望了，我不会道歉求饶，我做的没有错，我也只是想活下去。我的性命比你珍贵的多。'}",
	[86779] = "{id = 86779, Speaker = 6373, Text = '你的傲慢会让你受尽折磨。'}",
	[86780] = "{id = 86780, Speaker = 6373, Text = '在诅咒中嚎叫着死去吧！'}",
	[86789] = "{id = 86789, Speaker = 6369, Text = '怎么？想报仇？来啊！动手杀了我啊！我看你有没有这样的能力！'}",
	[86790] = "{id = 86790, Speaker = 6373, Text = '是吗？你会死去的，死在他们手里！'}",
	[86791] = "{id = 86791, Speaker = 6373, Text = '在无尽的魔物中哀嚎着死去吧！'}",
	[86800] = "{id = 86800, Speaker = 6370, Text = '没想到时间支配者这么强大，更没想到我这次的计划会破坏在你的手上。'}",
	[86801] = "{id = 86801, Speaker = 6370, Text = '或许我应该选择其他的人献给时间支配者。'}",
	[86802] = "{id = 86802, Speaker = 6373, Text = '从一开始你就没想过让我们活着离开钟楼吧？你这个奸诈的小人。'}",
	[86803] = "{id = 86803, Speaker = 6370, Text = '当然，你难道天真的以为你们能够得到我许诺给你们的东西吗？'}",
	[86804] = "{id = 86804, Speaker = 6373, Text = '比起我，你才是真正的魔鬼，伯纳德，你知道为什么在离开的时候没有一个人愿意陪在你身边吗？'}",
	[86805] = "{id = 86805, Speaker = 6373, Text = '他们多少都明白你的心思，你注定是被他们舍弃的。'}",
	[86806] = "{id = 86806, Speaker = 6370, Text = '要动手了吗？我不会坐以待毙的！'}",
	[86807] = "{id = 86807, Speaker = 6370, Text = '而你呢？魔鬼。你又是什么样的人？'}",
	[86816] = "{id = 86816, Speaker = 6371, Text = '失去了灵魂的人怎么能够继续存在这世间呢？来吧…给我一个解脱！'}",
	[86817] = "{id = 86817, Speaker = 6371, Text = '是什么让我变成了这幅样子呢？让我们来看看吧。'}",
	[86818] = "{id = 86818, Speaker = 6367, Text = '看着我干什么！虽然我没有战斗能力，但是我对这里熟悉无比！没有我你们走不出这里！'}",
	[86819] = "{id = 86819, Speaker = 6368, Text = '哼，我也是这个意思，如果想留下我们，你们也走不了！'}",
	[86820] = "{id = 86820, Speaker = 6369, Text = '那还用想什么！留下黑鲁！只要我们抢走他身上的药剂，他对我们来说已经没有任何利用价值了！'}",
	[86821] = "{id = 86821, Speaker = 6370, Text = '那就决定是黑鲁了。时间支配者，我们商议决定，留下药剂师。'}",
	[86822] = "{id = 86822, Speaker = 6372, Text = '你们已经决定了？很好，你们可以走了！'}",
	[86823] = "{id = 86823, Speaker = 6372, Text = '黑鲁，我需要你的灵魂，但是你帮助了我，我也愿意为你做一些事……比如，把抛下你的人永远留在这里！'}",
	[86824] = "{id = 86824, Speaker = 6371, Text = '嘿…我还能怎么选？我当然是选择留下他们了！结果你也看到了。死亡丧钟响起，没有人能够逃脱这里。'}",
	[86825] = "{id = 86825, Speaker = 6371, Text = '而我，以前是个懦弱的人，现在依然是，没有时间支配者赐予的力量，我什么都不是。'}",
	[86826] = "{id = 86826, Speaker = 6371, Text = '就这样结束吧！愿死亡的丧钟不再响起。'}",
