	[87255] = "{id = 87255, Speaker = 6400, Text = '阿奇索伦，我们回去吧？看样子那个鬼魅今天并没有出现……'}",
	[87256] = "{id = 87256, Speaker = 6401, Text = '是啊，我们回去吧…这里阴森森的，怪可怕的…'}",
	[87257] = "{id = 87257, Speaker = 6399, Text = '直觉告诉我，那个鬼魅说不定就是斯科特！我们多久没有见到斯科特了？'}",
	[87258] = "{id = 87258, Speaker = 6399, Text = '再等等…也许鬼魅就快出现了呢！'}",
	[87259] = "{id = 87259, Speaker = 6400, Text = '好吧…等等！不对！好像有什么东西过来了，数量还不少！'}",
	[87260] = "{id = 87260, Speaker = 6400, Text = '可恶，怎么越杀越多！我们分开跑！去钟楼门口集合！'}",
	[87261] = "{id = 87261, Speaker = 6399, Text = '德斯黎你要小心！'}",
	[87262] = "{id = 87262, Speaker = 6401, Text = '好！'}",
	[87274] = "{id = 87274, Speaker = 6404, Text = '这次的收获不少啊，嘿，这些材料够我喝半个月好酒了！'}",
	[87275] = "{id = 87275, Speaker = 6400, Text = '是啊！嘿嘿，我有钱给蒙妮卡买好一点的法杖了！'}",
	[87276] = "{id = 87276, Speaker = 6400, Text = '等等！那是……'}",
	[87277] = "{id = 87277, Speaker = 6400, Text = '斯科特！小心！'}",
	[87278] = "{id = 87278, Speaker = 6404, Text = '天啊！它在吞噬我！救我！德斯黎！'}",
	[87279] = "{id = 87279, Speaker = 6400, Text = '撑住，我的朋友！'}",
	[87280] = "{id = 87280, Speaker = 6404, Text = '它在撕扯着我的身体！…不！这样下去我们会一起死的！'}",
	[87281] = "{id = 87281, Speaker = 6400, Text = '你是想？？不！斯科特！'}",
	[87291] = "{id = 87291, Speaker = 6399, Text = '德斯黎…斯科特…我一定会为你们报仇的！'}",
	[87292] = "{id = 87292, Speaker = 6399, Text = '再等等…这个该死的鬼影一定会出现的！'}",
	[87293] = "{id = 87293, Speaker = 6399, Text = '来了，为我的朋友们偿命吧！神秘的鬼影！'}",
	[87294] = "{id = 87294, Speaker = 6399, Text = '哈！你也有今天！…面具？？'}",
	[87295] = "{id = 87295, Speaker = 6399, Text = '让我来看看你究竟是谁！'}",
	[87296] = "{id = 87296, Speaker = 6399, Text = '怎么是你？？'}",
	[87297] = "{id = 87297, Speaker = 6401, Text = '还是被你发现了……没想到吧？阿奇索伦？'}",
	[87307] = "{id = 87307, Speaker = 6401, Text = '阿奇索伦，小心！'}",
	[87308] = "{id = 87308, Speaker = 6400, Text = '又是时间黑洞！快！阿奇索伦，抓紧我！'}",
	[87309] = "{id = 87309, Speaker = 6399, Text = '我！我抓不到你的手！'}",
	[87310] = "{id = 87310, Speaker = 6400, Text = '我绝对不会允许，这样的事情发生第二次！'}",
	[87311] = "{id = 87311, Speaker = 6401, Text = '不！德斯黎！不要！'}",
	[87312] = "{id = 87312, Speaker = 6399, Text = '德斯黎！你！'}",
	[87313] = "{id = 87313, Speaker = 6400, Text = '阿奇索伦！带蒙妮卡出去！'}",
	[87314] = "{id = 87314, Speaker = 6400, Text = '蒙妮卡，影子的事…只能拜托你了…'}",
	[87315] = "{id = 87315, Speaker = 6401, Text = '德斯黎……'}",
	[87316] = "{id = 87316, Speaker = 6399, Text = '走！刚才的动静惊扰到了魔物！'}",
	[87338] = "{id = 87338, Speaker = 6405, Text = '该死该死，明明就差这么一点点，这魔药就完成了！！'}",
	[87339] = "{id = 87339, Speaker = 6405, Text = '……'}",
	[87340] = "{id = 87340, Speaker = 6392, Text = '怎么回事他好像看不见我们。'}",
	[87341] = "{id = 87341, Speaker = 6392, Text = '可恶，这可能是哥哥之前说的半成品。', Option = '半成品？'}",
	[87342] = "{id = 87342, Speaker = 6392, Text = '也就是说我们没有真正回到过去，只是看到过去的影像而已。'}",
