	[87344] = "{id = 87344, Speaker = 6406, Text = '高斯林！我不会让你把研究完成的！'}",
	[87345] = "{id = 87345, Speaker = 6405, Text = '呃啊啊啊……'}",
	[87346] = "{id = 87346, Speaker = 0, Text = '神秘巫师转眼间消失的无影无踪。', Option = '回到现实'}",
