	[87368] = "{id = 87368, Speaker = 6393, Text = '哈哈哈哈哈，有了这瓶魔药，我就可以统治世界。'}",
	[87369] = "{id = 87369, Speaker = 6414, Text = '哈哈哈哈哈我要杀光所有人！'}",
