	[90100] = "{id = 90100, Speaker = 1024, Text = '哦吼~是[c][ffff00][PlayerName][-][/c]啊，想不到你那么快就脱离菜鸟成为了一名了不起的冒险家了。', Emoji = 7}",
	[90101] = "{id = 90101, Speaker = 1024, Text = '最近关于你的冒险传闻从斐扬到朱诺无人不知无人不晓。[c][ffff00]冒险者公会[-][/c]的众人都会铭记你所带来的荣耀。', Emoji = 9}",
	[90102] = "{id = 90102, Speaker = 1024, Text = '想必你不会止步于此吧，嗯，是时候回去找你的职业导师聊聊了，说不定他会为你指明一条新的未来航路。', Emoji = 7}",
	[90103] = "{id = 90103, Speaker = 1101, Text = '嗯！我的朋友，你已经成为一名能够独当一面的合格[c][ffff00]骑士[-][/c]了！为了守护大陆和人民你所付出的努力绝不会被遗忘！荣耀与骑士团同在！', Emoji = 6}",
	[90104] = "{id = 90104, Speaker = 1101, Text = '该是你成为一名[c][ffff00]骑士领主[-][/c]的时候了。如果确定了就开始职业宣誓吧！', Option = '{开始测试,0}{稍等,2}', Emoji = 26}",
	[90105] = "{id = 90105, Speaker = 1101, Text = '嗯！很好！我的朋友！跟我来吧！荣耀的时刻！', Option = '{是否接受转职,6}{还没准备好,5}', Emoji = 23}",
	[90106] = "{id = 90106, Speaker = 1101, Text = '就让我们一起见证一名伟大的[c][ffff00]骑士领主[-][/c]的诞生吧！愿荣耀与骑士团同在！', Option = '开始转职', Emoji = 7}",
	[90107] = "{id = 90107, Speaker = 1101, Text = '是否要离开转职大厅？', Option = '{离开这里,2}{稍等一下,1}', Emoji = 8}",
	[90108] = "{id = 90108, Speaker = 1102, Text = '(看你的眼神明显变得尊敬起来)', Emoji = 14}",
	[90109] = "{id = 90109, Speaker = 1102, Text = '呼呼呼，是[c][ffff00][PlayerName][-][/c]啊，魔导力量的渴求者，看来你在探索之路上已经有了很大的进展。', Emoji = 25}",
	[90110] = "{id = 90110, Speaker = 1102, Text = '时机差不多也到了你该继续走向真相的那一步了，来成为[c][ffff00]超魔导士[-][/c]吧！', Emoji = 11}",
	[90111] = "{id = 90111, Speaker = 1102, Text = '(杜赫塔开始咏唱奇怪的咒文，但是你开始尝试跟着读懂它们)', Option = '{开始跟着咏唱,0}{稍等,2}', Emoji = 14}",
	[90112] = "{id = 90112, Speaker = 1102, Text = '呼呼呼，我将精神力的魔导元素传达给你的触媒了，希望他能协助你成为达到真相的钥匙。接下来就让我们开始仪式吧。', Option = '{是否接受转职,6}{还没准备好,5}', Emoji = 25}",
	[90113] = "{id = 90113, Speaker = 1102, Text = '就让我们一起见证一名伟大的[c][ffff00]超魔导士[-][/c]的诞生吧！永远勿忘真理和正义！', Option = '开始转职', Emoji = 20}",
	[90114] = "{id = 90114, Speaker = 1102, Text = '是否要离开转职大厅？', Option = '{离开这里,2}{稍等一下,1}', Emoji = 8}",
	[90115] = "{id = 90115, Speaker = 1103, Text = '…真没想到，[c][ffff00][PlayerName][-][/c]，你居然还活着，并且还做了不少让我也觉得刮目相看的事啊~', Emoji = 14}",
	[90116] = "{id = 90116, Speaker = 1103, Text = '哼哼哼…既然如此，想要成为[c][ffff00]十字刺客[-][/c]这点我会给予你最大的帮助的。', Emoji = 14}",
	[90117] = "{id = 90117, Speaker = 1103, Text = '当然~前提是你依然坚持，即使会掉丢性命也要如此做的话。', Option = '{开始测试,0}{稍等,2}', Emoji = 5}",
	[90118] = "{id = 90118, Speaker = 1103, Text = '哼哼哼…看来这些你已近了如指掌了。不怕死就跟我来吧，这一路我都会继续好好看着你的呢。哼哼哼。', Option = '{是否接受转职,6}{还没准备好,5}', Emoji = 14}",
	[90119] = "{id = 90119, Speaker = 1103, Text = '这个人的生命已经与我们刺客联盟血脉联系在一起，是时候让他成为一名[c][ffff00]十字刺客[-][/c]了！', Option = '开始转职', Emoji = 11}",
	[90120] = "{id = 90120, Speaker = 1103, Text = '是否要离开转职大厅？', Option = '{离开这里,2}{稍等一下,1}', Emoji = 14}",
	[90121] = "{id = 90121, Speaker = 1104, Text = '哦呀~哦呀~看看是谁来了~这不是我们伟大的[c][ffff00][PlayerName][-][/c]嘛！', Emoji = 2}",
	[90122] = "{id = 90122, Speaker = 1104, Text = '和公会的传闻一样，你已经成为一名合格的老猎人了呀~', Emoji = 4}",
	[90123] = "{id = 90123, Speaker = 1104, Text = '你应该知道，这门技艺是没有终极的，下一步，该是你成为[c][ffff00]神射手[-][/c]的时候了呢，是不是已经期待很久了呀！嘻嘻嘻。', Emoji = 27}",
	[90124] = "{id = 90124, Speaker = 1104, Text = '接下来只需填一下这张神射手的申请表就可以了呢！赶快吧！', Option = '{开始测试,0}{稍等,2}', Emoji = 21}",
	[90125] = "{id = 90125, Speaker = 1104, Text = '啊哈哈哈~今天真的是个值得纪念的日子呢~就让我们抬头挺胸高高兴兴地去进行转职仪式吧~', Option = '{是否接受转职,6}{还没准备好,5}', Emoji = 17}",
	[90126] = "{id = 90126, Speaker = 1104, Text = '就让我们一起见证一名伟大的 神射手 的诞生吧！他必将为公会带来更多的光荣！', Option = '开始转职', Emoji = 20}",
	[90127] = "{id = 90127, Speaker = 1104, Text = '是否要离开转职大厅？', Option = '{离开这里,2}{稍等一下,1}', Emoji = 3}",
	[90128] = "{id = 90128, Speaker = 1105, Text = '(修女带着微笑缓缓地抬起了头)', Emoji = 11}",
	[90129] = "{id = 90129, Speaker = 1105, Text = '感谢主神，[c][ffff00][PlayerName][-][/c]，你所做的一切都为主神降临于人间的赐福增添了圣洁的光芒。', Emoji = 6}",
	[90130] = "{id = 90130, Speaker = 1105, Text = '教会希望你能将这份光芒带给大陆上更多的人们，当然，是以[c][ffff00]神官[-][/c]的身份！', Emoji = 30}",
	[90131] = "{id = 90131, Speaker = 1105, Text = '如果你已经拥有这份觉悟，便就此宣誓吧。', Option = '{开始测试,0}{稍等,2}', Emoji = 9}",
	[90132] = "{id = 90132, Speaker = 1105, Text = '赞美主神，让我们这就前往进行你[c][ffff00]神官[-][/c]的任职仪式吧。', Option = '{是否接受转职,6}{还没准备好,5}', Emoji = 30}",
	[90133] = "{id = 90133, Speaker = 1105, Text = '就让我们一起见证一名伟大的[c][ffff00]神官[-][/c]的诞生吧！愿主的光芒永远照耀你！', Option = '开始转职', Emoji = 30}",
	[90134] = "{id = 90134, Speaker = 1105, Text = '是否要离开转职大厅？', Option = '{离开这里,2}{稍等一下,1}', Emoji = 8}",
	[91007] = "{id = 91007, Speaker = 1008, Text = '欢迎你，冒险者！'}",
	[91064] = "{id = 91064, Speaker = 1068, Text = '我为我弟弟自豪！'}",
	[91065] = "{id = 91065, Speaker = 1068, Text = '马上要去依斯鲁得支援了。弟弟，保重啊！'}",
	[91114] = "{id = 91114, Speaker = 1002, Text = '我的心上人很久都没回家了，你看到他能马上通知我吗？'}",
	[93002] = "{id = 93002, Speaker = 1015, Text = '期待您的再次光临！', Emoji = 17, Action = {actionid = 34}}",
	[93003] = "{id = 93003, Speaker = 1015, Text = '已经存储好了呢，谢谢您的使用！', Emoji = 6, Action = {num = 1, actionid = 34}}",
	[93005] = "{id = 93005, Speaker = 1182, Text = '你愿意与你心中的挚爱一起登上我们的爱情摩天轮吗？', Option = '{Yes I do,2}{容我三思,1}'}",
	[93006] = "{id = 93006, Speaker = 1182, Text = 'Sorry~只有与你心爱的人组队才可以登上我们的爱情摩天轮哦~', Emoji = 16}",
	[93007] = "{id = 93007, Speaker = 1182, Text = 'Sorry~你确认你带对了你的另一半吗？', Emoji = 25}",
	[93008] = "{id = 93008, Speaker = 1182, Text = 'Sorry~登陆摩天轮需要消耗一张“爱的证明”哦！', Emoji = 13}",
	[93011] = "{id = 93011, Speaker = 1345, Text = '吉芬的那些魔法师算什么，想要获得伟大的生灵力量的话，可以向我许愿哦，当然也可以找我来解除一些不喜欢的魔法效果，收费的哦！', Option = '{赐予我力量吧,6}{解除魔法,29}'}",
	[93012] = "{id = 93012, Speaker = 1345, Text = '恭喜你已经通过了试炼，赐予你力量！'}",
	[93013] = "{id = 93013, Speaker = 1345, Text = '最后一步都无法坚持，赐你野猪之力，想要解除只需要找我认错即可。'}",
	[93014] = "{id = 93014, Speaker = 1345, Text = '不是也是给我变！~要恢复记得找我认罪。'}",
	[93015] = "{id = 93015, Speaker = 1345, Text = '本宝宝最讨厌半途而废的，不认罪就一直当猪吧~哼！'}",
	[93016] = "{id = 93016, Speaker = 1345, Text = '恭喜你贯彻了神猪之路！'}",
	[93017] = "{id = 93017, Speaker = 1345, Text = '晚了小野猪~哦呵呵呵呵呵~'}",
	[93018] = "{id = 93018, Speaker = 1345, Text = '你居然想消灭人类？太可怕了，不来忏悔你就一直当猪吧！'}",
	[93019] = "{id = 93019, Speaker = 1345, Text = '不喜欢波利也敢来找我？本宝宝不开心了，想要恢复请找我认罪。'}",
	[93020] = "{id = 93020, Speaker = 1345, Text = '波利哪里不可爱了！魂淡去猪圈里忏悔吧！'}",
	[93021] = "{id = 93021, Speaker = 1345, Text = '恭喜你通过考验，赐你本宝宝神力！'}",
	[93022] = "{id = 93022, Speaker = 1345, Text = '没准备？你来找我干嘛？猪啊！'}",
	[93023] = "{id = 93023, Speaker = 1345, Text = '既然你还没准备好，就继续你的“猪”生旅程吧！'}",
	[93024] = "{id = 93024, Speaker = 1345, Text = '算了原谅你了~马里麻利哄！'}",
	[93025] = "{id = 93025, Speaker = 1182, Text = '你要体验哪种摩天轮包间呢？', Option = '{1号,10}{2号,11}'}",
