	[93034] = "{id = 93034, Speaker = 2122, Text = '那真是太遗憾了…多好的伙伴啊！'}",
	[93038] = "{id = 93038, Speaker = 1166, Text = '冒险家击败魔物解决危机，我研究魔物拯救人类。', Emoji = 5}",
	[93044] = "{id = 93044, Speaker = 2077, Text = '欢迎~伟大的资深冒险家，我们为[c][ffff00]“资深冒险家”[-][/c]追加了一批新头饰~希望你会喜欢~'}",
	[93045] = "{id = 93045, Speaker = 2078, Text = '哇哦~~又到了追加新头饰的季节，我们为[c][ffff00]“王牌冒险家”[-][/c]追加了一批新头饰~希望你会喜欢~'}",
	[94010] = "{id = 94010, Speaker = 1182, Text = '初次见面，尊贵的客人，我们是致力于为大陆人们带来幸福和快乐的结社组织：[c][ffff00]幻想马戏团[-][/c]。', Emoji = 9}",
	[94011] = "{id = 94011, Speaker = 1182, Text = '而在下便是负责在此处魔法源泉搭建起这个[c][ffff00]梦幻摩天轮[-][/c]以供人们的娱乐的魔法建筑师：路德维希·西斯廷·巴特尔特。', Emoji = 20}",
	[94012] = "{id = 94012, Speaker = 1182, Text = '没错，正如您所听到的传闻一样，能来到这里一起游玩过的男女两人都爱神所祝福呦。', Emoji = 4}",
	[94013] = "{id = 94013, Speaker = 1182, Text = '啊哈~客人您只需要出示一张我们[c][ffff00]幻想马戏团[-][/c]特别提供的[c][ffff00]幻想马戏团门票[-][/c]便可带着你心仪之人进入游玩啦~', Emoji = 2}",
	[94014] = "{id = 94014, Speaker = 1182, Text = '那么，想与你(妳)心中的挚爱一起登上我们的[c][ffff00]梦幻摩天轮[-][/c]吗？', Option = '{Yes I do,2}{容我三思,1}'}",
	[94015] = "{id = 94015, Speaker = 1182, Text = '哎呀呀呀~尊贵的客人，只有与你心爱的人组队才可以登上我们的[c][ffff00]梦幻摩天轮[-][/c]哦~、'}",
	[94016] = "{id = 94016, Speaker = 1182, Text = '哎哎哎？！尊贵的客人，你确认你带对了你的另一半吗？！'}",
	[94017] = "{id = 94017, Speaker = 1182, Text = '哦呀哦呀~尊贵的客人，想要游玩[c][ffff00]梦幻摩天轮[-][/c]需要消耗一张[c][ffff00]幻想马戏团特别邀请券[-][/c]哦~'}",
	[94018] = "{id = 94018, Speaker = 1182, Text = '尊贵的客人，我们是致力于为大陆人们带来幸福和快乐的结社组织：[c][ffff00]幻想马戏团[-][/c]。', Emoji = 17}",
	[94043] = "{id = 94043, Speaker = 0, Text = '这附近似乎有时空裂隙出现，去找找看吧。'}",
	[94044] = "{id = 94044, Speaker = 0, Text = '这附近似乎有时空裂隙出现，去找找看吧。'}",
	[94045] = "{id = 94045, Speaker = 0, Text = '嗯？ 这是…'}",
	[94046] = "{id = 94046, Speaker = 2129, Text = '这里是米德加尔特旅游协会，我们的海龟可是很海底旅游必备的哦！'}",
	[94061] = "{id = 94061, Speaker = 2503, Text = '这里虽然很危险，但是也隐藏着无数好看的风景哦~~~'}",
	[95001] = "{id = 95001, Speaker = 1015, Text = '普隆德拉是卢恩·米德加尔特王国的首都，周边是城墙所包围构成的城下街，以北便是普隆德拉王城。而城墙外枝叶繁茂，拥有各种丰富的自然生态。同时，这里也是冒险者们组队和接受各种冒险任务的主要城市。'}",
	[95015] = "{id = 95015, Speaker = 1058, Text = '龙区是大陆上有数的神秘地区，生存了无数强大的生物，在冒险者们的齐心协力下，终于开始揭开了它神秘的面纱。'}",
	[95017] = "{id = 95017, Speaker = 1058, Text = '犬妖森林，神秘且强大的犬妖们栖息的地方，胆敢闯入森林中的人都会被犬妖消灭，但也有不怕死的冒险者进入森林试炼。'}",
	[95022] = "{id = 95022, Speaker = 1015, Text = '斐扬是隐藏在群山之中的山岳之城，与外面世界的隔绝，是的斐扬依然得以保留它的传统和地方特色，并未受到卢恩·米德加尔特王国的影响。'}",
	[96058] = "{id = 96058, Speaker = 1044, Text = '你是[c][ffff00][PlayerName][-][/c]吧！来得太及时了，最近运送物资路上屡次遭到魔物袭击，商队正忙着盘点货物。能麻烦你帮我们几个忙吗？', Option = '没问题！'}",
	[96081] = "{id = 96081, Speaker = 1185, Text = '回来了啊！护送完成得很出色嘛！相信卡博尔会尽早把宠物相关问题解决的！谢谢你啦！'}",
	[96102] = "{id = 96102, Speaker = 1069, Text = '看来旅行大嘴鸟很喜欢你。', Option = '骑上大嘴鸟'}",
	[96106] = "{id = 96106, Speaker = 1067, Text = '奥斯汀会让你了解伊米尔之心的故事。'}",
	[96112] = "{id = 96112, Speaker = 1412, Text = '无论是人类还是魔物，过量吸收粉尘都会狂暴化，感谢你，勇敢的冒险者。'}",
	[96140] = "{id = 96140, Speaker = 1054, Text = '乐园团的各种设施和便利服务已经都向你们开放了，非常期待你们的表现哦！', Emoji = 17}",
	[96141] = "{id = 96141, Speaker = 1188, Text = '完成得不错嘛，恭喜顺利通过考试项目一！(竟没被波伊坑到，运气真好！)', Emoji = 17}",
	[96142] = "{id = 96142, Speaker = 1024, Text = '提升冒险等级需要缴纳[UpAdvLvCost]哦。', Option = '{我有钱,2},{要钱没有,3}'}",
	[96143] = "{id = 96143, Speaker = 1024, Text = '您的钱包需要增肥了。'}",
	[96144] = "{id = 96144, Speaker = 1024, Text = '恭喜你冒险家。'}",
	[96146] = "{id = 96146, Speaker = 1058, Text = '你好~我是 [c][ffff00]音乐精灵[-][/c] [c][ffff00]艾莉儿[-][/c] ，世界第一美少女偶像！为了回馈大家的厚爱，我会用 [c][ffff00]爱的魔法为您治疗[-][/c]哦！在冒险中受到伤害的话，记得来找我呦，我会努力来治愈你的身心！', Emoji = 2}",
	[96147] = "{id = 96147, Speaker = 1058, Text = '迷路了吗？跟着魔法的指引，可以找到你的伙伴…', Option = '谢谢', Emoji = 4}",
	[96148] = "{id = 96148, Speaker = 1068, Text = '你来得正好，冒险者！讨伐队急需人手，下水道的情况仍然在恶化！', Option = '怎么回事？', Emoji = 6}",
	[96149] = "{id = 96149, Speaker = 1068, Text = '下水道作为城市地下管道的汇集地，常年阴暗潮湿，平时只需定时清理下即可。不过最近生活在下水道的魔物变得很不安分，所以才组织了讨伐队来解决问题以防事态升级。'}",
	[96150] = "{id = 96150, Speaker = 1068, Text = '由于近阶段全国上下魔物事件激增，虽然有志愿者报名，但讨伐队的人手依旧非常紧张。偏偏下水道是首都的地下重要设施，不赶紧把魔物清理掉的话可是会影响居民正常生活的。'}",
	[96151] = "{id = 96151, Speaker = 1068, Text = '你先准备下吧，一会可以找我或边上的[c][ffff00]莫戈沙[-][/c]再了解下，说不定能帮上很多，我们已经忙不过来了…', Option = '好的！'}",
	[96152] = "{id = 96152, Speaker = 1068, Text = '不错，干劲十足的样子啊！那就马上开工吧！我刚统计下水道工作人员名单，发现[c][ffff00]修理工彼得[-][/c]还没回站点，不知道是不是过来路上遇见危险了，能帮忙去找下吗？', Option = '没问题！'}",
	[96153] = "{id = 96153, Speaker = 1122, Text = '总算有人来了！好多魔物！怎么办！得立刻离开这个鬼地方！', Option = '别害怕', Emoji = 24}",
	[96154] = "{id = 96154, Speaker = 1122, Text = '收到反映有很多管道堵塞，结果过来一看，管道并没问题，原来是魔物数量过多在管道里乱窜导致的。不过清理魔物可不在我们修理工的工作范围内…'}",
	[96155] = "{id = 96155, Speaker = 1122, Text = '虽然有些魔物本身就在下水道里栖居，但现在不仅变得体型庞大而且还有狂暴的倾向，真伤脑筋。看！一波[c][ffff00]魔化的盗虫[-][/c]过来了！', Option = '交给我吧！'}",
	[96156] = "{id = 96156, Speaker = 1122, Text = '呼！太惊险了！想起来了！给，这是雷根公司新研发的[c][ffff00]诱鼠喷雾[-][/c]，使用后会引诱白鼠过来，清理完应该能暂时缓解下附近管道堵塞的状况。', Option = '这就开始'}",
	[96157] = "{id = 96157, Speaker = 1122, Text = '太好了！还想起件事，这是我之前无聊抓到的几只白鼠，本来想带回去当宠物的！现在看来这些小家伙对你们比较有帮助，作为活体样本带给[c][ffff00]拉姆[-][/c]好好研究下，说不定能发现些什么。'}",
	[96158] = "{id = 96158, Speaker = 1122, Text = '见到拉姆替我报声平安。我把附近的管道最后检查一遍就回去…你自己一路小心，带着魔物回去可能会有危险…', Option = '好哒！'}",
	[96159] = "{id = 96159, Speaker = 0, Text = '白鼠的吱吱声引来了同类的攻击…'}",
	[96160] = "{id = 96160, Speaker = 0, Text = '白鼠的吱吱声再次引来了同类的攻击…'}",
	[96161] = "{id = 96161, Speaker = 1121, Text = '彼得没事就好，谢谢你带回这些[c][ffff00]白鼠[-][/c]，一会就送到魔物研究院去检查下，应该会发现一些线索…'}",
	[96162] = "{id = 96162, Speaker = 1121, Text = '哟，太好了！总算来人帮忙了！下水道的魔物数量激增，靠我们这点人手完全不够。能帮忙一起先将清理下[c][ffff00]白鼠[-][/c]吗？', Option = '没问题！'}",
	[96163] = "{id = 96163, Speaker = 1121, Text = '呼，似乎数量少了点~悄悄告诉你哦，其实我很害怕小型魔物，尤其是[c][ffff00]白鼠[-][/c]。有次梦见它们在我身上乱爬，最后还蹿出一只[c][ffff00]蓝鼠[-][/c]，一下子就把我吓醒了，还失眠了好几天！'}",
	[96164] = "{id = 96164, Speaker = 1121, Text = '这样下去可不是办法…如果能采集一些魔物身上的样本进行研究，或许就能查出原因了，要不就从白鼠开始！'}",
	[96165] = "{id = 96165, Speaker = 1121, Text = '这些[c][ffff00]白鼠[-][/c]可能带有病毒！我需要采样一些它们的[c][ffff00]牙齿[-][/c]带回去做研究~我在这继续统计下水道的环境变化，你能帮我这个忙吗？', Option = '包在我身上！'}",
	[96166] = "{id = 96166, Speaker = 1121, Text = '谢谢你的帮助！果然有点异常，得好好研究下找找原因。拉姆好像有事找你，赶紧去看看吧~', Option = '好哒！', Emoji = 6}",
	[96184] = "{id = 96184, Speaker = 1068, Text = '有了活体样本白鼠和它的牙齿标本，相信魔物研究院那边很快就会有调查结果了。'}",
	[96185] = "{id = 96185, Speaker = 1068, Text = '对了，之前修理工波兹在咕哝着什么黄金的事，现在人也没影了，能麻烦你帮忙找下吗？', Option = '没问题！', Emoji = 4}",
	[96186] = "{id = 96186, Speaker = 1123, Text = '啊~可恶的盗虫！不要过来…冒险者，你来得太及时了，赶紧帮我消灭一些[c][ffff00]盗虫[-][/c]吧！', Option = '交给我吧！', Emoji = 1}",
	[96187] = "{id = 96187, Speaker = 1123, Text = '看在你帮忙的份上，告诉你个好事！我最近发现盗虫非常喜欢聚集在这，路过留心观察了附近，竟然发现管道里有金灿灿的亮光，估计是金块，和拉姆说了但他竟然不信！'}",
	[96188] = "{id = 96188, Speaker = 1123, Text = '哈哈哈！看到那团亮光没，好大的金块！我[c][ffff00]波兹[-][/c]的好日子总算要来了！咦？好像卡住了。帮我把好兄弟彼得叫来帮忙吧，完事后我们三个人平分！', Option = '我这就去！', Emoji = 17}",
	[96189] = "{id = 96189, Speaker = 1122, Text = '哇哦，发现金块了！竟然有这样的好事，[c][ffff00]波兹[-][/c]的运气可真好啊！赶紧过去看看！', Emoji = 13}",
	[96190] = "{id = 96190, Speaker = 1123, Text = '你们总算来啦！我刚看了，金块在管道里面卡住了一部分…得找工具疏通下，然后把它搬出来！前面不远处有维修备用的铲子，马上过去拿吧！'}",
	[96191] = "{id = 96191, Speaker = 1123, Text = '一人一把！很好，赶紧回去挖金块！'}",
	[96192] = "{id = 96192, Speaker = 1123, Text = '开始吧！太激动人心了！！快快快！！！', Option = '加油！', Emoji = 27}",
	[96193] = "{id = 96193, Speaker = 1123, Text = '哇！这金灿灿的光太让人陶醉了！', Emoji = 13}",
	[96194] = "{id = 96194, Speaker = 1122, Text = '哈哈哈！快出来了，继续努力挖！'}",
	[96195] = "{id = 96195, Speaker = 1122, Text = '等一下！有问题啊！！怎么金块看起来有点像巨大的昆虫后腿啊！！！莫非…', Emoji = 19}",
	[96196] = "{id = 96196, Speaker = 1123, Text = '天哪！不是金块！！是传说中的[c][ffff00]黄金虫[-][/c]！！！', Emoji = 1}",
	[96197] = "{id = 96197, Speaker = 1123, Text = '怎么会这样！赶紧逃啊！我们得马上去找拉姆报告下，好端端的下水道怎么会有黄金虫啊！', Option = '我来带路！', Emoji = 18}",
	[96198] = "{id = 96198, Speaker = 1068, Text = '竟然连黄金虫都出现了，看来事态有点严重，我马上和骑士团反映下情况。你们几个没事就好。'}",
	[96199] = "{id = 96199, Speaker = 1123, Text = '呜呜呜…我的金子啊…'}",
	[96200] = "{id = 96200, Speaker = 1122, Text = '好了啦，波兹，能捡回一条命已经不错了。看来还是勤劳工作靠谱点，哈哈！', Emoji = 25}",
	[96223] = "{id = 96223, Speaker = 1050, Text = '对卢西娅使用曼特疗伤药。'}",
	[96231] = "{id = 96231, Speaker = 1050, Text = '查看管道入口。'}",
	[96258] = "{id = 96258, Speaker = 1118, Text = '[c][ffff00][PlayerName][-][/c]，下水道的任务完成的非常好！普隆德拉终于有了一名像样的英雄，人民会感激你的！'}",
	[96259] = "{id = 96259, Speaker = 1118, Text = '你似乎不太高兴，我不清楚在下水道发生了什么事情，但是冒险者，这都是你成长的经历。'}",
	[96260] = "{id = 96260, Speaker = 1118, Text = '这是你成为冒险家路上，第一件值得记录并传颂的事迹，请把它好好地记录在你的冒险手册上吧！'}",
	[96261] = "{id = 96261, Speaker = 1118, Text = '承接冒险者手册引导任务。'}",
	[96363] = "{id = 96363, Speaker = 1307, Text = '圣光照耀着你，来选择一把喜欢的武器吧！', Option = '{锤,2},{法杖,3}'}",
	[96364] = "{id = 96364, Speaker = 1262, Text = '只要我有，只要你要，我都愿意给你，不过得拿乐园币来换哦！'}",
	[96365] = "{id = 96365, Speaker = 1398, Text = '魔王的力量仍旧残存于普勒花园里。'}",
	[96371] = "{id = 96371, Speaker = 1398, Text = '只有牵着心爱之人的手，才可以让艾卡拉奇的植物生长。'}",
	[96381] = "{id = 96381, Emoji = 4}",
	[96382] = "{id = 96382, Speaker = 1398, Text = '去吧，用你们的爱意来唤醒那些植物吧！'}",
	[96432] = "{id = 96432, Speaker = 2140, Text = '欢迎光临！'}",
	[96433] = "{id = 96433, Speaker = 2141, Text = '欢迎光临！'}",
	[96434] = "{id = 96434, Speaker = 2142, Text = '欢迎光临！'}",
	[96435] = "{id = 96435, Speaker = 2143, Text = '欢迎光临！'}",
	[96438] = "{id = 96438, Speaker = 2146, Text = '欢迎光临！'}",
	[96439] = "{id = 96439, Speaker = 2147, Text = '欢迎光临！'}",
	[96440] = "{id = 96440, Speaker = 2148, Text = '欢迎光临！'}",
	[96441] = "{id = 96441, Speaker = 2149, Text = '欢迎光临！'}",
	[96442] = "{id = 96442, Speaker = 2150, Text = '欢迎光临！'}",
	[96443] = "{id = 96443, Speaker = 2151, Text = '欢迎光临！'}",
	[96450] = "{id = 96450, Speaker = 2503, Text = '深海中居然有着如此巨大的神殿…他的主人到底是谁呢？'}",
	[96455] = "{id = 96455, Speaker = 1195, Text = '魔物的狂暴已经深入到了普隆德拉的下水道二层，请尽快前往那里，避免皇城深陷危机。'}",
	[96456] = "{id = 96456, Speaker = 1195, Text = '虽然北部森林容易让人迷路，但我们也绝不允许那里滋生太多的狂暴魔物。'}",
	[96457] = "{id = 96457, Speaker = 1195, Text = '幽灵船的浮现让依斯鲁得的居民身陷危机，请尽快出发。'}",
	[96458] = "{id = 96458, Speaker = 1195, Text = '海底洞穴的建筑群也是探险队常去的地方，请清理那里的魔物保证安全。'}",
	[96459] = "{id = 96459, Speaker = 1195, Text = '传说中女神居住的地方，请帮助吉芬的法师抗击山脉的魔物。'}",
	[96460] = "{id = 96460, Speaker = 1195, Text = '蚂蚁洞穴的危机深深的影响着梦罗克城的安全，请尽快抗击洞穴内的魔物。'}",
	[96461] = "{id = 96461, Speaker = 1195, Text = '斐杨森林的魔物骚乱一直没停止过，这一次威胁到了斐杨城的安全了，请去削减那里魔物的数量。'}",
	[96462] = "{id = 96462, Speaker = 1195, Text = '金字塔内封印者魔王的一部分，请肃清金字塔内狂暴的魔物，防止封印的进一步削弱。'}",
	[96463] = "{id = 96463, Speaker = 1195, Text = '可怕的斐杨洞穴，至今我忘记不了那里阴森的气息，请阻止那里狂暴的魔物。'}",
	[96464] = "{id = 96464, Speaker = 1195, Text = '龙区已经非常接近克雷斯特汉姆古城，请立刻出发前往那里镇压魔物。'}",
	[96465] = "{id = 96465, Speaker = 1195, Text = '古城的魔物骚乱只是噩梦的开始，无乱如何请镇压那里的狂暴魔物。'}",
