	[97044] = "{id = 97044, Speaker = 0, Text = '这一次，世界由我来守护！！'}",
	[100023] = "{id = 100023, Speaker = 0, Text = '一张人体实验的图纸，似乎是置身研究所内。'}",
	[100024] = "{id = 100024, Speaker = 0, Text = '桌面上凌乱的摆放着各种可怕的实验方案。'}",
