	[100031] = "{id = 100031, Speaker = 0, Text = '记忆幻影中的男子，将手中试管里的绿色液体一饮而尽。'}",
	[100032] = "{id = 100032, Speaker = 5724, Text = '我感觉…身体有些热…'}",
	[100033] = "{id = 100033, Speaker = 5724, Text = '咳咳咳...咳咳'}",
