	[100388] = "{id = 100388, Text = '主人越来越有强者的气息了！'}",
	[100389] = "{id = 100389, Text = '战场是无情的，主人请牢记！'}",
	[100390] = "{id = 100390, Text = '战争总会有牺牲。'}",
	[100391] = "{id = 100391, Text = '吃一堑长一智！'}",
