	[100396] = "{id = 100396, Text = '只有吃饱了才能全力战斗！啊呜~'}",
	[100397] = "{id = 100397, Text = '男人就应该默默的吃干净！啊呜~'}",
	[100398] = "{id = 100398, Text = '坚持住，主人！'}",
	[100399] = "{id = 100399, Text = '主人！你负伤啦，赶紧喝口药水！'}",
	[100400] = "{id = 100400, Text = '医疗兵！医疗兵在哪里？？'}",
	[100401] = "{id = 100401, Text = '这是对在下的军功的嘉奖么，主人？'}",
