	[100410] = "{id = 100410, Text = '人是铁，饭是钢，玩具士兵饿得慌！'}",
	[100411] = "{id = 100411, Text = '在下好像听到了吃饭的集结号！'}",
	[100412] = "{id = 100412, Text = '在下的胡子都饿得失去了光泽...'}",
	[100413] = "{id = 100413, Text = '虽然在下手笨，但会努力做出主人喜欢的头饰！'}",
