	[100415] = "{id = 100415, Text = '精工出细活。'}",
	[100416] = "{id = 100416, Text = '主人给的礼物，在下会用性命珍惜！'}",
	[100417] = "{id = 100417, Text = '这是主人对在下军功的肯定。'}",
	[100418] = "{id = 100418, Text = '主人对在下的恩情在下没齿难忘！'}",
	[100419] = "{id = 100419, Text = '别太小看玩具士兵的尊严！'}",
	[100420] = "{id = 100420, Text = '我只认可强者！而你不够资格！'}",
