	[100425] = "{id = 100425, Text = '你的坚持打动了在下！'}",
	[100426] = "{id = 100426, Text = '在下誓愿追随你！'}",
	[100427] = "{id = 100427, Text = '一二！一二！请不要阻碍在下行军！'}",
	[100428] = "{id = 100428, Text = '这不是演习！这不是演习！闲杂人等速速退让！'}",
