	[100432] = "{id = 100432, Text = '主人~主人~熊熊好想你呀~'}",
	[100433] = "{id = 100433, Text = '终于从黑不溜秋的宠物蛋里溜出来啦！ '}",
	[100434] = "{id = 100434, Text = '玩具熊应该呆在宽敞舒适的礼盒里！而不是蛋！'}",
