	[100439] = "{id = 100439, Text = '和熊熊打勾勾，要再来找我玩唷。'}",
	[100440] = "{id = 100440, Text = '不能做抛弃玩具的坏孩子唷！'}",
	[100441] = "{id = 100441, Text = '这就是主人的公会嘛？好热闹吖~'}",
	[100442] = "{id = 100442, Text = '人群之中，只有主人是我唯一的爱！'}",
	[100443] = "{id = 100443, Text = '我是不是公会里最可爱的熊？'}",
	[100444] = "{id = 100444, Text = '万岁~这是主人好人有好报。'}",
	[100445] = "{id = 100445, Text = '熊熊让你平时多做好事，灵验了吧？'}",
	[100446] = "{id = 100446, Text = '有玩具熊的人，运气都不会太差！'}",
	[100447] = "{id = 100447, Text = '别灰心，熊熊是主人软绵绵的后盾！！'}",
