	[100449] = "{id = 100449, Text = '精炼还不如给熊熊买个新礼盒...'}",
	[100450] = "{id = 100450, Text = '熊熊的报恩时间~主人收下礼物吧。'}",
	[100451] = "{id = 100451, Text = '这是熊熊对主人的爱！'}",
	[100452] = "{id = 100452, Text = '最好的宝贝就应该送给最喜欢的主人~'}",
	[100453] = "{id = 100453, Text = '升级以后我摸起来更软了唷。'}",
