	[100458] = "{id = 100458, Text = '主人要记得把熊熊修好...'}",
	[100459] = "{id = 100459, Text = '你的双手，是我的心之所向，梦之所属。'}",
	[100460] = "{id = 100460, Text = '这时候熊熊深深感受到生而为熊的幸福~'}",
