	[100586] = "{id = 100586, Speaker = 5809, Text = '[c][ffff00][PlayerName][-][/c]，塞壬，首先恭喜你们顺利解读了我给你们传达的暗号。'}",
	[100587] = "{id = 100587, Speaker = 5809, Text = '成功的抵达——秘密之翼的总部。', Option = '秘密之翼'}",
