	[100592] = "{id = 100592, Speaker = 5809, Text = '我虽然身为朱诺的大总统，表面上掌握政治商业与军事。'}",
	[100593] = "{id = 100593, Speaker = 5809, Text = '但遗憾的是，我的手上并没有太多实权。'}",
	[100594] = "{id = 100594, Speaker = 5809, Text = '因为雷根贝勒公司的势力太过强大，已经基本垄断了朱诺的商业命脉。', Option = '什么'}",
	[100595] = "{id = 100595, Speaker = 5809, Text = '不仅仅是商业！'}",
	[100596] = "{id = 100596, Speaker = 5809, Text = '还有一半以上的兵权被暗握在雷根贝勒公司手中。'}",
	[100597] = "{id = 100597, Speaker = 5809, Text = '但经过我多年的调查，发现这个公司并没有我们想像的那么简单。', Option = '哦？'}",
	[100598] = "{id = 100598, Speaker = 5809, Text = '我发现其中有许多阴谋和黑暗深不见底。'}",
	[100599] = "{id = 100599, Speaker = 5809, Text = '朱诺人民光明的未来不能毁在这个阴暗的公司手中！'}",
	[100600] = "{id = 100600, Speaker = 5809, Text = '所以我成立了反抗雷根贝勒公司的秘密组织——秘密之翼。'}",
	[100601] = "{id = 100601, Speaker = 5809, Text = '我联合了卢恩王国和共和国内优秀的精英冒险者们。'}",
	[100602] = "{id = 100602, Speaker = 5809, Text = '我怀疑，这次魔物袭击水晶的事件，以及你们所说的生化怪物。'}",
	[100603] = "{id = 100603, Speaker = 5809, Text = '都和雷根贝勒脱不了干系！', Option = '是嘛'}",
	[100604] = "{id = 100604, Speaker = 5809, Text = '但我的权力受到压制，无法直接出面，所以只能派出秘密之翼来帮助你们！'}",
	[100605] = "{id = 100605, Speaker = 5809, Text = '当然，其中还包括你认识的人，冒险者。'}",
	[100606] = "{id = 100606, Speaker = 0, Text = '是赛依连以及他的小队！'}",
