	[102672] = "{id = 102672, Speaker = 4687, Text = '这里真是太危险了，智慧残卷还是放在你身上安全。'}",
	[102673] = "{id = 102673, Speaker = 4687, Text = '那我就先回老师那儿去了。'}",
	[102674] = "{id = 102674, Speaker = 4687, Text = '你记得待会儿去老师那里找我噢。'}",
	[102754] = "{id = 102754, Speaker = 0, Text = '杰拉德和四位学生按顺序开始了仪式的咏唱。'}",
	[102755] = "{id = 102755, Speaker = 4055, Text = '伟大的智慧巨人密米尔啊……'}",
	[102756] = "{id = 102756, Speaker = 4056, Text = '我们以人类虔诚的求知欲向您请求……'}",
	[102757] = "{id = 102757, Speaker = 4057, Text = '如果您在遥远的另一头听见了呼唤……'}",
	[102758] = "{id = 102758, Speaker = 4058, Text = '哪怕传授我们知识的冰山一角……'}",
	[102759] = "{id = 102759, Speaker = 4059, Text = '……', Emoji = 14}",
	[102760] = "{id = 102760, Speaker = 4055, Text = '格拉，你在等什么，就差你的最后一句咏唱了！', Emoji = 7}",
	[102761] = "{id = 102761, Speaker = 4056, Text = '是啊，格拉，快说话啊，为什么阴沉着脸？'}",
	[102762] = "{id = 102762, Speaker = 4059, Text = '哈哈哈哈哈……愚蠢的人啊。', Emoji = 5}",
	[102763] = "{id = 102763, Speaker = 4059, Text = '你们真的以为我是你们的同类么？'}",
	[102764] = "{id = 102764, Speaker = 4059, Text = '这具身体原本的精神早就被我以伪神的力量侵蚀了！'}",
	[102765] = "{id = 102765, Speaker = 4059, Text = '最后一句咏唱，听好吧……'}",
	[102766] = "{id = 102766, Speaker = 4059, Text = '我以恶魔的名义向异界征召，回归吧，属于我原本的魔力！'}",
	[102767] = "{id = 102767, Speaker = 4060, Text = '哈哈哈，我终于拿回了自己的魔力。'}",
	[102768] = "{id = 102768, Speaker = 4060, Text = '还顺便偷了一具年轻的肉体。', Emoji = 5}",
	[102769] = "{id = 102769, Speaker = 4055, Text = '你……究竟是什么时候侵占了格拉的身体和精神的？！'}",
	[102770] = "{id = 102770, Speaker = 4060, Text = '哼，他第一次被智慧井拉入异世界的地方，就是囚禁我的牢笼。'}",
	[102771] = "{id = 102771, Speaker = 4060, Text = '原本我以为要被他消灭了，可惜这个人类实在太弱了，竟然连失去大部分魔力的我都无法抵抗。'}",
	[102772] = "{id = 102772, Speaker = 4060, Text = '早在那个时候我就顺势侵占了他的精神了。'}",
	[102773] = "{id = 102773, Speaker = 4060, Text = '现在，这场召唤仪式的魔力通道变成了我取回伪神魔力的通道。'}",
	[102774] = "{id = 102774, Speaker = 4060, Text = '人类！为我的重生而献祭吧！'}",
	[102775] = "{id = 102775, Speaker = 4055, Text = '混账！你休想侵占我学生的身体来实施恶行！'}",
	[102776] = "{id = 102776, Speaker = 4055, Text = '冒险者，快！'}",
	[102777] = "{id = 102777, Speaker = 4055, Text = '伪神融合精神需要时间，他现在还没完全和格拉合为一体。'}",
	[102778] = "{id = 102778, Speaker = 4055, Text = '只要现在打败他，就能救回格拉！', Option = '知道了！'}",
	[102779] = "{id = 102779, Speaker = 0, Text = '随着你的最后一击，伪神的魔力逐渐消散了。'}",
	[102780] = "{id = 102780, Speaker = 4055, Text = '格拉，格拉，你快醒醒！', Emoji = 18}",
	[102781] = "{id = 102781, Speaker = 4056, Text = '呜呜呜，格拉你别死啊！'}",
	[102782] = "{id = 102782, Speaker = 4057, Text = '坚持住啊，格拉！'}",
	[102783] = "{id = 102783, Speaker = 4058, Text = '格拉，我们相信你啊！'}",
	[102784] = "{id = 102784, Speaker = 0, Text = '……格拉渐渐有了呼吸。'}",
	[102785] = "{id = 102785, Speaker = 4059, Text = '我……这是怎么了？我只记得我被吸进智慧井，然后……'}",
	[102786] = "{id = 102786, Speaker = 4055, Text = '别说了格拉，是我没有保护好你，是我没有保护好你们。'}",
	[102787] = "{id = 102787, Speaker = 4055, Text = '你能回来就好，回来就好。', Emoji = 24}",
	[102788] = "{id = 102788, Speaker = 4055, Text = '冒险者，这次真的是多亏有你在，才没有酿成大祸。'}",
	[102789] = "{id = 102789, Speaker = 4055, Text = '我以贤者学院的名义向你表示最真诚的感谢！'}",
	[102790] = "{id = 102790, Speaker = 4055, Text = '作为谢礼，我希望将密米尔号角送给你。', Option = '什么？'}",
	[102791] = "{id = 102791, Speaker = 4055, Text = '密米尔号角经过这次召唤，已经耗尽了大部分魔力了，留在我身边也只是个摆设。'}",
	[102792] = "{id = 102792, Speaker = 4055, Text = '我想你可以带着它继续冒险，让号角在更遥远的地方，找回原有的魔力。'}",
	[102793] = "{id = 102793, Speaker = 4055, Text = '希望密米尔号角能被你再次吹响，响彻整个大陆的天空！'}",
	[102803] = "{id = 102803, Speaker = 4055, Text = '冒险者，你准备好见证神迹了么？', Option = '准备好了'}",
	[102804] = "{id = 102804, Speaker = 4056, Text = '冒险者，你准备好见证神迹了么？', Option = '准备好了'}",
	[102805] = "{id = 102805, Speaker = 4057, Text = '冒险者，你准备好见证神迹了么？', Option = '准备好了'}",
	[102806] = "{id = 102806, Speaker = 4058, Text = '冒险者，你准备好见证神迹了么？', Option = '准备好了'}",
	[102807] = "{id = 102807, Speaker = 4059, Text = '冒险者，你准备好见证神迹了么？', Option = '准备好了'}",
	[102808] = "{id = 102808, Speaker = 4060, Text = '哈哈哈哈哈……愚蠢的人类啊。'}",
	[102818] = "{id = 102818, Speaker = 5881, Text = '你们终究还是来了呀~'}",
	[102819] = "{id = 102819, Speaker = 5881, Text = '很有勇气。', Option = '什么意思'}",
	[102820] = "{id = 102820, Speaker = 5881, Text = '我当然是~嘿嘿'}",
	[102821] = "{id = 102821, Speaker = 5881, Text = '热烈欢迎咯~'}",
	[102822] = "{id = 102822, Speaker = 5882, Text = '少来！'}",
	[102823] = "{id = 102823, Speaker = 5881, Text = '好了，我们说正事。'}",
	[102824] = "{id = 102824, Speaker = 5881, Text = '这的确，是塞壬父亲以前办公室的旧址。'}",
	[102825] = "{id = 102825, Speaker = 5882, Text = '什么！！', Emoji = 7}",
	[102826] = "{id = 102826, Speaker = 5881, Text = '这房间里所有的一切。'}",
	[102827] = "{id = 102827, Speaker = 5881, Text = '都属于你父亲。'}",
	[102828] = "{id = 102828, Speaker = 5881, Text = '现在属于你了。'}",
	[102829] = "{id = 102829, Speaker = 0, Text = '塞壬一脸怀疑和惊讶的看着眼前有些疯疯癫癫的研究员。'}",
	[102830] = "{id = 102830, Speaker = 5881, Text = '是时候让你们知道真相了。'}",
	[102831] = "{id = 102831, Speaker = 5881, Text = '去打开那只木箱吧。'}",
	[102832] = "{id = 102832, Speaker = 0, Text = '封尘的木箱被缓缓打开，阳光照在箱子上扬起一层薄薄的灰尘。'}",
	[102833] = "{id = 102833, Speaker = 0, Text = '箱子里躺着一把精致的小匕首，看的出有些岁月，但依然十分锋利。'}",
	[102834] = "{id = 102834, Speaker = 0, Text = '接过匕首的塞壬眼角抽搐了一下。'}",
	[102835] = "{id = 102835, Speaker = 5882, Text = '这是我父亲的匕首，上面刻着他的名字…', Emoji = 14}",
	[102836] = "{id = 102836, Speaker = 5882, Text = '你们把我父亲怎么样了！！！'}",
	[102837] = "{id = 102837, Speaker = 5881, Text = '你们不要冲动。'}",
	[102838] = "{id = 102838, Speaker = 5881, Text = '我不想伤害你们，只想告诉你真相，塞壬。'}",
	[102839] = "{id = 102839, Speaker = 5882, Text = '是什么！！'}",
	[102840] = "{id = 102840, Speaker = 5881, Text = '你父亲当年为我工作，我们一起参与了索斯计划。', Option = '索斯计划？'}",
	[102841] = "{id = 102841, Speaker = 5881, Text = '这是你父亲的研究，他发明了让人体变强壮的基因药剂。'}",
	[102842] = "{id = 102842, Speaker = 5881, Text = '我们实验的第一个对象就是索斯。', Option = '然后呢'}",
	[102843] = "{id = 102843, Speaker = 5881, Text = '可惜索斯是个失败品，他没有控制自己思维的能力。'}",
	[102844] = "{id = 102844, Speaker = 5881, Text = '你父亲他…'}",
	[102845] = "{id = 102845, Speaker = 5882, Text = '什么！！'}",
	[102846] = "{id = 102846, Speaker = 5881, Text = '惨死在失控的索斯手下…'}",
	[102847] = "{id = 102847, Speaker = 5881, Text = '塞壬，你父亲很爱你。'}",
	[102848] = "{id = 102848, Speaker = 5881, Text = '他奄奄一息的对我说，让我把这只匕首交给你。'}",
	[102849] = "{id = 102849, Speaker = 5881, Text = '你一定要为他…'}",
	[102850] = "{id = 102850, Speaker = 5881, Text = '报仇！'}",
	[102851] = "{id = 102851, Speaker = 5882, Text = '不可能！', Emoji = 7}",
	[102852] = "{id = 102852, Speaker = 5882, Text = '我父亲还活着！！！'}",
	[102853] = "{id = 102853, Speaker = 5882, Text = '我不相信！！！！！'}",
	[102854] = "{id = 102854, Speaker = 5882, Text = '这不是真的！！！！！！！！'}",
	[102855] = "{id = 102855, Speaker = 5883, Text = '塞壬，你冷静一点！'}",
	[102856] = "{id = 102856, Speaker = 5882, Text = '不！！！！'}",
	[102857] = "{id = 102857, Speaker = 0, Text = '塞壬根本不顾赛尼亚的劝阻，悲愤的冲出了宝伏的办公室。', Emoji = 18}",
	[102890] = "{id = 102890, Speaker = 5878, Text = '不许你们伤害索斯！！', Emoji = 12}",
	[102891] = "{id = 102891, Speaker = 5882, Text = '叽叽喳喳的吵死了。'}",
	[102892] = "{id = 102892, Speaker = 5878, Text = '！！！', Emoji = 12}",
	[102893] = "{id = 102893, Speaker = 0, Text = '这道栏杆似乎放不下来！'}",
	[102894] = "{id = 102894, Speaker = 5882, Text = '我见过这种机关！这是熔洞牢栏。'}",
	[102895] = "{id = 102895, Speaker = 5882, Text = '是那些可恶的熔岩恶魔铸出来的!'}",
	[102896] = "{id = 102896, Speaker = 5882, Text = '我们得把路上的熔岩恶魔全都清理掉！'}",
	[102897] = "{id = 102897, Speaker = 5882, Text = '现在应该能降下牢栏了！'}",
	[102898] = "{id = 102898, Speaker = 5883, Text = '你们小心！是熔洞领主的火焰尊魔！'}",
	[102899] = "{id = 102899, Speaker = 5883, Text = '能遇到它，看来今天我们的运气真的有些差。'}",
	[102900] = "{id = 102900, Speaker = 5882, Text = '火焰挡住了通往前方的路！'}",
	[102901] = "{id = 102901, Speaker = 5882, Text = '攻击尊魔本身是无效的！'}",
	[102902] = "{id = 102902, Speaker = 5882, Text = '把它身上分出来的火焰全部消灭才能消灭它！'}",
	[102903] = "{id = 102903, Speaker = 5882, Text = '快看！挡住我们的火焰墙消失了！'}",
	[102904] = "{id = 102904, Speaker = 0, Text = '随着轰隆声，火焰墙熄灭了…'}",
	[102905] = "{id = 102905, Speaker = 0, Text = '仿佛是里面的怪物已放弃了抵抗。'}",
	[102906] = "{id = 102906, Speaker = 5882, Text = '住手！'}",
	[102907] = "{id = 102907, Speaker = 5882, Text = '把它交给我，这件事必须我自己来完成。'}",
	[102908] = "{id = 102908, Speaker = 0, Text = '塞壬掏出了父亲给他留下的匕首。'}",
	[102909] = "{id = 102909, Speaker = 5882, Text = '怪物！！为什么不反击！！！'}",
	[102910] = "{id = 102910, Speaker = 5882, Text = '反击啊！！！'}",
	[102911] = "{id = 102911, Speaker = 5883, Text = '塞壬…'}",
	[102912] = "{id = 102912, Speaker = 5883, Text = '杀了它，你的父亲也不会回来了。'}",
	[102913] = "{id = 102913, Speaker = 5883, Text = '放过它吧...也放过你自己…'}",
	[102914] = "{id = 102914, Speaker = 5882, Text = '…', Emoji = 18}",
	[102915] = "{id = 102915, Speaker = 0, Text = '塞壬低头看了看父亲的匕首，面无表情的看着眼前的怪物。'}",
	[102916] = "{id = 102916, Speaker = 0, Text = '空洞的眼神里看不到丝毫的怜悯。'}",
	[102917] = "{id = 102917, Speaker = 5882, Text = '不反击就下地狱去吧！'}",
	[102918] = "{id = 102918, Speaker = 5882, Text = '这刀，为了我父亲！！！！！'}",
	[102919] = "{id = 102919, Speaker = 0, Text = '索斯像一只将死的巨兽般倒在地上。'}",
	[102920] = "{id = 102920, Speaker = 0, Text = '从索斯居然身上掉出了一根与塞壬脖子上一模一样的项链。'}",
	[102921] = "{id = 102921, Speaker = 5876, Text = '塞…壬…'}",
	[102922] = "{id = 102922, Speaker = 5876, Text = '我的…儿…'}",
	[102923] = "{id = 102923, Speaker = 5876, Text = '子…'}",
	[102924] = "{id = 102924, Speaker = 5882, Text = '什么！！！', Emoji = 7}",
	[102925] = "{id = 102925, Speaker = 5882, Text = '你说什么！！！！！'}",
	[102926] = "{id = 102926, Speaker = 5882, Text = '不，你不是…'}",
	[102927] = "{id = 102927, Speaker = 5882, Text = '你给我起来！！！'}",
	[102928] = "{id = 102928, Speaker = 0, Text = '索斯的眼里慢慢没有了光…'}",
	[102929] = "{id = 102929, Speaker = 0, Text = '无论塞壬如何击打它都没有再醒来。'}",
	[102930] = "{id = 102930, Speaker = 5883, Text = '不好！随着索斯的死，索斯创造出的这个空间开始塌方了。'}",
	[102931] = "{id = 102931, Speaker = 5883, Text = '我们必须马上离开这里！'}",
	[102932] = "{id = 102932, Speaker = 5882, Text = '不，我还没有问清楚到底是怎么回事。'}",
	[102933] = "{id = 102933, Speaker = 5882, Text = '我还没有问清楚！！！'}",
	[102934] = "{id = 102934, Speaker = 5883, Text = '没时间了，我们现在就得离开！！'}",
	[103056] = "{id = 103056, Speaker = 4706, Text = '哼，你就是这支羽毛笔的主人么？'}",
	[103057] = "{id = 103057, Speaker = 4706, Text = '受死吧！', Option = '！？'}",
	[103058] = "{id = 103058, Speaker = 4706, Text = '哈，我终于报了人类的仇了。', Emoji = 5}",
	[103059] = "{id = 103059, Speaker = 4706, Text = '别做无用的反抗了。'}",
	[103060] = "{id = 103060, Speaker = 4706, Text = '我在爪子上施了剧毒，'}",
	[103061] = "{id = 103061, Speaker = 4706, Text = '你现在只能任我宰割。', Option = '……'}",
	[103062] = "{id = 103062, Speaker = 4706, Text = '嘴硬是么，竟然还不求饶认错？', Option = '求饶认错？'}",
	[103063] = "{id = 103063, Speaker = 4706, Text = '人类果然是虚伪的动物，'}",
	[103064] = "{id = 103064, Speaker = 4706, Text = '死到临头还假装无辜。'}",
	[103065] = "{id = 103065, Speaker = 4706, Text = '我来告诉你，你们的罪行吧。', Option = '……'}",
	[103066] = "{id = 103066, Speaker = 4706, Text = '几天前，魔物怪盗团拉我入伙。'}",
	[103067] = "{id = 103067, Speaker = 4706, Text = '我本来不想帮他们偷东西的，但是…'}",
	[103068] = "{id = 103068, Speaker = 4706, Text = '他们告诉我人类正在用鸟人哈比的羽毛，'}",
	[103069] = "{id = 103069, Speaker = 4706, Text = '制作人类使用的羽毛笔！', Option = '这…', Emoji = 12}",
	[103070] = "{id = 103070, Speaker = 4706, Text = '我很愤怒，所以我不仅答应了帮他们偷人类的东西。'}",
	[103071] = "{id = 103071, Speaker = 4706, Text = '还用偷来的羽毛笔在这做了陷阱，'}",
	[103072] = "{id = 103072, Speaker = 4706, Text = '就是等待你这样的人类上钩！'}",
	[103073] = "{id = 103073, Speaker = 4706, Text = '怎么样，被我的剧毒伤到的感觉？'}",
	[103074] = "{id = 103074, Speaker = 4706, Text = '这就是你们利用鸟人的毛，为自己牟利的下场！', Option = '对…不起'}",
	[103075] = "{id = 103075, Speaker = 4706, Text = '以为道歉就有用了么？'}",
	[103076] = "{id = 103076, Speaker = 4706, Text = '我才不相信你们人类假惺惺的眼泪。', Emoji = 12}",
	[103077] = "{id = 103077, Speaker = 4706, Text = '接下来我还要引诱更多人类落入陷阱，'}",
	[103078] = "{id = 103078, Speaker = 4706, Text = '被我的剧毒折磨至死。', Option = '不要啊'}",
	[103079] = "{id = 103079, Speaker = 4706, Text = '噢？你想保护其他人类？'}",
	[103080] = "{id = 103080, Speaker = 4706, Text = '……'}",
	[103081] = "{id = 103081, Speaker = 4706, Text = '好，你要是有勇气在中毒的情况下，'}",
	[103082] = "{id = 103082, Speaker = 4706, Text = '连续抗下我三次法术。'}",
	[103083] = "{id = 103083, Speaker = 4706, Text = '我就放过其他人类。', Option = '一言为定！'}",
	[103084] = "{id = 103084, Speaker = 4706, Text = '接招吧，这是第一击！', Option = '忍痛承受'}",
	[103085] = "{id = 103085, Speaker = 4706, Text = '哼，看你能死撑多久，第二击！', Option = '继续承受'}",
	[103086] = "{id = 103086, Speaker = 4706, Text = '你…你为什么还不躲开？'}",
	[103087] = "{id = 103087, Speaker = 4706, Text = '为了保护那些人类，你就真的连牺牲自己都不后悔么？', Option = '我不后悔'}",
	[103088] = "{id = 103088, Speaker = 4706, Text = '……', Emoji = 14}",
	[103089] = "{id = 103089, Speaker = 4706, Text = '好，那我成全你。'}",
	[103090] = "{id = 103090, Speaker = 0, Text = '身上的剧毒被解开了，感觉好了很多。'}",
	[103091] = "{id = 103091, Speaker = 4706, Text = '算了，毒我已经帮你解了。'}",
	[103092] = "{id = 103092, Speaker = 4706, Text = '没见过你这么傻的人类，真是无聊。'}",
	[103093] = "{id = 103093, Speaker = 4706, Text = '……', Emoji = 14}",
	[103094] = "{id = 103094, Speaker = 4706, Text = '还愣着干嘛，收好你的破羽毛笔赶紧走！', Option = '嗯…对不起'}",
	[103095] = "{id = 103095, Speaker = 0, Text = '这精致的羽毛笔或许就是失窃物之一。'}",
